INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(76, 16, 'Chương 1: Tôi không hối hận', 'chuong-1-toi-khong-hoi-han', 1, 'aa', '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độ ấm từ sau lưng chậm rãi vây quanh, hô hấp nóng bỏng ở bên tai: “Sợ không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơi thở xa lạ quanh quẩn bên tai khiến người ta lạnh lẽo đến không dám lên tiếng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp như cảm giác được người đàn ông hơi khựng lại, sau đó lại vang lên giọng nói của anh: “Bây giờ hối hận vẫn còn kịp.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô căng thẳng siết chặt hai tay, lắc đầu: “Tôi không hối hận…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đang ở trong thời kỳ xinh đẹp nhất, nhưng…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một đêm này đau đớn mà dài lâu…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cuối cùng nửa đêm về sáng người đàn ông đứng dậy đi vào phòng tắm, Lâm Tử Lạp mới kéo thân thể mệt mỏi, mặc đồ ra khỏi phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dưới lầu khách sạn có người phụ nữ trung niên giới thiệu công việc này cho cô đang đứng đó, thấy Lâm Tử Lạp đi tới, bà ta đưa cho cô một cái túi màu đen: “Đây là tiền thù lao của cô.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp gần như không chút do dự nhận lấy, cầm tiền nhanh chóng chạy đi, thậm chí còn không quan tâm sự đau đớn dưới thân, chỉ muốn nhanh chóng đến bệnh viện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bầu trời vẫn chưa sáng khiến hành lang rất im lặng, dưới đất trước phòng phẫu thuật để hai cái cáng cứu thương, vì không đóng tiền nên không được đưa vào phòng phẫu thuật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhìn mà đau lòng không thôi, nức nở nói: “Tôi có tiền, mau cứu mẹ và em trai tôi đi…” Cô nghẹn ngào đưa tiền trong tay cho bác sĩ, bác sĩ nhìn thoáng qua, đưa cho y tá kiểm kê, sau đó mới kêu nhân viên đưa người bị thương vào trong phòng phẫu thuật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không thấy bọn họ đẩy em trai mình vào, vội vàng nhào lên bắt lấy bác sĩ cầu xin: “Còn cả em trai tôi nữa, ông cứu thằng bé đi…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bác sĩ thở dài: “Thật ngại quá, em trai cô đã không cứu được nữa rồi…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không cứu được nữa?!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lời này như tiếng sét động trời giáng mạnh lên đầu Lâm Tử Lạp, khiến trước mắt cô đen lại…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đau, lồng ngực như bị người ta dùng con dao quấy phá, đau đến run rẩy ngồi cuộn lại dưới đất, tám năm trước, cô mười tuổi, ba ngoại tình vứt bỏ mẹ, đuổi mẹ đang mang thai và cô ra nước ngoài xa lạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó em trai được sinh ra, lúc ba tuổi phát hiện mắc bệnh tự kỷ, vốn cuộc sống đã túng thiếu, bệnh của em trai lại liên tiếp gặp nạn, cô và mẹ đi làm công cho người khác khắp nơi mới có thể sống qua ngày. Nhưng một vụ tai nạn giao thông, ở nước ngoài không có người thân, không có tiền, không ai giúp đỡ, khiến cô cảm nhận được cái gì là cùng đường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bị ép đến đường cùng, cô đã bán đi mình cũng không thể cứu em trai về.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có một sự đau đớn, nó không dữ dội, nhưng lại khiến người ta cảm thấy không dễ chịu, hít thở cũng khó khăn, bầu trời thì xám xịt, nhưng cô phải chấp nhận, còn phải cười mà chấp nhận, vì cô còn mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mẹ cần cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi được điều trị, sức khoẻ của mẹ chuyển biết tốt, nhưng khi biết em trai đã chết, bà như sụp đổ đến nơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Là Lâm Tử Lạp ôm bà, khóc nói: “Mẹ, mẹ vẫn còn con, cố sống tốt vì con đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Suốt một tháng ở bệnh viện, Trang Kha Nguyệt thường xuyên ngẩn người ngồi bên giường, Lâm Tử Lạp biết bà nhớ em trai. Nếu không phải vì mình, chỉ sợ mẹ đã đi theo em trai rồi, vì phải chăm sóc mẹ, cô bị đuổi học, nhưng vết thương của mẹ đã chuyển biến tốt đẹp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô xách đồ ăn đi vào bệnh viện, đến trước cửa phòng bệnh, khi cô nâng tay vừa định mở cửa, lại nghe thấy giọng nói ở bên trong…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô rất quen với nó, cho dù đã cách xa tám năm, cô vẫn nhớ rõ dáng vẻ ông ta ép mẹ ly hôn với mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi đưa bọn họ đến đây, ông ta chưa từng đến nhìn bọn họ một cái, hôm nay lại đột nhiên xuất hiện ở đây là có ý gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Kha Nguyệt, lúc trước bà và bà chủ nhà họ Tông tình như chị em, đã quyết định hôn ước từ nhỏ, theo đạo lý thì hôn ước bà quyết định phải do con gái bà lấy chồng…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lâm Viên Trung, ông có ý gì?!” Thân hình Trang Kha Nguyệt gầy yếu, bất chấp trên người còn có vết thương mà giãy dụa đứng lên muốn đánh ông ta, ông ta còn là người hay sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sắp xếp cho bà và con gái ở lại cái nơi quỷ quái xa lạ này, chưa từng quan tâm sống chết của bọn họ, hôm nay lại muốn con gái bà lập gia đình?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cậu cả nhà họ Tông cũng là con trai bạn tốt của bà, trông ưa nhìn, dòng dõi nhà họ Tông bà cũng biết đó, lấy bên đó chỉ có hưởng phúc thôi…” Nói đến phía sau, giọng ông ta nhỏ lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cậu cả nhà họ Tông đúng là cao quý thật, dáng vẻ tuấn tú lịch sự, nhưng một tháng trước, anh ra nước ngoài làm việc bị rắn độc cắn, tê liệt thần kinh, không thể hành động, còn không thể làm chuyện kia.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Gả qua đó chẳng khác nào ở goá cả.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi lấy.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp đột nhiên đẩy cửa ra, cô đứng trước cửa, hai tay siết lại nắm chặt hộp đựng cơm trong tay: “Lấy chồng cũng được, nhưng tôi có một điều kiện.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung nhìn ra cửa, thấy đứa con gái đã tám năm không gặp này, trong chốc lát hoảng hốt mất mấy giây. Lúc đưa cô đến, cô vẫn là một đứa nhỏ mười tuổi, bây giờ đã trưởng thành rồi, làn da cô trắng ngần, lại rất gầy, khuôn mặt nhỏ nhắn còn chưa lớn bằng lòng bàn tay, khô khan không hề tươi tắn chúng nào, như không được phát triển tốt vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không hề khiến người ta yêu thích như con gái nhỏ trong nhà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sự không nỡ trong lòng cũng bớt đi, dẫu sao vẻ ngoài của cô cũng không đẹp lắm, cho dù lấy người chồng không làm được chuyện kia cũng không quá uất ức.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ vậy, Lâm Viên Trung cũng không thấy có gì không tốt: “Điều kiện gì, nói đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi về nước với mẹ, trả tất cả những thứ thuộc về mẹ cho chúng tôi, tôi đồng ý lấy chồng.” Lâm Tử Lạp liên tục siết chặt tay, dần dần mới bình tĩnh lại được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy hàng năm không ở trong nước, nhưng trước đây cô đã nghe nói tới nhà họ Tông của thành phố B, gia tộc khổng lồ, của cải bạc triệu, cậu chủ của nhà họ Tông đương nhiên là cao quý. Lâm Tử Lạp không cảm thấy chuyện tốt như vậy sẽ đến tay mình, nói không chừng cậu cả nhà họ Tông kia rất xấu, hoặc là thân thể có chỗ khiếm khuyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng cho dù thế, đây cũng là một cơ hội rất tốt để về nước với cô, lợi dụng tốt, còn có thể giành lại tài sản là của hồi môn của mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lạp Lạp…” Trang Kha Nguyệt muốn khuyên cô, chuyện lớn như cưới xin không thể đùa giỡn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đã đi theo bà chịu rất nhiều khổ cực rồi, không thể để cô đưa cả hôn nhân vào được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung nghe thấy thế, lo rằng Lâm Tử Lạp bị Trang Kha Nguyệt thuyết phục sẽ không muốn lấy chồng, vội nói: “Được, chỉ cần cô chịu lấy chồng, sẽ cho cô về nước.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Của hồi môn của mẹ tôi thì sao?” Lâm Tử Lạp nhìn người ba trên danh nghĩa của mình, giọng nói cực kỳ lạnh lẽo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc trước khi Trang Kha Nguyệt lấy ông ta thật sự có rất nhiều của hồi môn, đó là một số lượng không nhỏ, bây giờ kêu Lâm Viên Trung lấy ra khiến ông ta rất không nỡ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ba, có lẽ đứa em gái kia của tôi rất xinh đẹp, nó nên có thứ tốt hơn, nếu lấy một người đàn ông thân thể có chỗ thiếu hụt thì cả đời cũng xong rồi, huống hồ ông và mẹ tôi đã ly hôn, ông cũng nên trả lại tiền bà đưa đến nhà họ Lâm đúng không.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung chột dạ né tránh không dám nhìn thẳng vào cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ở nước ngoài sao lại biết cậu cả nhà họ Tông kia là một người không vẹn toàn?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung nào biết Lâm Tử Lạp chỉ đang đoán thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến cô phải lấy một người đàn ông không bình thường, Lâm Viên Trung nghiến răng: “Đợi cô gả qua đó sẽ đưa cho cô.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con gái nhỏ của ông ta như hoa như ngọc, sao có thể lấy một người đàn ông không làm được chuyện kia chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có cao quý bao nhiêu, không thể làm chuyện vợ chồng thì khác gì một người tàn phế đâu?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến đây, Lâm Viên Trung cũng không khó chịu như vậy nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng trong lòng lại ghét Lâm Tử Lạp hơn mấy phần, chỉ toàn nghĩ việc lấy tiền từ trong tay ông ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung lạnh lùng nhìn cô một cái: “Mẹ cô đúng là không dạy dỗ cô đàng hoàng, không hề biết lễ phép chút nào!”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp rất muốn nói người làm cha là ông không có trách nhiệm hay sao? Ném cô lại đây chưa từng để tâm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng lúc này cô không thể nói, lợi thế của cô quá yếu, chọc giận Lâm Viên Trung không có lợi với cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chuẩn bị một chút, ngày mai trở về.” Lâm Viên Trung vung tay áo rời khỏi phòng bệnh.</span></p>', 0, '2022-06-30 07:07:12', '2022-06-30 07:20:06'),
(78, 16, 'Chương 2: Mang thai rồi', 'chuong-2-mang-thai-roi', 2, 'aa', '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lạp Lạp, hôn nhân là chuyện lớn cả đời, mẹ không cho con làm thế.” Ít nhiều gì Trang Kha Nguyệt cũng biết ý đồ của Lâm Tử Lạp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp để hộp đựng cơm lên ngăn tủ đầu giường, vừa lấy ra vừa nói: “Còn cũng không lấy người ngoài, không phải là con trai của bạn mẹ sao.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bà ấy mất sớm, mẹ cũng không hiểu con trai bà ấy, cho dù có nuốt lời, mẹ cũng muốn con lấy người mình thích, chứ không phải dùng hôn nhân làm lợi thế, như vậy, mẹ thà rằng ở lại đây cả đời.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người mình thích?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho dù sao này gặp được, cô cũng không có tư cách.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cúi đầu, lấy ai cũng không quan trọng, quan trọng là lấy lại mọi thứ bị người ta cướp đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt không thể thuyết phục Lâm Tử Lạp thay đổi quyết định, ngày hôm sau bọn họ lập tức về nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung ghét bỏ mẹ con bọn họ, không cho bọn họ vào cửa nhà họ Lâm, mà cho bọn họ thuê phòng bên ngoài ở, đợi đến ngày kết hôn, Lâm Tử Lạp trở về là được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa hay Lâm Tử Lạp cũng không muốn về, trở về, mẹ sẽ phải đối mặt với đồ vợ bé phá hoại hôn nhân của bà, so với việc mất tự nhiên không bằng ở lại đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Yên tĩnh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt vẫn rất lo lắng: “Lạp Lạp, nếu đây là một cuộc hôn nhân tốt, thì đã không đến tay con rồi, cho dù mẹ và bà Tông từng có… tình cảm qua lại.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không muốn nói những chuyện này với mẹ, vì thế đổi chủ đề: “Mẹ, mau ăn gì đó đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt thở dài, rõ ràng Lâm Tử Lạp không muốn nói chuyện này, cô đi theo mình chịu khổ, bây giờ ngay cả hôn nhân cũng phải hy sinh.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cầm đũa trong tay, nhưng không hề có khẩu vị, còn bị buồn nôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Con không thoải mái sao?” Trang Kha Nguyệt quan tâm hỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không muốn khiến bà lo lắng, nói bừa là ngồi máy bay xong không có khẩu vị.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buông đũa xuống đi về phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cửa phòng đóng lại, cô tựa lên ván cửa, tuy cô chưa từng có thai, nhưng cô từng thấy dáng vẻ khi mẹ mang thai, bà cũng buồn nôn, ăn không ngon.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà tình trạng của cô lúc này cũng giống thế.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cách đêm đó đã hơn một tháng rồi, ngày đèn đỏ của cô cũng bị muộn mười ngày…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không dám nghĩ tiếp nữa, đêm đó đã rất nhục nhã rồi. Nếu không phải vì mẹ và em trai, cô cũng sẽ không bán mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô run rẩy…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô mang thai rồi, sáu tuần.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ra khỏi bệnh viện, trong đầu Lâm Tử Lạp vẫn quanh quẩn câu cô mang thai rồi của bác sĩ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp lừa Trang Kha Nguyệt đến bệnh viện kiểm tra, sau đó kết quả là như vậy, tâm trạng cô rất rối ren, không biết phải làm sao bây giờ, sinh ra, hay phá bỏ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tay cô không nhịn được phủ lên bụng, tuy bất ngờ, thậm chí là nhục nhã, nhưng cô vẫn hơi không nỡ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có cảm giác vui sướng, và chờ mong khi lần đầu làm mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô hoảng hốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trở về nơi ở, Lâm Tử Lạp giấu giấy siêu âm B đi, mới đẩy cửa ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng Lâm Viên Trung cũng ở đó, sắc mặt cô thoáng chốc sa sầm lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông ta đến làm gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sắc mặt Lâm Viên Trung cũng không được tốt lắm, hình như vì không thấy cô khiến ông ta đợi lâu, lạnh lùng nói: “Đi thay quần áo.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhíu mày: “Tại sao?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Nếu phải gả đến nhà họ Tông, cô và cậu cả nhà họ Tông kia cũng phải gặp mặt.” Lâm Viên Trung quan sát cô từ trên xuống dưới: “Cô ăn mặc rách nát thế này đi gặp cậu ta à? Muốn khiến tôi mất mặt sao?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đau là cảm giác thế nào?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cho rằng bán mình, em trai chết, đã khiến cô đau đến chết lặng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng nghe thấy lời nói vô tình của Lâm Viên Trung, trái tim vẫn sẽ đau chứ không còn chết lặng nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông ta đưa mình và mẹ đến một đất nước phương Tây nghèo hèn, sau đó chưa từng quan tâm bọn họ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô lấy đâu ra tiền?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu cô có tiền, sao em trai có thể chết vì chữa trị chậm trễ được?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai tay buông xuống bên người cô siết chặt thành nắm đấm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hình như Lâm Viên Trung cũng nghĩ đến điều này, vẻ mặt hơi xấu hổ: “Đi thôi, người nhà họ Tông sắp đến rồi, không tiện để bọn họ đợi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lạp Lạp…” Trang Kha Nguyệt lo lắng, vẫn muốn khuyên Lâm Tử Lạp, bà đã mất đi con trai rồi, bây giờ chỉ muốn chăm sóc con gái thật tốt, tiền bạc đã không còn quan trọng nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng không muốn con gái lại bước vào nhà họ Lâm, hoặc là nhà họ Tông.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà giàu phức tạp, lại còn không biết cậu cả nhà họ Tông kia trông ra sao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà lo lắng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ.” Lâm Tử Lạp cho bà một ánh mắt an ủi, để bà yên tâm hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đi mau.” Lâm Viên Trung không kiên nhẫn thúc giục, sợ Lâm Tử Lạp đổi ý, còn đẩy cô một.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung không thích cô, cô cũng không có chút tình cảm với người ba này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tám năm, tất cả tình thân máu mủ đều đã tiêu mòn hết rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp ăn mặc thật sự rất cũ kỹ, lại phải gặp nhà họ Tông, Lâm Viên Trung dẫn cô đến một tiệm đồ nữ xa hoa, mua một bộ quần áo tàm tạm cho cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiến vào tiệm còn có nhân viên đến tiếp đón, Lâm Viên Trung đẩy Lâm Tử Lạp về phía trước: “Lấy đồ nào nó mặc được.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhân viên quan sát cô một lượt, đại khái đã biết cô mặc số mấy: “Đi theo tôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhân viên cầm một cái váy dài màu lam nhạt đưa cho cô: “Cô đến phòng thử đồ xem sao.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhận lấy, đi tới phòng thử đồ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bạch, anh phải cưới con gái của nhà họ Lâm sao?” Giọng nói của người phụ nữ mơ hồ mang theo uất ức.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp đột nhiên nghe thấy giọng nói nên đưa mắt nhìn qua phòng bên cạnh, xuyên qua khe hỡ của cánh cửa, cô nhìn thấy người phụ nữ ôm lấy cổ người đàn ông làm nũng: “Anh đừng cưới cô gái khác được không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhìn người phụ nữ, ánh mắt như mang theo bất đắc dĩ, đây là chuyện cưới xin mẹ quyết định cho anh, không thể đổi ý được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng nghĩ đến đêm đó, anh lại không đành lòng để cô ta thất vọng: “Đêm đó, có phải rất đau không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn một tháng trước, anh ra nước ngoài đến một nước lạc hậu khảo sát một hạng mục, kết quả bị một loại rắn cắn, độc của nó rất mạnh, nếu không trút ra trên người phụ nữ, sẽ khô nóng mà chết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn Bạch Lộ Khiết, đã làm thuốc giải cho anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bản thân anh biết lúc đó mình không khống chế được bao nhiêu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai cũng nói lần đầu tiên của phụ nữ rất đau, anh lại còn không thương tiếc, có thể nghĩ được cô ta đau bao nhiêu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng cô ta lại cam chịu như vậy, chưa từng lên tiếng một lần nào, chỉ run rẩy trong lòng anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết thích anh, anh luôn biết, nhưng chưa từng cho cô ta cơ hội.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một là không yêu cô ta, hai là vì mẹ đã quyết định hôn ước cho anh rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng cô ta vẫn luôn ở bên cạnh anh, từ đó về sau, anh cảm thấy mình nên cho người phụ nữ này một thân phận.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến bây giờ anh vẫn nhớ rõ dấu đỏ kia rực rỡ đến mức nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết vùi trong lòng anh, mắt hơi rũ xuống, thẹn thùng ừ một tiếng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta thích Tông Triển Bạch, mấy năm nay vẫn ở bên cạnh anh với thân phận thư ký, nhưng cô ta đã không phải xử nữ từ lâu. Cô ta không thể để Tông Triển Bạch biết được, đàn ông để ý sự trong sạch của phụ nữ bao nhiêu cô ta hiểu rõ. Cho nên cô ta bỏ một khoản tiền cho dân trên thị trấn tìm một cô gái chưa từng làm chuyện đó đưa đến căn phòng kia.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đợi sau khi cô gái đó ra ngoài, cô ta mới đi vào tạo hiện tượng giả rằng đêm đó là cô ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thích quần áo ở đây thì mua thêm mấy món đi.” Tông Triển Bạch xoa đầu cô ta, cưng chiều nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đây là phòng VIP, cô không thể vào được, cô đi tới gian bên phải đi.” Nhân viên nhắc nhở Lâm Tử Lạp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiệm quần áo xa hoa thế này, phòng thử đồ đều độc lập với nhau, mà phòng VIP càng xa hoa hơn, trong phòng thử đồ có chỗ để thử đồ, gian ngoài là để bạn bè đợi hoặc nghỉ ngơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“À.” Lâm Tử Lạp cầm quần áo đi tới phòng bên phải.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc thay quần áo trong phòng thử đồ, Lâm Tử Lạp còn đang nghĩ, hình như trong cuộc trò chuyện của một nam một nữ khi nãy có nhà họ Lâm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chẳng lẽ người đàn ông kia…</span></p>', 0, '2022-06-30 07:31:49', '2022-06-30 07:31:49');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(79, 16, 'Chương 3: Tôi phải lấy anh', 'chuong-3-toi-phai-lay-anh', 3, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thay quần áo xong, Lâm Tử Lạp đi ra khỏi phòng thử đồ, nhìn sang phòng thử bên trái, cửa đã đóng lại rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Rất hợp với khí chất của cô.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhân viên rất có mắt nhìn, chỉ cần nhìn người là có thể lấy ra quần áo hợp với người đó, Lâm Tử Lạp mặc váy dài mày xanh nhạt, khiến làn da càng trắng ngần hơn, dây thắt lưng bên hông quấn quanh vòng eo mảnh khảnh, nhìn hơi gầy quá mức, nhưng khuôn mặt lại rất xinh đẹp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung thấy hợp thì lập tức trả tiền, lúc này mới phát hiện cái váy tới tận chín mươi triệu, nhưng nghĩ đến cô phải gặp người nhà họ Tông, bèn cắn răng tính tiền, giọng nói lạnh như băng: “Đi thôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp đã sớm cảm nhận được sự vô tình của ông ta, nhưng sự lạnh lùng lúc này vẫn khiến tim cô khó chịu đến đau đớn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cúi đầu theo sau ông ta lên xe.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chẳng mấy chốc xe đã dừng lại trước cổng lớn của biệt thự.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tài xế kéo cửa xe cho Lâm Viên Trung, ông ta khom người đi xuống, Lâm Tử Lạp theo sau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đứng trước cổng biệt thự, hoảng hốt mất mấy giây, lúc cô và mẹ vì bệnh của em trai mà sống không bằng chết, ba của cô lại đang hạnh phúc hưởng thụ trong biệt thự khí thế với vợ bé của mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai tay cô không nhịn được siết chặt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô đứng ở đó làm gì?” Lâm Viên Trung không thấy có người đi theo sau mình, quay đầu lại nhìn thoáng qua, thì thấy cô sững sờ đứng trước cửa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp vội vàng đuổi theo, nghe giúp việc trong nhà nói người nhà họ Tông vẫn chưa đến, Lâm Viên Trung bèn kêu cô đợi trong phòng khách.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỗ bên cạnh cửa sổ sát đất của phòng khách có để một cây đàn piano, hiệu Seidel của Đức, rất đắt, lúc sinh nhật năm tuổi của cô, mẹ đã mua cho cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ nhỏ cô đã thích, bốn tuổi rưỡi thì bắt đầu học đàn, sau đó bị đưa đi, cô cũng không chạm vào nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không nhịn được sờ lên, quen thuộc lại hưng phấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngón trỏ cô ấn trên phím đàn, nhẹ nhàng dùng sức, ting một tiếng, âm thanh du dương trong trẻo vang lên, vì rất lâu không đàn, nên ngón tay của cô cứng hơn rất nhiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đồ của tôi, ai cho cô động vào?!” Một giọng nói lanh lảnh mang theo tức giận vang lên sau lưng cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đồ của cô ta?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp xoay người, thấy Lâm Hàn Uyển hùng hổ đứng sau lưng mình, nhớ cô ta nhỏ hơn mình một tuổi, năm nay mười bảy, kế thừa ưu điểm của Thẩm Thanh Anh, dáng vẻ không tệ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng điệu bộ nhe răng trợn mắt lúc này có mấy phần dữ tợn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Của cô?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bọn họ phá huỷ cuộc hôn nhân của mẹ, dùng tiền của mẹ, bây giờ ngay cả đồ mẹ tặng cho mình của trở thành của cô ta?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô từ từ siết chặt tay, liên tục thầm nói với mình là đừng xúc động, đừng kích động, vì bây giờ cô vẫn không thể cướp lại đồ thuộc về mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô phải nhịn!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không phải cô gái nhỏ chỉ biết khóc bị ba đưa đi tám năm trước, bây giờ cô đã trưởng thành rồi!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô… là Lâm Tử Lạp?!” Lâm Hàn Uyển hiểu ra, hôm nay là ngày người nhà họ Tông đến, ba đón hai mẹ con kia về nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Hàn Uyển vẫn còn nhớ dáng vẻ đáng thương khi Lâm Viên Trung đưa bọn họ ra nước ngoài, Lâm Tử Lạp quỳ dưới đất ôm chân Lâm Viên Trung năn nỉ ông ta, kêu ông ta đừng đưa mình đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ba đón cô về, có phải cô vui lắm không?” Lâm Hàn Uyển khoanh tay, khinh thường nhìn cô: “Cô đừng có đắc ý quá, đưa cô về chỉ vì muốn cô gả vào nhà họ Tông thôi, nghe nói người đàn ông kia…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong, Lâm Hàn Uyển châm chọc che miệng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhớ đến Lâm Tử Lạp phải gả cho người đàn ông không thể làm chuyện kia, còn không thể đi lại, không nhịn được thấy vui sướng khi người gặp hoạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôn nhân là chuyện lớn cả đời, lấy một người đàn ông như vậy, không phải cả đời đều bị huỷ bỏ sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhíu mày.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng lúc này, giúp việc đi tới: “Người của nhà họ Tông đến rồi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung tự mình ra cửa nghênh đón.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp xoay người, lập tức nhìn thấy người đàn ông ngồi xe lăn bị người ta đẩy vào, ngũ quan sắc nét, tướng mạo ngay thẳng, cho dù ngồi trên xe lăn cũng khiến người ta không dám khinh thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khuôn mặt này, không phải người đàn ông tán tỉnh với người phụ nữ kia trong phòng thử đồ mà cô thấy sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh lại có thể là cậu cả của nhà họ Tông?!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng ở phòng thử đồ, rõ ràng cô thấy anh đứng được mà, còn ôm người phụ nữ kia nữa, chân đâu có tật gì.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xảy ra chuyện gì vậy?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi cô vẫn chưa nghĩ kỹ vì sao người đàn ông này phải giả vờ, Lâm Viên Trung gọi cô một tiếng: “Tử Lạp mau đến đây, đây là cậu cả nhà họ Tông.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung nhún vai nịnh hót, cong lưng nói: “Cậu Tông, đây là Lạp Lạp.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung thầm thấy thương tiếc, đường đường là cậu cả nhà họ Tông, còn ưa nhìn như thế, lại trở thành tàn phế rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhìn Lam Tử Lạp, thấy tuổi không lớn, quá gầy, dáng vẻ hơi thiếu dinh dưỡng, anh cau mày.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây là hôn sự mẹ quyết định cho anh, làm con trai, anh không thể làm trái lời hẹn. Nên sau khi ra nước ngoài bất ngờ bị rắn độc cắn, anh đã đưa ra tin nói độc kia không giải được, tàn phế rồi, còn không thể làm chuyện kia, chính là muốn nhà họ Lâm đổi ý.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng không ngờ nhà họ Lâm không hề đổi ý.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch im lặng không nói gì, sắc mặt nặng nề, Lâm Viên Trung nghĩ rằng anh không hài lòng, vội giải thích: “Bây giờ con bé còn nhỏ, vừa đủ mười tám, nuôi lớn rồi chắc chắn là một người đẹp.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch thầm cười lạnh, người đẹp thì không nhìn ra, nhưng cũng thấy không tầm thường, anh là một “người què” còn muốn con gái mình lấy anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh lạnh nhạt, nở nụ cười sâu xa: “Tôi ra nước ngoài làm việc, không cẩn thận bị thương, cái chân này sợ là không đi lại được nữa, cũng không thể thực hiện trách nhiệm của người làm chồng…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi không ngại.” Lâm Tử Lạp lập tức trả lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung đã đồng ý với cô, chỉ cần gả vào nhà họ Tông sẽ trả lại của hồi môn của mẹ, cho dù hôm trước vào cửa, hôm sau ly hôn, bây giờ cô cũng phải đồng ý.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong khoảng thời gian này, cô đã suy nghĩ cẩn thận tất cả, rõ ràng anh có thể đứng lên, nhưng đến nhà họ Lâm lại ngồi xe lăn, có lẽ là vì người phụ nữ kia, cũng không muốn thực hiện giao hẹn, muốn nhà họ Lâm đổi ý trước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng anh không ngờ Lâm Viên Trung sẵn sàng hy sinh đứa con gái không được cưng chiều là cô để hoàn thành giao hẹn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch híp mắt nhìn chằm chằm vào cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp bị anh nhìn đến lạnh sống lưng, đáy lòng chua xót, cô nào có muốn gả vào nhà họ Tông chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không đồng ý, sao cô có thể về nước, sao có thể lấy lại những gì đã mất được?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cong môi cười, cay đắng và chua xót trong đó chỉ mỗi cô biết: “Chúng ta được đính hôn từ lúc còn nhỏ, anh trở thành thế nào, tôi cũng phải lấy anh.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh mắt Tông Triển Bạch lại càng nặng nề hơn, người phụ nữ này cũng biết nói chuyện đấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung cũng không nghe ra cái gì không đúng, thăm dò hỏi: “Vậy ngày cưới…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vẻ mặt Tông Triển Bạch thay đổi trong nháy mắt, cuối cùng lại bình tĩnh đáp: “Đương nhiên sẽ làm theo giao hẹn, đây là chuyện hai nhà quyết định từ trước, sao có thể nuốt lời được.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp rũ mắt, giấu đi tâm tư của mình, không dám nhìn vào anh, rất rõ ràng anh cũng không hài lòng với chuyện cưới xin này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ đồng ý cũng chỉ vì giao hẹn thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vậy cũng tốt.” Lâm Viên Trung thầm vui mừng, dùng một đứa con gái không xuất sắc kết thông gia với nhà họ Tông, đương nhiên là chuyện tốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy nhà họ Lâm cũng có tiền, nhưng so với nhà họ Tông chỉ là cá lớn gặp cá bé, cũng như cá mập và con tép vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàn toàn không thể đánh đồng với nhau!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung khom lưng, nhỏ giọng nói: “Tôi đã cho người chuẩn bị bữa tối rồi, ở lại ăn cơm xong hãy đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhíu mày, trò hề nịnh nọt cung kính này của ông ta khiến người ta rất khó chịu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không cần, tôi còn có việc.” Tông Triển Bạch từ chối, Quang Kình đẩy anh ra ngoài, khi đi ngang qua bên cạnh Lâm Tử Lạp, anh hơi nâng tay lên, ý bảo Quang Kình dừng lại, anh nâng mắt hỏi: “Cô Lâm có rảnh không?”</span></p>', 0, '2022-06-30 23:32:50', '2022-06-30 23:32:50'),
(80, 16, 'Chương 4: Kết hôn không có lễ cưới và nghi thức', 'chuong-4-ket-hon-khong-co-le-cuoi-va-nghi-thuc', 4, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy là câu hỏi, nhưng lại khiến người ta không thể từ chối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp gật đầu, nhìn dáng vẻ của anh là có lời muốn nói với cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng lúc cô cũng muốn nói chuyện với anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung cảnh cáo nhìn cô: “Có chừng có mực một chút.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đừng có còn chưa gả vào đã đắc tội người ta, thấy dáng vẻ lạnh lùng của Tông Triển Bạch, có lẽ là không hài lòng với Lâm Tử Lạp, nhưng có quan hệ thông gia với nhà họ Tông cũng có chỗ lợi cho nhà họ Lâm, cũng có thể giúp đỡ cho nghiệp vụ của công ty.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông ta cũng không muốn Lâm Tử Lạp làm thất bại chuyện này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp vờ như không phát hiện, đi theo sau Quang Kình ra ngoài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô hiểu Lâm Viên Trung có ý định gì, ông ta lấy đâu ra tự tin sau khi cô gả vào nhà họ Tông sẽ giúp ông ta vậy?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ vì ông ta là ba cô ư?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng ông ta có xem mình là con gái không? Biết tám năm nay cô sống thế nào không?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp đang suy nghĩ mơ hồ, đầu đập phải một “vách tường” cứng rắn khiến cô tỉnh táo lại, lập tức phát hiện một khuôn mặt không thể xoi mói đang gần trong gang tấc kiêu ngạo nhìn cô.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quả… quả nhiên anh có thể đứng được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chứng minh cô đã đoán đúng rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp bị nhìn đến căng da đầu, cô cố giả vờ bình tĩnh ngẩng đầu nhìn anh: “Anh cố ý giả vờ đúng không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch híp mắt, không vui vì bị người ta nhìn thấu, giọng điệu không cao không thấp nhưng lại đu khiến người ta sợ hãi: “Vì sao tôi là một người què còn muốn kết hôn với tôi? Coi trọng cái gì của tôi? Tiền bạc, muốn làm bà chủ nhà giàu à?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp chỉ cảm thấy bị anh nhìn đến xương cốt dưới da thịt cũng lạnh lẽo, trái tim như bị siết chặt, hít thở cũng khó khăn, nhưng vẻ mặt lại giả bình tĩnh: “Lúc tôi hai tuổi đã có hôn ước với ngài Tông, chẳng lẽ lúc tôi hai tuổi đã biết lợi ích của tiền bạc và làm bà chủ nhà giàu? Cho nên kiên quyết để hai người mẹ để hai chúng ta đính hôn?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô dừng lại một chúng, giọng điệu như cũng nhẹ nhàng hơn: “Lúc tôi hai tuổi, ngài Tông đã mười tuổi, lớn hơn tôi tám tuổi, tôi có chê anh già sao?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ha, Tông Triển Bạch cười lạnh, người phụ nữ này đâu chỉ biết nói, rõ ràng là nhanh mồm nhanh miệng!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Miệng lưỡi rất lợi hại!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh già?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong không khí tràn ngập mùi thuốc súng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bốn mắt nhìn nhau, tia lửa văng khắp nơi, ai cũng không chịu nhường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai tay rũ xuống bên người của Lâm Tử Lạp siết chặt thành quyền, mục đích cô gả vào nhà họ Tông chỉ vì Lâm Viên Trung hứa trả lại đồ cưới của mẹ cho cô mà thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng không phải muốn làm kẻ thù với người đàn ông này, giọng nói của cô dịu dàng trở lại, dáng vẻ khiêm tốn: “Ngài Tông, tôi biết anh không muốn cưới tôi, thật ra cũng không phải không thể…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cố ý nhìn lại nhìn sắc mặt của Tông Triển Bạch, vẻ mặt của anh chỉ hơi thay đổi, nhưng cô vẫn thấy được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ngài Tông, chúng ta giao dịch đi.” Lâm Tử Lạp lên tiếng, thật ra cô cũng không muốn gả vào nhà họ Tông lắm, cô sẽ đồng ý chỉ vì muốn trở về từ nước ngoài, giành lại của hồi môn thuộc về mẹ mà thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ha.” Tông Triển Bạch cười khẽ một tiếng, hình như cảm thấy buồn cười và hoang đường, giao dịch với anh?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nuốt một ngụm nước miếng, lưng vì căng thẳng mà đổ một tầng mồ hôi lạnh, Tông Triển Bạch rất cao, cô phải ngẩng đầu lên nhìn anh: “Tôi biết anh giả vờ là muốn nhà họ Lâm đổi ý chuyện này, tôi sẽ đồng ý là có nỗi khổ của mình.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Điều này khiến Tông Triển Bạch có hứng thú.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô muốn gì?” Nếu là giao dịch, chắc chắn có điều kiện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Một tháng, kết hôn một tháng xong tôi sẽ ly hôn với anh.” Thời gian một tháng đã đủ rồi, chỉ cần lấy được của hồi môn của mẹ, cô sẽ ly hôn với anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhíu mày: “Đây là giao dịch cô muốn nói với tôi?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đúng vậy, chúng ta nhất định phải kết hôn, đây là giao hẹn của hai người mẹ, chúng ta cũng không thể nuốt lời, đây là sự tôn trọng với bọn họ. Nhưng sau khi kết hôn, tính cách chúng ta không hợp thì ly hôn một cách rõ ràng, như vậy cũng không xem như nuốt lời. Vừa hay anh cũng không cần sống cả đời với người mình không thích, anh cũng không có hại, chỉ có lợi thôi…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói đến đây, giọng điệu của Lâm Tử Lạp thong thả hơn một chút: “Tôi nghĩ, chắc ngài Tông đã có người mình thích mới có thể nghĩ mọi cách khiến nhà họ Lâm thất hứa đúng không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sắc mặt Tông Triển Bạch trở nên nặng nề mà nghiêm túc, thầm nói: “Không ngờ cô còn rất thông minh đấy.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phải, anh muốn cho Tần Lộ Khiết một thân phận, sự ngây ngô và cam chịu của cô ta lúc đó đã khiến anh xúc động.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhìn vào khuôn mặt bình tĩnh của cô: “Cô thì sao, kết hôn một tháng có lợi ích gì với cô?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cũng không cho rằng cô chỉ suy nghĩ vì mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp căng thẳng trong lòng, cũng không thể nói là vì của hồi môn của mẹ được đúng không?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng nếu không có một cái lý do, hình như anh cũng sẽ không tin.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ tôi rất xem trọng hôn ước này, sức khoẻ của bà không được tốt, cho nên tôi cũng không muốn khiến bà thất vọng.” Khi nói chuyện ánh mắt cô hơi trốn tránh, vì cô nói dối, mẹ hoàn toàn không muốn cô gả vào nhà họ Tông.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng điệu của Tông Triển Bạch đột nhiên mang theo uy hiếp nặng nề khó hiểu, như nhìn thấu suy nghĩ của cô vậy: “Vậy sao?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp như đứng ngồi không yên, ánh mắt anh quá sắc bén, như có thể nhìn thấu lòng người vậy, vào lúc cô không biết làm sao cho phải, điện thoại trong túi anh reo lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhìn cô một cái, lấy điện thoại di động ra, nhìn thấy tên trên thông báo, sắc mặt dịu dàng hơn một chút xoay người nghe máy, hình như lại nghĩ đến điều gì đó, quay đầu lại: “Nếu một tháng, chúng ta cũng không cần thiết tổ chức lễ cưới.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không có sự lựa chọn, chỉ có thể đồng ý: “Được.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mười hai tháng tám, Quang Kình đến đón Lâm Tử Lạp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không có nghi thức, không có lễ cưới, chỉ có một tờ giấy chứng nhận kết hôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cũng không quan tâm lắm, vì cô hiểu rõ đây chỉ là một giao dịch theo yêu cầu thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu không phải đính hôn từ nhỏ, chỉ sợ bọn họ cũng chưa từng gặp nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chẳng mấy chốc xe đã dừng lại trước cổng biệt thự.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiến trúc rộng lớn dưới ánh mặt trời, cực kỳ có khí thế.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vào đi.” Quang Kình làm tư thế mời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không nhiệt tình, cũng không lấy lòng cô, chỉ làm theo quy tắc, có lẽ cũng biết hôn nhân giữa cô và Tông Triển Bạch chỉ là hoàn thành giao hẹn thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng không phải mợ chủ thật sự của nhà họ Tông.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Toà nhà tuy lớn, nhưng cũng chẳng có bao nhiêu người, chỉ có một giúp việc, Quang Kình cũng không giới thiệu nhiều, đưa cô vào phòng rồi đi ngay.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không quen lắm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đây là chỗ ở của cậu chủ, tôi là má Vương chăm lo cho sinh hoạt của cậu ấy, cô cũng có thể gọi tôi như thế.” Má Vương dẫn cô đến phòng: “Có cần gì cứ nói với tôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời gian một tháng không phải quá dài, Lâm Tử Lạp mang theo đồ dùng sinh hoạt của mình, tuy có lẽ sẽ không làm phiền bà, nhưng vẫn nói: “Được.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Má Vương mở cửa phòng ra, xoay người nhìn cô, vốn định nói gì với cô, nhưng cuối cùng chỉ thở dài: “Có lẽ đêm nay cậu chủ sẽ không về, hôm nay là sinh nhật của cô Tần.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy bọn họ không làm lễ cưới, nhưng tốt xấu gì người này cũng là vợ trên pháp luật của anh, hôm nay nói thế nào cũng là ngày cưới đầu tiên của bọn họ, anh ở bên ngoài với người phụ nữ khác, má Vương thấy Lâm Tử Lạp đáng thương, mới vừa vào cửa đã bị Tông Triển Bạch lạnh nhạt như vậy, sau này chẳng phải sẽ càng thê thảm hơn sao?</span></p>', 0, '2022-07-01 01:12:17', '2022-07-01 01:12:17');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(81, 16, 'Chương 5: Động phòng hoa chúc', 'chuong-5-dong-phong-hoa-chuc', 5, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp như đoán được ý của má Vương, cũng không giải thích mà chỉ cười.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô và Tông Triển Bạch chỉ là giao dịch thôi, sinh hoạt cá nhân của anh cô không có quyền hỏi đến.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh không ở đây, Lâm Tử Lạp còn thoải mái hơn một chút.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp tiến vào phòng mới nhìn rõ bày biện bên trong, phong cách trang trí trắng đen độc đáo, đơn giản gọn gàng, vừa xa hoa lại không dung tục, lịch sự tao nhã, có hương vị riêng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đây là phòng của cậu chủ.” Má Vương cười, nếu đã kết hôn thì chính là vợ chồng, đương nhiên phải ở cùng nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp há miệng, lại phát hiện nói không nên lời, chỉ có thể gật đầu đáp lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đêm đầu tiên ngủ ở một nơi xa lạ rất khó, cô bèn tựa trên đầu giường xem Đông Thành 58 trong điện thoại, chuẩn bị tìm một công việc, có công việc mới có thể yên ổn chăm sóc tốt cho mẹ, cho đứa nhỏ trong bụng một tương lai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">A…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp chợt nhìn thấy có thông báo phiên dịch, thông báo tuyển phiên dịch cũng không lạ, ngạc nhiên là phải biết tiếng nước A.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước A là nước cô bị Lâm Viên Trung đưa đi kia, rất là lạc hậu, chỗ nhiệt đới, cũng không có bao nhiêu người học ngôn ngữ của nước đó, ngôn ngữ thịnh hành trên thế giới đều là ngôn ngữ của các nước khá phát triển và có thực lực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiền lương đãi ngộ cũng không tệ.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì thế cô để lại thong tin cá nhân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó buông điện thoại, nằm xuống ngủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh trăng chiếu xuống trước cửa sổ như nhưng tia sáng chảy xuống, cực kỳ dịu dàng, đêm dài tĩnh lặng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người phụ nữ trên giường vô thức ngủ mất, có chùm ánh sáng trắng từ bên ngoài chạy vào trong sân, một chiếc xe Maybach dừng lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cửa xe mở ra, bóng dáng cao lớn đi xuống, anh sải bước vào trong nhà, nhịp bước cũng không vững vàng như bình thường, có mấy phần loạng choạng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh kéo cổ áo, miệng lưỡi hơi khô khốc, anh bước vào nhà rót một ly nước, yết hầu cứng rắn không ngừng cuộn lên cuộn xuống, đồng tử tối đen phủ kín men say, uống ly nước xong, cảm giác nóng bỏng nơi cổ họng cũng dịu đi không ít. Anh xã giao uống không ít rượu đế, sinh nhật của Tần Lộ Khiết lại uống mấy ly vang đỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người vốn có tửu lượng cao như anh cũng có hơi say.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh cởi áo khoác để trên sofa, không đến phòng tắm mà đi thẳng vào phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong phòng không mở đèn, rất tối, anh quen thuộc đi tới bên giường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nằm xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp đang ngủ say cảm thấy có tiếng động, nhưng chẳng mấy chốc đã yên tĩnh lại, cô co người tiếp tục ngủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sáng sớm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từng đợt ánh sáng rọi vào như sợi vàng, chiếu sáng cả căn phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai người ngủ rất say.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tựa như một đôi người yêu ngọt ngào vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lông mi người đàn ông hơi rung động, từ từ mở to mắt ra. Một đêm say rượu, anh chỉ cảm thấy đầu óc nặng nề, cần tắm để tỉnh táo lại. Khi anh nâng cánh tay muốn đứng dậy, lại phát hiện có gì đó đè lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh nghiêng đầu, lập tức nhìn thấy một người phụ nữ nằm trong lòng mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô gái có mái tóc đen như thác nước, từng sợi rơi trên cánh tay anh, gò má trắng ngần, lông mi xoăn vểnh như cánh bướm, đôi môi hồng nhạt hơi nhếch lên, phát ra tiếng hít thở đều đều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh mắt anh từ từ di chuyển xuống, cần cổ mảnh khảnh, xương quai xanh thanh thoát.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô hít thở phập phồng, lại mang theo chút hương vị quyến rũ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Yến hầu của anh không nhịn được lăn lộn, cho dù với Tần Lộ Khiết cũng chưa từng có xúc động như vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh chau mày, hình như rất không thích phản ứng không thể khống chế thân thể thế này, nhưng lại không dời mắt đi được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lúc ngủ mơ, Lâm Tử Lạp mơ thấy mình đang bị một con sư tử hung mãnh nhìn chằm chằm trên thảo nguyên châu Phi rộng lớn, như muốn ăn mình luôn vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô bừng tỉnh khỏi giấc mộng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng vào khoảnh khắc mở mắt, đập vào mắt là một đôi đồng tử sâu thẳm bình tĩnh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đại não thoáng trống rỗng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô mở to hai mắt, che ngực nói năng lộn xộn: “Sao… sao anh lại ở đây?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người đàn ông bình tĩnh dời mắt đi, từ từ vén chăn lên: “Đây là chỗ của tôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp muốn phản bác, nhìn thấy hoàn cảnh trong phòng, lời tới bên miệng lại nuốt xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không phải anh đi sinh nhật bạn gái ư? Sao lại về đây?” Lâm Tử Lạp bước xuống từ trên giường, đứng sang một bên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng điệu mang theo chút chất vấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôm qua nghe má Vương nói buổi tối anh không về, sau đó mới thả lỏng cảnh giác nên ngủ hơi say, thế nhưng cả việc anh vào phòng cũng không biết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôm qua cô lại có thể ngủ cùng một phòng với người đàn ông này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến hôm qua mình ngủ trong lòng anh, hai má đã cực kỳ nóng rát.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cúi đầu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cởi cúc áo sơ mi, tối qua anh không cởi quần áo, trên quần áo còn mang theo mùi rượu, còn nhăn nhúm dính trên người rất không thoải mái, anh nhìn cô gái ngại ngùng đứng bên giường một cái, hơi cong môi: “Sinh nhật của bạn gái, có quan trọng bằng đêm động phòng hoa chúc ư?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp: “…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây là giao dịch, bọn họ không phải vợ chồng, đêm động phòng hoa chúc cái gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cởi áo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp vội quay đầu, người đàn ông này lại có thể làm ngay trước mặt cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ sau đêm đó, cô rất không thích đàn ông, đặc biệt là tiếp xúc gần với bọn họ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô hoảng hốt: “Tôi… tôi đi ra ngoài trước.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong thì chạy khỏi phòng nhanh như chớp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cũng không để tâm lắm, cởi thắt lưng đi vào phòng tắm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh cần tắm rửa cho tỉnh táo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếng nước ào ào vang lên từ trong phòng tắm, chừng một tiếng trôi qua, anh tắm rửa xong mang theo mùi hương của sữa rửa tắm, mái tóc đen ngắn hơi rối, áo tắm màu trắng vây lấy dáng người thon dài, vạt áo hơi mở, làn da màu mật ong như ẩn như hiển, tản ra sức quyến rũ nam tính không thể khinh thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh đi đến trước tủ quần áo, khi chuẩn bị lấy quần áo lại phát hiện một cái túi in hoa hướng dương xa lạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh khựng lại, là của người phụ nữ kia à? Còn in hoa, sao cô ngây thơ thế?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn nữa còn không chút khách sáo để đồ của cô vào tủ anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh nhíu mày, lấy quần áo ra mặc vào, khi buông giá áo xuống không cẩn thận làm rơi túi của cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khoá không kéo lại, vì rớt xuống nên làm rơi hết đồ bên trong ra, quần áo đơn giản, đồ dùng sinh hoạt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh ngồi xổm xuống, khi vừa định nhặt lên, lại phát hiện một tờ giấy siêu âm B…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp, nữ, kiểm tra thời kỳ đầu, sáu tuần.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người phụ nữ kia mang thai rồi?</span></p>', 0, '2022-07-01 01:13:03', '2022-07-01 01:13:03'),
(82, 16, 'Chương 6: Ba người sống nương tựa lẫn nhau', 'chuong-6-ba-nguoi-song-nuong-tua-lan-nhau', 6, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cau mày, có cảm giác bị lừa gạt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong phòng khách, má Vương đã thức dậy chuẩn bị bữa sáng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy Lâm Tử Lạp mặc áo ngủ ngồi một mình trên sofa, bà cười híp mắt: “Hôm qua ngủ ngon không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà cho rằng tối qua Tông Triển Bạch ở với Tần Lộ Khiết không về, ban đêm nghe thấy tiếng động thì thức dậy xem thử, biết hôm qua Tông Triển Bạch đã về rồi, còn ngủ trong phòng nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây là người vợ bà chủ quyết định cho cậu chủ, đương nhiên phải tốt, cuối cùng cậu chủ cũng kết hôn rồi, má Vương luôn chăm sóc anh đương nhiên rất vui.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng điệu của bà quá nhiệt tình, mơ hồ mang theo chút mờ ám.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cứng nhắc cười gượng: “Ngon… ngon lắm.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vậy cô mau thay quần áo đi, tôi chuẩn bị bữa sáng, đợi lát nữa ăn cơm luôn.” Má Vương đi vào nhà ăn, bắt đầu nấu bữa sáng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cúi đầu nhìn áo ngủ trên người mình, quần áo cô mang đến còn ở trong phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chắc người đàn ông bên trong đã mặc đồ rồi nhỉ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đứng dậy đi tới của phòng ngủ, giơ tay lên gõ cửa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không ai đáp lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô gõ lại lần nữa, vẫn không có ai đáp lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hết cách, cô đành thử đẩy cửa phòng ra, cửa cũng không khoá trái từ bên trong, cô chỉ đẩy đã mở rồi.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vào khoảnh khắc mở cửa phòng ra, lập tức đối diện với bầu không khí lạnh lẽo như gió mùa đông tháng mười hai, thổi đến người ta run rẩy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người đàn ông ngồi trên giường, lạnh lùng nhìn chằm chằm một tờ giấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tờ giấy kia…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhanh chóng hiểu ra anh đang cầm gì trong tay, sau đó nhìn thấy một đống lộn xộn dưới đất, có cảm giác bực bội vì bị người ta xoi mói sự riêng tư. Cô chạy vào giành lấy, chất vấn: “Sao anh lại làm thế, không được người khác đồng ý còn xem đồ của người khác, có hiểu cái gì là riêng tư không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ha hả.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cười lạnh một tiếng: “Riêng tư?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dáng vẻ ngoài cười như trong không cười kia của anh cực kỳ đáng sợ: “Trong bụng cô có đứa con hoang còn lấy tôi, bây giờ lại nói riêng tư với tôi?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi… tôi…” Lâm Tử Lạp muốn giải thích, nhưng trong lúc nhất thời không tìm thấy lý do thích hợp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch đứng dậy, sải bước không nhanh không chậm đi tới, mỗi một bước đều như áp suất không khí ép đến gần, mây đen nghìn nghịt cuồn cuộn trên mặt anh: “Nói, cô có mục đích gì?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Muốn anh làm ba kế, trở thành con trai trưởng của nhà họ Tông?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giao dịch lúc trước chỉ là âm mưu tạm thời của cô?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sắc mặt anh càng u ám hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp mím môi, người run lên, không ngừng lùi về sau, hai tay che bụng, sợ anh làm hại đến đứa nhỏ trong bụng mình: “Không phải tôi cố ý giấu anh, chúng ta chỉ là hôn nhân giao dịch, cho nên tôi mới… tôi mới không nói, hoàn toàn không có mục đích gì hết.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng điệu của Tông Triển Bạch mang theo uy hiếp nặng nề: “Thật ư?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp che bụng, im lặng lùi về sau, cố bình tĩnh: “Thật sự, chuyện thế này sao có thể lừa gạt được, nếu tôi có suy nghĩ không nên có nào, sẽ không được chết tử tế. Hơn nữa nếu tôi thật sự đổ cho ngài Tông, tôi nghĩ chắc anh cũng có thủ đoạn giết chết tôi đúng không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy động tác của cô rất nhỏ nhẹ, nhưng Tông Triển Bạch vẫn phát hiện, quét mắt nhìn qua dáng vẻ bảo vệ bụng của cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó nhìn chằm chằm mặt cô: “Vì sao không nói rõ điều kiện tiên quyết?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cũng không dễ dàng tin tưởng cô như vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai tay đang che bụng của cô từ từ siết chặt lại, đứa nhỏ này thật sự quá bất ngờ với cô, cũng là người thân có cùng quan hệ huyết thống của cô, cô đã mất đi em trai rồi, cho nên cô muốn sinh đứa nhỏ này ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau này có thể giống như mẹ lúc trước, ba người sống nương tự lẫn nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến đêm đó, cô không nhịn được run rẩy, lòng bàn tay chảy mồ hôi lạnh: “Tôi… tôi cũng chỉ vừa biết không lâu.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thậm chí cô còn không dám nói với Trang Kha Nguyệt, một mình đến bệnh viện kiểm tra, cô không dám để ở nhà là vì sợ bà phát hiện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không ngờ lại làm ầm ĩ đến mức này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khiến Tông Triển Bạch nghi ngờ động cơ của cô không đơn giản.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô mới bao nhiêu tuổi, lại có thể…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đời tư bừa bãi đến mức nào chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sắc mặt Tông Triển Bạch cực kỳ u ám, cảnh cáo: “Trong một tháng này, biết điều một chút cho tôi, nếu để tôi biết cô làm gì…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không đâu, chắc chắn không đâu, tôi nhất định sẽ biết thân biết phận, nếu còn làm ra chuyện gì nữa, mặc cho ngài Tông xử lý.” Lâm Tử Lạp vội đảm bảo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho dù không có được sự tin tưởng của anh, cũng không thể để anh nghi ngờ động cơ của mình được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô vốn đã ở trong hoàn cảnh khó khăn rồi, nếu còn có thêm kẻ thù sẽ rất bất lợi với cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhìn chằm chằm cô, ánh mắt tìm tòi nghiên cứu, dường như đang phán đoan sự đáng tin trong lời của cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cốc cốc… lúc này, má Vương đi tới: “Bữa sáng nấu xong rồi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch không nhìn nữa, giấu đi sát khí: “Dọn dẹp sạch sẽ đồ đạc dưới đất.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong thì xoay người đi ra ngoài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch vừa rời khỏi, hai chân Lâm Tử Lạp đã mềm nhũn, cô chống lên tủ thấp phía sau, một lúc lâu mới bình tĩnh lại, cô ngồi xổm xuống, nhặt đồ đạc dưới đất lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại nhìn thấy giấy siêu âm B trong tay, nước mắt rơi xuống nhỏ lên tờ giấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp lau mặt, cô không được khóc, đó là biểu hiện của sự yếu đuối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không thể yếu đuối, mẹ và đứa nhỏ trong bụng đều cần cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Gấp tờ giấy lại cho vào túi xách, thay quần áo đi ra ngoài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong nhà ăn đã không có ai nữa, trên bàn cơm để tách cà phê rỗng và đĩa ăn rỗng, có lẽ anh đã ăn xong rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp tự nhiên thở phào một hơi, ở chung với người đàn ông kia thật sự quá áp lực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đi tới trước bàn ăn bữa sáng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ăn xong, cô ra ngoài, đã nói trước sẽ về nhà, cô sợ Trang Kha Nguyệt lo lắng cho mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa vào cửa đã bị Trang Kha Nguyệt kéo lại: “Cậu cả của nhà họ Tông kia…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ.” Giọng điệu của Lâm Tử Lạp rất mạnh, cô không muốn nói nhiều về chuyện này: “Anh ta rất tốt, đừng lo cho con.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt thở dài, con gái lớn có quyết định của riêng mình, cũng không thích nghe bà nhiều lời, tâm trạng hơi mất mát: “Mẹ chỉ quan tâm con thôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sợ người đàn ông kia không tốt với cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp ôm lấy bà, không phải cô cố ý, chỉ là giằng co, thuyết phục Tông Triển Bạch khiến cô tốn hết sức lực, cảm thấy mệt mỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ, con chỉ hết mệt thôi, con không cố ý đâu.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ biết, mẹ không trách con.” Trang Kha Nguyệt cũng cảm giác được sự mỏi mệt từ cô: “Nếu mệt thì ngủ một lát đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp gật đầu, mặc dù không muốn ngủ, nhưng cô thật sự thấy mệt, trở về phòng, cũng không biết ngủ thiếp đi từ bao giờ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giữa trưa, Trang Kha Nguyệt nấu cơm xong, kêu cô thức dậy ăn cơm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngồi trên bàn cơm, Trang Kha Nguyệt xới cơm cho con gái: “Mẹ có nấu món cá con thích ăn đấy.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt thấy áy náy với con gái, tuy sinh ra cô lại không thể cho cô tuổi thơ tốt đẹp, khiến cô đi theo mình chịu khổ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhìn cá chua ngọt mẹ nấu trên bàn, mùi chua ngọt nhàn nhạt, trước đây cô thích ăn nó nhất. Nhưng lúc này người thấy mùi đó, dạ dày đột nhiên cuồn cuộn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không nhịn được, oẹ…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lạp Lạp.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không thể giải thích che miệng chạy vào trong toilet, nhoài người bên bồn rửa tay nôn khan.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt lo lắng đi tới, bà là người từng trải, nhìn thấy phản ứng của con gái, sắc mặt trở nên trắng bệch. Nhưng bà không tin tưởng lắm, con gái rất dè dặt, rất ngoan ngoãn, ở trường cũng chưa từng quen bạn trai, cô rất tự trọng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt run rẩy hỏi: “Lạp Lạp, con sao thế?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cứng người, hai tay cầm lấy bồn rửa tay không ngừng siết chặt, cô quyết định giữ đứa nhỏ này, như vậy, sớm muộn gì Trang Kha Nguyệt cũng sẽ biết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô xoay người nhìn mẹ, cố lấy can đảm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ, con có thai rồi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong chốc lát Trang Kha Nguyệt không thể đứng vững, lùi về sau một bước, thật khó tin, cô chỉ mới mười tám thôi mà.</span></p>', 0, '2022-07-01 01:14:01', '2022-07-01 01:14:01');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(83, 16, 'Chương 7: Phá thai không đau', 'chuong-7-pha-thai-khong-dau', 7, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Xảy ra chuyện gì rồi?!” Trang Kha Nguyệt chất vấn, hình như trong nháy mắt đã hiểu ra điều gì đó: “Chẳng lẽ tiền này không phải người gây tai nạn bồi thường?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà xảy ra tai nạn giao thông bị thương, phí chôn cất của con trai tốn không ít tiền, trước khi về nước con gái còn cho mình một chút tiền, nói là tiền còn lại của người gây tai nạn bồi thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không biết nên nói sao, thật sự quá khó khăn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sự im lặng của cô chính là ngầm thừa nhận, một cô gái như cô, sao có thể xoay sở được bao nhiêu tiền, Trang Kha Nguyệt đau lòng, lại không dám tin: “Con, chẳng lẽ con bán mình rồi…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà bắt lấy cổ tay Lâm Tử Lạp: “Đứa nhỏ này không thể sinh ra được, bây giờ đến bệnh viện với mẹ ngay!”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tại sao?” Lâm Tử Lạp muốn tránh khỏi bà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Con sinh rồi, cả đời cũng sẽ bị phá huỷ!” Đứa nhỏ này cô không thể sinh, cô đã lập gia đình rồi, nếu để người ta biết, chắc chắn cô sẽ toi đời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ, con xin mẹ để con sinh ra đi.” Lâm Tử Lạp khóc cầu xin.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cầu xin thế nào Trang Kha Nguyệt cũng không chịu, thái độ rất kiên quyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lập tức kéo Lâm Tử Lạp vào bệnh viện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không đi, bà còn dùng cái chết để uy hiếp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không thể không đi, phá thai phải làm kiểm tra đủ kiểu, khi Trang Kha Nguyệt đi lấy giấy xét nghiệm, một mình cô ngồi trên ghế dài trong hành lang, hai tay ôm bụng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước mắt không nhịn được rơi xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa chua xót lại vừa bất đắc dĩ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bạch, em không sao đâu, đừng căng thẳng thế, chỉ bị bỏng một chút thôi mà.” Tần Lộ Khiết cười yếu ớt, trên người mặt váy bó sát màu đen vây quanh dáng người lồi lõm tinh tế, vai khoác một cái áo khoác âu phục, Tông Triển Bạch mặc áo sơ mi màu trắng, cổ tay áo xắn lên, để lộ cánh tay rắn chắc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vẻ mặt lo lắng: “Bị bỏng không xử lý kỹ càng sẽ để lại sẹo.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết vùi người vào lòng Tông Triển Bạch: “Nếu để lại sẹo, anh có ghét em không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chỉ biết nói bậy!”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết cười khanh khách, biết Tông Triển Bạch không phải người nông cạn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng nói này…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp từ từ quay đầu lại, lập tức nhìn thấy Tần Lộ Khiết dựa vào Tông Triển Bạch từ từ đi tới trong hành lang.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dáng vẻ đó cực kỳ xứng đôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khiến cho cô giống như một thằng hề, tuổi còn trẻ đã mất đi trong sạch, trong bụng còn mang thai đứa nhỏ không biết ba là ai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi cô nhìn đến ngơ ngác, chợt có một ánh mắt ngạc nhiên nhìn tới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bệnh nhân tiếp theo.” Cửa phòng phẫu thuật mở ra, y tá đứng trước cửa, phía sau là một người phụ nữ trẻ tuổi che bụng đi ra từ bên trong, miệng còn nhắc nhở: “Con mẹ nó, phá thai không đau sao lại đau như vậy chứ?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết nhìn qua theo ánh mắt của Tông Triển Bạch…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vào khoảnh khắc nhìn thấy Lâm Tử Lạp, cô ta như cảm thấy quen thuộc, nhưng lại không nhớ là từng gặp ở đâu, cô ta nhìn về phía Tông Triển Bạch: “Anh quen cô ta à?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không quen.” Tông Triển Bạch lạnh lùng cong môi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch có rất nhiều thành kiến với Lâm Tử Lạp, đời tư của cô bừa bãi, nhỏ như vậy đã bị làm to bụng, một bên bày tỏ tình thương người mẹ với anh, một bên chạy tới phá thai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đồ tâm cơ!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Nghĩ kỹ rồi chứ?” Y tá liên tục xác định.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không muốn bị người ta nhìn thấy sự chật vật của mình, cho dù trong lòng không muốn, đau lòng, bất đắc dĩ, nhưng vẫn gật đầu: “Tôi nghĩ kỹ rồi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đi vào theo tôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cúi đầu, không nhìn tới bất kỳ ai, đi vào phòng phẫu thuật theo y tá. Cửa phòng phẫu thuật đóng lại, ngăn cách mọi thứ bên ngoài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết mơ hồ hơi lo lắng, cô ta cảm thấy Tông Triển Bạch đang tức giận, duỗi tay khoác lên tay anh, dịu dàng nói: “Bạch.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch lạnh mặt: “Đi thôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết kéo tay anh, quay đầu lại nhìn qua phòng phẫu thuật đã đóng cửa, lại nhìn phản ứng của Tông Triển Bạch, không giống không quen lắm, nhưng đi theo bên cạnh anh lâu như vậy, bên cạnh anh cũng chưa từng xuất hiện phụ nữ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Điều này cô ta hiểu rất rõ, người phụ nữ khi nãy là ai?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sao anh lại tức giận thế?!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bạch, người phụ nữ khi nãy…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch ôm cô ta, cũng không muốn nói nhiều về chủ đề này: “Người không quan trọng, không cần quan tâm.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết chỉ có thể im lặng, cho dù trong lòng tò mò cũng không nói gì nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong phòng phẫu thuật, nhìn thấy những dụng cụ lạnh như băng kia, Lâm Tử Lạp lùi bước, không, cô không thể bỏ đứa nhỏ này, không thể được!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Nằm lên.” Bác sĩ ra hiệu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi không làm nữa.” Lâm Tử Lạp lắc đầu, xoay người bỏ chạy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô chạy rất nhanh, vì quá kích động nên không chú ý đường đi phía trước, đụng vào lòng một người đàn ông đi tới từ đối diện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô che trán, liên tục xin lỗi: “Xin lỗi, xin lỗi…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lâm Tử Lạp?” Hà Khiếu Thiên thấy giống cô, nhưng không dám chắc chắn nên dò hỏi.</span></p>', 0, '2022-07-01 01:14:36', '2022-07-01 01:14:36'),
(84, 16, 'Chương 8: Anh muốn tôi nói thế nào?', 'chuong-8-anh-muon-toi-noi-the-nao', 8, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp từ từ ngẩng đầu, thấy rõ khuôn mặt của người đàn ông thì ngạc nhiên nói: “Bác sĩ Hà?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có một đám người đứng sau lưng anh, Lâm Tử Lạp càng ngạc nhiên hơn: “Sao… sao anh lại ở đây?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Em trai mắc chứng tự kỷ, đều là Hà Khiếu Thiên khám cho thằng bé, thường xuyên qua lại nên hai người có quen nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên cười dịu dàng, còn chưa lên tiếng, Viện trưởng của bệnh viện này đã nói: “Bác sĩ Hà đến giảng dạy cho bệnh viện của tôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên là bác sĩ tâm lý có tiếng, đặc biệt rất giỏi về chứng tự kỷ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô thì sao, sao cô lại ở đây, có chỗ nào không thoải mái à?” Hà Khiếu Thiên hỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến thái độ kiên quyết của mẹ, cả người Lâm Tử Lạp run lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lạp Lạp!” Trang Kha Nguyệt cầm giấy kiểm tra trong tay, vội vàng chạy tới từ một bên hành lang khác. Bà quay lại, nghe y tá nói cô chạy mất rồi thì hoảng sợ, thấy cô nên kích động kêu một tiếng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp mím môi, khoang mũi chua xót: “Mẹ…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên nói với Viện trưởng đứng bệnh cạnh: “Mọi người về trước đi, tôi có chút việc.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bác sĩ Hà có việc thì chúng tôi cũng không làm phiền nữa, nhưng tôi thật lòng mời bác sĩ Hà đến bệnh viện của chúng tôi làm việc, có gì cần bác sĩ cứ việc nói, tôi nhất định sẽ cố hết sức đáp ứng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên dịu dàng nói: “Tôi sẽ suy nghĩ.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bác gái, có chuyện gì chúng ta ra ngoài nói, ở đây không hợp.” Trong bệnh viện nhiều người lui tới, không hợp để nói chuyện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt cũng có quen Hà Khiếu Thiên, khi khám bệnh cho con trai, có đôi khi thật sự không trả nổi tiền, đều là bác sĩ Hà ứng trước.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt cực kỳ tôn trọng anh ấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì thế bà nắm chặt lấy cổ tay Lâm Tử Lạp, sợ cô lại bỏ chạy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa đi ra cổng lớn bệnh viện, Lâm Tử Lạp đã quỳ gối trước mặt Trang Kha Nguyệt: “Mẹ, con xin mẹ, Tân Kỳ đã mất rồi, để con giữ nó lại có được không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên nhíu mày, có ý gì? Nhưng chỉ chốc lát Hà Khiếu Thiên đã hiểu ra, đưa mắt nhìn bụng cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy giấy kiếm tra trong tay Trang Kha Nguyệt, gần như đã hiểu rõ, cô mang thai rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khiếp sợ, khó tin.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh ấy rất muốn biết đã xảy ra chuyện gì, nhưng bây giờ không phải lúc để hỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp rất ít khi khóc trước mặt Trang Kha Nguyệt, cho dù lúc em trai chết, cô cũng chỉ len lén khóc, chưa từng rơi nước mắt trước mặt bà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trần Nguyên Khánh không phải ép cô, chỉ là cô sinh đứa nhỏ này ra rồi, thì còn có tương lai hay sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai cũng nói làm mẹ sẽ trở nên mạnh mẽ, nhìn dáng vẻ của cô, muốn để cô bỏ đi rất khó, Trang Kha Nguyệt thở dài: “Tuỳ con vậy.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong thì bỏ đi mất, trong lòng khó chịu, không biết nên đối mặt với con gái thế nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp từ từ ngồi xổm xuống, người thì cậy mạnh, nước mắt lại xin hàng, cô không muốn khóc, nhưng không nhịn xuống được, tổn thương và đau đớn chất chứa trong lòng như muốn ăn mòn tim phổi cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trước khi về nước anh ấy từng đi tìm các cô, mới biết bọn họ về nước rồi, em trai cô đã qua đời trong tai nạn giao thông.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lúc đó đã xảy ra chuyện gì, anh ấy không hề hay biết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên ngồi xổm xuống vỗ lưng cho cô, khi quen cô gái này, cô mới chỉ là một đứa nhỏ mười mấy tuổi, cũng đã rất hiểu chuyện, chăm sóc em trai, chăm sóc mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có lần, anh ấy tận mắt thấy tiền của cô chỉ đủ mua hai phần cơm, cô đưa cơm cho mẹ và em ăn, rõ ràng mình chưa ăn, lại nói với Trang Kha Nguyệt là ăn rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiểu chuyện đến khiến người ta đau lòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên đưa tay muốn sờ đầu cô, an ủi cô, nhưng tay còn chưa rơi xuống, Lâm Tử Lạp đột nhiên ngẩng đầu nhìn anh ấy: “Cảm ơn sự giúp đỡ của anh trước kia, sau này em có tiền chắc chắn sẽ trả lại cho anh.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tay Hà Khiếu Thiên khựng lại trên tóc cô, bàn tay từ từ nắm lại rồi dời đi, cười nói: “Đồ ngốc, là anh tự muốn giúp, không cần trả.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp lắc đầu: “Anh tốt bụng, nhưng em vẫn nhớ.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có năng lực sau này nhất định sẽ trả.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên đỡ cô dậy: “Em nghỉ ngơi ở đâu, anh đưa em về.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này Lâm Tử Lạp lo lắng cho Trang Kha Nguyệt, cho nên gật đầu nói địa chỉ ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến nơi, cô đẩy cửa xuống xe, Hà Khiếu Thiên hỏi: “Sau này có về kia nữa không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp xoay người nhìn anh ấy, lắc đầu: “Không về nữa.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khó khăn lắm mới về được đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp về chỗ ở, lập tức nhìn thấy Trang Kha Nguyệt ngồi trên ghế lau nước mắt, trái tim cô như bị thứ gì đó xé rách.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt lau nước mắt, không nhìn cô: “Mẹ không sao, con về đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Là mẹ không chăm sóc tốt cho con.” Trang Kha Nguyệt lau nước mắt, nhưng lau rồi vẫn còn, không dừng lại được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhào tới ôm bà, hai mẹ con ôm nhau khóc lớn, trút ra sự chua xót trong lòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lúc sau đó, bọn họ mới bình tĩnh lại, Lâm Tử Lạp nói với Trang Kha Nguyệt giao dịch của mình và Tông Triển Bạch, kêu bà đừng lo lắng cho mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt cực kỳ khiếp sợ, hôn nhân sao có thể là trò đùa chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy bà không tán thành hôn nhân giao dịch gì đó, nhưng con gái mang thai rồi, không còn trong sạch nữa, người đàn ông nhà họ Tông kia cũng không chấp nhận được, vậy cũng tốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau này để bà chăm sóc con gái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi tối Lâm Tử Lạp về lại biệt thự, Tông Triển Bạch không ở nhà, ăn bữa tối xong cô đi tản bộ một vòng trong biệt thự cho tiêu cơm, tiện thể xem thử hoàn cảnh xung quanh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó cũng muộn rồi, cô về phòng, nhưng lại thấy khát, nên đến phòng bếp rót một ly nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Uống nước xong, lúc Lâm Tử Lạp chuẩn bị về phòng ngủ, cửa nhà vang lên tiếng mở chốt, sau đó bị đẩy ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó, một bóng dáng cao lớn sải bước đi vào, rồi đến một bóng dáng xinh đẹp đi ra từ sau lưng anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp sửng sốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không ngờ đã muộn thế này rồi, Tông Triển Bạch còn dẫn người phụ nữ anh thích về nhà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết nhìn thấy cô cũng sửng sốt, đây không phải người phụ nữ trong bệnh viện ngày đó sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta ngẩng đầu nhìn sườn mặt lạnh lùng của Tông Triển Bạch.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngày đó anh tức giận cái gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có liên quan đến người phụ nữ này sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lòng dạ của phụ nữ luôn rất nhạy bén, sự khác thường của Tông Triển Bạch khiến Tần Lộ Khiết bắt đầu đề phòng Lâm Tử Lạp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vậy tôi về phòng trước.” Lâm Tử Lạp cũng không muốn làm bóng đèn khiến người ta khó chịu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đợi đã.” Tông Triển Bạch nặng nề nhìn cô, cô mặc áo ngủ rất bảo thủ, làn váy màu trắng dài đến mắt cá chân, chỉ để lộ hai cánh tay trắng trẻo mảnh khảnh, nhìn cũng có mấy phần trong sáng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng nghĩ đến hành động của cô, trong lòng lại chán ghét thêm mấy phần: “Ngoài tôi ra, Lộ Khiết chính là chủ của nơi này, hiểu ý của tôi chứ?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cảm thấy mình không làm gì cả, cô cũng chưa bao giờ coi mình là chủ ở đây, vậy tại sao phải nhấn mạnh nhưu vậy chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi biết rồi, vậy tôi đi ngủ đây.” Lâm Tử Lạp quay người, đi về phòng mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô Lâm.” Tần Lộ Khiết nhìn cô: “Xin lỗi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp chẳng hiểu sao, ngạc nhiên nhìn cô ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên mặt cô ta đầy sự áy náy: “Tuy cô và Bạch có hôn ước, nhưng hai chúng tôi quen biết nhau lâu hơn cô, nếu không phải cô, người hôm nay gả vào đây chính là tôi, hai chúng tôi thật lòng yêu nhau, cho nên…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cho nên cái gì?” Lâm Tử Lạp cảm thấy người phụ nữ này thật kỳ lạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô rất hiểu thân phận của mình, cũng không có cản trở bọn họ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta nói những lời này làm gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chỉ cảm thấy cô lấy Bạch rồi, nhưng Bạch không yêu cô là vì liên quan đến tôi, cho nên tôi cảm thấy rất áy náy.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không cần đâu.” Theo tư duy của người bình thường, quan hệ gượng gạo thế này không nên quấy rầy lẫn nhau mới phải?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Làm thế này là muốn thể hiện cô ta hiền lành trước mặt Tông Triển Bạch à?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tự nhiên Lâm Tử Lạp không có thiện cảm với cô ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch híp mắt nhìn chằm chằm mặt cô: “Cô có thái độ gì vậy?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp mím môi, cô có thái độ gì, cô chỉ muốn sống yên ổn mấy tháng này thôi, sau khi lấy được đồ thuộc về mình thì cô sẽ rời đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Là người phụ nữ này quá kỳ lạ, cứ đi lên nói những lời này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô nên trả lời thế nào đây?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Anh muốn tôi trả lời thế nào?” Lời này của Tần Lộ Khiết, cô không có cách nào trả lời.</span></p>', 0, '2022-07-01 01:15:27', '2022-07-01 01:15:27'),
(85, 66, 'Chương 1: Cút Ra Ngoài', 'chuong-1-cut-ra-ngoai', 1, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Kiều Phương Hạ.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bên tai Kiều Phương Hạ vang lên tiêng hét tức giận, khiến cô trở lên tỉnh táo, mở mắt ra bắt gặp một đôi mắt âm hiểm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ngẩn ra mất giây, mới nhận ra mình đang nằm trên giường của Lệ Đình Tuấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau lần bạo ngược tối qua, toàn thân cơ thể Kiều Phương Hạ không có chỗ nào là không xanh tím, trên xương quai xanh trải đầy dấu đỏ, ánh mắt Lệ Đình Tuấn chạm vào thân thể nhỏ nhắn của cô, đồng tử đột nhiên nhíu chặt, sau đó kéo chăn ném lên người cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này Kiều Phương Hạ mới hoảng sợ trốn vào trong chăn, đôi mắt trong veo của cô đột nhiên bị hơi nước làm ẩm ướt.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô nhìn thấy Lệ Đình Tuấn đứng dậy, không nhìn cô lấy một cái mà đi vào phòng tắm, có chút không biết làm sao gọi anh một tiếng: “Anh.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng nói vừa rơi xuống, ánh mắt Lệ Đình Tuấn sắc bén liếc về phía cô: “Anh?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bởi vì giọng nói của anh quá lạnh lùng, Kiều Phương Hạ bị anh nhìn khiến trong lòng run rẩy, sau đó hoảng loạn thay đổi: “Cậu chủ.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tối qua khi cô về nhà thì bị cúp điện, còn chưa làm rõ xảy ra chuyện gì thì Lệ Đình Tuấn đã bắt lấy cô lôi vào trong phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không biết anh uống say hay bị làm sao, trong bóng tối chỉ gửi thấy mùi rượi mà mùi máu tanh nhàn nhạt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô chống đối khóc lóc, nhưng sức lực Lệ Đình Tuấn mạnh kinh người, giống như một dã thú mất đi lý trí vậy, dày vò cô đến ngất đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ chuyện đã xảy ra rồi, chuyện tối qua giống như một cơn ác mộng vậy, toàn thân cô đau đớn, xương cốt rã rời giống như bị đánh một trận vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đang muốn giải thích, đột nhiên nhìn thấy phần bụng đầy sức sống của Lệ Đình Tuấn, vậy mà lại có một vết máu dài chừng mười mấy phân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn chưa nhìn rõ, Lệ Đình Tuấn đã xoay người đi đến mép giường, giơ tay, bóp lấy cái cằm nhỏ nhắn của cô, trầm giọng nói: “Kiều Phương Hạ, cô cho rằng cô dùng loại phương thức này thì tôi có thể tha cho cô sao?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không phải, em…” Kiều Phương Hạ hoảng loạn phủ định.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn chưa nói xong, nhìn thấy trong mắt Lệ Đình Tuấn có mất phần ghê tởm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ nhìn thấy vẻ mặt không hề che đậy của anh, trái tim chùng xuống, ngẩn ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngoài cửa, đột nhiên truyền đến giọng nói kinh ngạc của bà Trần: “Đây không phải là quần áo của cô chủ sao? Sao lại ở ngoài cửa thế này?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lệ Đình Tuấn buông Kiều Phương Hạ ra, quay người trực tiếp lấy một cái áo sơ mi ném cho Kiều Phương Hạ, trầm giọng nói: “Mặc vào, ra ngoài.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ anh bảo cô ra ngoài sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng bên ngoài toàn là người, mọi người sẽ phát hiện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mặc dù nói cô và Lệ Đình Tuấn hoàn toàn không phải là anh em ruột thịt, nhưng trên dưới nhà họ Lệ ai mà không biết, mẹ cô An Phương Diệp là tình nhân của ba Lệ Đình Tuấn chứ.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ hoảng loạn không khống chế được run rẩy, nước mắt trực tiếp rơi xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô kéo lấy cánh tay Lệ Đình Tuấn, nhẹ giọng cầu xin: “Anh, anh nghe em giải thích được không? Tối qua em…”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lệ Đình Tuấn hung dữ hất tay cô ra, gương mặt tràn đầy lạnh lùng: “Kiều Phương Hạ, cô thật là đê tiện giống như mẹ cô vậy.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ bị hất ra ngã về giường, xương cốt đau nhức giống như bị lung lay.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó điều khiến người ta đau lòng hơn chính là câu nói anh vừa nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện của ba mẹ, lúc trước cô còn nhỏ, không thể hiểu cũng không thể nhúng tay, cô chỉ nhớ cô luôn khóc xin ba mẹ đừng ly hôn, nhưng bọn họ vẫn ly hôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi mẹ đưa cô đến nhà họ Lệ, cô chẳng qua chỉ mới sáu tuổi, nhà họ Lệ không tiếp nhận bọn họ, mẹ để cô đi nịnh nọt người thanh niên lạnh như băng này, khiến mẹ con bọn họ có chỗ đứng trong nhà họ Lệ, cho nên cô làm theo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô biết, Lệ Đình Tuấn vẫn luôn không thích cô, ghét cô giống như một con sâu bám lấy anh vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng cho dù bản tính anh có lãnh đạm, cũng chưa bao giờ dùng thái độ ghét bỏ này để đối xử với cô.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ không biết rốt cuộc là chỗ nào xảy ra vấn đề, cô cũng không hiểu tại sao chỉ qua mấy ngày không gặp mà Lệ Đình Tuấn lại biến thành thế này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rõ ràng lần trước rời đi, anh còn nói sẽ mang về món bánh mà cô thích.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">||||| Truyện đề cử: Bảo Bối! Anh Xin Lỗi |||||</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cho cô mười giây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cút ra ngoài.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lệ Đình Tuấn nghiến rằng, trầm tiếng nói với cô..</span></p>', 0, '2022-07-01 05:39:33', '2022-07-01 05:39:33');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(86, 66, 'Chương 2: Con Dâu Tương Lai Của Nhà Họ Lệ', 'chuong-2-con-dau-tuong-lai-cua-nha-ho-le', 2, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi Kiều Phương Hạ mặc quần áo vào và đẩy cửa bước ra ngoài, một nửa người nhà họ Lệ đều đứng vây quanh bên ngoài cửa, kinh ngạc nhìn cô thương tích đầy người, bước chân trần từ trong phòng đi ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ chỉ cảm thấy những ánh mắt đó như những con dao sắc nhọn cứa lên cơ thể cô, khiến cô cảm thấy đau thấu xương tủy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà Trần thấy cô lập tức tiến lên, lấy tấm thảm mỏng quấn lên người cô, đưa cô quay về phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đầu óc Kiều Phương Hạ lúc này rất rối bời, cô ngồi trong phòng của mình, nghe thấy tiếng trò chuyện mờ hồ truyền đến từ vòng bên: “Trước mặt bao nhiêu người như vậy, dù sao con cũng phải đối xử với con bé có trách nhiệm một chút chứ.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Trách nhiệm sao?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lệ Đình Tuấn bật cười đầy mỉa mai: “Vậy thì con sẽ thành toàn cho cô ta.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi chiều, nhà họ Kiều liền cho người đến.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hành lý của Kiều Phương Hạ giống như một đống rác bị vứt trước cổng nhà họ Lệ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đuổi Kiều Phương Hạ về chỗ bên ba ruột của cô là việc làm cuối cùng mà nhà họ Lệ có dính líu đến cô.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bầu trời bỗng đổ mưa to, nhưng Kiều Phương Hạ không mở ô lên, cô chỉ đứng im ở đó, ngước mắt nhìn người đàn ông đang đứng trên hành lang, lạnh lùng nhìn cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Anh ơi.” Đôi môi Kiều Phương Hạ run rẩy, đôi mắt cô mờ đi bởi nước mưa, cố gắng lên tiếng cầu xin Lệ Đình Tuấn lần cuối cùng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chính anh đã làm chuyện đó với cô, nhưng cô không dám khẳng định liệu lúc đó anh có phải bị kẻ khác hãm hại không.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không trách anh, nhưng tại sao Lệ Đình Tuấn lại nhẫn tâm đuổi cô đi cơ chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không muốn quay về nhà họ Kiều, chính anh cũng biết điều đó mà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đối diện với lời cầu xin của Kiều Phương Hạ, gương mặt mặt Lệ Đình Tuấn không có lấy một tia thương xót, anh quay người lại, biến mất sau cánh cửa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cánh cửa sắt được chạm khắc tinh xảo cũng nặng nề đóng lại trước mắt cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện Kiều Phương Hạ leo lên giường của Lệ Đình Tuấn, chỉ trong vài ngày ngắn ngủi đã được đồn thổi khắp thành phố Hạ Du.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người đều cho rằng, do mẹ của Kiều Phương Hạ đã cuỗm mất hơn ba mươi tỷ của nhà họ Lệ rồi bỏ trốn cùng tình nhân, chỉ để lại một mình Kiều Phương Hạ ở trong nhà họ Lệ, vì để bảo vệ chỗ đứng của chính mình, có thể tiếp tục trèo cao ở nhà họ Lệ, nên Kiều Phương Hạ đã làm ra chuyện đáng xấu hổ như vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một tháng sau, Kiều Phương Hạ được lặng lẽ đưa ra nước ngoài, cả hai nhà họ Lệ và họ Kiều đều chọn cạnh im lặng khi đối diện với chuyện này, dù sao thì với địa vị của nhà họ Lệ ở thành phố Hạ Du này, dần dần cũng không có bất cứ ai nhắc đến chuyện này nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ba năm rưỡi sau, tại nhà họ Kiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tài xế ra sân bay đón cô cả đã trở về.” Người hầu nói nhỏ với Kiều Đông Phương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Đông Phương nhìn chiếc Audi đỗ ở dưới lầu, không nói câu nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lẽ ra đây không phải là thời điểm thích hợp để Kiều Phương Hạ quay về nước, việc học hành của cô ở bên nước ngoài vẫn chưa hoàn thành, song ông cụ nhà họ Kiều đột ngột trở bệnh nặng, vẫn luôn mong muốn được gặp Kiều Phương Hạ lần cuối, vì vậy Kiều Đông Phương không còn cách nào khác, chỉ đành để Kiều Phương Hạ quay về.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bên ngoài trời mưa rất to, Kiều Phương Hạ cầm một chiếc ô màu đen, bước ra từ chiếc xe Audi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ngẩng đầu lên, vừa hay bắt gặp ánh mặt Kiều Đông Phương đang nhìn cô, hai người nhìn nhau vài giây, rồi Kiều Phương Hạ vô cảm thu lại tầm mắt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thật khéo làm sao, bất kể ngày cô bị đuổi khỏi nhà họ Lệ hay là ngày cô bị đưa ra nước ngoài, thậm chỉ cả ngày hôm nay, khi cô về nước, trời đều đổ mưa to.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chị về rồi.” Cô con gái thứ hai của Kiều Đông Phương, Kiều Diệp Ngọc đứng cạnh cửa, nở một nụ cười xinh đẹp, lên tiếng chào hỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ừm.” Kiều Phương Hạ chỉ thờ ơ đáp lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong cô liền mang hành lý vào bên trong, thẳng lưng bước qua cửa nhà họ Kiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên đường trở về, cô nghe quản gia nhà họ Kiều kể một số chuyện, Kiều Diệp Ngọc đã thi đỗ Đại họ Sân khấu điện ảnh, tuy mới học năm hai những đã có không ít hợp đồng quay phim trong tay, cô ta vừa mới quay xong một bộ phim cổ trang, mặc dù chưa công chiếu nhưng đã rất nổi tiếng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người đều đồn thổi rằng, những hợp đồng của Kiều Diệp Ngọc đều do nhà họ Lệ đích thân giành lấy, bởi vì Kiều Diệp Ngọc đã trở thành con dâu tương lai của nhà họ Lệ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giờ đây khi nghĩ đến nhà họ Lệ, trong lòng Kiều Phương Hạ chỉ còn lại sự bình thản.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mối quan hệ giữa nhà họ Lệ và nhà họ Kiều là gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Diệp Ngọc sẽ gả cho người đàn ông hô mưa gọi gió ở thành phố Hạ Du này sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đã có chuyện gì xảy ra trong khi cô ở nước ngoài?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng dù sao thì cô cũng không quan tâm đến những chuyện này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi đi chào hỏi ông nội xong, Kiều Phương Hạ quay trở lại phòng khách, liền trông thấy Kiều Đông Phương đang ngồi đó chờ cô, dường như ông ta có chuyện gì đó muốn nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Nếu như đã về rồi thì đừng đi nữa.” Kiều Đông Phương nói với cô</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ bước đến bên ghế sô pha, cô ngồi xuống, hai chân tao nhã bắt chéo nhau, cô nhìn thẳng vào Kiều Đông Phương, nhếch miệng nói: “Sao thế? Có chuyện cần tôi sao?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Gần bốn năm không gặp, Kiều Đông Phương cảm thấy Kiều Phương Hạ đã thay đổi kha nhiều, nhíu mày đáp: “Ngày mai ba có sắp xếp cho con đi xem mắt ở bữa tiệc của giám đốc Tiêu, tối nay nghỉ ngơi cho tốt.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xem mắt?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">E là ông ta chỉ muốn nhanh chóng đuổi cô ra khỏi cái nhà này mà thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ nở một nụ cười yếu ớt: “Ông Kiều cảm thấy một người già nua héo mòn như tôi còn có người dám lấy không? Chuyện năm đó ầm ĩ như vậy, ông không sợ sẽ khiến nhà họ Kiều mất mặt sao?”.</span></p>', 0, '2022-07-01 19:14:34', '2022-07-01 19:14:34'),
(87, 66, 'Chương 3: Kỳ Tài Trong Giới Kinh Doanh', 'chuong-3-ky-tai-trong-gioi-kinh-doanh', 3, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe thấy Kiều Phương Hạ ăn nói trắng trợn như vậy, gương mặt Kiều Đông Phương liền tỏ vẻ không hài lòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ có vẻ ngoài giống mẹ ruột của cô, An Phương Diệp đến bảy tám phần, làn da trắng như tuyết, dáng vóc cao ráo nóng bỏng, trời sinh đã vô cùng quyến rũ, nhưng lại có khuôn mặt thanh khiết, đôi mắt trong veo, giống như một cô gái chưa từng nhiễm bụi trần.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trước khi qua nước ngoài, cô vẫn còn trẻ, những đường nét trên gương mặt vẫn chưa nở rộ, giờ đây cô như bông hoa dưới ánh mặt trời, kiêu hãnh tỏa hương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây chính là vốn liếng của cô, trẻ tuổi và xinh đẹp, chắc chắn sẽ khiến những thương nhân lớn tuổi không thể rời mắt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Năm đó Kiều Đông Phương cũng đã nhìn trúng người mẹ đê tiện của Kiều Phương Hạ vì cái vẻ ngoài ấy, vì vậy mới cưới bà ta về nhà.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ nhìn lại mới thấy, đúng là mẹ nào con nấy, An Phương Diệp là người đàn bà ti tiện, sinh ra Kiều Phương Hạ vừa mới lớn đã biết bò lên giường của Lệ Đình Tuấn, đúng là chả ra cái thá gì.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ sớm đã quen thuộc với ánh mắt đầy khinh miệt của Kiều Đông Phương, vì vậy cô làm như không nhìn thấy, trực tiếp đối mắt với ông ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Rất nhiều thương nhân và những kẻ tai to mặt lớn sẽ có mặt ở đó, lo mà biểu hiện cho tốt.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Đông Phương lạnh lùng nói tiếp: “Ông nội con mong rằng trước khi mất có thể thành toàn cho chuyện chung thân đại sự của con.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dứt lời, Kiều Đông Phương liền đứng dậy, rời khỏi phòng khách, không cho Kiều Phương Hạ bất cứ cơ hội từ chối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ nhìn theo bóng lưng của Kiều Đông Phương, sắc mặt trở nên lạnh lùng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô bất ngờ lên tiếng nói: “Nếu như đi xem mắt, chẳng phải nên ăn mặc lộng lẫy một chút sao?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Đông Phương nghe thấy vậy liền dừng bước, không chút kiên nhẫn rút ra một tấm thẻ ngân hàng từ trong túi ra đưa cho cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông ta biết rõ, Kiều Phương Hạ và An Phương Diệp mê tiền như nhau, chỉ cần đưa tiền cho bà ta, bắt bà ta ăn cứt, bà ta cũng vui vẻ ăn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cảm ơn.” Kiều Phương Hạ nhận lấy tấm thẻ, thờ ơ bước ra khỏi cửa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng lúc này Tống Vân Lan bước từ cầu thang xuống, vừa hay nhìn thấy cảnh vừa rồi, gương mặt lạnh lùng, cất giọng đầy châm biếm: “Dù sao cũng là tiền mà Diệp Ngọc của chúng ta kiếm được, anh dặn cô ta yên tâm mà dùng.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đối với Tống Vân Lan, trong lòng Kiều Đông Phương quả thực có chút hổ thẹn với bà ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Năm xưa khi ly hôn với An Phương Diệp, ông ta đã hứa như đinh đóng cột rằng sẽ vạch rõ giới hạn với hai mẹ con An Phương Diệp, nhưng cho tới hiện tại, ông ta vẫn không thể rũ sạch mọi quan hệ với Kiều Phương Hạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đợi qua ngày mai là mọi chuyện sẽ ổn thôi, em cứ yên tâm.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn nữa mấy người này cũng là những người máu mặt, cho dù Phương Hạ gả cho ai đi chăng nữa, nhà chúng ta cũng không chịu thiệt đâu, sính lễ cũng phải lên đến vài tỷ ấy chứ.” Ông ta nhẹ nhàng dỗ dành.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tốt nhất là nên như vậy, nếu không cái nhà này phải nuôi thứ rác rưởi như cô ta đến bao giờ nữa?” Tô Vân Lan độc địa nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bên ngoài, Kiều Phương Hạ chỉ im lặng lắng nghe cuộc đối thoại giữa hai người bọn họ, nắm chặt lấy tấm thẻ trong tay.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc nãy ra ngoài cô mới phát hiện ra mình quên không mang theo chuột laptop, nên mới định quay lại để lấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hóa ra, Kiều Đông Phương muốn bán cô đi, muốn tận dụng một chút giá trị cuối cùng của cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không khỏi nhếch môi nở một nụ cười châm biếm, xoay người rời khỏi nhà họ Kiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trung tâm thương mại Universal.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên điện thoại, King gửi một tin nhắn: “Đã đến chưa?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đến rồi, đang tìm quán cà phê.” Kiều Phương Hạ vừa trả lời tin nhắn, vừa bước vào bên trong.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đang bước đến chân cần thang máy, bỗng một đám người xuất hiện, ngang ngược đẩu những người khác ra: “Tránh ra, tránh ra.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ chỉ vô thức liếc nhìn về phía đó, nghĩ rằng đã xảy ra một vụ tai nạn ở thang máy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đó có phải Tổng giám đốc Lệ không? Nghe nói hôm nay Tổng giám đốc Lệ đến đây để đàm phán việc thu mua lại trung tâm thương mại Universal, chắc thông tin này không sai đâu.” Có hai phóng viên đang cầm máy ảnh đứng ở phía trước Kiều Phương Hạ, vội vàng xông về phía thang máy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Nếu có được trung tâm thương mại Universal, thì chẳng phải tập đoàn WL đã thâu tóm được công ty thứ mười rồi sao? Tuy nhiên tôi không ngờ rằng, chỉ trong vòng chưa đến bốn năm, Tổng giám đốc Lệ lại có thể khiến tập đoàn của mình cải tử hoàn sinh, quả là kỳ tài trong giới kinh doanh.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tổng giám đốc Lệ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ nghe thấy vậy liền vô cùng sửng sốt, cô ngước mắt về phía câu thang cuốn ở tầng ba của trung tâm thương mại Universal.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngoại trừ hai người vệ sĩ ở đằng trước và đằng sau, còn có một người đàn ông mặc vest thẳng tắp, dáng người cao lớn đứng chính giữa, một tay ôm một em bé sữa trông mới hai ba tuổi, anh như thể hoàng đế giáng thế, khí thế ngút trời..</span></p>', 0, '2022-07-01 19:15:11', '2022-07-01 19:15:11'),
(88, 67, 'Chương 1: Kẻ Ngốc Gả Thay', 'chuong-1-ke-ngoc-ga-thay', 1, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tại biệt thự nhà họ Thời.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ, con sẽ không kết hôn, có chết con cũng không kết hôn, hu hu hu...”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha nhào vào lòng mẹ khóc như mưa, khuôn mặt nhỏ nhắn xinh đẹp tràn ngập tuyệt vọng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta có nằm mơ cũng không nghĩ đến việc mình sẽ phải hẹn hò vui vẻ với một người mù!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy rằng Thịnh Hàn Ngọc khi trưởng thành vô cùng đẹp trai, thậm chí còn đẹp hơn cả minh tinh, nhưng có ích gì chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta không muốn kết hôn với một người đàn ông mù, cả đời này cô sẽ biến thành trò cười của thiên hạ!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Con gái ngoan, đừng khóc, mẹ sẽ tìm cách giúp con, mẹ sẽ không bao giờ để con gả cho người tàn tật.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đừng khóc, con khóc làm mẹ rất đau lòng.” Giang Nhã Đan vừa dỗ dành con gái, vừa hung hăng trừng mắt với chồng đang ngồi đối diện.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện này đều do ông ấy, không xem xét rõ ràng trước khi quyết định.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho dù nhà họ Thịnh có quyền có thế, tài sản dư thừa nhưng Giang Nhã Đan sẽ không bao giờ chấp nhận để đứa con gái bảo bối kết hôn với một người đàn ông mù.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vũ Kha không thể nào kết hôn với Thịnh Hàn Ngọc được.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vũ Thành, anh không được đẩy con gái của chúng ta vào con đường chết!” Giang Nhã Đan lau nước mắt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Thành thở dài bất đắc dĩ nói: “Em nghĩ anh bằng lòng sao? Nhưng em cũng biết hoàn cảnh của gia đình chúng ta, nợ nần bên ngoài nhiều như vậy, nếu như Vũ Kha gả đi gia đình chúng ta sẽ không phải phá sản.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Ông chủ nhà họ Thịnh đã hứa chỉ cần chúng ta gả con gái đi, họ liền chia cho chúng ta 10% cổ phần, cộng thêm 350 tỷ là quà đính hôn...\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trước khi ông nói hết lời, hai mẹ con đã ngừng khóc, trong ánh mắt lộ ra một tia thèm thuồng!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha không ngừng đung đưa cánh tay mẹ mà làm nũng: “Mẹ ơi, con muốn có 10% cổ phần của nhà họ Thịnh, hơn nữa quà đính hôn 350 tỷ cũng đủ để gia đình chúng ta trở thành nhà quyền thế bậc nhất Giang Châu...!\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giang Nhã Đan nghĩ rằng con gái mình đã thay đổi quyết định, mặc dù cũng bị cám dỗ bởi một điều kiện thuận lợi như vậy, nhưng bà vẫn do dự khi nghĩ đến hạnh phúc cả đời của con: “Con gái ngốc, đừng đồng ý nhanh như vậy, hãy suy nghĩ kĩ càng.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ...”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha ngồi thẳng người, khuôn mặt xinh đẹp thoáng qua một tia ranh mãnh: “Bố mẹ ơi, bố đã quên rằng nhà chúng ta vẫn còn một cô con gái hay sao? Mẹ có thể để Thời Du Huyên đó gả đi, chia cổ phần cho con và báo hiếu cho bố mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàn hảo!\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà họ Thời có hai cô con gái, cô chị Thời Vũ Kha là con ruột, cô ta trắng trẻo xinh đẹp, là mỹ nữ bậc nhất ở Giang Châu!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô con gái thứ hai tên Thời Du Huyên, là con gái của bạn tốt Thời Vũ Thành, bố mẹ cô bị tai nạn xe hơi qua đời ngay sau khi cô được sinh ra, vì vậy Thời Vũ Thành đã nhận cô làm con nuôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô em gái thực ra xinh đẹp hơn chị, nhưng năm mười hai tuổi không may bị ngã cầu thang khiến đầu óc trở nên không bình thường, trở thành một đứa ngốc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Thành không đồng ý: “Không được, nếu Vũ Kha không muốn kết hôn thì chúng ta từ chối, dù sao cũng không thể gả Huyên Huyên đi được.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giang Nhã Đan cau mày, đồng tình với ý kiến của con gái: “Vũ Kha nói đúng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên cũng là con gái của nhà họ Thời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dù sao thì nhà họ Thịnh nói muốn cưới con gái chúng ta, như vậy thì gả ai cũng như nhau.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Thành vẫn phản đối: \"Đây không phải là chuyện vớ vẩn hay sao, Vũ Kha đi xem mắt, sau đó lại để cho Huyên Huyên kết hôn ư?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Huyên Huyên bị bệnh tâm thần, Thịnh Hàn Ngọc sẽ không thể nào đồng ý kết hôn với Huyên Huyên cho dù cậu ấy bị tàn tật.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Với gia thế nhà họ Thịnh như vậy, chúng ta không thể tùy ý động vào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu không đến lúc đó cả nhà chúng ta đều phải chết.\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông ta ban đầu không đồng ý nhưng dần dần bị vợ và con gái tẩy não.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giang Nhã Đan nói: “Lúc trước nhà họ Thịnh đưa người đến mai mối cũng không nói rõ là con gái nào, họ chỉ nói rằng muốn xem mắt con gái nhà chúng ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn nữa hai người đi gặp riêng trong buổi gặp mặt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cậu chủ Thịnh bị mù nên cậu ấy sẽ không biết người đó là Vũ Kha.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Thành: “...”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha nói: “Đúng vậy đó bố, anh ta không biết con trông như thế nào, vì vậy để một đứa ngốc kết hôn thay con cũng không sao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thịnh Hàn Ngọc bị mù và Thời Du Huyên bị ngốc.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giống như một cặp trời sinh.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giang Nhã Đan: “Ông xã, anh đồng ý đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà họ Thịnh có 10% cổ phần, lại thêm 100 triệu quà đính hôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Với số tiền này Vũ Kha đủ để tìm được một người đàn ông xuất sắc kết hôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà họ Thời sẽ trở nên nổi tiếng ở Giang Châu!”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên ôm con gấu nấp sau cây cột, lẳng lặng nghe bọn họ bàn cãi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cúi đầu, cho dù có người phát hiện cô đang ngồi ở đây, cũng không có ai để ý đến đôi mắt xinh đẹp của Thời Du Huyên lóe lên vẻ tinh anh, khóe miệng cong lên một đường đẹp đẽ, lộ ra tia châm biếm!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dáng vẻ này căn bản không hề giống người ngốc nghếch.</span></p>', 0, '2022-07-01 19:31:50', '2022-07-01 19:31:50');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(89, 67, 'Chương 2: Giả Heo Ăn Hổ', 'chuong-2-gia-heo-an-ho', 2, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai mẹ con khuyên nhủ hơn nửa ngày, cuối cùng Thời Vũ Thành cũng xuôi theo: \"Được rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong ông ta liền đứng lên chuẩn bị đi đến phòng sách, vừa đi được hai ba bước thì quay đầu dặn dò: \"Vũ Kha, con đối xử với em gái tốt một chút, đừng có suốt ngày gọi con bé là kẻ ngốc, là con gái thì không nên quá cay nghiệt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Dạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" Thời Vũ Kha trả lời, cô ta không phục nhăn mặt nhìn về bóng lưng của bố nhưng hoàn toàn không nói cho bố những suy nghĩ trong lòng của mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ là nghĩ đến việc kẻ ngốc đó sẽ nhanh chóng thay mình gả cho người mù của nhà họ Thịnh, nhưng mà tất cả cổ phần của nhà họ Thịnh đều sẽ thuộc về mình, tâm trạng cô ta cảm thấy tốt hơn nhiều.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Huyên Huyên? Em ở đâu? Chị chỉ em chơi trò này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên ở phía sau cây cột đi đến: \"Em ở đây, Huyên Huyên thích chơi trò chơi nhất! \"</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha lấy tờ mười nghìn và tờ một trăm nghìn từ trong túi ra, cô ta ném hai tờ tiền ấy xuống đất rồi mím môi cười chờ xem kịch vui!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quả nhiên Thời Du Huyên không khiến cô ta thất vọng, cô không do dự nhặt tờ mười nghìn lên nhét vào trong túi, còn không thèm nhìn tờ một trăm nghìn đó một cái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha cười, đôi lông mày cong lên, cô ta tiếp tục ném mười tờ mười nghìn và một trăm nghìn xuống sàn nhà, nhưng Thời Du Huyên vẫn không lưỡng lự nhặt tờ mười nghìn lên, sau đó nhìn về phía cô ta cười với một vẻ mặt ngây thơ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Không đúng, một lần nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta ném tiền một lần nữa, nhưng lần nào Thời Du Huyên cũng chỉ nhặt tờ mười nghìn lên, hoàn toàn lơ đi tờ một trăm nghìn đó, lần nào cũng vậy!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha thẳng tay lấy tờ mười nghìn cuối cùng trong túi ra vứt xuống cũng bị Thời Du Huyên cho vào túi, tất cả tờ trăm nghìn đều bị kẻ ngốc đó \"vứt bỏ\" trên sàn nhà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta ôm cánh tay mẹ nũng nịu: \"Mẹ nhìn xem, con muốn đối xử tốt với con bé đó một chút, nhưng nó chẳng hề muốn thế.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Được rồi, nghe lời bố con đối xử với nó tốt một chút, ngày hôm nay mẹ mệt rồi, mẹ trở về phòng nghỉ ngơi một chút đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" Chỉ cần con gái ruột của mình vui vẻ thì Giang Nhã Đan không quan tâm Thời Du Huyên bị \"thua thiệt\" hay \"bị lợi dụng\".</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trò chơi kết thúc, Thời Vũ Kha nhặt hết tờ một trăm nghìn dưới đất lên rồi vỗ vào mặt Thời Du Huyên: \"Kẻ ngốc cũng chỉ là một kẻ ngốc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" Nói xong cô ta hài lòng cất tiền vào trong ví, xoay người trở về phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên ngây ngốc cười lớn, nụ cười trong trẻo và ngu ngơ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ nhỏ đến lớn đã chơi trò này không biết bao nhiêu lần, lúc nào Thời Du Huyên cũng sẽ chọn tờ tiền có giá trị nhỏ, lơ đi tờ tiền mệnh giá lớn, Thời Vũ Kha cười nhạo cô ngu ngốc nhưng lại không biết chính vì vậy mà Thời Du Huyên có cả một núi tiền.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Năm Thời Du Huyên mười hai tuổi, cô đứng bên ngoài phòng sách vô tình nghe được bố mẹ cãi nhau!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc đó cô mới biết bố ruột của mình đã giữ lại một số tiền rất lớn trong nhà, mà khoản tiền này đã bất ngờ chuyển sang tên của mẹ nuôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Thành yêu cầu Giang Nhã Đan trả tiền lại để đưa cho con gái nuôi của mình.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên vừa nghĩ đến điều này liền nhận ra hai năm gần đây cô suýt chút nữa phải chết một cách \"bất ngờ\", có lẽ có liên quan đến mẹ nuôi!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có những người vì tiền mà không từ bất cứ thủ đoạn nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi chiều hôm đó Thời Du Huyên bị Vũ Kha dùng sức đẩy từ trên cầu thang xuống!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô lăn đến cuối chân cầu thang, hôn mê bất tỉnh, được cấp cứu ở bệnh viện nhưng sau đó lại trở thành kẻ ngốc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kẻ ngốc sẽ không có ngày tự mình đứng lên được, nhiều năm trôi qua như vậy, Thời Du Huyên giả vờ ngốc chính là vì muốn tự bảo vệ mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng may Thời Vũ Thành đối xử với cô rất tốt, để cho Thời Du Huyên ở trong căn nhà này khiến cô cảm thấy cũng có chút ấm áp, còn Giang Nhã Đan nhìn thấy cô như vậy sẽ không so sánh với con gái xuất sắc của bà ta nữa, cũng sẽ tha thứ cho cô hơn, chỉ như vậy mới sống bình yên đến năm hai mươi tuổi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đồng ý gả thay vì muốn báo đáp công ơn nuôi dưỡng của Thời Vũ Thành!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ đây về sau những gì cô nợ gia đình này đều không còn nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng tất cả những gì Giang Nhã Đan và Thời Vũ Kha đã làm với cô, sau khi cô gả thay thì cũng đến lúc phải tính toán rõ ràng rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên trở về phòng đóng cửa lại, sau đó ôm con gấu bông của mình trốn vào trong tủ quần áo, kéo khóa ở phía sau lưng của con gấu, đưa tay vào lục lọi! Sau đó lôi ra một cái máy tính xách tay nhỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Máy tính xách tay im lặng khởi động, trên màn hình không có biểu tượng phần mềm gì, Thời Du Huyên thuần thục mở phần mềm đã được ẩn đi, gõ mật mã đăng nhập vào Zalo.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ảnh đại diện của Giản Nghi Ninh liên tục nhấp nháy, sau khi bấm vào thì hàng loạt tin nhắn hiện ra: “Ảnh Tử, tập đoàn Đính Thịnh có một hạng mục bí mật lớn với vốn đầu tư lên đến mấy chục tỉ, chúng ta có nên lấy không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mua đề án của Khánh Phong nữa, thấy thế nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô có tham gia vào hội đồng quản trị của công ty không?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thật ra thì hạng mục mấy chục tỉ khiến cả nước chấn động không phải là chuyện Giản Nghi Ninh quan tâm nhất, điều cô ấy quan tâm nhất chính là hội đồng quản trị, Ảnh Tử có tham gia vào đó hay không!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ảnh Tử là tên trên mạng của Thời Du Huyên, hai người vô tình quen biết nhau trên mạng hồi đầu năm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giản Nghi Ninh là cộng sự của Thời Du Huyên, hai người cùng nhau điều hành một công ty đầu tư mạo hiểm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên phụ trách điều khiển công ty, đưa ra những quyết sách quan trọng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn Giản Nghi Ninh phụ trách việc điều động nhân sự và sắp xếp kế hoạch rõ ràng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Công ty thành lập đã được năm năm, có thể nói hai người hợp tác rất ăn ý với nhau, chỉ có điều Giản Nghi Ninh không biết mặt mũi của Ảnh Tử như thế nào, đã lớn tuổi hay còn trẻ, là nam hay là nữ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mười ngón tay của Thời Du Huyên như đang bay lượn trên bàn phím, nhanh chóng gõ sách lược quyết định rồi gửi đi, sau đó đóng máy tính xách tay lại và nhét vào bên trong con gấu bông, cô đi ra khỏi tủ quần áo, mở cửa phòng nói: \"Đói thật đấy, Huyên Huyên đói quá!\"</span></p>', 0, '2022-07-01 19:32:24', '2022-07-01 19:32:24'),
(90, 68, 'Chương 1: Rất nhiều dấu hôn', 'chuong-1-rat-nhieu-dau-hon', 1, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mưa đêm đột ngột quất ràn rạt vào mái nhà chờ xe bus.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn An Nhiên vội lấy cặp sách che đầu, kiên nhắn chờ đợi chuyến xe cuối cùng trên đường phố vắng lặng Bỗng, một chiếc Hummer từ xa đi lại, đến gần nhà chờ xe bus nơi cô đang đứng thì bất thình lình bẻ lái, quặt thắng vào lề đường An Nhiên tránh vội, hết hồn nhận ra nơi mình đứng cách đuôi chiếc Hummer hầm hố đúng nửa mét. Chiếc xe kia tông thẳng vào gốc cây cổ thụ bên đường vắng vẻ, nằm im lìm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô kinh hãi chạy lên xem xét. Đang chạy mà bẻ lái nguy hiểm, không phải tài xế vừa đột ngột lên cơn đau tim hay bị đột quy đấy chứ? Hoặc là trong xe có tranh chấp gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chưa kịp nghĩ xong, cửa xe rầm một tiếng mở ra, bóng người cao lớn bên trong bước xuống. Bộ vest đen đắt tiền lập tức ướt sũng nước mưa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người kia có vẻ thật sự bị bệnh, áo quần xộc xệch, mái tóc đen rũ xuống che lấp một nửa khuôn mặt đẹp trai đến đáng kinh ngạc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên nhanh chóng chạy đến hỏi han: “Vừa rồi thật nguy hiểm! Anh có sao…?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe tiếng phụ nữ, Tống Thành cố giương mí mắt đang sụp xuống của mình lên nhìn nhưng chỉ thấy những hình ảnh nhòe nhoẹt bởi nước mưa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngay giờ phút này, hắn chỉ cảm thấy nóng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nóng như thiêu như đốt.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từng miligram thuốc đang chạy rần rật trong huyết quản của hẳn, làm cơ thể săn chắc cơ bắp của Tống Thành run lên, tỏa nhiệt khác thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhận thấy cơ thể mềm mại của phụ nữ đang tiến lại thì phía dưới của hản nóng lên, cứng đến phát đau, thôi thúc hắn áp chặt nơi mát mẻ nào đó để xả nhiệt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con mẹ nó! Tên khốn kia lại dám hạ thuốc kích dục với hắn!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên thấy Tống Thành đột ngột xông đến thì vội giơ tay ra đỡ, sợ hắn bị choáng váng sau cú tông xe. Cô lay gọi: “Đừng ngất! Tôi đưa anh đi bệnh viện”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khuôn mặt đẹp đế của Tống Thành áp vào hõm cổ An Nhiên, thở ra một hơi nóng rấy như sốt cao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng lúc đó, một chiếc lao đến, bật đèn sáng choang. Một tay An Nhiên đỡ thân thể nặng nề của Tống Thành, một tay vẫy loạn xạ ra tín hiệu gọi xe kia dừng lại. Chiếc xe màu đỏ rực đi chậm lại, đến gần hai người. An Nhiên đột nhiên thấy cảm giác quen thuộc, chưa kịp gọi thì đột nhiên tài xế kia rồ ga vọt đi mất. Trước khi đi qua, bánh xe còn hắt nguyên một vũng nước mưa tạt thẳng từ đầu tới chân cô ướt sũng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xui xéo! Quá xui xẻo!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô mất kiên nhắn, quay sang nói với Tống Thành – lúc này đã cởi áo khoác vứt trên đường, hai tay hắn ôm cô chặt cứng: “Chịu khó một chút, tôi đưa anh đi bệnh viện.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Hừm..”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một âm thanh trầm thấp từ cổ họng hẳn bay. Không để cô nghĩ nhiều, cánh môi nóng rực của hắn đã phủ lên môi cô, ngấu nghiến. Hương hoa mộc thanh ngọt khiến hắn thoải mái không ít, da thịt vốn ngứa ngáy cũng được xoa dịu phần nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn An Nhiên kinh hãi tột độ, cố đẩy đối phương ra nhưng sức cô so với hắn không khác gì con mèo hen. Tổng Thành sung sướng dùng lưỡi tách hai cánh môi thơm ngọt ra, sục sạo vào bên trong, sâu hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước bợt của cô phù hợp với cơn khát của hắn. Hắn chỉ có một thôi thúc mãnh liệt là phải làm gì đó đế vượt qua kiếp nạn này. Tống Thành vừa hôn, vừa liếm láp khoang miệng ẩm ướt, hai bàn tay tham lam hư hỏng không ngần ngại giật tung hàng khuy của chiếc áo sơ mi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chớp mắt, cơ thể thon thả của thiếu nữ mười bảy tuổi bị phơi bày dưới cơn mưa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không!!!” Cô gào lên, hoảng hốt tìm cách thoát thân. “Mau buông… đồ khốn này!… Bỏ chân Tôi ra…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành bị thuốc kích thích dẫn dắt, đẩy cô ngã xuống thảm cỏ trên vỉa hè, hung bạo cầm cổ chân trắng nõn của cô giơ lên, một tay xé rách dây quần lót bên trong. An Nhiên gào khóc âm], nhưng giữa đường vắng vẻ, khuya khoắt, lại mưa 1o như trút nước, không một ai giúp được cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh đèn vàng ấm áp chiếu lên thân thể thiếu nữ mềm nõn khiến hai mắt Tống Thành tối sắm lại. Mùi hương ngọt ngào chui vào khoang mũi hắn khiến cho cơn khát càng thêm cháy cổ nếu hắn không có cô ngay lúc này. Bàn tay to lớn của Tống Thành cầm lấy tay An Nhiên, khiến cô không cách nào che chắn nơi riêng tư đang bị người ta đòi hỏi nữa. Chút lương tri cuối cùng khiến Tống Thành thốt ra một tiếng khàn đục: “Xin lỗi”, rồi hắn cúi xuống hôn lên nơi ngọt ngào nhất của người con gái. Chỉ một lát nữa thôi, nơi này sẽ bị hẳn xé toang, ít nhất, hẳn có thể tận dụng chút tỉnh táo ít ỏi sót lại mà chuẩn bị trước cho cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Môi mỏng của hẳn áp lên da thịt An Nhiên, mái tóc dày dụi vào bụng dưới, cọ xát hai bên bắp đùi đang cố kẹp chặt khiến cơn ngứa của cô bùng lên, râm ran chạy lan xuống từng đầu ngón chân co quắp. Đầu lưỡi to lớn luồn lách linh hoạt len lỏi vào giữa những cánh hoa ướt đắm chọc vào tận sâu bên trong, vơ vét mật nước cuốn vào miệng. Mùi hoa mộc càng nồng đậm trong khoang mũi, trong miệng hẳn.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con mẹ nó, ngọt! Hãn say chết rồi! Môi hẳn mút chặt từng lớp cánh hoa, đầu lưỡi miêu tả tỉ mi mỗi nếp gấp nhỏ, mềm như thạch.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước mưa đã sâm sấp mặt cỏ, dính nhớp cả tấm lưng trần trụi của An Nhiên. Những lá cỏ non như hùa chung với khao khát tình dục của Tống “Thành, ra sức mơn trớn làn da cô. Sau một hồi bị gã đàn ông xa lạ điên cuồng hồn liếm, phía dưới của An Nhiên bắt đầu rậm rịch rỉ nước. Hương vị của cô hòa cùng nước mưa tạo thành chất lỏng mềm mại, óng ánh phun ra, tưới ướt môi lưỡi Tống Thành, nhỏ xuống cả cắm hẳn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Em thật ngọt ngào..”, hắn hài lòng khen ngợi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quả thực, hắn thích hương vị cô, thích một cách bản năng như thể tất cả từng tế bào trên người hắn đều cần cô bao bọc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành nâng cô dậy, đặt cô ngồi lên người hắn và bắt đầu xâm nhập, bất chấp móng tay cô cào rách da thịt hắn. Cơn đau nho nhỏ khiến tính chiếm hữu trong hẳn như con thú bị đứt cương, hắn không biết trời đất gì nữa, chỉ một mạch cắm thẳng vào nơi sâu nhất, hưởng thụ cảm giác thống khoái đang thỏa mãn cơ thể. Thứ thô dài, nóng rực như thép nung của hắn thô bạo tiến vào bên trong ấm áp, mềm mại của cô. Vì là lần đầu nên lối vào chặt khít, An Nhiên đau đến mức muốn ngất đi nhưng nước mưa lạnh lẽo làm cô tỉnh lại, từng giây từng phút cảm nhận cách hắn chiếm lấy cô. Ngực cô bị hẳn ngậm chặt, đầu lưỡi càn rỡ xoay vòng, cùng với eo lưng dẻo dai liên tục thúc mạnh khiến toàn thân cô dập đềnh như cưỡi trên con sóng dữ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không… đừng… cầu xin anh..”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Những tiếng rên tắc nghẹn, gãy đôi hòa lẫn với tiếng thở gấp gáp. Tống Thành đoạt lấy An Nhiên hết lần này đến lần khác, không một chút khoan nhượng. Hắn ở trên cô, dưới cô, bên trong cô, khiến cô thét lên bằng đủ mọi cách. Bên trong ấm áp của cô hút chặt hắn không buông, dù hẳn đã bản vào hai lần, lấp đầy mọi chỗ trống nhưng dường như vẫn chưa đủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lượng thuốc kích dục đã tan đi không ít nhưng Tống Thành vẫn không muốn ngừng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chưa bao giờ hẳn cuồng nhiệt đến thế. Hết lần này đến lần khác, hắn lại càng cứng hơn, lớn hơn, kiên trì không mệt mỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Một lần nữa, ngoan nào”, hẳn hổn hển dỗ dành, “Một lần nữa thôi”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên không còn sức để phản kháng nữa, cam chịu để mặc hản gặm cản khắp nơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cuộc xâm lược của Tống Thành sẽ kéo dài cả đêm nếu như chiếc xe bus không xuất hiện đúng lúc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh đèn xe từ xa cùng màu đỏ – vàng chói lọi như đánh thức An Nhiên đang sắp ngất đi vì đau đớn. Cô đồn hết sức lực, nghiến chặt quai hàm cản bắp tay Tống Thành đến bật máu rồi chạy một mạch về phía chiếc xe Tài xế thấy cô bé vừa lên xe váy áo xộc xệch, một bên giày còn bị rơi mất, cũng không nói năng gì. Chiếc xe bus chạy thẳng một mạch, bỏ lại Tống Thành đang thắn thờ ngồi bên cạnh chiếc Hummer đã vỡ nát một bên đèn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên ngồi khóc rấm rứt nửa tiếng thì xe bus về đến nhà. Thật may người lớn trong nhà đã đi ngủ hết, cô vội lết tấm thân đau đớn về phòng thì gặp ngay Vũ Như cũng vừa mở cửa phòng đi ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chị..”, An Nhiên hốt hoảng dùng cặp che dấu cơ thể rách nát của mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vũ Như liếc mắt một cái lập tức hiếu ngay vấn đề. Váy bị xé rách, áo sơmi đứt cúc, cần cổ trắng nốn đầy vết hôn đỏ ửng, khắp nơi trên dưới cơ thế Nguyễn An Nhiên đều tỏa ra hương vị tình dục.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn Vũ Như cười: “Em đi học thêm thế nào mà cổ đầy dấu hôn vậy?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên sợ hãi lắc đầu: “Không c‹ rồi cô vội chui vào phòng mình nhưng Vũ Như đã vào theo.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta thích thú nhìn em gái kế của mình run rẩy. Từ nhỏ, Vũ Như đã thích châm chọc An Nhiên. Ngay từ giây phút cha dượng nhặt được đứa con gái này trong rừng, cô ta đã thấy ngứa mắt. Bàn tay thô lỗ giật tung một bên cổ áo sơ-mi An Nhiên, làm lộ ra vô số vết đỏ trên khắp ngực, cổ và xương quai xanh. Cô ta nheo mắt: “Hẳn là làm cũng dữ dội đi”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên cảm thấy nhục nhã vô cùng, uất ức hỏi: “Chị, chiếc xe kia đúng là chị lái. Vì sao lúc đó lại bỏ mặc em?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn Vũ Như “A” lên một tiếng. Người đó quả thực đúng là em gái cô thật! Khi đó trời mưa, cô không muốn xuống xe nên đã phóng đi luôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dù sao chiếc Hummer kia bị tai nạn, đèn xe nát bét, cô mà dừng lại có khi lại rước thêm phiền phức. Mà khéo sao An Nhiên lại có mặt ở đó, còn đang ôm lấy một người đàn ông, tư thế mười phần mập mờ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Em à, chị Vũ Như của em đâu có độc ác như vậy, nếu lúc đó chị nhận ra em thì chắc chắn sẽ dừng xe”, Vũ Như cười thâm hiểm, “Chắc hẳn em sang đường tùy tiện, làm người ta bị tai nạn giao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thông đúng không? Cái xe đó cũng là loại đắt tiền, đèn xe vỡ toang như vậy, em có tiết kiệm tiền tiêu vặt cả năm cũng không đền nổi đâu”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong, cô ta lại lướt mắt một lượt từ đầu đến chân An Nhiên, mỉa mai: “Tiền thì đền không nối, nhưng vàng thì lại khác. Ngàn vàng chứ đâu có ít”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Những lời châm chọc của Vũ Như làm An Nhiên tái mặt. Chẳng phải đang ám chỉ cô làm người ta bị tai nạn, không có tiền nên phải mang thân thể ra đền bù cho tài xế sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một người chị lại bỏ rơi đứa em gái mới mười bảy tuổi của mình. Ở giữa đường. Trong đêm tối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Với gã đàn ông xa lạ. Nghĩ thế nào cũng quá tàn nhãn!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên tức giận đến ứa nước mắt, nhưng không để cô kịp nói gì, Vũ Như đã chặn họng trước: “Chị thấy em mang thân thể cho người ta chơi đùa như vậy thì rất đau lòng đó. Lại còn lấy trộm giày của chịt Loại phiên bản giới hạn này người ta không bán nữa, làm mất một chiếc rồi thì đền tiền cho chị đi”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giày này rõ ràng Vũ Như tự tay ném vào thùng rác, nói đĩ rất đau chân, dáng vẻ thô kệch khác hẳn hình ảnh quảng cáo, cô thấy tiếc nên mới nhặt lại để đi mà. Nhưng An Nhiên chưa kịp nói gì thì Vũ Như đã xách chiếc giày kia đi thẳng về phòng.</span></p>', 0, '2022-07-01 19:35:32', '2022-07-01 19:35:32');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(91, 68, 'Chương 270: Full', 'chuong-270-full', 270, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cánh cửa đầy xích sắt mở toang, kéo theo âm thanh loảng xoảng rợn người.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người đàn ông năm co quắp trên sàn, thân thể rách nát, đầy những vết máu ứ đọng loang lố, răng cửa rụng cả hai chiếc, môi và mắt hẳn sưng phù đến mức mặt mũi biến dạng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đi ra đii” Tiếng người quát chói tai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“May phước cho mày là cậu chủ khai ân vì mày khai báo thành thật đấy”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chí Vỹ ngấng mặt lên, thều thào hỏi “Còn Vũ Như thì sao?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô ta à?” Người kia cười khẩy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tội của cô ta còn chưa hết đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Địa ngục này vẫn phải ở lại thêm mấy ngày nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mày có thích ở chung luôn không?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe thế, Chí Vỹ hoảng sợ lắc đầu, vội vã chạy theo hướng người kia chỉ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn còn sống mà thoát ra khỏi đây đã là mừng lắm rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đời này hắn đã được dạy dỗ một bài học thật đất giá: không thể tùy tiện vu oan giá họa cho người khác.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên đường chạy ra ngoài, hẳn còn nghe loáng thoáng tiếng trẻ con líu lo: “Bố ơi, hôm nay mình đi đón mẹ và em gái được chưa?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chí Vỹ đánh liều ngoái đầu lại nhìn, thấy phía xa là một người đàn ông dáng hình cao lớn, đĩnh đạc đang cúi xuống xoa đầu đứa trẻ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đứa bé lại nói “Con không đòi bế đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lưng bố còn đau”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dường như nhận ra có kẻ đang nhìn lén, Tống Thành quắc mắt ngẩng lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong mắt hắn, cái thứ dơ bẩn kia không khác gì một con nhặng xanh bẩn thỉu.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn hơn cả bẩn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ vì cái mồm của hắn mà An Nhiên đã có một thời học sinh thê thảm, tiếng xấu theo đến tận bây giờ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Truyện Cung Đấu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chờ một lát ông ngoại con đến, chúng ta cùng đi”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vâng” Bé con ngoan ngoãn chờ đợi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lát sau, dường như nhịn hết nổi, nó lại líu lo: “Bà ngoại cũng sẽ đến chứ ạ?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“ừ”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thế còn ông trẻ thì sao ạ? Ông cũng đến đúng không?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ông trẻ nào?” Tống Thành ù ù cạc cạc hỏi lại “Ông trẻ là người yêu của bà ngoại, là người mà bố bảo là bạn thân của bố ấy” Cá Chép vui vẻ nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chú ấy nhìn rất trẻ, nhưng vì là người yêu của bà ngoại nên con phải gọi là ông trẻ thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông Nhĩ dặn con thế ạ”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành muốn đập đầu vào tường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thì ra con trai hẳn không phải hỏi Nguyễn Chính Quốc mà đang hỏi đến Vương Cư, Tô Ái Phương và cả Cao Vĩnh Mạnh nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện này nói ra thì dài, nhưng xảy ra nhanh đến mức khó tin.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngày đó hắn được đưa vào viện cấp cứu xong, hai ngày sau, tình cờ thế nào lại đúng lúc Vương Cư ôm theo Cá Chép chạy vào đó theo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thì ra ông ta thực sự xuống tay bắt cóc thẳng bé.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng không ngờ đứa trẻ lại chống trả kịch liệt, đang ở trên ô tô cũng dám mở cửa lao xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kết quả là lăn ra đường, va phải cục đá, máu chảy đầm đìa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cậu bé có nhóm máu hiếm” Tiếng y tá lo lắng vang lên bên ngoài, tình cờ lọt vào tai Tống, Thành.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bệnh viện chúng ta lại vừa mới dùng hết nhóm máu đó cho bệnh nhân ở phòng VIP rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chắc phải chờ rất lâu đấy: ‘Vương Cư là người có tiền, liền vùi dập đối phương ngay tức khäc: “Bắt đứa bé ba tuổi nãm chờ máu, các người có lương tâm không? Tôi không cần biết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Các cô mau mua hết máu hiếm về đây, đừng để cái bệnh viện này bị giải thể.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành nghe thấy thì đi ra mở cửa, không ngần ngại nói “Tôi chính là người có nhóm máu hiếm đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai đang cần máu?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy con trai năm chỏng gọng ở đó, hắn suýt chút nữa lật tung cả mả tổ nhà Vương Cư lên!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">‘Vương Cư biết mình sai, bị bắt ngay tại trận, không thể cãi được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành thuận lợi cho máu, Cá Chép cũng tiếp nhận dễ dàng khiến hẳn sinh nghi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhóm máu này hiếm không phải vì ít người có mà vì khả năng đào thải rất lớn, không phải cứ hai người cùng nhóm máu cho nhau mà được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">‘Vậy mà bé con này lại nghiễm nhiên nhận của hẳn mấy trăm cc máu mà chẳng có tác dụng phụ hay biểu hiện nào chứng tỏ bị đào thải cả.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi chiều, Ân Lãm đến báo cáo tình hình thẩm vấn Chí Vỹ và Nguyễn Vũ Như, mang theo một tin tức động trời: “Năm xưa, An Nhiên bị người lạ cưỡng bức mà có thai, không phải vì ăn chơi trác táng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Toàn bộ đều là tin đồn thất thiệt do Vũ Như xúi bẩy Chí Vỹ dựng lên”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe đến đây, dường như có cái gì đó chạy rần rật trong huyết quản của Tống Thành.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn đăm chiêu nghĩ lại một đoạn quá khứ đen tối mà mình vẫn muốn quên đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần đó, trong cơn mưa tầm tã, hắn đã gặp một người con gái rất mềm mại, ngọt ngào.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô bị hẳn làm đến lăn lộn khổ sở, sau đó bỏ chạy mất dạng Đến giờ hẳn mới nhớ ra, cô gái đó đi xe bus, trong khi Vũ Như ở thời điểm đó có một chiếc xe đỏ chót.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn nữa, ngay từ lần gặp đầu tiên, Vũ Như tuy giữ lễ nhưng ánh mắt thèm khát vẫn lén lút quét khắp người hắn, chẳng bao lâu liền bộc lộ ham muốn tiền tài vô độ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu vậy, chắc chắn đêm đó, nếu xảy ra chuyện, cô ta sẽ không chọn cách bỏ chạy sau khi nhìn thấy hẳn cùng chiếc siêu xe của mình Quả nhiên,cho Vũ Như một liều thuốc kích dục, treo cô ta lên như một miếng mố trước đám đàn ông khiến cô ta mất hết trí.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cái bí mật giấu kín suốt bốn năm nay bị phun ra bằng sạch.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này Tống Thành mới giật mình hoảng hốt, hắn đã lơ đãng suốt bốn năm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì hẳn vẫn đinh ninh rằng người đó là Vũ Như, cho nên không bao giờ muốn nhớ lại đoạn kí ức đó, cứ thế mắt nhẫm mắt mở cho qua.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thế nhưng sự vô tâm đó lại đối thành bốn năm An Nhiên lăn lộn khổ sở cùng con trai hắn, kéo theo vô vàn hệ lụy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hẳn có chết cũng không bồi hết tội cho cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cầm tờ giấy xét nghiệm DNA trên tay, Tống Thành run rẩy không nói lên lời Trong lúc hẳn còn đang mải xúc động, cửa phòng bị đẩy toang ra, Tô Ái Phương ào vào như cơn gi “Cậu em đẹp trai, nghe nói cậu nấm viện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đã chết chưa?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếp theo là Cao Vĩnh Mạnh phe phẩy cái quạt đi vào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không rõ giác quan thứ sau của hẳn nhạy đến đâu mà đến phút chót, khi Tô Ái Phương lên chuyên cơ, hẳn lại đổi ý bám theo cho bằng được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho nên mới có ngày hôm nay cho Tống Thành ngồi im trên giường bệnh chứng kiến, Tô Ái Phương tung tăng lao vào chào hỏi, Cao Vĩnh Mạnh lạnh lùng theo sau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến khi thấy Tô Ái Phương chết đứng giữa trời khi trông thấy Vương Cư ngồi đó, hẳn vội vàng dìu chị xuống cái ghế phía xa, khó chịu làm bình vôi nghe hai ông bà trung niên nhận nhau, tâm sự nỉ non sau mấy chục năm xa cách.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại đến lúc Tô Ái Phương đòi đến thăm cô vợ đang hôn mê của Tống Thành, mọi chuyện mới gọi là bung bét.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ở đâu ra người chị hẳn quen biết đã lâu lại biến thành mẹ vợ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kẻ thù truyền kiếp của bố mình thì biến thành cha vợ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chưa hết, Tống Thành đau đầu nhìn thắng bạn thân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ nó ngang nhiên theo đuổi Tô Ái Phương, nếu nó thành công, chẳng lẽ nó sẽ thành cha dượng của vợ mình sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có lẽ cảm giác bị nghiệp quật tới tấp chính là đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tạm gác lại mọi chuyện lùm xùm về vai vế gia đình này, hẳn quay lại vấn đề chính: Phải đối diện với sự thật về nhân duyên giữa hẳn và An Nhiên như thế nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu để cô biết chuyện này, chẳng phải là đả kích lớn hay sao? Nhất là khi bác sĩ lại vừa vui sướng chạy vào thông báo với hắn, An Nhiên quả thực đã có thai Hắn muốn hét lên vui sướng nhưng một phần khác trong hắn lại bảo: “Khôn hồn thì im mồm”.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đứng trước bố mẹ vợ thực sự vừa từ không khí mọc ra, nếu hắn để lộ chuyện năm xưa từng cưỡng bức con gái yêu quý của bọn họ, cho dù hắn không cố ý, hắn vẫn cầm chắc cái chết rồi!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vợ à, em mau tỉnh lại nhanh nhanh, nếu không sẽ thành bà góa đấy! Tống Thành não nuột thở dài, Khi An Nhiên mở mắt tỉnh lại lần thứ hai, xung quanh không có nhiều khuôn mặt như lần trước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ có hai khuôn mặt tuấn tú giống hệt nhau, một lớn một nhỏ đang nhìn cô chăm chẩm Cô nâng tay, lần này tay nâng lên được, nhéo hai cái má của bọn họ một cái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“A… đau quá mẹ ơi..” Cá Chép nhăn nhó kêu lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành thì cam chịu mặc cho cô nhéo, “Đau à?” An Nhiên lẩm bẩm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thế thì đây không phải là mơ rồi”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ ơi, không phải mơ đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con và bố tới đón mẹ và em gái về đấy ạ”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Em gái gì?” An Nhiên có cảm giác mình đã ngủ một giấc lâu lắm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cá Chép xoa xoa bụng mẹ, âu yếm nói: “Em gái trong này này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con yêu mẹ, yêu cả em nữa.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên ngỡ ngàng nhìn bụng mình, lại quay sang nhìn Tống Thành kinh ngạc.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hẳn thực sự đây, bằng xương bằng thịt, không phải mờ ảo như sương khói nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước mắt cô lại trào ra, không kìm lại được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô chỉ muốn nhào vào lòng hẳn mà thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ ơi, đừng khóc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tí nữa có cả ông ngoại, bà ngoại, ông trẻ sẽ đến thăm mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi có cả ông ngoại cũ cũng sẽ tới, cả cô Minh Châu, chú Diệp Phong, ông Nhĩ, bà Hai, mẹ đừng khóc nhè nữa nhé.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai cũng yêu mẹ mà.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên lại bị một phen ù ù cạc cạc, không hiểu cái gì ông ngoại cũ, bà ngoại mới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô lại nhìn Tống Thành.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ đầu đến cuối hẳn chưa nói một lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cuối cùng, hẳn run rẩy đưa tay xoa tóc cô, thì thầm: “Có nhiều chuyện đã xảy ra trong lúc em ngủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhiều thông tin mà đến anh khi biết được cũng mất mấy ngày mới tiêu hóa hết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho nên, vợ à, chúng mình về nhà rồi lần lượt anh kể em nghe được không; An Nhiên bị hắn dỗ ngọt, rất muốn gật đầu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng nhớ đến việc bọn họ đã li hôn, cô lại ngần ngừ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Về nhà nào?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ống Thành vuốt ve mái tóc đã dài ra của cô, cười nói: “Nhà của chúng mình”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thế còn đám cưới?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn bật cười: “Đến bây giờ mà em vẫn chưa sợ đám cưới à?” Thấy cô lắc đầu, ánh mắt buồn rầu không giấu giếm, hẳn lại nói “Cho em hay, bố mẹ anh đã đồng ý việc đến gặp bố mẹ em để hỏi cưới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thậm chí còn gặp tất cả các bậc trưởng bối trong nhà em, một người” Nghĩ đến việc vợ chồng Tống Sơn, Hạ Cẩm tới nói chuyện với Vương Cư và Tô Ái Phương, Tống Thành không nhịn nổi có chút mong chờ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói gì thì nói, danh tiếng của Vương Cư sánh ngang với Tống Quỳnh Như, thuộc cấp bậc toàn cầu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn của cải của Tô Ái Phương thì vô số núi vàng núi bạc, so với Hạ Cẩm chỉ có hơn, không có kém.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đến lúc ấy, em không được vứt bỏ anh đâu đất An Nhiên gật đầu, mim cười âu yếm nhìn hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vứt bỏ người sẵn sàng hi sinh cả tính mạng đế bảo vệ mình ư? Cô đâu có ngốc như vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhận được đảm bảo của An Nhiên, Tống Thành hít một hơi, sau đó ôm lấy cả cô và con trai, nói “Chúng ta có rất nhiều chuyện để nói với nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nào, để anh đưa em về nhà, được không?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Được” An Nhiên mỉm cười ôm lấy hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vê nhà của chúng ta”</span></p>', 0, '2022-07-01 19:36:27', '2022-07-01 19:36:27'),
(93, 70, 'Chương 1: Nhân phẩm có vấn đề (1)', 'chuong-1-nhan-pham-co-van-de-1', 1, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ưm, ta đang ở nơi nào thế này?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lắc lư cái đầu vừa tỉnh lại sau khi chìm vào hôn mê, Lục Thiếu Du cảm thấy xung quanh mình rất ẩm ướt, hơn nữa toàn thân đau nhức như muốn tê liệt, gió từ bốn phía thổi qua, tạt vào người hắn khiến hắn không nhịn được mà rùng mình một cái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đầu Lục Thiếu Du toát mồ hôi lạnh, hắn vội vàng mở choàng mắt. Không gian xung quanh chỉ là một mảnh đen kịt, trên không trung, một vầng trăng lưỡi liềm đang treo lơ lửng. Sau một lát, mượn ánh sáng yếu ớt phát ra từ mặt trăng hắn mới nhìn được cảnh vật rõ ràng hơn một chút. Hiện tại hắn đang ở trong một đầm nước, lúc này hẳn là ban đêm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- A...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cơn đau điếng từ đại não truyền đến khiến hắn phải kêu lên một tiếng thảm thiết. Lục Thiếu Du cảm nhận được có một lực lượng kỳ lạ đánh vào đầu mình khiến hắn lập tức ngất đi một lần nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi trời gần sáng Lục Thiếu Du mới từ từ tỉnh lại, nhưng khi nhận được tin tức mới từ đại não Lục Thiếu Du thiếu chút nữa lại ngất đi.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhân phẩm của ta bạo phát sao, xuyên việt rồi, đây là không phải là nằm mơ chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du mở to hai mắt chăm chú nhìn bốn phía, theo những tin tức nhận được từ đại não, cùng với việc đã từng xem qua mấy tiểu thuyết xuyên việt, Lục Thiếu Du khẳng định rằng mình đã bị xuyên việt rồi. Tình huống này tưởng chừng như không có thật, thậm chí trong trăm triệu hoàn cảnh cũng chưa chắc tìm được một, vậy mà lại xảy ra trên người hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du ngẫm lại, mình vừa mới tốt nghiệp đại học hạng ba, cũng tìm được công việc vặt vãnh ở trong một văn phòng nhỏ. Trong khi đang giúp cấp trên photo tài liệu thì không ngờ máy photocopy bị rò điện, hắn bị chết bất đắc kỳ tử mà xuyên qua.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiếp trước không nói làm gì, tiền đồ xa vời, xuyên qua cũng tốt, nói không chừng xuyên qua đến một thế giới khác sẽ đạt được thành tựu lớn hơn. Dựa theo những hiểu biết của bản thân hắn trong tiểu thuyết, người xuyên qua, không một người nào đạt được thành tựu quá thấp. Nguyên một đám không phải thê thiếp thành đàn thì cũng là bá chủ của thế giới mới, kém cỏi nhất cũng làm phú nhị gia, hẳn là mình cũng không quá xui xẻo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xác định mình bị xuyên qua, Lục Thiếu Du không có bất kỳ cảm giác đau khổ hay tiếc nuối gì mà ngược lại hắn cảm thấy mình như được giải thoát. Kiếp trước hắn không phải là người có chức có quyền, cũng không phải người giàu có, tiền đồ rất xa vời, tương lai mờ mịt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nơi này là chỗ nào, tại sao ta lại xuyên đến chỗ quỷ quái này?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du đánh giá xung quanh, bắt đầu lục lọi ký ức còn lưu lại trong đầu, một đoạn ký ức không thuộc về hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau một lát, Lục Thiếu Du có loại xúc động muốn tự sát để xem có thể xuyên qua lần nữa hay không. Người khác xuyên qua không phải thành thiếu gia thì cũng là Vương gia, không thì cũng anh tuấn tiêu sái, phong độ bức người, là công tử gia tộc hoặc thế lực lớn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà thân thể hắn xuyên vào mặc dù ở thế giới này cũng là một thiếu gia nhưng lại hữu danh vô thực. Vị thiếu gia này cũng tên là Lục Thiếu Du, năm nay vừa mới mười sáu tuổi, là một người con riêng, địa vị của hắn trong gia tộc ngay cả người hầu đều không bằng. Ngoài ra hắn còn có một mẫu thân, trước là người hầu trong gia tộc, sau khi sinh hạ vị thiếu gia này thì càng bị vợ cả ức hiếp, cùng trải qua ngày tháng so với nô tài còn không bằng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai mẫu thân con, ngày bình thường thì luôn bị người trong gia tộc ức hiếp, ngay cả nô bộc cũng không để họ vào mắt. Đặc biệt là mấy nha hoàn đi theo vợ cả, càng hay tìm cớ để làm nhục bọn họ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngay như ngày hôm qua, chỉ vì chống đối vài câu mà vị thiếu gia này đã bị bọn chúng đánh cho một trận, nhưng không ngờ tới bị đánh đến chết. Đoán chừng những người kia thấy người bị chết mà sợ hãi nên mới đem vị thiếu gia này vất xuống vách núi, thi thể rơi vào đầm nước dưới vách núi, còn mình thì vì nhân phẩm bạo phát mà xuyên qua nhập vào thân thể của hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ mình xuyên không lấy được thân thế như vậy, Lục Thiếu Du có chút khóc không ra nước mắt, rốt cuộc nhân phẩm của mình tốt hay không tốt đây? Kiếp trước mình cũng chưa làm chuyện tốt gì, xem ra nhân phẩm cũng không thể tốt được a.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhập gia thì tùy tục, Lục Thiếu Du cũng không có cách nào khác, may mắn lắm mới không chết, trên đời này làm gì có chuyện thập toàn thập mỹ, dù thế nào đi chăng nữa không chết chính là có phúc, gặp đại nạn mà không chết tất sau này sẽ có phúc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nể mặt ta và ngươi cùng tên là Lục Thiếu Du, ta lại dùng thân thể của ngươi, mối thù của ngươi sau này có cơ hội ta sẽ giúp ngươi báo, mẫu thân của ngươi ta cũng sẽ giúp ngươi chăm sóc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du thầm thì tự nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du vừa nói xong, đầu óc dường như thông suốt, một cỗ trọc khí thoát ra từ đầu rồi tiêu tán không không gian.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Xem ra oán khí của ngươi cũng sâu a, mối thù của ngươi, Lục Thiếu Du ta chỉ cần có cơ hội nhất định sẽ báo cho ngươi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du chăm chú nhìn lên trời cao nói lại một lần nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong, Lục Thiếu Du không thể không nghĩ đến một vấn đề nan giải lúc này. Bây giờ hắn đang nằm trong đầm nước, dưới lưng dường như tựa vào một thân cây khô, nước trong đầm rất lạnh, xem ra bây giờ hẳn là mùa đông. Mà hắn ngâm trong nước đã lạnh đã rất lâu rồi thì phải, cả người đã sớm tê cóng, không còn bao nhiêu tri giác.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giãy dụa cố bơi về hướng bên cạnh đầm nước, Lục Thiếu Du không khỏi thầm rủa một tiếng, hai chân của hắn dường như bị chặt đứt, hai tay thì ngay từ đầu còn không cảm thấy gì nhưng vừa cố di chuyển một chút thì phát hiện ra hai tay cũng bị gãy xương. Lúc này, hắn căn bản không thể dùng sức, đừng nói bơi đến bên cạnh đầm nước, ngay cả di chuyển nửa phân cũng khó có khả năng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ông trời a, ngươi đừng chơi ta như vậy mà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du bất đắc dĩ nhìn trời mắng, bây giờ mình hoàn toàn không nhúc nhích được, không phải nằm chờ bị động lạnh đến chết sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vèo...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một hồi âm thanh truyền vào trong tai Lục Thiếu Du, giống như là có con vật gì đó di chuyển trong nước với tốc độ vô cùng nhanh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không phải là Yêu thú chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong đầu Lục Thiếu Du hiện lên một từ đáng sợ, theo như tin tức trong đầu lưu lại, Lục Thiếu Du biết được thế giới này khác với thế giới trước kia của mình. Đây là thế giới lấy vũ vi tôn, toàn bộ thế giới bao la vô cùng, trong đó có Yêu thú, Linh thú, Vũ giả và Linh sư tồn tại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vũ giả và Linh sư trong thế giới này có địa vị vô cùng cao, đặc biệt là Linh sư, địa vị của họ có thể nói là tối thượng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà thân thể của vị thiếu gia hắn xuyên qua này, không có cách nào trở thành Vũ giả cũng vô pháp trở thành Linh sư, chính vì vậy địa vị trong gia tộc mới thấp kém không bằng cả nô bộc.</span></p>', 0, '2022-07-02 01:41:23', '2022-07-02 01:41:23');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(94, 70, 'Chương 2: Nhân phẩm có vấn đề (2)', 'chuong-2-nhan-pham-co-van-de-2', 2, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- G ri...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngay tại lúc Lục Thiếu Du đang suy nghĩ xem có phải yêu thú đang tiến về phía mình hay không thì từ trên không một chuỗi âm thanh rít gào truyền đến. Ngay lập tức trước mắt hắn như bị mây đen bao phủ. Một con chim khổng lồ dài hơn trăm thước xuất hiện trên không trung.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cách chim mở rộng, bay lượn ngay khoảng không trên đầu Lục Thiếu Du. Con chim này có điểm giống chim ưng nhưng phủ bên ngoài bộ lông màu xanh, dưới bụng có lông tơ màu trắng. Hai chân có móng vuốt sắc nhọn uốn cong, thoạt nhìn như lưỡi dao sắc bén, hai mắt Cự Ưng chăm chú nhìn xuống phía hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Yêu thú, chẳng lẽ ta vừa mới xuyên qua đã phải trở thành món ăn trong bụng yêu thú sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du vô cùng kinh ngạc, ở kiếp trước lấy đâu ra một con chim ưng lớn như vậy. Lập tức, Lục Thiếu Du bất đắc dĩ mà nở nụ cười khổ, bây giờ mình không thể di chuyển, yêu thú mà muốn ăn thịt mình thì cũng chỉ có thể trơ mắt nhìn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vèo vèo...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du cảm giác được phía sau mình tựa hồ đang có con vật gì đó bay đến, hai cánh nó vuốt trên mặt nước làm mặt nước cuộn sóng, mà hắn cũng bị sóng cuộn ra phía ngoài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dát dát...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên không trung, Cự Ưng kêu lên một tiếng, hung quang trong mắt vừa lộ, hai cánh chấn động, mang theo gió lốc lao thẳng xuống dưới, móng vuốt co lại, như xuyên qua không gian lao xuống tựa tia chớp.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mạng ta xong rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lòng Lục Thiếu Du chỉ còn một mảnh lạnh lẽo, vừa mới xuyên qua cuối cùng lại trở thành món ăn trong bụng của yêu thú, vận số của ta cũng quá kém đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vèo...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngay lúc Lục Thiếu Du tuyệt vọng, trước mắt hắn đột ngột xuất hiện một một mảnh tối đen, một cái bóng to lớn từ phía sau đầu hắn nhảy lên. Tốc độ quá nhanh, Lục Thiếu Du vội ngước mắt nhìn lên cũng chỉ có thể trông thấy là một con Yêu thú phi hành lớn hơn trăm thước có điểm giống con dơi, toàn thân màu trắng tinh, dưới bụng yêu thú có một lỗ hổng to, máu tươi từ đó không ngừng chảy ra, hẳn là con yêu thú này đang bị thương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phanh! Phanh!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ trong tích tắc, hai con yêu thú đã đụng độ nhau trên không, những tiếng rít gào xuyên qua không gian vang vọng xung quanh Lục Thiếu Du. Hắn nằm trong đầm nước, mặt nước không ngừng dao động nhưng bản thân hắn thì lại không thể nhúc nhích.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi va chạm mấy lần, Lục Thiếu Du thấy, Yêu thú Cự Ưng thu lại móng vuốt, hai cánh chấn động, mang theo gió lốc hung hăng chụp xuống Yêu thú dơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chi chi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con dơi khổng lồ rít lên, ngay lúc Lục Thiếu Du còn đang kinh ngạc, trong miệng yêu thú đã tràn ra một đoàn sương mù màu trắng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sương mù màu trắng phun ra, giữa không trung một cỗ hàn khí bắt đầu khuếch tán. Trong tích tắc, hàn khí quét qua khiến cả đầm nước bị đóng băng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên không, Cự Ưng cũng bị đông lạnh trở thành khối băng, thân hình khổng lồ lập tức rơi thẳng xuống đầm nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ầm...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cự Ưng đã rơi xuống trên mặt băng, mặt băng bị phá vỡ, xuất hiện vô số khe nứt. Cùng lúc này con yêu thú dơi dường như cũng trở nên suy yếu, nó hạ xuống mặt băng, từ từ tiến tới gần Cự Ưng Yêu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Rắc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du vừa nhìn lại đã thấy yêu thú dơi há miệng lộ ra hai chiếc răng nanh bén nhọn cắn xuống, một ngụm đem da cùng lông trên bụng Cự Ưng xé ra, một viên ngọc to cỡ nắm tay trẻ con, toàn thân phát ra ánh sáng màu xanh, bên trên còn dính máu lăn xuống mặt băng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Yêu đan? Yêu thú ít nhất phải đạt tứ giai mới có yêu đan.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du từ trí nhớ trong đầu biết được, loại yêu đan này, yêu thú ít nhất phải đạt đến tứ giai mới có, những yêu thú tam giai trở xuống sẽ không có yêu đan. Yêu đan hình như là rất giá trị.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Dát dát...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi Cự Ưng bị yêu thú dơi xé mở bụng lộ ra yêu đan. Trong nháy mắt, quanh thân Cự Ưng phát ra một ngọn lửa, ngay lập tức làm băng tan, cái mỏ sắc bén mổ thẳng vào vết thương trên bụng của yêu thú dơi. Ngay sau đó bụng của yêu thú dơi không ngừng chảy máu, một viên ngọc lớn chừng nắm tay trẻ con dính đầy tơ máu tràn ngập tia sáng nhu hòa bị đánh rơi trên mặt băng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đây cũng là yêu đan sao...?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du kinh ngạc nói, yêu thú tứ giai trở lên rất khó bắt gặp, không ngờ dưới vách núi lại này một lát mà thấy những hai con.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ô ô!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Yêu thú dơi bị đau, nó kêu lên thảm thiết rồi dùng cái răng nanh cắn mạnh vào cổ của Cự Ưng, trực tiếp cắn đứt cổ Cự Ưng, tia máu không ngừng phun ra, lần này con Cự Ưng hoàn toàn chết hẳn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ, mặt băng bắt đầu thay đổi lần thứ hai, Lục Thiếu Du lạnh run cả người, môi trắng bệch không có chút huyết sắc, mặt tái xanh, thân thể hầu như cứng đơ. Nhìn hai con yêu thú khổng lồ đại chiến như một ảo giác khi xem phim 3D, thật là làm cho người ta kinh hoàng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chi chi...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên mặt nước, yêu thú dơi ngậm viên yêu đan của Cự Ưng trong miệng của mình, hai cánh xẹt qua trên mặt nước lạnh. Ngay sau đó, nó chậm rãi hướng tới gần Lục Thiếu Du.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phiền toái rồi đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du nhìn con yêu thú dơi đang hướng tới gần mình, trong lòng cảm thấy tuyệt vọng, hai mắt yêu thú kia đang nhìn hắn chằm chằm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chi chi!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Yêu thú dơi bay đến bên người Lục Thiếu Du, cổ họng của con dơi phát ra một tiếng vang, với thân thể khổng lồ, một con mắt của nó còn lớn hơn cả người Lục Thiếu Du. Trong mắt của nó không hề có ác ý, ngược lại ánh mắt rất chân thành nhìn chăm chú vào Lục Thiếu Du.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du tưởng rằng mình chết chắc rồi, hắn nhìn vào thân hình to lớn của yêu thú, dường như yêu thú này không có ác ý với mình:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngươi có chuyện gì muốn ta giúp sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du thử nói một câu để thăm dò, nghe nói yêu thú cao giai đều là linh vật của thiên địa, nhất định sẽ có linh trí.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chi chi!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe được Lục Thiếu Du nói, con yêu thú dơi phát ra một âm thanh vui sướng, khẽ gật đầu, hình như đang đáp lại Lục Thiếu Du.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con dơi này quả thật hiểu được lời của mình, Lục Thiếu Du sửng sốt một chút. Nếu như là trước đây, nhất định Lục Thiếu Du sẽ bị dọa cho giật hết cả mình, nhưng bây giờ hắn lại cảm thấy không có gì là quá kinh ngạc, bản thân hắn có thể chuyển kiếp thì còn có cái gì không thể tiếp nhận đây?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ta nói yêu thú huynh này, ngươi nhìn ta đi, tự thân ta còn khó bảo toàn, làm sao có thể giúp huynh?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du nhìn yêu thú dơi cười khổ một cái rồi nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chi chi</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tựa như nghe hiểu Lục Thiếu Du nói, con dơi yêu thú cúi đầu chăm chú đánh giá hắn, sau đó phát ra một âm thanh trầm thấp, nhảy mạnh lên lấy hai móng vuốt ấn vào bụng Lục Thiếu Du, Lục Thiếu Du không tự chủ được há hốc miệng ra.</span></p>', 0, '2022-07-02 01:41:57', '2022-07-02 01:41:57'),
(95, 72, 'Chương 1: Kẻ thế thân', 'chuong-1-ke-the-than', 1, NULL, '<div class=\"chapter-c\" id=\"chapter-c\" style=\"box-sizing: border-box; margin: 0px; font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; line-height: 46.8px; color: rgb(43, 43, 43); font-size: 26px; overflow-wrap: break-word; background-color: rgb(244, 244, 244);\">\r\n<p>Thời tiết ở thành phố Nam Dương vào tháng mười hai rất rất lạnh.<br />\r\nTrận tuyết lớn phủ xuống cả thành phố khiến ban đêm lạnh lẽo lại càng thêm vắng vẻ, đìu hiu.<br />\r\nNhưng lúc này tại phòng VVIP trong một khách sạn bảy sao, bầu không khí lại vô cùng nóng bỏng.<br />\r\n“Đau…” Giọng nói nũng nịu của cô gái khiến người khác nghe mà thương tiếc, thân thể cô gái cong lên, trên trán toàn là mồ hôi.<br />\r\n“Ngoan, ráng chịu thêm một chút nữa là hết đau rồi, được không?” Bờ môi mỏng gợi cảm của người đàn ông dán vào tai cô gái, hơi thở ấm áp, giọng nói quyến rũ khiến thân thể căng thẳng của cô dần dần thả lỏng.<br />\r\n“Đừng sợ…”</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; display: flex;\">&nbsp;</div>\r\nCô gái cắn môi, gương mặt tái nhợt giàn giụa nước mắt, nắm tay siết chặt của cô từ từ buông ra.<br />\r\n“Tử Diễm…”<br />\r\nNgười đàn ông thấy cô đã thả lỏng thì dịu dàng gọi tên cô.<br />\r\nMột nụ hôn đầy mùi rượu kéo dài từ cổ cô xuống dưới.<br />\r\nCô gái từ từ ngước mắt lên, dựa vào ánh sáng yếu ớt ở đầu giường nhìn vào mắt người đàn ông.<br />\r\n“Đừng sợ, có anh ở đây, đừng sợ được không?” Người đàn ông nở nụ cười, đôi mắt sâu thẳm hấp dẫn người nhìn vào, đôi mắt ấy đang nhìn cô vô cùng chân thành.<br />\r\nCô gái nhìn anh đầy mơ màng, dần dần, ánh mắt cô cũng hiện lên vẻ quyến rũ say lòng người nhưng sau đó cô đã chìm trong nụ hôn của người đàn ông…<br />\r\nTuyết bên ngoài càng lúc càng lớn còn đêm của bọn họ chỉ vừa mới bắt đầu.<br />\r\nBa giờ rưỡi sáng.<br />\r\nĐiện thoại của cô gái sáng lên một cái, trên màn hình điện thoại là số 198, nội dung chỉ là một khoảng trống.<br />\r\nCô vội vàng ngồi dậy, nhẹ chân nhẹ tay mặc quần áo, từ từ bước xuống giường.<br />\r\nCô quay đầu nhìn lại người đàn ông đang ngủ trên chiếc giường lớn.<br />\r\nGương mặt hoàn mỹ dưới ngọn đèn vẫn đẹp trai như thế, dù đang ngủ thì cũng là bức tranh cực kỳ đẹp đẽ.<br />\r\nCô gái cắn môi, hít một hơi thật sâu rồi không lưu luyến gì nữa.<br />\r\nCô quay người, dứt khoát rời khỏi phòng, mở cửa.<br />\r\n“Được không?” Có một cô gái đội mũ và đeo kính râm đứng ngoài cửa, cô ta lén đứng một bên, cẩn thận hỏi.<br />\r\n“Ừ.”<br />\r\n“Thật không?” Cô gái đeo kính cười, lập tức đứng thẳng người.<br />\r\n“Ừ, anh ta ngủ rồi.”<br />\r\n“Được, mày đi đi!” Cô gái kia chuẩn bị đi vào.<br />\r\nCó điều, cô vẫn đứng nguyên ở cửa không đi: “Tiền đâu?”<br />\r\nCô gái đeo kính đen nghe thế thì nhíu mày lại, gỡ mũ và kính râm xuống.<br />\r\nCô ta giống hệt cô gái đang đứng ở cửa.<br />\r\nCó điều, cô gái đứng ở cửa lại mang đến cho người khác cảm giác thông minh và ưu tú hơn hẳn.<br />\r\n“Tao mà thèm lừa mày à, tiền thì mày cứ tới chỗ bố lấy, mày mau đi đi, tránh ra dùm, nếu làm hỏng chuyện tốt của tao thì mày chết chắc đó!” Cô gái kia hung dữ nói với cô.<br />\r\n“Được!” Cô nghe thế thì tránh đường.<br />\r\n“Mang mấy cái này rồi đi!” Cô gái kia nhét kính râm và mũ vào tay cô, vội vàng đi vào phòng đóng cửa lại.<br />\r\nCô mỉm cười, nụ cười chứa đầy sự cay đắng. Nhưng cô không hề có chút lưu luyến nào, đội mũ và kính râm, thẳng lưng rời khỏi đó.<br />\r\nTrên xe taxi, cô nhìn bầu trời đầy tuyết, lấy điện thoại nhấn nút gọi.<br />\r\n“Bác sĩ Vương, mọi người đừng dừng thuốc của mẹ tôi, tôi sẽ lập tức chuyển tiền qua, đúng thế, tôi đảm bảo!” Sau khi cô chắc chắn bệnh viện sẽ không dừng thuốc thì cúp điện thoại.<br />\r\nCô tên là Vân Tử Lăng, năm nay hai mươi tuổi.<br />\r\nCô gái lúc nãy tên là Vân Tử Diễm, cũng hai mươi tuổi.<br />\r\nCô là chị em song sinh với cô ta.<br />\r\nVân Tử Diễm sinh sớm hơn cô mười giây, là chị của cô.<br />\r\nCó điều, lúc hai người được ba tuổi thì bố mẹ cô ly hôn. Cô được mẹ nuôi còn Vân Tử Diễm theo bố.<br />\r\nTừ đó về sau, hai người chưa bao giờ xuất hiện cùng một lúc.\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<hr />\r\n<p>&nbsp;</p>', 0, '2022-07-03 02:51:28', '2022-07-03 02:51:28');

