INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(76, 16, 'Chương 1: Tôi không hối hận', 'chuong-1-toi-khong-hoi-han', 1, 'aa', '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độ ấm từ sau lưng chậm rãi vây quanh, hô hấp nóng bỏng ở bên tai: “Sợ không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơi thở xa lạ quanh quẩn bên tai khiến người ta lạnh lẽo đến không dám lên tiếng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp như cảm giác được người đàn ông hơi khựng lại, sau đó lại vang lên giọng nói của anh: “Bây giờ hối hận vẫn còn kịp.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô căng thẳng siết chặt hai tay, lắc đầu: “Tôi không hối hận…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đang ở trong thời kỳ xinh đẹp nhất, nhưng…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một đêm này đau đớn mà dài lâu…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cuối cùng nửa đêm về sáng người đàn ông đứng dậy đi vào phòng tắm, Lâm Tử Lạp mới kéo thân thể mệt mỏi, mặc đồ ra khỏi phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dưới lầu khách sạn có người phụ nữ trung niên giới thiệu công việc này cho cô đang đứng đó, thấy Lâm Tử Lạp đi tới, bà ta đưa cho cô một cái túi màu đen: “Đây là tiền thù lao của cô.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp gần như không chút do dự nhận lấy, cầm tiền nhanh chóng chạy đi, thậm chí còn không quan tâm sự đau đớn dưới thân, chỉ muốn nhanh chóng đến bệnh viện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bầu trời vẫn chưa sáng khiến hành lang rất im lặng, dưới đất trước phòng phẫu thuật để hai cái cáng cứu thương, vì không đóng tiền nên không được đưa vào phòng phẫu thuật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhìn mà đau lòng không thôi, nức nở nói: “Tôi có tiền, mau cứu mẹ và em trai tôi đi…” Cô nghẹn ngào đưa tiền trong tay cho bác sĩ, bác sĩ nhìn thoáng qua, đưa cho y tá kiểm kê, sau đó mới kêu nhân viên đưa người bị thương vào trong phòng phẫu thuật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không thấy bọn họ đẩy em trai mình vào, vội vàng nhào lên bắt lấy bác sĩ cầu xin: “Còn cả em trai tôi nữa, ông cứu thằng bé đi…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bác sĩ thở dài: “Thật ngại quá, em trai cô đã không cứu được nữa rồi…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không cứu được nữa?!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lời này như tiếng sét động trời giáng mạnh lên đầu Lâm Tử Lạp, khiến trước mắt cô đen lại…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đau, lồng ngực như bị người ta dùng con dao quấy phá, đau đến run rẩy ngồi cuộn lại dưới đất, tám năm trước, cô mười tuổi, ba ngoại tình vứt bỏ mẹ, đuổi mẹ đang mang thai và cô ra nước ngoài xa lạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó em trai được sinh ra, lúc ba tuổi phát hiện mắc bệnh tự kỷ, vốn cuộc sống đã túng thiếu, bệnh của em trai lại liên tiếp gặp nạn, cô và mẹ đi làm công cho người khác khắp nơi mới có thể sống qua ngày. Nhưng một vụ tai nạn giao thông, ở nước ngoài không có người thân, không có tiền, không ai giúp đỡ, khiến cô cảm nhận được cái gì là cùng đường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bị ép đến đường cùng, cô đã bán đi mình cũng không thể cứu em trai về.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có một sự đau đớn, nó không dữ dội, nhưng lại khiến người ta cảm thấy không dễ chịu, hít thở cũng khó khăn, bầu trời thì xám xịt, nhưng cô phải chấp nhận, còn phải cười mà chấp nhận, vì cô còn mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mẹ cần cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi được điều trị, sức khoẻ của mẹ chuyển biết tốt, nhưng khi biết em trai đã chết, bà như sụp đổ đến nơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Là Lâm Tử Lạp ôm bà, khóc nói: “Mẹ, mẹ vẫn còn con, cố sống tốt vì con đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Suốt một tháng ở bệnh viện, Trang Kha Nguyệt thường xuyên ngẩn người ngồi bên giường, Lâm Tử Lạp biết bà nhớ em trai. Nếu không phải vì mình, chỉ sợ mẹ đã đi theo em trai rồi, vì phải chăm sóc mẹ, cô bị đuổi học, nhưng vết thương của mẹ đã chuyển biến tốt đẹp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô xách đồ ăn đi vào bệnh viện, đến trước cửa phòng bệnh, khi cô nâng tay vừa định mở cửa, lại nghe thấy giọng nói ở bên trong…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô rất quen với nó, cho dù đã cách xa tám năm, cô vẫn nhớ rõ dáng vẻ ông ta ép mẹ ly hôn với mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi đưa bọn họ đến đây, ông ta chưa từng đến nhìn bọn họ một cái, hôm nay lại đột nhiên xuất hiện ở đây là có ý gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Kha Nguyệt, lúc trước bà và bà chủ nhà họ Tông tình như chị em, đã quyết định hôn ước từ nhỏ, theo đạo lý thì hôn ước bà quyết định phải do con gái bà lấy chồng…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lâm Viên Trung, ông có ý gì?!” Thân hình Trang Kha Nguyệt gầy yếu, bất chấp trên người còn có vết thương mà giãy dụa đứng lên muốn đánh ông ta, ông ta còn là người hay sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sắp xếp cho bà và con gái ở lại cái nơi quỷ quái xa lạ này, chưa từng quan tâm sống chết của bọn họ, hôm nay lại muốn con gái bà lập gia đình?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cậu cả nhà họ Tông cũng là con trai bạn tốt của bà, trông ưa nhìn, dòng dõi nhà họ Tông bà cũng biết đó, lấy bên đó chỉ có hưởng phúc thôi…” Nói đến phía sau, giọng ông ta nhỏ lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cậu cả nhà họ Tông đúng là cao quý thật, dáng vẻ tuấn tú lịch sự, nhưng một tháng trước, anh ra nước ngoài làm việc bị rắn độc cắn, tê liệt thần kinh, không thể hành động, còn không thể làm chuyện kia.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Gả qua đó chẳng khác nào ở goá cả.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi lấy.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp đột nhiên đẩy cửa ra, cô đứng trước cửa, hai tay siết lại nắm chặt hộp đựng cơm trong tay: “Lấy chồng cũng được, nhưng tôi có một điều kiện.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung nhìn ra cửa, thấy đứa con gái đã tám năm không gặp này, trong chốc lát hoảng hốt mất mấy giây. Lúc đưa cô đến, cô vẫn là một đứa nhỏ mười tuổi, bây giờ đã trưởng thành rồi, làn da cô trắng ngần, lại rất gầy, khuôn mặt nhỏ nhắn còn chưa lớn bằng lòng bàn tay, khô khan không hề tươi tắn chúng nào, như không được phát triển tốt vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không hề khiến người ta yêu thích như con gái nhỏ trong nhà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sự không nỡ trong lòng cũng bớt đi, dẫu sao vẻ ngoài của cô cũng không đẹp lắm, cho dù lấy người chồng không làm được chuyện kia cũng không quá uất ức.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ vậy, Lâm Viên Trung cũng không thấy có gì không tốt: “Điều kiện gì, nói đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi về nước với mẹ, trả tất cả những thứ thuộc về mẹ cho chúng tôi, tôi đồng ý lấy chồng.” Lâm Tử Lạp liên tục siết chặt tay, dần dần mới bình tĩnh lại được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy hàng năm không ở trong nước, nhưng trước đây cô đã nghe nói tới nhà họ Tông của thành phố B, gia tộc khổng lồ, của cải bạc triệu, cậu chủ của nhà họ Tông đương nhiên là cao quý. Lâm Tử Lạp không cảm thấy chuyện tốt như vậy sẽ đến tay mình, nói không chừng cậu cả nhà họ Tông kia rất xấu, hoặc là thân thể có chỗ khiếm khuyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng cho dù thế, đây cũng là một cơ hội rất tốt để về nước với cô, lợi dụng tốt, còn có thể giành lại tài sản là của hồi môn của mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lạp Lạp…” Trang Kha Nguyệt muốn khuyên cô, chuyện lớn như cưới xin không thể đùa giỡn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đã đi theo bà chịu rất nhiều khổ cực rồi, không thể để cô đưa cả hôn nhân vào được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung nghe thấy thế, lo rằng Lâm Tử Lạp bị Trang Kha Nguyệt thuyết phục sẽ không muốn lấy chồng, vội nói: “Được, chỉ cần cô chịu lấy chồng, sẽ cho cô về nước.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Của hồi môn của mẹ tôi thì sao?” Lâm Tử Lạp nhìn người ba trên danh nghĩa của mình, giọng nói cực kỳ lạnh lẽo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc trước khi Trang Kha Nguyệt lấy ông ta thật sự có rất nhiều của hồi môn, đó là một số lượng không nhỏ, bây giờ kêu Lâm Viên Trung lấy ra khiến ông ta rất không nỡ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ba, có lẽ đứa em gái kia của tôi rất xinh đẹp, nó nên có thứ tốt hơn, nếu lấy một người đàn ông thân thể có chỗ thiếu hụt thì cả đời cũng xong rồi, huống hồ ông và mẹ tôi đã ly hôn, ông cũng nên trả lại tiền bà đưa đến nhà họ Lâm đúng không.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung chột dạ né tránh không dám nhìn thẳng vào cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ở nước ngoài sao lại biết cậu cả nhà họ Tông kia là một người không vẹn toàn?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung nào biết Lâm Tử Lạp chỉ đang đoán thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến cô phải lấy một người đàn ông không bình thường, Lâm Viên Trung nghiến răng: “Đợi cô gả qua đó sẽ đưa cho cô.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con gái nhỏ của ông ta như hoa như ngọc, sao có thể lấy một người đàn ông không làm được chuyện kia chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có cao quý bao nhiêu, không thể làm chuyện vợ chồng thì khác gì một người tàn phế đâu?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến đây, Lâm Viên Trung cũng không khó chịu như vậy nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng trong lòng lại ghét Lâm Tử Lạp hơn mấy phần, chỉ toàn nghĩ việc lấy tiền từ trong tay ông ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung lạnh lùng nhìn cô một cái: “Mẹ cô đúng là không dạy dỗ cô đàng hoàng, không hề biết lễ phép chút nào!”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp rất muốn nói người làm cha là ông không có trách nhiệm hay sao? Ném cô lại đây chưa từng để tâm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng lúc này cô không thể nói, lợi thế của cô quá yếu, chọc giận Lâm Viên Trung không có lợi với cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chuẩn bị một chút, ngày mai trở về.” Lâm Viên Trung vung tay áo rời khỏi phòng bệnh.</span></p>', 0, '2022-06-30 07:07:12', '2022-06-30 07:20:06'),
(78, 16, 'Chương 2: Mang thai rồi', 'chuong-2-mang-thai-roi', 2, 'aa', '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lạp Lạp, hôn nhân là chuyện lớn cả đời, mẹ không cho con làm thế.” Ít nhiều gì Trang Kha Nguyệt cũng biết ý đồ của Lâm Tử Lạp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp để hộp đựng cơm lên ngăn tủ đầu giường, vừa lấy ra vừa nói: “Còn cũng không lấy người ngoài, không phải là con trai của bạn mẹ sao.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bà ấy mất sớm, mẹ cũng không hiểu con trai bà ấy, cho dù có nuốt lời, mẹ cũng muốn con lấy người mình thích, chứ không phải dùng hôn nhân làm lợi thế, như vậy, mẹ thà rằng ở lại đây cả đời.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người mình thích?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho dù sao này gặp được, cô cũng không có tư cách.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cúi đầu, lấy ai cũng không quan trọng, quan trọng là lấy lại mọi thứ bị người ta cướp đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt không thể thuyết phục Lâm Tử Lạp thay đổi quyết định, ngày hôm sau bọn họ lập tức về nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung ghét bỏ mẹ con bọn họ, không cho bọn họ vào cửa nhà họ Lâm, mà cho bọn họ thuê phòng bên ngoài ở, đợi đến ngày kết hôn, Lâm Tử Lạp trở về là được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa hay Lâm Tử Lạp cũng không muốn về, trở về, mẹ sẽ phải đối mặt với đồ vợ bé phá hoại hôn nhân của bà, so với việc mất tự nhiên không bằng ở lại đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Yên tĩnh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt vẫn rất lo lắng: “Lạp Lạp, nếu đây là một cuộc hôn nhân tốt, thì đã không đến tay con rồi, cho dù mẹ và bà Tông từng có… tình cảm qua lại.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không muốn nói những chuyện này với mẹ, vì thế đổi chủ đề: “Mẹ, mau ăn gì đó đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt thở dài, rõ ràng Lâm Tử Lạp không muốn nói chuyện này, cô đi theo mình chịu khổ, bây giờ ngay cả hôn nhân cũng phải hy sinh.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cầm đũa trong tay, nhưng không hề có khẩu vị, còn bị buồn nôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Con không thoải mái sao?” Trang Kha Nguyệt quan tâm hỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không muốn khiến bà lo lắng, nói bừa là ngồi máy bay xong không có khẩu vị.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buông đũa xuống đi về phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cửa phòng đóng lại, cô tựa lên ván cửa, tuy cô chưa từng có thai, nhưng cô từng thấy dáng vẻ khi mẹ mang thai, bà cũng buồn nôn, ăn không ngon.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà tình trạng của cô lúc này cũng giống thế.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cách đêm đó đã hơn một tháng rồi, ngày đèn đỏ của cô cũng bị muộn mười ngày…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không dám nghĩ tiếp nữa, đêm đó đã rất nhục nhã rồi. Nếu không phải vì mẹ và em trai, cô cũng sẽ không bán mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô run rẩy…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô mang thai rồi, sáu tuần.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ra khỏi bệnh viện, trong đầu Lâm Tử Lạp vẫn quanh quẩn câu cô mang thai rồi của bác sĩ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp lừa Trang Kha Nguyệt đến bệnh viện kiểm tra, sau đó kết quả là như vậy, tâm trạng cô rất rối ren, không biết phải làm sao bây giờ, sinh ra, hay phá bỏ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tay cô không nhịn được phủ lên bụng, tuy bất ngờ, thậm chí là nhục nhã, nhưng cô vẫn hơi không nỡ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có cảm giác vui sướng, và chờ mong khi lần đầu làm mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô hoảng hốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trở về nơi ở, Lâm Tử Lạp giấu giấy siêu âm B đi, mới đẩy cửa ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng Lâm Viên Trung cũng ở đó, sắc mặt cô thoáng chốc sa sầm lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông ta đến làm gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sắc mặt Lâm Viên Trung cũng không được tốt lắm, hình như vì không thấy cô khiến ông ta đợi lâu, lạnh lùng nói: “Đi thay quần áo.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhíu mày: “Tại sao?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Nếu phải gả đến nhà họ Tông, cô và cậu cả nhà họ Tông kia cũng phải gặp mặt.” Lâm Viên Trung quan sát cô từ trên xuống dưới: “Cô ăn mặc rách nát thế này đi gặp cậu ta à? Muốn khiến tôi mất mặt sao?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đau là cảm giác thế nào?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cho rằng bán mình, em trai chết, đã khiến cô đau đến chết lặng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng nghe thấy lời nói vô tình của Lâm Viên Trung, trái tim vẫn sẽ đau chứ không còn chết lặng nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông ta đưa mình và mẹ đến một đất nước phương Tây nghèo hèn, sau đó chưa từng quan tâm bọn họ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô lấy đâu ra tiền?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu cô có tiền, sao em trai có thể chết vì chữa trị chậm trễ được?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai tay buông xuống bên người cô siết chặt thành nắm đấm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hình như Lâm Viên Trung cũng nghĩ đến điều này, vẻ mặt hơi xấu hổ: “Đi thôi, người nhà họ Tông sắp đến rồi, không tiện để bọn họ đợi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lạp Lạp…” Trang Kha Nguyệt lo lắng, vẫn muốn khuyên Lâm Tử Lạp, bà đã mất đi con trai rồi, bây giờ chỉ muốn chăm sóc con gái thật tốt, tiền bạc đã không còn quan trọng nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng không muốn con gái lại bước vào nhà họ Lâm, hoặc là nhà họ Tông.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà giàu phức tạp, lại còn không biết cậu cả nhà họ Tông kia trông ra sao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà lo lắng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ.” Lâm Tử Lạp cho bà một ánh mắt an ủi, để bà yên tâm hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đi mau.” Lâm Viên Trung không kiên nhẫn thúc giục, sợ Lâm Tử Lạp đổi ý, còn đẩy cô một.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung không thích cô, cô cũng không có chút tình cảm với người ba này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tám năm, tất cả tình thân máu mủ đều đã tiêu mòn hết rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp ăn mặc thật sự rất cũ kỹ, lại phải gặp nhà họ Tông, Lâm Viên Trung dẫn cô đến một tiệm đồ nữ xa hoa, mua một bộ quần áo tàm tạm cho cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiến vào tiệm còn có nhân viên đến tiếp đón, Lâm Viên Trung đẩy Lâm Tử Lạp về phía trước: “Lấy đồ nào nó mặc được.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhân viên quan sát cô một lượt, đại khái đã biết cô mặc số mấy: “Đi theo tôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhân viên cầm một cái váy dài màu lam nhạt đưa cho cô: “Cô đến phòng thử đồ xem sao.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhận lấy, đi tới phòng thử đồ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bạch, anh phải cưới con gái của nhà họ Lâm sao?” Giọng nói của người phụ nữ mơ hồ mang theo uất ức.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp đột nhiên nghe thấy giọng nói nên đưa mắt nhìn qua phòng bên cạnh, xuyên qua khe hỡ của cánh cửa, cô nhìn thấy người phụ nữ ôm lấy cổ người đàn ông làm nũng: “Anh đừng cưới cô gái khác được không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhìn người phụ nữ, ánh mắt như mang theo bất đắc dĩ, đây là chuyện cưới xin mẹ quyết định cho anh, không thể đổi ý được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng nghĩ đến đêm đó, anh lại không đành lòng để cô ta thất vọng: “Đêm đó, có phải rất đau không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn một tháng trước, anh ra nước ngoài đến một nước lạc hậu khảo sát một hạng mục, kết quả bị một loại rắn cắn, độc của nó rất mạnh, nếu không trút ra trên người phụ nữ, sẽ khô nóng mà chết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn Bạch Lộ Khiết, đã làm thuốc giải cho anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bản thân anh biết lúc đó mình không khống chế được bao nhiêu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai cũng nói lần đầu tiên của phụ nữ rất đau, anh lại còn không thương tiếc, có thể nghĩ được cô ta đau bao nhiêu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng cô ta lại cam chịu như vậy, chưa từng lên tiếng một lần nào, chỉ run rẩy trong lòng anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết thích anh, anh luôn biết, nhưng chưa từng cho cô ta cơ hội.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một là không yêu cô ta, hai là vì mẹ đã quyết định hôn ước cho anh rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng cô ta vẫn luôn ở bên cạnh anh, từ đó về sau, anh cảm thấy mình nên cho người phụ nữ này một thân phận.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến bây giờ anh vẫn nhớ rõ dấu đỏ kia rực rỡ đến mức nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết vùi trong lòng anh, mắt hơi rũ xuống, thẹn thùng ừ một tiếng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta thích Tông Triển Bạch, mấy năm nay vẫn ở bên cạnh anh với thân phận thư ký, nhưng cô ta đã không phải xử nữ từ lâu. Cô ta không thể để Tông Triển Bạch biết được, đàn ông để ý sự trong sạch của phụ nữ bao nhiêu cô ta hiểu rõ. Cho nên cô ta bỏ một khoản tiền cho dân trên thị trấn tìm một cô gái chưa từng làm chuyện đó đưa đến căn phòng kia.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đợi sau khi cô gái đó ra ngoài, cô ta mới đi vào tạo hiện tượng giả rằng đêm đó là cô ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thích quần áo ở đây thì mua thêm mấy món đi.” Tông Triển Bạch xoa đầu cô ta, cưng chiều nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đây là phòng VIP, cô không thể vào được, cô đi tới gian bên phải đi.” Nhân viên nhắc nhở Lâm Tử Lạp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiệm quần áo xa hoa thế này, phòng thử đồ đều độc lập với nhau, mà phòng VIP càng xa hoa hơn, trong phòng thử đồ có chỗ để thử đồ, gian ngoài là để bạn bè đợi hoặc nghỉ ngơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“À.” Lâm Tử Lạp cầm quần áo đi tới phòng bên phải.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc thay quần áo trong phòng thử đồ, Lâm Tử Lạp còn đang nghĩ, hình như trong cuộc trò chuyện của một nam một nữ khi nãy có nhà họ Lâm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chẳng lẽ người đàn ông kia…</span></p>', 0, '2022-06-30 07:31:49', '2022-06-30 07:31:49');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(79, 16, 'Chương 3: Tôi phải lấy anh', 'chuong-3-toi-phai-lay-anh', 3, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thay quần áo xong, Lâm Tử Lạp đi ra khỏi phòng thử đồ, nhìn sang phòng thử bên trái, cửa đã đóng lại rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Rất hợp với khí chất của cô.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhân viên rất có mắt nhìn, chỉ cần nhìn người là có thể lấy ra quần áo hợp với người đó, Lâm Tử Lạp mặc váy dài mày xanh nhạt, khiến làn da càng trắng ngần hơn, dây thắt lưng bên hông quấn quanh vòng eo mảnh khảnh, nhìn hơi gầy quá mức, nhưng khuôn mặt lại rất xinh đẹp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung thấy hợp thì lập tức trả tiền, lúc này mới phát hiện cái váy tới tận chín mươi triệu, nhưng nghĩ đến cô phải gặp người nhà họ Tông, bèn cắn răng tính tiền, giọng nói lạnh như băng: “Đi thôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp đã sớm cảm nhận được sự vô tình của ông ta, nhưng sự lạnh lùng lúc này vẫn khiến tim cô khó chịu đến đau đớn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cúi đầu theo sau ông ta lên xe.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chẳng mấy chốc xe đã dừng lại trước cổng lớn của biệt thự.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tài xế kéo cửa xe cho Lâm Viên Trung, ông ta khom người đi xuống, Lâm Tử Lạp theo sau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đứng trước cổng biệt thự, hoảng hốt mất mấy giây, lúc cô và mẹ vì bệnh của em trai mà sống không bằng chết, ba của cô lại đang hạnh phúc hưởng thụ trong biệt thự khí thế với vợ bé của mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai tay cô không nhịn được siết chặt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô đứng ở đó làm gì?” Lâm Viên Trung không thấy có người đi theo sau mình, quay đầu lại nhìn thoáng qua, thì thấy cô sững sờ đứng trước cửa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp vội vàng đuổi theo, nghe giúp việc trong nhà nói người nhà họ Tông vẫn chưa đến, Lâm Viên Trung bèn kêu cô đợi trong phòng khách.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỗ bên cạnh cửa sổ sát đất của phòng khách có để một cây đàn piano, hiệu Seidel của Đức, rất đắt, lúc sinh nhật năm tuổi của cô, mẹ đã mua cho cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ nhỏ cô đã thích, bốn tuổi rưỡi thì bắt đầu học đàn, sau đó bị đưa đi, cô cũng không chạm vào nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không nhịn được sờ lên, quen thuộc lại hưng phấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngón trỏ cô ấn trên phím đàn, nhẹ nhàng dùng sức, ting một tiếng, âm thanh du dương trong trẻo vang lên, vì rất lâu không đàn, nên ngón tay của cô cứng hơn rất nhiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đồ của tôi, ai cho cô động vào?!” Một giọng nói lanh lảnh mang theo tức giận vang lên sau lưng cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đồ của cô ta?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp xoay người, thấy Lâm Hàn Uyển hùng hổ đứng sau lưng mình, nhớ cô ta nhỏ hơn mình một tuổi, năm nay mười bảy, kế thừa ưu điểm của Thẩm Thanh Anh, dáng vẻ không tệ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng điệu bộ nhe răng trợn mắt lúc này có mấy phần dữ tợn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Của cô?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bọn họ phá huỷ cuộc hôn nhân của mẹ, dùng tiền của mẹ, bây giờ ngay cả đồ mẹ tặng cho mình của trở thành của cô ta?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô từ từ siết chặt tay, liên tục thầm nói với mình là đừng xúc động, đừng kích động, vì bây giờ cô vẫn không thể cướp lại đồ thuộc về mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô phải nhịn!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không phải cô gái nhỏ chỉ biết khóc bị ba đưa đi tám năm trước, bây giờ cô đã trưởng thành rồi!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô… là Lâm Tử Lạp?!” Lâm Hàn Uyển hiểu ra, hôm nay là ngày người nhà họ Tông đến, ba đón hai mẹ con kia về nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Hàn Uyển vẫn còn nhớ dáng vẻ đáng thương khi Lâm Viên Trung đưa bọn họ ra nước ngoài, Lâm Tử Lạp quỳ dưới đất ôm chân Lâm Viên Trung năn nỉ ông ta, kêu ông ta đừng đưa mình đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ba đón cô về, có phải cô vui lắm không?” Lâm Hàn Uyển khoanh tay, khinh thường nhìn cô: “Cô đừng có đắc ý quá, đưa cô về chỉ vì muốn cô gả vào nhà họ Tông thôi, nghe nói người đàn ông kia…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong, Lâm Hàn Uyển châm chọc che miệng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhớ đến Lâm Tử Lạp phải gả cho người đàn ông không thể làm chuyện kia, còn không thể đi lại, không nhịn được thấy vui sướng khi người gặp hoạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôn nhân là chuyện lớn cả đời, lấy một người đàn ông như vậy, không phải cả đời đều bị huỷ bỏ sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhíu mày.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng lúc này, giúp việc đi tới: “Người của nhà họ Tông đến rồi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung tự mình ra cửa nghênh đón.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp xoay người, lập tức nhìn thấy người đàn ông ngồi xe lăn bị người ta đẩy vào, ngũ quan sắc nét, tướng mạo ngay thẳng, cho dù ngồi trên xe lăn cũng khiến người ta không dám khinh thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khuôn mặt này, không phải người đàn ông tán tỉnh với người phụ nữ kia trong phòng thử đồ mà cô thấy sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh lại có thể là cậu cả của nhà họ Tông?!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng ở phòng thử đồ, rõ ràng cô thấy anh đứng được mà, còn ôm người phụ nữ kia nữa, chân đâu có tật gì.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xảy ra chuyện gì vậy?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi cô vẫn chưa nghĩ kỹ vì sao người đàn ông này phải giả vờ, Lâm Viên Trung gọi cô một tiếng: “Tử Lạp mau đến đây, đây là cậu cả nhà họ Tông.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung nhún vai nịnh hót, cong lưng nói: “Cậu Tông, đây là Lạp Lạp.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung thầm thấy thương tiếc, đường đường là cậu cả nhà họ Tông, còn ưa nhìn như thế, lại trở thành tàn phế rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhìn Lam Tử Lạp, thấy tuổi không lớn, quá gầy, dáng vẻ hơi thiếu dinh dưỡng, anh cau mày.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây là hôn sự mẹ quyết định cho anh, làm con trai, anh không thể làm trái lời hẹn. Nên sau khi ra nước ngoài bất ngờ bị rắn độc cắn, anh đã đưa ra tin nói độc kia không giải được, tàn phế rồi, còn không thể làm chuyện kia, chính là muốn nhà họ Lâm đổi ý.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng không ngờ nhà họ Lâm không hề đổi ý.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch im lặng không nói gì, sắc mặt nặng nề, Lâm Viên Trung nghĩ rằng anh không hài lòng, vội giải thích: “Bây giờ con bé còn nhỏ, vừa đủ mười tám, nuôi lớn rồi chắc chắn là một người đẹp.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch thầm cười lạnh, người đẹp thì không nhìn ra, nhưng cũng thấy không tầm thường, anh là một “người què” còn muốn con gái mình lấy anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh lạnh nhạt, nở nụ cười sâu xa: “Tôi ra nước ngoài làm việc, không cẩn thận bị thương, cái chân này sợ là không đi lại được nữa, cũng không thể thực hiện trách nhiệm của người làm chồng…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi không ngại.” Lâm Tử Lạp lập tức trả lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung đã đồng ý với cô, chỉ cần gả vào nhà họ Tông sẽ trả lại của hồi môn của mẹ, cho dù hôm trước vào cửa, hôm sau ly hôn, bây giờ cô cũng phải đồng ý.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong khoảng thời gian này, cô đã suy nghĩ cẩn thận tất cả, rõ ràng anh có thể đứng lên, nhưng đến nhà họ Lâm lại ngồi xe lăn, có lẽ là vì người phụ nữ kia, cũng không muốn thực hiện giao hẹn, muốn nhà họ Lâm đổi ý trước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng anh không ngờ Lâm Viên Trung sẵn sàng hy sinh đứa con gái không được cưng chiều là cô để hoàn thành giao hẹn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch híp mắt nhìn chằm chằm vào cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp bị anh nhìn đến lạnh sống lưng, đáy lòng chua xót, cô nào có muốn gả vào nhà họ Tông chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không đồng ý, sao cô có thể về nước, sao có thể lấy lại những gì đã mất được?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cong môi cười, cay đắng và chua xót trong đó chỉ mỗi cô biết: “Chúng ta được đính hôn từ lúc còn nhỏ, anh trở thành thế nào, tôi cũng phải lấy anh.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh mắt Tông Triển Bạch lại càng nặng nề hơn, người phụ nữ này cũng biết nói chuyện đấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung cũng không nghe ra cái gì không đúng, thăm dò hỏi: “Vậy ngày cưới…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vẻ mặt Tông Triển Bạch thay đổi trong nháy mắt, cuối cùng lại bình tĩnh đáp: “Đương nhiên sẽ làm theo giao hẹn, đây là chuyện hai nhà quyết định từ trước, sao có thể nuốt lời được.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp rũ mắt, giấu đi tâm tư của mình, không dám nhìn vào anh, rất rõ ràng anh cũng không hài lòng với chuyện cưới xin này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ đồng ý cũng chỉ vì giao hẹn thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vậy cũng tốt.” Lâm Viên Trung thầm vui mừng, dùng một đứa con gái không xuất sắc kết thông gia với nhà họ Tông, đương nhiên là chuyện tốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy nhà họ Lâm cũng có tiền, nhưng so với nhà họ Tông chỉ là cá lớn gặp cá bé, cũng như cá mập và con tép vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàn toàn không thể đánh đồng với nhau!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung khom lưng, nhỏ giọng nói: “Tôi đã cho người chuẩn bị bữa tối rồi, ở lại ăn cơm xong hãy đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhíu mày, trò hề nịnh nọt cung kính này của ông ta khiến người ta rất khó chịu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không cần, tôi còn có việc.” Tông Triển Bạch từ chối, Quang Kình đẩy anh ra ngoài, khi đi ngang qua bên cạnh Lâm Tử Lạp, anh hơi nâng tay lên, ý bảo Quang Kình dừng lại, anh nâng mắt hỏi: “Cô Lâm có rảnh không?”</span></p>', 0, '2022-06-30 23:32:50', '2022-06-30 23:32:50'),
(80, 16, 'Chương 4: Kết hôn không có lễ cưới và nghi thức', 'chuong-4-ket-hon-khong-co-le-cuoi-va-nghi-thuc', 4, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy là câu hỏi, nhưng lại khiến người ta không thể từ chối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp gật đầu, nhìn dáng vẻ của anh là có lời muốn nói với cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng lúc cô cũng muốn nói chuyện với anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Viên Trung cảnh cáo nhìn cô: “Có chừng có mực một chút.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đừng có còn chưa gả vào đã đắc tội người ta, thấy dáng vẻ lạnh lùng của Tông Triển Bạch, có lẽ là không hài lòng với Lâm Tử Lạp, nhưng có quan hệ thông gia với nhà họ Tông cũng có chỗ lợi cho nhà họ Lâm, cũng có thể giúp đỡ cho nghiệp vụ của công ty.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông ta cũng không muốn Lâm Tử Lạp làm thất bại chuyện này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp vờ như không phát hiện, đi theo sau Quang Kình ra ngoài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô hiểu Lâm Viên Trung có ý định gì, ông ta lấy đâu ra tự tin sau khi cô gả vào nhà họ Tông sẽ giúp ông ta vậy?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ vì ông ta là ba cô ư?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng ông ta có xem mình là con gái không? Biết tám năm nay cô sống thế nào không?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp đang suy nghĩ mơ hồ, đầu đập phải một “vách tường” cứng rắn khiến cô tỉnh táo lại, lập tức phát hiện một khuôn mặt không thể xoi mói đang gần trong gang tấc kiêu ngạo nhìn cô.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quả… quả nhiên anh có thể đứng được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chứng minh cô đã đoán đúng rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp bị nhìn đến căng da đầu, cô cố giả vờ bình tĩnh ngẩng đầu nhìn anh: “Anh cố ý giả vờ đúng không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch híp mắt, không vui vì bị người ta nhìn thấu, giọng điệu không cao không thấp nhưng lại đu khiến người ta sợ hãi: “Vì sao tôi là một người què còn muốn kết hôn với tôi? Coi trọng cái gì của tôi? Tiền bạc, muốn làm bà chủ nhà giàu à?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp chỉ cảm thấy bị anh nhìn đến xương cốt dưới da thịt cũng lạnh lẽo, trái tim như bị siết chặt, hít thở cũng khó khăn, nhưng vẻ mặt lại giả bình tĩnh: “Lúc tôi hai tuổi đã có hôn ước với ngài Tông, chẳng lẽ lúc tôi hai tuổi đã biết lợi ích của tiền bạc và làm bà chủ nhà giàu? Cho nên kiên quyết để hai người mẹ để hai chúng ta đính hôn?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô dừng lại một chúng, giọng điệu như cũng nhẹ nhàng hơn: “Lúc tôi hai tuổi, ngài Tông đã mười tuổi, lớn hơn tôi tám tuổi, tôi có chê anh già sao?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ha, Tông Triển Bạch cười lạnh, người phụ nữ này đâu chỉ biết nói, rõ ràng là nhanh mồm nhanh miệng!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Miệng lưỡi rất lợi hại!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh già?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong không khí tràn ngập mùi thuốc súng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bốn mắt nhìn nhau, tia lửa văng khắp nơi, ai cũng không chịu nhường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai tay rũ xuống bên người của Lâm Tử Lạp siết chặt thành quyền, mục đích cô gả vào nhà họ Tông chỉ vì Lâm Viên Trung hứa trả lại đồ cưới của mẹ cho cô mà thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng không phải muốn làm kẻ thù với người đàn ông này, giọng nói của cô dịu dàng trở lại, dáng vẻ khiêm tốn: “Ngài Tông, tôi biết anh không muốn cưới tôi, thật ra cũng không phải không thể…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cố ý nhìn lại nhìn sắc mặt của Tông Triển Bạch, vẻ mặt của anh chỉ hơi thay đổi, nhưng cô vẫn thấy được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ngài Tông, chúng ta giao dịch đi.” Lâm Tử Lạp lên tiếng, thật ra cô cũng không muốn gả vào nhà họ Tông lắm, cô sẽ đồng ý chỉ vì muốn trở về từ nước ngoài, giành lại của hồi môn thuộc về mẹ mà thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ha.” Tông Triển Bạch cười khẽ một tiếng, hình như cảm thấy buồn cười và hoang đường, giao dịch với anh?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nuốt một ngụm nước miếng, lưng vì căng thẳng mà đổ một tầng mồ hôi lạnh, Tông Triển Bạch rất cao, cô phải ngẩng đầu lên nhìn anh: “Tôi biết anh giả vờ là muốn nhà họ Lâm đổi ý chuyện này, tôi sẽ đồng ý là có nỗi khổ của mình.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Điều này khiến Tông Triển Bạch có hứng thú.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô muốn gì?” Nếu là giao dịch, chắc chắn có điều kiện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Một tháng, kết hôn một tháng xong tôi sẽ ly hôn với anh.” Thời gian một tháng đã đủ rồi, chỉ cần lấy được của hồi môn của mẹ, cô sẽ ly hôn với anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhíu mày: “Đây là giao dịch cô muốn nói với tôi?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đúng vậy, chúng ta nhất định phải kết hôn, đây là giao hẹn của hai người mẹ, chúng ta cũng không thể nuốt lời, đây là sự tôn trọng với bọn họ. Nhưng sau khi kết hôn, tính cách chúng ta không hợp thì ly hôn một cách rõ ràng, như vậy cũng không xem như nuốt lời. Vừa hay anh cũng không cần sống cả đời với người mình không thích, anh cũng không có hại, chỉ có lợi thôi…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói đến đây, giọng điệu của Lâm Tử Lạp thong thả hơn một chút: “Tôi nghĩ, chắc ngài Tông đã có người mình thích mới có thể nghĩ mọi cách khiến nhà họ Lâm thất hứa đúng không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sắc mặt Tông Triển Bạch trở nên nặng nề mà nghiêm túc, thầm nói: “Không ngờ cô còn rất thông minh đấy.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phải, anh muốn cho Tần Lộ Khiết một thân phận, sự ngây ngô và cam chịu của cô ta lúc đó đã khiến anh xúc động.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhìn vào khuôn mặt bình tĩnh của cô: “Cô thì sao, kết hôn một tháng có lợi ích gì với cô?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cũng không cho rằng cô chỉ suy nghĩ vì mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp căng thẳng trong lòng, cũng không thể nói là vì của hồi môn của mẹ được đúng không?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng nếu không có một cái lý do, hình như anh cũng sẽ không tin.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ tôi rất xem trọng hôn ước này, sức khoẻ của bà không được tốt, cho nên tôi cũng không muốn khiến bà thất vọng.” Khi nói chuyện ánh mắt cô hơi trốn tránh, vì cô nói dối, mẹ hoàn toàn không muốn cô gả vào nhà họ Tông.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng điệu của Tông Triển Bạch đột nhiên mang theo uy hiếp nặng nề khó hiểu, như nhìn thấu suy nghĩ của cô vậy: “Vậy sao?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp như đứng ngồi không yên, ánh mắt anh quá sắc bén, như có thể nhìn thấu lòng người vậy, vào lúc cô không biết làm sao cho phải, điện thoại trong túi anh reo lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhìn cô một cái, lấy điện thoại di động ra, nhìn thấy tên trên thông báo, sắc mặt dịu dàng hơn một chút xoay người nghe máy, hình như lại nghĩ đến điều gì đó, quay đầu lại: “Nếu một tháng, chúng ta cũng không cần thiết tổ chức lễ cưới.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không có sự lựa chọn, chỉ có thể đồng ý: “Được.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mười hai tháng tám, Quang Kình đến đón Lâm Tử Lạp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không có nghi thức, không có lễ cưới, chỉ có một tờ giấy chứng nhận kết hôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cũng không quan tâm lắm, vì cô hiểu rõ đây chỉ là một giao dịch theo yêu cầu thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu không phải đính hôn từ nhỏ, chỉ sợ bọn họ cũng chưa từng gặp nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chẳng mấy chốc xe đã dừng lại trước cổng biệt thự.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiến trúc rộng lớn dưới ánh mặt trời, cực kỳ có khí thế.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vào đi.” Quang Kình làm tư thế mời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không nhiệt tình, cũng không lấy lòng cô, chỉ làm theo quy tắc, có lẽ cũng biết hôn nhân giữa cô và Tông Triển Bạch chỉ là hoàn thành giao hẹn thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng không phải mợ chủ thật sự của nhà họ Tông.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Toà nhà tuy lớn, nhưng cũng chẳng có bao nhiêu người, chỉ có một giúp việc, Quang Kình cũng không giới thiệu nhiều, đưa cô vào phòng rồi đi ngay.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không quen lắm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đây là chỗ ở của cậu chủ, tôi là má Vương chăm lo cho sinh hoạt của cậu ấy, cô cũng có thể gọi tôi như thế.” Má Vương dẫn cô đến phòng: “Có cần gì cứ nói với tôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời gian một tháng không phải quá dài, Lâm Tử Lạp mang theo đồ dùng sinh hoạt của mình, tuy có lẽ sẽ không làm phiền bà, nhưng vẫn nói: “Được.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Má Vương mở cửa phòng ra, xoay người nhìn cô, vốn định nói gì với cô, nhưng cuối cùng chỉ thở dài: “Có lẽ đêm nay cậu chủ sẽ không về, hôm nay là sinh nhật của cô Tần.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy bọn họ không làm lễ cưới, nhưng tốt xấu gì người này cũng là vợ trên pháp luật của anh, hôm nay nói thế nào cũng là ngày cưới đầu tiên của bọn họ, anh ở bên ngoài với người phụ nữ khác, má Vương thấy Lâm Tử Lạp đáng thương, mới vừa vào cửa đã bị Tông Triển Bạch lạnh nhạt như vậy, sau này chẳng phải sẽ càng thê thảm hơn sao?</span></p>', 0, '2022-07-01 01:12:17', '2022-07-01 01:12:17');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(81, 16, 'Chương 5: Động phòng hoa chúc', 'chuong-5-dong-phong-hoa-chuc', 5, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp như đoán được ý của má Vương, cũng không giải thích mà chỉ cười.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô và Tông Triển Bạch chỉ là giao dịch thôi, sinh hoạt cá nhân của anh cô không có quyền hỏi đến.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh không ở đây, Lâm Tử Lạp còn thoải mái hơn một chút.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp tiến vào phòng mới nhìn rõ bày biện bên trong, phong cách trang trí trắng đen độc đáo, đơn giản gọn gàng, vừa xa hoa lại không dung tục, lịch sự tao nhã, có hương vị riêng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đây là phòng của cậu chủ.” Má Vương cười, nếu đã kết hôn thì chính là vợ chồng, đương nhiên phải ở cùng nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp há miệng, lại phát hiện nói không nên lời, chỉ có thể gật đầu đáp lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đêm đầu tiên ngủ ở một nơi xa lạ rất khó, cô bèn tựa trên đầu giường xem Đông Thành 58 trong điện thoại, chuẩn bị tìm một công việc, có công việc mới có thể yên ổn chăm sóc tốt cho mẹ, cho đứa nhỏ trong bụng một tương lai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">A…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp chợt nhìn thấy có thông báo phiên dịch, thông báo tuyển phiên dịch cũng không lạ, ngạc nhiên là phải biết tiếng nước A.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước A là nước cô bị Lâm Viên Trung đưa đi kia, rất là lạc hậu, chỗ nhiệt đới, cũng không có bao nhiêu người học ngôn ngữ của nước đó, ngôn ngữ thịnh hành trên thế giới đều là ngôn ngữ của các nước khá phát triển và có thực lực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiền lương đãi ngộ cũng không tệ.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì thế cô để lại thong tin cá nhân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó buông điện thoại, nằm xuống ngủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh trăng chiếu xuống trước cửa sổ như nhưng tia sáng chảy xuống, cực kỳ dịu dàng, đêm dài tĩnh lặng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người phụ nữ trên giường vô thức ngủ mất, có chùm ánh sáng trắng từ bên ngoài chạy vào trong sân, một chiếc xe Maybach dừng lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cửa xe mở ra, bóng dáng cao lớn đi xuống, anh sải bước vào trong nhà, nhịp bước cũng không vững vàng như bình thường, có mấy phần loạng choạng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh kéo cổ áo, miệng lưỡi hơi khô khốc, anh bước vào nhà rót một ly nước, yết hầu cứng rắn không ngừng cuộn lên cuộn xuống, đồng tử tối đen phủ kín men say, uống ly nước xong, cảm giác nóng bỏng nơi cổ họng cũng dịu đi không ít. Anh xã giao uống không ít rượu đế, sinh nhật của Tần Lộ Khiết lại uống mấy ly vang đỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người vốn có tửu lượng cao như anh cũng có hơi say.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh cởi áo khoác để trên sofa, không đến phòng tắm mà đi thẳng vào phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong phòng không mở đèn, rất tối, anh quen thuộc đi tới bên giường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nằm xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp đang ngủ say cảm thấy có tiếng động, nhưng chẳng mấy chốc đã yên tĩnh lại, cô co người tiếp tục ngủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sáng sớm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từng đợt ánh sáng rọi vào như sợi vàng, chiếu sáng cả căn phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai người ngủ rất say.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tựa như một đôi người yêu ngọt ngào vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lông mi người đàn ông hơi rung động, từ từ mở to mắt ra. Một đêm say rượu, anh chỉ cảm thấy đầu óc nặng nề, cần tắm để tỉnh táo lại. Khi anh nâng cánh tay muốn đứng dậy, lại phát hiện có gì đó đè lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh nghiêng đầu, lập tức nhìn thấy một người phụ nữ nằm trong lòng mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô gái có mái tóc đen như thác nước, từng sợi rơi trên cánh tay anh, gò má trắng ngần, lông mi xoăn vểnh như cánh bướm, đôi môi hồng nhạt hơi nhếch lên, phát ra tiếng hít thở đều đều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh mắt anh từ từ di chuyển xuống, cần cổ mảnh khảnh, xương quai xanh thanh thoát.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô hít thở phập phồng, lại mang theo chút hương vị quyến rũ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Yến hầu của anh không nhịn được lăn lộn, cho dù với Tần Lộ Khiết cũng chưa từng có xúc động như vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh chau mày, hình như rất không thích phản ứng không thể khống chế thân thể thế này, nhưng lại không dời mắt đi được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lúc ngủ mơ, Lâm Tử Lạp mơ thấy mình đang bị một con sư tử hung mãnh nhìn chằm chằm trên thảo nguyên châu Phi rộng lớn, như muốn ăn mình luôn vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô bừng tỉnh khỏi giấc mộng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng vào khoảnh khắc mở mắt, đập vào mắt là một đôi đồng tử sâu thẳm bình tĩnh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đại não thoáng trống rỗng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô mở to hai mắt, che ngực nói năng lộn xộn: “Sao… sao anh lại ở đây?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người đàn ông bình tĩnh dời mắt đi, từ từ vén chăn lên: “Đây là chỗ của tôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp muốn phản bác, nhìn thấy hoàn cảnh trong phòng, lời tới bên miệng lại nuốt xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không phải anh đi sinh nhật bạn gái ư? Sao lại về đây?” Lâm Tử Lạp bước xuống từ trên giường, đứng sang một bên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng điệu mang theo chút chất vấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôm qua nghe má Vương nói buổi tối anh không về, sau đó mới thả lỏng cảnh giác nên ngủ hơi say, thế nhưng cả việc anh vào phòng cũng không biết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôm qua cô lại có thể ngủ cùng một phòng với người đàn ông này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến hôm qua mình ngủ trong lòng anh, hai má đã cực kỳ nóng rát.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cúi đầu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cởi cúc áo sơ mi, tối qua anh không cởi quần áo, trên quần áo còn mang theo mùi rượu, còn nhăn nhúm dính trên người rất không thoải mái, anh nhìn cô gái ngại ngùng đứng bên giường một cái, hơi cong môi: “Sinh nhật của bạn gái, có quan trọng bằng đêm động phòng hoa chúc ư?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp: “…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây là giao dịch, bọn họ không phải vợ chồng, đêm động phòng hoa chúc cái gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cởi áo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp vội quay đầu, người đàn ông này lại có thể làm ngay trước mặt cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ sau đêm đó, cô rất không thích đàn ông, đặc biệt là tiếp xúc gần với bọn họ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô hoảng hốt: “Tôi… tôi đi ra ngoài trước.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong thì chạy khỏi phòng nhanh như chớp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cũng không để tâm lắm, cởi thắt lưng đi vào phòng tắm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh cần tắm rửa cho tỉnh táo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếng nước ào ào vang lên từ trong phòng tắm, chừng một tiếng trôi qua, anh tắm rửa xong mang theo mùi hương của sữa rửa tắm, mái tóc đen ngắn hơi rối, áo tắm màu trắng vây lấy dáng người thon dài, vạt áo hơi mở, làn da màu mật ong như ẩn như hiển, tản ra sức quyến rũ nam tính không thể khinh thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh đi đến trước tủ quần áo, khi chuẩn bị lấy quần áo lại phát hiện một cái túi in hoa hướng dương xa lạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh khựng lại, là của người phụ nữ kia à? Còn in hoa, sao cô ngây thơ thế?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn nữa còn không chút khách sáo để đồ của cô vào tủ anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh nhíu mày, lấy quần áo ra mặc vào, khi buông giá áo xuống không cẩn thận làm rơi túi của cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khoá không kéo lại, vì rớt xuống nên làm rơi hết đồ bên trong ra, quần áo đơn giản, đồ dùng sinh hoạt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh ngồi xổm xuống, khi vừa định nhặt lên, lại phát hiện một tờ giấy siêu âm B…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp, nữ, kiểm tra thời kỳ đầu, sáu tuần.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người phụ nữ kia mang thai rồi?</span></p>', 0, '2022-07-01 01:13:03', '2022-07-01 01:13:03'),
(82, 16, 'Chương 6: Ba người sống nương tựa lẫn nhau', 'chuong-6-ba-nguoi-song-nuong-tua-lan-nhau', 6, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cau mày, có cảm giác bị lừa gạt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong phòng khách, má Vương đã thức dậy chuẩn bị bữa sáng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy Lâm Tử Lạp mặc áo ngủ ngồi một mình trên sofa, bà cười híp mắt: “Hôm qua ngủ ngon không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà cho rằng tối qua Tông Triển Bạch ở với Tần Lộ Khiết không về, ban đêm nghe thấy tiếng động thì thức dậy xem thử, biết hôm qua Tông Triển Bạch đã về rồi, còn ngủ trong phòng nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây là người vợ bà chủ quyết định cho cậu chủ, đương nhiên phải tốt, cuối cùng cậu chủ cũng kết hôn rồi, má Vương luôn chăm sóc anh đương nhiên rất vui.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng điệu của bà quá nhiệt tình, mơ hồ mang theo chút mờ ám.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cứng nhắc cười gượng: “Ngon… ngon lắm.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vậy cô mau thay quần áo đi, tôi chuẩn bị bữa sáng, đợi lát nữa ăn cơm luôn.” Má Vương đi vào nhà ăn, bắt đầu nấu bữa sáng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cúi đầu nhìn áo ngủ trên người mình, quần áo cô mang đến còn ở trong phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chắc người đàn ông bên trong đã mặc đồ rồi nhỉ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đứng dậy đi tới của phòng ngủ, giơ tay lên gõ cửa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không ai đáp lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô gõ lại lần nữa, vẫn không có ai đáp lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hết cách, cô đành thử đẩy cửa phòng ra, cửa cũng không khoá trái từ bên trong, cô chỉ đẩy đã mở rồi.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vào khoảnh khắc mở cửa phòng ra, lập tức đối diện với bầu không khí lạnh lẽo như gió mùa đông tháng mười hai, thổi đến người ta run rẩy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người đàn ông ngồi trên giường, lạnh lùng nhìn chằm chằm một tờ giấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tờ giấy kia…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhanh chóng hiểu ra anh đang cầm gì trong tay, sau đó nhìn thấy một đống lộn xộn dưới đất, có cảm giác bực bội vì bị người ta xoi mói sự riêng tư. Cô chạy vào giành lấy, chất vấn: “Sao anh lại làm thế, không được người khác đồng ý còn xem đồ của người khác, có hiểu cái gì là riêng tư không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ha hả.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cười lạnh một tiếng: “Riêng tư?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dáng vẻ ngoài cười như trong không cười kia của anh cực kỳ đáng sợ: “Trong bụng cô có đứa con hoang còn lấy tôi, bây giờ lại nói riêng tư với tôi?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi… tôi…” Lâm Tử Lạp muốn giải thích, nhưng trong lúc nhất thời không tìm thấy lý do thích hợp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch đứng dậy, sải bước không nhanh không chậm đi tới, mỗi một bước đều như áp suất không khí ép đến gần, mây đen nghìn nghịt cuồn cuộn trên mặt anh: “Nói, cô có mục đích gì?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Muốn anh làm ba kế, trở thành con trai trưởng của nhà họ Tông?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giao dịch lúc trước chỉ là âm mưu tạm thời của cô?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sắc mặt anh càng u ám hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp mím môi, người run lên, không ngừng lùi về sau, hai tay che bụng, sợ anh làm hại đến đứa nhỏ trong bụng mình: “Không phải tôi cố ý giấu anh, chúng ta chỉ là hôn nhân giao dịch, cho nên tôi mới… tôi mới không nói, hoàn toàn không có mục đích gì hết.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng điệu của Tông Triển Bạch mang theo uy hiếp nặng nề: “Thật ư?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp che bụng, im lặng lùi về sau, cố bình tĩnh: “Thật sự, chuyện thế này sao có thể lừa gạt được, nếu tôi có suy nghĩ không nên có nào, sẽ không được chết tử tế. Hơn nữa nếu tôi thật sự đổ cho ngài Tông, tôi nghĩ chắc anh cũng có thủ đoạn giết chết tôi đúng không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy động tác của cô rất nhỏ nhẹ, nhưng Tông Triển Bạch vẫn phát hiện, quét mắt nhìn qua dáng vẻ bảo vệ bụng của cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó nhìn chằm chằm mặt cô: “Vì sao không nói rõ điều kiện tiên quyết?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch cũng không dễ dàng tin tưởng cô như vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai tay đang che bụng của cô từ từ siết chặt lại, đứa nhỏ này thật sự quá bất ngờ với cô, cũng là người thân có cùng quan hệ huyết thống của cô, cô đã mất đi em trai rồi, cho nên cô muốn sinh đứa nhỏ này ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau này có thể giống như mẹ lúc trước, ba người sống nương tự lẫn nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến đêm đó, cô không nhịn được run rẩy, lòng bàn tay chảy mồ hôi lạnh: “Tôi… tôi cũng chỉ vừa biết không lâu.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thậm chí cô còn không dám nói với Trang Kha Nguyệt, một mình đến bệnh viện kiểm tra, cô không dám để ở nhà là vì sợ bà phát hiện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không ngờ lại làm ầm ĩ đến mức này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khiến Tông Triển Bạch nghi ngờ động cơ của cô không đơn giản.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô mới bao nhiêu tuổi, lại có thể…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đời tư bừa bãi đến mức nào chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sắc mặt Tông Triển Bạch cực kỳ u ám, cảnh cáo: “Trong một tháng này, biết điều một chút cho tôi, nếu để tôi biết cô làm gì…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không đâu, chắc chắn không đâu, tôi nhất định sẽ biết thân biết phận, nếu còn làm ra chuyện gì nữa, mặc cho ngài Tông xử lý.” Lâm Tử Lạp vội đảm bảo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho dù không có được sự tin tưởng của anh, cũng không thể để anh nghi ngờ động cơ của mình được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô vốn đã ở trong hoàn cảnh khó khăn rồi, nếu còn có thêm kẻ thù sẽ rất bất lợi với cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch nhìn chằm chằm cô, ánh mắt tìm tòi nghiên cứu, dường như đang phán đoan sự đáng tin trong lời của cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cốc cốc… lúc này, má Vương đi tới: “Bữa sáng nấu xong rồi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch không nhìn nữa, giấu đi sát khí: “Dọn dẹp sạch sẽ đồ đạc dưới đất.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong thì xoay người đi ra ngoài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch vừa rời khỏi, hai chân Lâm Tử Lạp đã mềm nhũn, cô chống lên tủ thấp phía sau, một lúc lâu mới bình tĩnh lại, cô ngồi xổm xuống, nhặt đồ đạc dưới đất lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại nhìn thấy giấy siêu âm B trong tay, nước mắt rơi xuống nhỏ lên tờ giấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp lau mặt, cô không được khóc, đó là biểu hiện của sự yếu đuối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không thể yếu đuối, mẹ và đứa nhỏ trong bụng đều cần cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Gấp tờ giấy lại cho vào túi xách, thay quần áo đi ra ngoài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong nhà ăn đã không có ai nữa, trên bàn cơm để tách cà phê rỗng và đĩa ăn rỗng, có lẽ anh đã ăn xong rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp tự nhiên thở phào một hơi, ở chung với người đàn ông kia thật sự quá áp lực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đi tới trước bàn ăn bữa sáng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ăn xong, cô ra ngoài, đã nói trước sẽ về nhà, cô sợ Trang Kha Nguyệt lo lắng cho mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa vào cửa đã bị Trang Kha Nguyệt kéo lại: “Cậu cả của nhà họ Tông kia…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ.” Giọng điệu của Lâm Tử Lạp rất mạnh, cô không muốn nói nhiều về chuyện này: “Anh ta rất tốt, đừng lo cho con.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt thở dài, con gái lớn có quyết định của riêng mình, cũng không thích nghe bà nhiều lời, tâm trạng hơi mất mát: “Mẹ chỉ quan tâm con thôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sợ người đàn ông kia không tốt với cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp ôm lấy bà, không phải cô cố ý, chỉ là giằng co, thuyết phục Tông Triển Bạch khiến cô tốn hết sức lực, cảm thấy mệt mỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ, con chỉ hết mệt thôi, con không cố ý đâu.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ biết, mẹ không trách con.” Trang Kha Nguyệt cũng cảm giác được sự mỏi mệt từ cô: “Nếu mệt thì ngủ một lát đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp gật đầu, mặc dù không muốn ngủ, nhưng cô thật sự thấy mệt, trở về phòng, cũng không biết ngủ thiếp đi từ bao giờ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giữa trưa, Trang Kha Nguyệt nấu cơm xong, kêu cô thức dậy ăn cơm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngồi trên bàn cơm, Trang Kha Nguyệt xới cơm cho con gái: “Mẹ có nấu món cá con thích ăn đấy.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt thấy áy náy với con gái, tuy sinh ra cô lại không thể cho cô tuổi thơ tốt đẹp, khiến cô đi theo mình chịu khổ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhìn cá chua ngọt mẹ nấu trên bàn, mùi chua ngọt nhàn nhạt, trước đây cô thích ăn nó nhất. Nhưng lúc này người thấy mùi đó, dạ dày đột nhiên cuồn cuộn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không nhịn được, oẹ…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lạp Lạp.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không thể giải thích che miệng chạy vào trong toilet, nhoài người bên bồn rửa tay nôn khan.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt lo lắng đi tới, bà là người từng trải, nhìn thấy phản ứng của con gái, sắc mặt trở nên trắng bệch. Nhưng bà không tin tưởng lắm, con gái rất dè dặt, rất ngoan ngoãn, ở trường cũng chưa từng quen bạn trai, cô rất tự trọng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt run rẩy hỏi: “Lạp Lạp, con sao thế?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cứng người, hai tay cầm lấy bồn rửa tay không ngừng siết chặt, cô quyết định giữ đứa nhỏ này, như vậy, sớm muộn gì Trang Kha Nguyệt cũng sẽ biết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô xoay người nhìn mẹ, cố lấy can đảm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ, con có thai rồi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong chốc lát Trang Kha Nguyệt không thể đứng vững, lùi về sau một bước, thật khó tin, cô chỉ mới mười tám thôi mà.</span></p>', 0, '2022-07-01 01:14:01', '2022-07-01 01:14:01');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(83, 16, 'Chương 7: Phá thai không đau', 'chuong-7-pha-thai-khong-dau', 7, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Xảy ra chuyện gì rồi?!” Trang Kha Nguyệt chất vấn, hình như trong nháy mắt đã hiểu ra điều gì đó: “Chẳng lẽ tiền này không phải người gây tai nạn bồi thường?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà xảy ra tai nạn giao thông bị thương, phí chôn cất của con trai tốn không ít tiền, trước khi về nước con gái còn cho mình một chút tiền, nói là tiền còn lại của người gây tai nạn bồi thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không biết nên nói sao, thật sự quá khó khăn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sự im lặng của cô chính là ngầm thừa nhận, một cô gái như cô, sao có thể xoay sở được bao nhiêu tiền, Trang Kha Nguyệt đau lòng, lại không dám tin: “Con, chẳng lẽ con bán mình rồi…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà bắt lấy cổ tay Lâm Tử Lạp: “Đứa nhỏ này không thể sinh ra được, bây giờ đến bệnh viện với mẹ ngay!”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tại sao?” Lâm Tử Lạp muốn tránh khỏi bà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Con sinh rồi, cả đời cũng sẽ bị phá huỷ!” Đứa nhỏ này cô không thể sinh, cô đã lập gia đình rồi, nếu để người ta biết, chắc chắn cô sẽ toi đời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ, con xin mẹ để con sinh ra đi.” Lâm Tử Lạp khóc cầu xin.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cầu xin thế nào Trang Kha Nguyệt cũng không chịu, thái độ rất kiên quyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lập tức kéo Lâm Tử Lạp vào bệnh viện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không đi, bà còn dùng cái chết để uy hiếp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không thể không đi, phá thai phải làm kiểm tra đủ kiểu, khi Trang Kha Nguyệt đi lấy giấy xét nghiệm, một mình cô ngồi trên ghế dài trong hành lang, hai tay ôm bụng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước mắt không nhịn được rơi xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa chua xót lại vừa bất đắc dĩ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bạch, em không sao đâu, đừng căng thẳng thế, chỉ bị bỏng một chút thôi mà.” Tần Lộ Khiết cười yếu ớt, trên người mặt váy bó sát màu đen vây quanh dáng người lồi lõm tinh tế, vai khoác một cái áo khoác âu phục, Tông Triển Bạch mặc áo sơ mi màu trắng, cổ tay áo xắn lên, để lộ cánh tay rắn chắc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vẻ mặt lo lắng: “Bị bỏng không xử lý kỹ càng sẽ để lại sẹo.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết vùi người vào lòng Tông Triển Bạch: “Nếu để lại sẹo, anh có ghét em không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chỉ biết nói bậy!”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết cười khanh khách, biết Tông Triển Bạch không phải người nông cạn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng nói này…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp từ từ quay đầu lại, lập tức nhìn thấy Tần Lộ Khiết dựa vào Tông Triển Bạch từ từ đi tới trong hành lang.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dáng vẻ đó cực kỳ xứng đôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khiến cho cô giống như một thằng hề, tuổi còn trẻ đã mất đi trong sạch, trong bụng còn mang thai đứa nhỏ không biết ba là ai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi cô nhìn đến ngơ ngác, chợt có một ánh mắt ngạc nhiên nhìn tới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bệnh nhân tiếp theo.” Cửa phòng phẫu thuật mở ra, y tá đứng trước cửa, phía sau là một người phụ nữ trẻ tuổi che bụng đi ra từ bên trong, miệng còn nhắc nhở: “Con mẹ nó, phá thai không đau sao lại đau như vậy chứ?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết nhìn qua theo ánh mắt của Tông Triển Bạch…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vào khoảnh khắc nhìn thấy Lâm Tử Lạp, cô ta như cảm thấy quen thuộc, nhưng lại không nhớ là từng gặp ở đâu, cô ta nhìn về phía Tông Triển Bạch: “Anh quen cô ta à?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không quen.” Tông Triển Bạch lạnh lùng cong môi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch có rất nhiều thành kiến với Lâm Tử Lạp, đời tư của cô bừa bãi, nhỏ như vậy đã bị làm to bụng, một bên bày tỏ tình thương người mẹ với anh, một bên chạy tới phá thai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đồ tâm cơ!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Nghĩ kỹ rồi chứ?” Y tá liên tục xác định.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp không muốn bị người ta nhìn thấy sự chật vật của mình, cho dù trong lòng không muốn, đau lòng, bất đắc dĩ, nhưng vẫn gật đầu: “Tôi nghĩ kỹ rồi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đi vào theo tôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cúi đầu, không nhìn tới bất kỳ ai, đi vào phòng phẫu thuật theo y tá. Cửa phòng phẫu thuật đóng lại, ngăn cách mọi thứ bên ngoài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết mơ hồ hơi lo lắng, cô ta cảm thấy Tông Triển Bạch đang tức giận, duỗi tay khoác lên tay anh, dịu dàng nói: “Bạch.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch lạnh mặt: “Đi thôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết kéo tay anh, quay đầu lại nhìn qua phòng phẫu thuật đã đóng cửa, lại nhìn phản ứng của Tông Triển Bạch, không giống không quen lắm, nhưng đi theo bên cạnh anh lâu như vậy, bên cạnh anh cũng chưa từng xuất hiện phụ nữ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Điều này cô ta hiểu rất rõ, người phụ nữ khi nãy là ai?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sao anh lại tức giận thế?!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bạch, người phụ nữ khi nãy…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch ôm cô ta, cũng không muốn nói nhiều về chủ đề này: “Người không quan trọng, không cần quan tâm.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết chỉ có thể im lặng, cho dù trong lòng tò mò cũng không nói gì nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong phòng phẫu thuật, nhìn thấy những dụng cụ lạnh như băng kia, Lâm Tử Lạp lùi bước, không, cô không thể bỏ đứa nhỏ này, không thể được!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Nằm lên.” Bác sĩ ra hiệu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi không làm nữa.” Lâm Tử Lạp lắc đầu, xoay người bỏ chạy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô chạy rất nhanh, vì quá kích động nên không chú ý đường đi phía trước, đụng vào lòng một người đàn ông đi tới từ đối diện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô che trán, liên tục xin lỗi: “Xin lỗi, xin lỗi…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lâm Tử Lạp?” Hà Khiếu Thiên thấy giống cô, nhưng không dám chắc chắn nên dò hỏi.</span></p>', 0, '2022-07-01 01:14:36', '2022-07-01 01:14:36'),
(84, 16, 'Chương 8: Anh muốn tôi nói thế nào?', 'chuong-8-anh-muon-toi-noi-the-nao', 8, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp từ từ ngẩng đầu, thấy rõ khuôn mặt của người đàn ông thì ngạc nhiên nói: “Bác sĩ Hà?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có một đám người đứng sau lưng anh, Lâm Tử Lạp càng ngạc nhiên hơn: “Sao… sao anh lại ở đây?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Em trai mắc chứng tự kỷ, đều là Hà Khiếu Thiên khám cho thằng bé, thường xuyên qua lại nên hai người có quen nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên cười dịu dàng, còn chưa lên tiếng, Viện trưởng của bệnh viện này đã nói: “Bác sĩ Hà đến giảng dạy cho bệnh viện của tôi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên là bác sĩ tâm lý có tiếng, đặc biệt rất giỏi về chứng tự kỷ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô thì sao, sao cô lại ở đây, có chỗ nào không thoải mái à?” Hà Khiếu Thiên hỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến thái độ kiên quyết của mẹ, cả người Lâm Tử Lạp run lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lạp Lạp!” Trang Kha Nguyệt cầm giấy kiểm tra trong tay, vội vàng chạy tới từ một bên hành lang khác. Bà quay lại, nghe y tá nói cô chạy mất rồi thì hoảng sợ, thấy cô nên kích động kêu một tiếng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp mím môi, khoang mũi chua xót: “Mẹ…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên nói với Viện trưởng đứng bệnh cạnh: “Mọi người về trước đi, tôi có chút việc.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bác sĩ Hà có việc thì chúng tôi cũng không làm phiền nữa, nhưng tôi thật lòng mời bác sĩ Hà đến bệnh viện của chúng tôi làm việc, có gì cần bác sĩ cứ việc nói, tôi nhất định sẽ cố hết sức đáp ứng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên dịu dàng nói: “Tôi sẽ suy nghĩ.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bác gái, có chuyện gì chúng ta ra ngoài nói, ở đây không hợp.” Trong bệnh viện nhiều người lui tới, không hợp để nói chuyện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt cũng có quen Hà Khiếu Thiên, khi khám bệnh cho con trai, có đôi khi thật sự không trả nổi tiền, đều là bác sĩ Hà ứng trước.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt cực kỳ tôn trọng anh ấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì thế bà nắm chặt lấy cổ tay Lâm Tử Lạp, sợ cô lại bỏ chạy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa đi ra cổng lớn bệnh viện, Lâm Tử Lạp đã quỳ gối trước mặt Trang Kha Nguyệt: “Mẹ, con xin mẹ, Tân Kỳ đã mất rồi, để con giữ nó lại có được không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên nhíu mày, có ý gì? Nhưng chỉ chốc lát Hà Khiếu Thiên đã hiểu ra, đưa mắt nhìn bụng cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy giấy kiếm tra trong tay Trang Kha Nguyệt, gần như đã hiểu rõ, cô mang thai rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khiếp sợ, khó tin.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh ấy rất muốn biết đã xảy ra chuyện gì, nhưng bây giờ không phải lúc để hỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp rất ít khi khóc trước mặt Trang Kha Nguyệt, cho dù lúc em trai chết, cô cũng chỉ len lén khóc, chưa từng rơi nước mắt trước mặt bà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trần Nguyên Khánh không phải ép cô, chỉ là cô sinh đứa nhỏ này ra rồi, thì còn có tương lai hay sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai cũng nói làm mẹ sẽ trở nên mạnh mẽ, nhìn dáng vẻ của cô, muốn để cô bỏ đi rất khó, Trang Kha Nguyệt thở dài: “Tuỳ con vậy.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong thì bỏ đi mất, trong lòng khó chịu, không biết nên đối mặt với con gái thế nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp từ từ ngồi xổm xuống, người thì cậy mạnh, nước mắt lại xin hàng, cô không muốn khóc, nhưng không nhịn xuống được, tổn thương và đau đớn chất chứa trong lòng như muốn ăn mòn tim phổi cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trước khi về nước anh ấy từng đi tìm các cô, mới biết bọn họ về nước rồi, em trai cô đã qua đời trong tai nạn giao thông.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lúc đó đã xảy ra chuyện gì, anh ấy không hề hay biết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên ngồi xổm xuống vỗ lưng cho cô, khi quen cô gái này, cô mới chỉ là một đứa nhỏ mười mấy tuổi, cũng đã rất hiểu chuyện, chăm sóc em trai, chăm sóc mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có lần, anh ấy tận mắt thấy tiền của cô chỉ đủ mua hai phần cơm, cô đưa cơm cho mẹ và em ăn, rõ ràng mình chưa ăn, lại nói với Trang Kha Nguyệt là ăn rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiểu chuyện đến khiến người ta đau lòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên đưa tay muốn sờ đầu cô, an ủi cô, nhưng tay còn chưa rơi xuống, Lâm Tử Lạp đột nhiên ngẩng đầu nhìn anh ấy: “Cảm ơn sự giúp đỡ của anh trước kia, sau này em có tiền chắc chắn sẽ trả lại cho anh.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tay Hà Khiếu Thiên khựng lại trên tóc cô, bàn tay từ từ nắm lại rồi dời đi, cười nói: “Đồ ngốc, là anh tự muốn giúp, không cần trả.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp lắc đầu: “Anh tốt bụng, nhưng em vẫn nhớ.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có năng lực sau này nhất định sẽ trả.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hà Khiếu Thiên đỡ cô dậy: “Em nghỉ ngơi ở đâu, anh đưa em về.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này Lâm Tử Lạp lo lắng cho Trang Kha Nguyệt, cho nên gật đầu nói địa chỉ ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến nơi, cô đẩy cửa xuống xe, Hà Khiếu Thiên hỏi: “Sau này có về kia nữa không?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp xoay người nhìn anh ấy, lắc đầu: “Không về nữa.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khó khăn lắm mới về được đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp về chỗ ở, lập tức nhìn thấy Trang Kha Nguyệt ngồi trên ghế lau nước mắt, trái tim cô như bị thứ gì đó xé rách.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt lau nước mắt, không nhìn cô: “Mẹ không sao, con về đi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Là mẹ không chăm sóc tốt cho con.” Trang Kha Nguyệt lau nước mắt, nhưng lau rồi vẫn còn, không dừng lại được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp nhào tới ôm bà, hai mẹ con ôm nhau khóc lớn, trút ra sự chua xót trong lòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lúc sau đó, bọn họ mới bình tĩnh lại, Lâm Tử Lạp nói với Trang Kha Nguyệt giao dịch của mình và Tông Triển Bạch, kêu bà đừng lo lắng cho mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trang Kha Nguyệt cực kỳ khiếp sợ, hôn nhân sao có thể là trò đùa chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy bà không tán thành hôn nhân giao dịch gì đó, nhưng con gái mang thai rồi, không còn trong sạch nữa, người đàn ông nhà họ Tông kia cũng không chấp nhận được, vậy cũng tốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau này để bà chăm sóc con gái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi tối Lâm Tử Lạp về lại biệt thự, Tông Triển Bạch không ở nhà, ăn bữa tối xong cô đi tản bộ một vòng trong biệt thự cho tiêu cơm, tiện thể xem thử hoàn cảnh xung quanh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó cũng muộn rồi, cô về phòng, nhưng lại thấy khát, nên đến phòng bếp rót một ly nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Uống nước xong, lúc Lâm Tử Lạp chuẩn bị về phòng ngủ, cửa nhà vang lên tiếng mở chốt, sau đó bị đẩy ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó, một bóng dáng cao lớn sải bước đi vào, rồi đến một bóng dáng xinh đẹp đi ra từ sau lưng anh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp sửng sốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không ngờ đã muộn thế này rồi, Tông Triển Bạch còn dẫn người phụ nữ anh thích về nhà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tần Lộ Khiết nhìn thấy cô cũng sửng sốt, đây không phải người phụ nữ trong bệnh viện ngày đó sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta ngẩng đầu nhìn sườn mặt lạnh lùng của Tông Triển Bạch.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngày đó anh tức giận cái gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có liên quan đến người phụ nữ này sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lòng dạ của phụ nữ luôn rất nhạy bén, sự khác thường của Tông Triển Bạch khiến Tần Lộ Khiết bắt đầu đề phòng Lâm Tử Lạp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vậy tôi về phòng trước.” Lâm Tử Lạp cũng không muốn làm bóng đèn khiến người ta khó chịu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đợi đã.” Tông Triển Bạch nặng nề nhìn cô, cô mặc áo ngủ rất bảo thủ, làn váy màu trắng dài đến mắt cá chân, chỉ để lộ hai cánh tay trắng trẻo mảnh khảnh, nhìn cũng có mấy phần trong sáng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng nghĩ đến hành động của cô, trong lòng lại chán ghét thêm mấy phần: “Ngoài tôi ra, Lộ Khiết chính là chủ của nơi này, hiểu ý của tôi chứ?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp cảm thấy mình không làm gì cả, cô cũng chưa bao giờ coi mình là chủ ở đây, vậy tại sao phải nhấn mạnh nhưu vậy chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tôi biết rồi, vậy tôi đi ngủ đây.” Lâm Tử Lạp quay người, đi về phòng mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô Lâm.” Tần Lộ Khiết nhìn cô: “Xin lỗi.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp chẳng hiểu sao, ngạc nhiên nhìn cô ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên mặt cô ta đầy sự áy náy: “Tuy cô và Bạch có hôn ước, nhưng hai chúng tôi quen biết nhau lâu hơn cô, nếu không phải cô, người hôm nay gả vào đây chính là tôi, hai chúng tôi thật lòng yêu nhau, cho nên…”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cho nên cái gì?” Lâm Tử Lạp cảm thấy người phụ nữ này thật kỳ lạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô rất hiểu thân phận của mình, cũng không có cản trở bọn họ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta nói những lời này làm gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chỉ cảm thấy cô lấy Bạch rồi, nhưng Bạch không yêu cô là vì liên quan đến tôi, cho nên tôi cảm thấy rất áy náy.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không cần đâu.” Theo tư duy của người bình thường, quan hệ gượng gạo thế này không nên quấy rầy lẫn nhau mới phải?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Làm thế này là muốn thể hiện cô ta hiền lành trước mặt Tông Triển Bạch à?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tự nhiên Lâm Tử Lạp không có thiện cảm với cô ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tông Triển Bạch híp mắt nhìn chằm chằm mặt cô: “Cô có thái độ gì vậy?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lâm Tử Lạp mím môi, cô có thái độ gì, cô chỉ muốn sống yên ổn mấy tháng này thôi, sau khi lấy được đồ thuộc về mình thì cô sẽ rời đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Là người phụ nữ này quá kỳ lạ, cứ đi lên nói những lời này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô nên trả lời thế nào đây?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Anh muốn tôi trả lời thế nào?” Lời này của Tần Lộ Khiết, cô không có cách nào trả lời.</span></p>', 0, '2022-07-01 01:15:27', '2022-07-01 01:15:27'),
(85, 66, 'Chương 1: Cút Ra Ngoài', 'chuong-1-cut-ra-ngoai', 1, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Kiều Phương Hạ.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bên tai Kiều Phương Hạ vang lên tiêng hét tức giận, khiến cô trở lên tỉnh táo, mở mắt ra bắt gặp một đôi mắt âm hiểm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ngẩn ra mất giây, mới nhận ra mình đang nằm trên giường của Lệ Đình Tuấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau lần bạo ngược tối qua, toàn thân cơ thể Kiều Phương Hạ không có chỗ nào là không xanh tím, trên xương quai xanh trải đầy dấu đỏ, ánh mắt Lệ Đình Tuấn chạm vào thân thể nhỏ nhắn của cô, đồng tử đột nhiên nhíu chặt, sau đó kéo chăn ném lên người cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này Kiều Phương Hạ mới hoảng sợ trốn vào trong chăn, đôi mắt trong veo của cô đột nhiên bị hơi nước làm ẩm ướt.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô nhìn thấy Lệ Đình Tuấn đứng dậy, không nhìn cô lấy một cái mà đi vào phòng tắm, có chút không biết làm sao gọi anh một tiếng: “Anh.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng nói vừa rơi xuống, ánh mắt Lệ Đình Tuấn sắc bén liếc về phía cô: “Anh?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bởi vì giọng nói của anh quá lạnh lùng, Kiều Phương Hạ bị anh nhìn khiến trong lòng run rẩy, sau đó hoảng loạn thay đổi: “Cậu chủ.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tối qua khi cô về nhà thì bị cúp điện, còn chưa làm rõ xảy ra chuyện gì thì Lệ Đình Tuấn đã bắt lấy cô lôi vào trong phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không biết anh uống say hay bị làm sao, trong bóng tối chỉ gửi thấy mùi rượi mà mùi máu tanh nhàn nhạt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô chống đối khóc lóc, nhưng sức lực Lệ Đình Tuấn mạnh kinh người, giống như một dã thú mất đi lý trí vậy, dày vò cô đến ngất đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ chuyện đã xảy ra rồi, chuyện tối qua giống như một cơn ác mộng vậy, toàn thân cô đau đớn, xương cốt rã rời giống như bị đánh một trận vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đang muốn giải thích, đột nhiên nhìn thấy phần bụng đầy sức sống của Lệ Đình Tuấn, vậy mà lại có một vết máu dài chừng mười mấy phân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn chưa nhìn rõ, Lệ Đình Tuấn đã xoay người đi đến mép giường, giơ tay, bóp lấy cái cằm nhỏ nhắn của cô, trầm giọng nói: “Kiều Phương Hạ, cô cho rằng cô dùng loại phương thức này thì tôi có thể tha cho cô sao?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không phải, em…” Kiều Phương Hạ hoảng loạn phủ định.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn chưa nói xong, nhìn thấy trong mắt Lệ Đình Tuấn có mất phần ghê tởm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ nhìn thấy vẻ mặt không hề che đậy của anh, trái tim chùng xuống, ngẩn ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngoài cửa, đột nhiên truyền đến giọng nói kinh ngạc của bà Trần: “Đây không phải là quần áo của cô chủ sao? Sao lại ở ngoài cửa thế này?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lệ Đình Tuấn buông Kiều Phương Hạ ra, quay người trực tiếp lấy một cái áo sơ mi ném cho Kiều Phương Hạ, trầm giọng nói: “Mặc vào, ra ngoài.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ anh bảo cô ra ngoài sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng bên ngoài toàn là người, mọi người sẽ phát hiện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mặc dù nói cô và Lệ Đình Tuấn hoàn toàn không phải là anh em ruột thịt, nhưng trên dưới nhà họ Lệ ai mà không biết, mẹ cô An Phương Diệp là tình nhân của ba Lệ Đình Tuấn chứ.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ hoảng loạn không khống chế được run rẩy, nước mắt trực tiếp rơi xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô kéo lấy cánh tay Lệ Đình Tuấn, nhẹ giọng cầu xin: “Anh, anh nghe em giải thích được không? Tối qua em…”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lệ Đình Tuấn hung dữ hất tay cô ra, gương mặt tràn đầy lạnh lùng: “Kiều Phương Hạ, cô thật là đê tiện giống như mẹ cô vậy.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ bị hất ra ngã về giường, xương cốt đau nhức giống như bị lung lay.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó điều khiến người ta đau lòng hơn chính là câu nói anh vừa nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện của ba mẹ, lúc trước cô còn nhỏ, không thể hiểu cũng không thể nhúng tay, cô chỉ nhớ cô luôn khóc xin ba mẹ đừng ly hôn, nhưng bọn họ vẫn ly hôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi mẹ đưa cô đến nhà họ Lệ, cô chẳng qua chỉ mới sáu tuổi, nhà họ Lệ không tiếp nhận bọn họ, mẹ để cô đi nịnh nọt người thanh niên lạnh như băng này, khiến mẹ con bọn họ có chỗ đứng trong nhà họ Lệ, cho nên cô làm theo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô biết, Lệ Đình Tuấn vẫn luôn không thích cô, ghét cô giống như một con sâu bám lấy anh vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng cho dù bản tính anh có lãnh đạm, cũng chưa bao giờ dùng thái độ ghét bỏ này để đối xử với cô.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ không biết rốt cuộc là chỗ nào xảy ra vấn đề, cô cũng không hiểu tại sao chỉ qua mấy ngày không gặp mà Lệ Đình Tuấn lại biến thành thế này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rõ ràng lần trước rời đi, anh còn nói sẽ mang về món bánh mà cô thích.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">||||| Truyện đề cử: Bảo Bối! Anh Xin Lỗi |||||</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cho cô mười giây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cút ra ngoài.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lệ Đình Tuấn nghiến rằng, trầm tiếng nói với cô..</span></p>', 0, '2022-07-01 05:39:33', '2022-07-01 05:39:33');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(86, 66, 'Chương 2: Con Dâu Tương Lai Của Nhà Họ Lệ', 'chuong-2-con-dau-tuong-lai-cua-nha-ho-le', 2, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi Kiều Phương Hạ mặc quần áo vào và đẩy cửa bước ra ngoài, một nửa người nhà họ Lệ đều đứng vây quanh bên ngoài cửa, kinh ngạc nhìn cô thương tích đầy người, bước chân trần từ trong phòng đi ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ chỉ cảm thấy những ánh mắt đó như những con dao sắc nhọn cứa lên cơ thể cô, khiến cô cảm thấy đau thấu xương tủy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà Trần thấy cô lập tức tiến lên, lấy tấm thảm mỏng quấn lên người cô, đưa cô quay về phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đầu óc Kiều Phương Hạ lúc này rất rối bời, cô ngồi trong phòng của mình, nghe thấy tiếng trò chuyện mờ hồ truyền đến từ vòng bên: “Trước mặt bao nhiêu người như vậy, dù sao con cũng phải đối xử với con bé có trách nhiệm một chút chứ.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Trách nhiệm sao?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lệ Đình Tuấn bật cười đầy mỉa mai: “Vậy thì con sẽ thành toàn cho cô ta.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi chiều, nhà họ Kiều liền cho người đến.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hành lý của Kiều Phương Hạ giống như một đống rác bị vứt trước cổng nhà họ Lệ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đuổi Kiều Phương Hạ về chỗ bên ba ruột của cô là việc làm cuối cùng mà nhà họ Lệ có dính líu đến cô.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bầu trời bỗng đổ mưa to, nhưng Kiều Phương Hạ không mở ô lên, cô chỉ đứng im ở đó, ngước mắt nhìn người đàn ông đang đứng trên hành lang, lạnh lùng nhìn cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Anh ơi.” Đôi môi Kiều Phương Hạ run rẩy, đôi mắt cô mờ đi bởi nước mưa, cố gắng lên tiếng cầu xin Lệ Đình Tuấn lần cuối cùng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chính anh đã làm chuyện đó với cô, nhưng cô không dám khẳng định liệu lúc đó anh có phải bị kẻ khác hãm hại không.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không trách anh, nhưng tại sao Lệ Đình Tuấn lại nhẫn tâm đuổi cô đi cơ chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không muốn quay về nhà họ Kiều, chính anh cũng biết điều đó mà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đối diện với lời cầu xin của Kiều Phương Hạ, gương mặt mặt Lệ Đình Tuấn không có lấy một tia thương xót, anh quay người lại, biến mất sau cánh cửa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cánh cửa sắt được chạm khắc tinh xảo cũng nặng nề đóng lại trước mắt cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện Kiều Phương Hạ leo lên giường của Lệ Đình Tuấn, chỉ trong vài ngày ngắn ngủi đã được đồn thổi khắp thành phố Hạ Du.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người đều cho rằng, do mẹ của Kiều Phương Hạ đã cuỗm mất hơn ba mươi tỷ của nhà họ Lệ rồi bỏ trốn cùng tình nhân, chỉ để lại một mình Kiều Phương Hạ ở trong nhà họ Lệ, vì để bảo vệ chỗ đứng của chính mình, có thể tiếp tục trèo cao ở nhà họ Lệ, nên Kiều Phương Hạ đã làm ra chuyện đáng xấu hổ như vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một tháng sau, Kiều Phương Hạ được lặng lẽ đưa ra nước ngoài, cả hai nhà họ Lệ và họ Kiều đều chọn cạnh im lặng khi đối diện với chuyện này, dù sao thì với địa vị của nhà họ Lệ ở thành phố Hạ Du này, dần dần cũng không có bất cứ ai nhắc đến chuyện này nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ba năm rưỡi sau, tại nhà họ Kiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tài xế ra sân bay đón cô cả đã trở về.” Người hầu nói nhỏ với Kiều Đông Phương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Đông Phương nhìn chiếc Audi đỗ ở dưới lầu, không nói câu nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lẽ ra đây không phải là thời điểm thích hợp để Kiều Phương Hạ quay về nước, việc học hành của cô ở bên nước ngoài vẫn chưa hoàn thành, song ông cụ nhà họ Kiều đột ngột trở bệnh nặng, vẫn luôn mong muốn được gặp Kiều Phương Hạ lần cuối, vì vậy Kiều Đông Phương không còn cách nào khác, chỉ đành để Kiều Phương Hạ quay về.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bên ngoài trời mưa rất to, Kiều Phương Hạ cầm một chiếc ô màu đen, bước ra từ chiếc xe Audi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ngẩng đầu lên, vừa hay bắt gặp ánh mặt Kiều Đông Phương đang nhìn cô, hai người nhìn nhau vài giây, rồi Kiều Phương Hạ vô cảm thu lại tầm mắt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thật khéo làm sao, bất kể ngày cô bị đuổi khỏi nhà họ Lệ hay là ngày cô bị đưa ra nước ngoài, thậm chỉ cả ngày hôm nay, khi cô về nước, trời đều đổ mưa to.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chị về rồi.” Cô con gái thứ hai của Kiều Đông Phương, Kiều Diệp Ngọc đứng cạnh cửa, nở một nụ cười xinh đẹp, lên tiếng chào hỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ừm.” Kiều Phương Hạ chỉ thờ ơ đáp lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong cô liền mang hành lý vào bên trong, thẳng lưng bước qua cửa nhà họ Kiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên đường trở về, cô nghe quản gia nhà họ Kiều kể một số chuyện, Kiều Diệp Ngọc đã thi đỗ Đại họ Sân khấu điện ảnh, tuy mới học năm hai những đã có không ít hợp đồng quay phim trong tay, cô ta vừa mới quay xong một bộ phim cổ trang, mặc dù chưa công chiếu nhưng đã rất nổi tiếng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người đều đồn thổi rằng, những hợp đồng của Kiều Diệp Ngọc đều do nhà họ Lệ đích thân giành lấy, bởi vì Kiều Diệp Ngọc đã trở thành con dâu tương lai của nhà họ Lệ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giờ đây khi nghĩ đến nhà họ Lệ, trong lòng Kiều Phương Hạ chỉ còn lại sự bình thản.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mối quan hệ giữa nhà họ Lệ và nhà họ Kiều là gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Diệp Ngọc sẽ gả cho người đàn ông hô mưa gọi gió ở thành phố Hạ Du này sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đã có chuyện gì xảy ra trong khi cô ở nước ngoài?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng dù sao thì cô cũng không quan tâm đến những chuyện này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi đi chào hỏi ông nội xong, Kiều Phương Hạ quay trở lại phòng khách, liền trông thấy Kiều Đông Phương đang ngồi đó chờ cô, dường như ông ta có chuyện gì đó muốn nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Nếu như đã về rồi thì đừng đi nữa.” Kiều Đông Phương nói với cô</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ bước đến bên ghế sô pha, cô ngồi xuống, hai chân tao nhã bắt chéo nhau, cô nhìn thẳng vào Kiều Đông Phương, nhếch miệng nói: “Sao thế? Có chuyện cần tôi sao?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Gần bốn năm không gặp, Kiều Đông Phương cảm thấy Kiều Phương Hạ đã thay đổi kha nhiều, nhíu mày đáp: “Ngày mai ba có sắp xếp cho con đi xem mắt ở bữa tiệc của giám đốc Tiêu, tối nay nghỉ ngơi cho tốt.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xem mắt?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">E là ông ta chỉ muốn nhanh chóng đuổi cô ra khỏi cái nhà này mà thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ nở một nụ cười yếu ớt: “Ông Kiều cảm thấy một người già nua héo mòn như tôi còn có người dám lấy không? Chuyện năm đó ầm ĩ như vậy, ông không sợ sẽ khiến nhà họ Kiều mất mặt sao?”.</span></p>', 0, '2022-07-01 19:14:34', '2022-07-01 19:14:34'),
(87, 66, 'Chương 3: Kỳ Tài Trong Giới Kinh Doanh', 'chuong-3-ky-tai-trong-gioi-kinh-doanh', 3, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe thấy Kiều Phương Hạ ăn nói trắng trợn như vậy, gương mặt Kiều Đông Phương liền tỏ vẻ không hài lòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ có vẻ ngoài giống mẹ ruột của cô, An Phương Diệp đến bảy tám phần, làn da trắng như tuyết, dáng vóc cao ráo nóng bỏng, trời sinh đã vô cùng quyến rũ, nhưng lại có khuôn mặt thanh khiết, đôi mắt trong veo, giống như một cô gái chưa từng nhiễm bụi trần.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trước khi qua nước ngoài, cô vẫn còn trẻ, những đường nét trên gương mặt vẫn chưa nở rộ, giờ đây cô như bông hoa dưới ánh mặt trời, kiêu hãnh tỏa hương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây chính là vốn liếng của cô, trẻ tuổi và xinh đẹp, chắc chắn sẽ khiến những thương nhân lớn tuổi không thể rời mắt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Năm đó Kiều Đông Phương cũng đã nhìn trúng người mẹ đê tiện của Kiều Phương Hạ vì cái vẻ ngoài ấy, vì vậy mới cưới bà ta về nhà.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ nhìn lại mới thấy, đúng là mẹ nào con nấy, An Phương Diệp là người đàn bà ti tiện, sinh ra Kiều Phương Hạ vừa mới lớn đã biết bò lên giường của Lệ Đình Tuấn, đúng là chả ra cái thá gì.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ sớm đã quen thuộc với ánh mắt đầy khinh miệt của Kiều Đông Phương, vì vậy cô làm như không nhìn thấy, trực tiếp đối mắt với ông ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Rất nhiều thương nhân và những kẻ tai to mặt lớn sẽ có mặt ở đó, lo mà biểu hiện cho tốt.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Đông Phương lạnh lùng nói tiếp: “Ông nội con mong rằng trước khi mất có thể thành toàn cho chuyện chung thân đại sự của con.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dứt lời, Kiều Đông Phương liền đứng dậy, rời khỏi phòng khách, không cho Kiều Phương Hạ bất cứ cơ hội từ chối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ nhìn theo bóng lưng của Kiều Đông Phương, sắc mặt trở nên lạnh lùng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô bất ngờ lên tiếng nói: “Nếu như đi xem mắt, chẳng phải nên ăn mặc lộng lẫy một chút sao?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Đông Phương nghe thấy vậy liền dừng bước, không chút kiên nhẫn rút ra một tấm thẻ ngân hàng từ trong túi ra đưa cho cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông ta biết rõ, Kiều Phương Hạ và An Phương Diệp mê tiền như nhau, chỉ cần đưa tiền cho bà ta, bắt bà ta ăn cứt, bà ta cũng vui vẻ ăn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cảm ơn.” Kiều Phương Hạ nhận lấy tấm thẻ, thờ ơ bước ra khỏi cửa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng lúc này Tống Vân Lan bước từ cầu thang xuống, vừa hay nhìn thấy cảnh vừa rồi, gương mặt lạnh lùng, cất giọng đầy châm biếm: “Dù sao cũng là tiền mà Diệp Ngọc của chúng ta kiếm được, anh dặn cô ta yên tâm mà dùng.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đối với Tống Vân Lan, trong lòng Kiều Đông Phương quả thực có chút hổ thẹn với bà ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Năm xưa khi ly hôn với An Phương Diệp, ông ta đã hứa như đinh đóng cột rằng sẽ vạch rõ giới hạn với hai mẹ con An Phương Diệp, nhưng cho tới hiện tại, ông ta vẫn không thể rũ sạch mọi quan hệ với Kiều Phương Hạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đợi qua ngày mai là mọi chuyện sẽ ổn thôi, em cứ yên tâm.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn nữa mấy người này cũng là những người máu mặt, cho dù Phương Hạ gả cho ai đi chăng nữa, nhà chúng ta cũng không chịu thiệt đâu, sính lễ cũng phải lên đến vài tỷ ấy chứ.” Ông ta nhẹ nhàng dỗ dành.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tốt nhất là nên như vậy, nếu không cái nhà này phải nuôi thứ rác rưởi như cô ta đến bao giờ nữa?” Tô Vân Lan độc địa nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bên ngoài, Kiều Phương Hạ chỉ im lặng lắng nghe cuộc đối thoại giữa hai người bọn họ, nắm chặt lấy tấm thẻ trong tay.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc nãy ra ngoài cô mới phát hiện ra mình quên không mang theo chuột laptop, nên mới định quay lại để lấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hóa ra, Kiều Đông Phương muốn bán cô đi, muốn tận dụng một chút giá trị cuối cùng của cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô không khỏi nhếch môi nở một nụ cười châm biếm, xoay người rời khỏi nhà họ Kiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trung tâm thương mại Universal.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên điện thoại, King gửi một tin nhắn: “Đã đến chưa?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đến rồi, đang tìm quán cà phê.” Kiều Phương Hạ vừa trả lời tin nhắn, vừa bước vào bên trong.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đang bước đến chân cần thang máy, bỗng một đám người xuất hiện, ngang ngược đẩu những người khác ra: “Tránh ra, tránh ra.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ chỉ vô thức liếc nhìn về phía đó, nghĩ rằng đã xảy ra một vụ tai nạn ở thang máy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đó có phải Tổng giám đốc Lệ không? Nghe nói hôm nay Tổng giám đốc Lệ đến đây để đàm phán việc thu mua lại trung tâm thương mại Universal, chắc thông tin này không sai đâu.” Có hai phóng viên đang cầm máy ảnh đứng ở phía trước Kiều Phương Hạ, vội vàng xông về phía thang máy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Nếu có được trung tâm thương mại Universal, thì chẳng phải tập đoàn WL đã thâu tóm được công ty thứ mười rồi sao? Tuy nhiên tôi không ngờ rằng, chỉ trong vòng chưa đến bốn năm, Tổng giám đốc Lệ lại có thể khiến tập đoàn của mình cải tử hoàn sinh, quả là kỳ tài trong giới kinh doanh.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tổng giám đốc Lệ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiều Phương Hạ nghe thấy vậy liền vô cùng sửng sốt, cô ngước mắt về phía câu thang cuốn ở tầng ba của trung tâm thương mại Universal.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngoại trừ hai người vệ sĩ ở đằng trước và đằng sau, còn có một người đàn ông mặc vest thẳng tắp, dáng người cao lớn đứng chính giữa, một tay ôm một em bé sữa trông mới hai ba tuổi, anh như thể hoàng đế giáng thế, khí thế ngút trời..</span></p>', 0, '2022-07-01 19:15:11', '2022-07-01 19:15:11'),
(88, 67, 'Chương 1: Kẻ Ngốc Gả Thay', 'chuong-1-ke-ngoc-ga-thay', 1, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tại biệt thự nhà họ Thời.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ, con sẽ không kết hôn, có chết con cũng không kết hôn, hu hu hu...”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha nhào vào lòng mẹ khóc như mưa, khuôn mặt nhỏ nhắn xinh đẹp tràn ngập tuyệt vọng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta có nằm mơ cũng không nghĩ đến việc mình sẽ phải hẹn hò vui vẻ với một người mù!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy rằng Thịnh Hàn Ngọc khi trưởng thành vô cùng đẹp trai, thậm chí còn đẹp hơn cả minh tinh, nhưng có ích gì chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta không muốn kết hôn với một người đàn ông mù, cả đời này cô sẽ biến thành trò cười của thiên hạ!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Con gái ngoan, đừng khóc, mẹ sẽ tìm cách giúp con, mẹ sẽ không bao giờ để con gả cho người tàn tật.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đừng khóc, con khóc làm mẹ rất đau lòng.” Giang Nhã Đan vừa dỗ dành con gái, vừa hung hăng trừng mắt với chồng đang ngồi đối diện.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện này đều do ông ấy, không xem xét rõ ràng trước khi quyết định.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho dù nhà họ Thịnh có quyền có thế, tài sản dư thừa nhưng Giang Nhã Đan sẽ không bao giờ chấp nhận để đứa con gái bảo bối kết hôn với một người đàn ông mù.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vũ Kha không thể nào kết hôn với Thịnh Hàn Ngọc được.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vũ Thành, anh không được đẩy con gái của chúng ta vào con đường chết!” Giang Nhã Đan lau nước mắt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Thành thở dài bất đắc dĩ nói: “Em nghĩ anh bằng lòng sao? Nhưng em cũng biết hoàn cảnh của gia đình chúng ta, nợ nần bên ngoài nhiều như vậy, nếu như Vũ Kha gả đi gia đình chúng ta sẽ không phải phá sản.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Ông chủ nhà họ Thịnh đã hứa chỉ cần chúng ta gả con gái đi, họ liền chia cho chúng ta 10% cổ phần, cộng thêm 350 tỷ là quà đính hôn...\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trước khi ông nói hết lời, hai mẹ con đã ngừng khóc, trong ánh mắt lộ ra một tia thèm thuồng!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha không ngừng đung đưa cánh tay mẹ mà làm nũng: “Mẹ ơi, con muốn có 10% cổ phần của nhà họ Thịnh, hơn nữa quà đính hôn 350 tỷ cũng đủ để gia đình chúng ta trở thành nhà quyền thế bậc nhất Giang Châu...!\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giang Nhã Đan nghĩ rằng con gái mình đã thay đổi quyết định, mặc dù cũng bị cám dỗ bởi một điều kiện thuận lợi như vậy, nhưng bà vẫn do dự khi nghĩ đến hạnh phúc cả đời của con: “Con gái ngốc, đừng đồng ý nhanh như vậy, hãy suy nghĩ kĩ càng.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ...”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha ngồi thẳng người, khuôn mặt xinh đẹp thoáng qua một tia ranh mãnh: “Bố mẹ ơi, bố đã quên rằng nhà chúng ta vẫn còn một cô con gái hay sao? Mẹ có thể để Thời Du Huyên đó gả đi, chia cổ phần cho con và báo hiếu cho bố mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàn hảo!\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà họ Thời có hai cô con gái, cô chị Thời Vũ Kha là con ruột, cô ta trắng trẻo xinh đẹp, là mỹ nữ bậc nhất ở Giang Châu!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô con gái thứ hai tên Thời Du Huyên, là con gái của bạn tốt Thời Vũ Thành, bố mẹ cô bị tai nạn xe hơi qua đời ngay sau khi cô được sinh ra, vì vậy Thời Vũ Thành đã nhận cô làm con nuôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô em gái thực ra xinh đẹp hơn chị, nhưng năm mười hai tuổi không may bị ngã cầu thang khiến đầu óc trở nên không bình thường, trở thành một đứa ngốc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Thành không đồng ý: “Không được, nếu Vũ Kha không muốn kết hôn thì chúng ta từ chối, dù sao cũng không thể gả Huyên Huyên đi được.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giang Nhã Đan cau mày, đồng tình với ý kiến của con gái: “Vũ Kha nói đúng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên cũng là con gái của nhà họ Thời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dù sao thì nhà họ Thịnh nói muốn cưới con gái chúng ta, như vậy thì gả ai cũng như nhau.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Thành vẫn phản đối: \"Đây không phải là chuyện vớ vẩn hay sao, Vũ Kha đi xem mắt, sau đó lại để cho Huyên Huyên kết hôn ư?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Huyên Huyên bị bệnh tâm thần, Thịnh Hàn Ngọc sẽ không thể nào đồng ý kết hôn với Huyên Huyên cho dù cậu ấy bị tàn tật.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Với gia thế nhà họ Thịnh như vậy, chúng ta không thể tùy ý động vào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu không đến lúc đó cả nhà chúng ta đều phải chết.\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông ta ban đầu không đồng ý nhưng dần dần bị vợ và con gái tẩy não.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giang Nhã Đan nói: “Lúc trước nhà họ Thịnh đưa người đến mai mối cũng không nói rõ là con gái nào, họ chỉ nói rằng muốn xem mắt con gái nhà chúng ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn nữa hai người đi gặp riêng trong buổi gặp mặt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cậu chủ Thịnh bị mù nên cậu ấy sẽ không biết người đó là Vũ Kha.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Thành: “...”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha nói: “Đúng vậy đó bố, anh ta không biết con trông như thế nào, vì vậy để một đứa ngốc kết hôn thay con cũng không sao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thịnh Hàn Ngọc bị mù và Thời Du Huyên bị ngốc.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giống như một cặp trời sinh.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giang Nhã Đan: “Ông xã, anh đồng ý đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà họ Thịnh có 10% cổ phần, lại thêm 100 triệu quà đính hôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Với số tiền này Vũ Kha đủ để tìm được một người đàn ông xuất sắc kết hôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà họ Thời sẽ trở nên nổi tiếng ở Giang Châu!”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên ôm con gấu nấp sau cây cột, lẳng lặng nghe bọn họ bàn cãi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô cúi đầu, cho dù có người phát hiện cô đang ngồi ở đây, cũng không có ai để ý đến đôi mắt xinh đẹp của Thời Du Huyên lóe lên vẻ tinh anh, khóe miệng cong lên một đường đẹp đẽ, lộ ra tia châm biếm!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dáng vẻ này căn bản không hề giống người ngốc nghếch.</span></p>', 0, '2022-07-01 19:31:50', '2022-07-01 19:31:50');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(89, 67, 'Chương 2: Giả Heo Ăn Hổ', 'chuong-2-gia-heo-an-ho', 2, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai mẹ con khuyên nhủ hơn nửa ngày, cuối cùng Thời Vũ Thành cũng xuôi theo: \"Được rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong ông ta liền đứng lên chuẩn bị đi đến phòng sách, vừa đi được hai ba bước thì quay đầu dặn dò: \"Vũ Kha, con đối xử với em gái tốt một chút, đừng có suốt ngày gọi con bé là kẻ ngốc, là con gái thì không nên quá cay nghiệt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Dạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" Thời Vũ Kha trả lời, cô ta không phục nhăn mặt nhìn về bóng lưng của bố nhưng hoàn toàn không nói cho bố những suy nghĩ trong lòng của mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ là nghĩ đến việc kẻ ngốc đó sẽ nhanh chóng thay mình gả cho người mù của nhà họ Thịnh, nhưng mà tất cả cổ phần của nhà họ Thịnh đều sẽ thuộc về mình, tâm trạng cô ta cảm thấy tốt hơn nhiều.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Huyên Huyên? Em ở đâu? Chị chỉ em chơi trò này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên ở phía sau cây cột đi đến: \"Em ở đây, Huyên Huyên thích chơi trò chơi nhất! \"</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha lấy tờ mười nghìn và tờ một trăm nghìn từ trong túi ra, cô ta ném hai tờ tiền ấy xuống đất rồi mím môi cười chờ xem kịch vui!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quả nhiên Thời Du Huyên không khiến cô ta thất vọng, cô không do dự nhặt tờ mười nghìn lên nhét vào trong túi, còn không thèm nhìn tờ một trăm nghìn đó một cái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha cười, đôi lông mày cong lên, cô ta tiếp tục ném mười tờ mười nghìn và một trăm nghìn xuống sàn nhà, nhưng Thời Du Huyên vẫn không lưỡng lự nhặt tờ mười nghìn lên, sau đó nhìn về phía cô ta cười với một vẻ mặt ngây thơ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Không đúng, một lần nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta ném tiền một lần nữa, nhưng lần nào Thời Du Huyên cũng chỉ nhặt tờ mười nghìn lên, hoàn toàn lơ đi tờ một trăm nghìn đó, lần nào cũng vậy!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Kha thẳng tay lấy tờ mười nghìn cuối cùng trong túi ra vứt xuống cũng bị Thời Du Huyên cho vào túi, tất cả tờ trăm nghìn đều bị kẻ ngốc đó \"vứt bỏ\" trên sàn nhà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta ôm cánh tay mẹ nũng nịu: \"Mẹ nhìn xem, con muốn đối xử tốt với con bé đó một chút, nhưng nó chẳng hề muốn thế.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Được rồi, nghe lời bố con đối xử với nó tốt một chút, ngày hôm nay mẹ mệt rồi, mẹ trở về phòng nghỉ ngơi một chút đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" Chỉ cần con gái ruột của mình vui vẻ thì Giang Nhã Đan không quan tâm Thời Du Huyên bị \"thua thiệt\" hay \"bị lợi dụng\".</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trò chơi kết thúc, Thời Vũ Kha nhặt hết tờ một trăm nghìn dưới đất lên rồi vỗ vào mặt Thời Du Huyên: \"Kẻ ngốc cũng chỉ là một kẻ ngốc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" Nói xong cô ta hài lòng cất tiền vào trong ví, xoay người trở về phòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên ngây ngốc cười lớn, nụ cười trong trẻo và ngu ngơ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ nhỏ đến lớn đã chơi trò này không biết bao nhiêu lần, lúc nào Thời Du Huyên cũng sẽ chọn tờ tiền có giá trị nhỏ, lơ đi tờ tiền mệnh giá lớn, Thời Vũ Kha cười nhạo cô ngu ngốc nhưng lại không biết chính vì vậy mà Thời Du Huyên có cả một núi tiền.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Năm Thời Du Huyên mười hai tuổi, cô đứng bên ngoài phòng sách vô tình nghe được bố mẹ cãi nhau!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc đó cô mới biết bố ruột của mình đã giữ lại một số tiền rất lớn trong nhà, mà khoản tiền này đã bất ngờ chuyển sang tên của mẹ nuôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Vũ Thành yêu cầu Giang Nhã Đan trả tiền lại để đưa cho con gái nuôi của mình.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên vừa nghĩ đến điều này liền nhận ra hai năm gần đây cô suýt chút nữa phải chết một cách \"bất ngờ\", có lẽ có liên quan đến mẹ nuôi!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có những người vì tiền mà không từ bất cứ thủ đoạn nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi chiều hôm đó Thời Du Huyên bị Vũ Kha dùng sức đẩy từ trên cầu thang xuống!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô lăn đến cuối chân cầu thang, hôn mê bất tỉnh, được cấp cứu ở bệnh viện nhưng sau đó lại trở thành kẻ ngốc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kẻ ngốc sẽ không có ngày tự mình đứng lên được, nhiều năm trôi qua như vậy, Thời Du Huyên giả vờ ngốc chính là vì muốn tự bảo vệ mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng may Thời Vũ Thành đối xử với cô rất tốt, để cho Thời Du Huyên ở trong căn nhà này khiến cô cảm thấy cũng có chút ấm áp, còn Giang Nhã Đan nhìn thấy cô như vậy sẽ không so sánh với con gái xuất sắc của bà ta nữa, cũng sẽ tha thứ cho cô hơn, chỉ như vậy mới sống bình yên đến năm hai mươi tuổi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô đồng ý gả thay vì muốn báo đáp công ơn nuôi dưỡng của Thời Vũ Thành!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ đây về sau những gì cô nợ gia đình này đều không còn nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng tất cả những gì Giang Nhã Đan và Thời Vũ Kha đã làm với cô, sau khi cô gả thay thì cũng đến lúc phải tính toán rõ ràng rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên trở về phòng đóng cửa lại, sau đó ôm con gấu bông của mình trốn vào trong tủ quần áo, kéo khóa ở phía sau lưng của con gấu, đưa tay vào lục lọi! Sau đó lôi ra một cái máy tính xách tay nhỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Máy tính xách tay im lặng khởi động, trên màn hình không có biểu tượng phần mềm gì, Thời Du Huyên thuần thục mở phần mềm đã được ẩn đi, gõ mật mã đăng nhập vào Zalo.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ảnh đại diện của Giản Nghi Ninh liên tục nhấp nháy, sau khi bấm vào thì hàng loạt tin nhắn hiện ra: “Ảnh Tử, tập đoàn Đính Thịnh có một hạng mục bí mật lớn với vốn đầu tư lên đến mấy chục tỉ, chúng ta có nên lấy không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mua đề án của Khánh Phong nữa, thấy thế nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô có tham gia vào hội đồng quản trị của công ty không?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thật ra thì hạng mục mấy chục tỉ khiến cả nước chấn động không phải là chuyện Giản Nghi Ninh quan tâm nhất, điều cô ấy quan tâm nhất chính là hội đồng quản trị, Ảnh Tử có tham gia vào đó hay không!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ảnh Tử là tên trên mạng của Thời Du Huyên, hai người vô tình quen biết nhau trên mạng hồi đầu năm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giản Nghi Ninh là cộng sự của Thời Du Huyên, hai người cùng nhau điều hành một công ty đầu tư mạo hiểm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời Du Huyên phụ trách điều khiển công ty, đưa ra những quyết sách quan trọng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn Giản Nghi Ninh phụ trách việc điều động nhân sự và sắp xếp kế hoạch rõ ràng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Công ty thành lập đã được năm năm, có thể nói hai người hợp tác rất ăn ý với nhau, chỉ có điều Giản Nghi Ninh không biết mặt mũi của Ảnh Tử như thế nào, đã lớn tuổi hay còn trẻ, là nam hay là nữ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mười ngón tay của Thời Du Huyên như đang bay lượn trên bàn phím, nhanh chóng gõ sách lược quyết định rồi gửi đi, sau đó đóng máy tính xách tay lại và nhét vào bên trong con gấu bông, cô đi ra khỏi tủ quần áo, mở cửa phòng nói: \"Đói thật đấy, Huyên Huyên đói quá!\"</span></p>', 0, '2022-07-01 19:32:24', '2022-07-01 19:32:24'),
(90, 68, 'Chương 1: Rất nhiều dấu hôn', 'chuong-1-rat-nhieu-dau-hon', 1, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mưa đêm đột ngột quất ràn rạt vào mái nhà chờ xe bus.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn An Nhiên vội lấy cặp sách che đầu, kiên nhắn chờ đợi chuyến xe cuối cùng trên đường phố vắng lặng Bỗng, một chiếc Hummer từ xa đi lại, đến gần nhà chờ xe bus nơi cô đang đứng thì bất thình lình bẻ lái, quặt thắng vào lề đường An Nhiên tránh vội, hết hồn nhận ra nơi mình đứng cách đuôi chiếc Hummer hầm hố đúng nửa mét. Chiếc xe kia tông thẳng vào gốc cây cổ thụ bên đường vắng vẻ, nằm im lìm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô kinh hãi chạy lên xem xét. Đang chạy mà bẻ lái nguy hiểm, không phải tài xế vừa đột ngột lên cơn đau tim hay bị đột quy đấy chứ? Hoặc là trong xe có tranh chấp gì?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chưa kịp nghĩ xong, cửa xe rầm một tiếng mở ra, bóng người cao lớn bên trong bước xuống. Bộ vest đen đắt tiền lập tức ướt sũng nước mưa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người kia có vẻ thật sự bị bệnh, áo quần xộc xệch, mái tóc đen rũ xuống che lấp một nửa khuôn mặt đẹp trai đến đáng kinh ngạc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên nhanh chóng chạy đến hỏi han: “Vừa rồi thật nguy hiểm! Anh có sao…?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe tiếng phụ nữ, Tống Thành cố giương mí mắt đang sụp xuống của mình lên nhìn nhưng chỉ thấy những hình ảnh nhòe nhoẹt bởi nước mưa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngay giờ phút này, hắn chỉ cảm thấy nóng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nóng như thiêu như đốt.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từng miligram thuốc đang chạy rần rật trong huyết quản của hẳn, làm cơ thể săn chắc cơ bắp của Tống Thành run lên, tỏa nhiệt khác thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhận thấy cơ thể mềm mại của phụ nữ đang tiến lại thì phía dưới của hản nóng lên, cứng đến phát đau, thôi thúc hắn áp chặt nơi mát mẻ nào đó để xả nhiệt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con mẹ nó! Tên khốn kia lại dám hạ thuốc kích dục với hắn!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên thấy Tống Thành đột ngột xông đến thì vội giơ tay ra đỡ, sợ hắn bị choáng váng sau cú tông xe. Cô lay gọi: “Đừng ngất! Tôi đưa anh đi bệnh viện”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khuôn mặt đẹp đế của Tống Thành áp vào hõm cổ An Nhiên, thở ra một hơi nóng rấy như sốt cao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng lúc đó, một chiếc lao đến, bật đèn sáng choang. Một tay An Nhiên đỡ thân thể nặng nề của Tống Thành, một tay vẫy loạn xạ ra tín hiệu gọi xe kia dừng lại. Chiếc xe màu đỏ rực đi chậm lại, đến gần hai người. An Nhiên đột nhiên thấy cảm giác quen thuộc, chưa kịp gọi thì đột nhiên tài xế kia rồ ga vọt đi mất. Trước khi đi qua, bánh xe còn hắt nguyên một vũng nước mưa tạt thẳng từ đầu tới chân cô ướt sũng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xui xéo! Quá xui xẻo!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô mất kiên nhắn, quay sang nói với Tống Thành – lúc này đã cởi áo khoác vứt trên đường, hai tay hắn ôm cô chặt cứng: “Chịu khó một chút, tôi đưa anh đi bệnh viện.”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Hừm..”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một âm thanh trầm thấp từ cổ họng hẳn bay. Không để cô nghĩ nhiều, cánh môi nóng rực của hắn đã phủ lên môi cô, ngấu nghiến. Hương hoa mộc thanh ngọt khiến hắn thoải mái không ít, da thịt vốn ngứa ngáy cũng được xoa dịu phần nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn An Nhiên kinh hãi tột độ, cố đẩy đối phương ra nhưng sức cô so với hắn không khác gì con mèo hen. Tổng Thành sung sướng dùng lưỡi tách hai cánh môi thơm ngọt ra, sục sạo vào bên trong, sâu hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước bợt của cô phù hợp với cơn khát của hắn. Hắn chỉ có một thôi thúc mãnh liệt là phải làm gì đó đế vượt qua kiếp nạn này. Tống Thành vừa hôn, vừa liếm láp khoang miệng ẩm ướt, hai bàn tay tham lam hư hỏng không ngần ngại giật tung hàng khuy của chiếc áo sơ mi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chớp mắt, cơ thể thon thả của thiếu nữ mười bảy tuổi bị phơi bày dưới cơn mưa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không!!!” Cô gào lên, hoảng hốt tìm cách thoát thân. “Mau buông… đồ khốn này!… Bỏ chân Tôi ra…</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành bị thuốc kích thích dẫn dắt, đẩy cô ngã xuống thảm cỏ trên vỉa hè, hung bạo cầm cổ chân trắng nõn của cô giơ lên, một tay xé rách dây quần lót bên trong. An Nhiên gào khóc âm], nhưng giữa đường vắng vẻ, khuya khoắt, lại mưa 1o như trút nước, không một ai giúp được cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh đèn vàng ấm áp chiếu lên thân thể thiếu nữ mềm nõn khiến hai mắt Tống Thành tối sắm lại. Mùi hương ngọt ngào chui vào khoang mũi hắn khiến cho cơn khát càng thêm cháy cổ nếu hắn không có cô ngay lúc này. Bàn tay to lớn của Tống Thành cầm lấy tay An Nhiên, khiến cô không cách nào che chắn nơi riêng tư đang bị người ta đòi hỏi nữa. Chút lương tri cuối cùng khiến Tống Thành thốt ra một tiếng khàn đục: “Xin lỗi”, rồi hắn cúi xuống hôn lên nơi ngọt ngào nhất của người con gái. Chỉ một lát nữa thôi, nơi này sẽ bị hẳn xé toang, ít nhất, hẳn có thể tận dụng chút tỉnh táo ít ỏi sót lại mà chuẩn bị trước cho cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Môi mỏng của hẳn áp lên da thịt An Nhiên, mái tóc dày dụi vào bụng dưới, cọ xát hai bên bắp đùi đang cố kẹp chặt khiến cơn ngứa của cô bùng lên, râm ran chạy lan xuống từng đầu ngón chân co quắp. Đầu lưỡi to lớn luồn lách linh hoạt len lỏi vào giữa những cánh hoa ướt đắm chọc vào tận sâu bên trong, vơ vét mật nước cuốn vào miệng. Mùi hoa mộc càng nồng đậm trong khoang mũi, trong miệng hẳn.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con mẹ nó, ngọt! Hãn say chết rồi! Môi hẳn mút chặt từng lớp cánh hoa, đầu lưỡi miêu tả tỉ mi mỗi nếp gấp nhỏ, mềm như thạch.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước mưa đã sâm sấp mặt cỏ, dính nhớp cả tấm lưng trần trụi của An Nhiên. Những lá cỏ non như hùa chung với khao khát tình dục của Tống “Thành, ra sức mơn trớn làn da cô. Sau một hồi bị gã đàn ông xa lạ điên cuồng hồn liếm, phía dưới của An Nhiên bắt đầu rậm rịch rỉ nước. Hương vị của cô hòa cùng nước mưa tạo thành chất lỏng mềm mại, óng ánh phun ra, tưới ướt môi lưỡi Tống Thành, nhỏ xuống cả cắm hẳn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Em thật ngọt ngào..”, hắn hài lòng khen ngợi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quả thực, hắn thích hương vị cô, thích một cách bản năng như thể tất cả từng tế bào trên người hắn đều cần cô bao bọc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành nâng cô dậy, đặt cô ngồi lên người hắn và bắt đầu xâm nhập, bất chấp móng tay cô cào rách da thịt hắn. Cơn đau nho nhỏ khiến tính chiếm hữu trong hẳn như con thú bị đứt cương, hắn không biết trời đất gì nữa, chỉ một mạch cắm thẳng vào nơi sâu nhất, hưởng thụ cảm giác thống khoái đang thỏa mãn cơ thể. Thứ thô dài, nóng rực như thép nung của hắn thô bạo tiến vào bên trong ấm áp, mềm mại của cô. Vì là lần đầu nên lối vào chặt khít, An Nhiên đau đến mức muốn ngất đi nhưng nước mưa lạnh lẽo làm cô tỉnh lại, từng giây từng phút cảm nhận cách hắn chiếm lấy cô. Ngực cô bị hẳn ngậm chặt, đầu lưỡi càn rỡ xoay vòng, cùng với eo lưng dẻo dai liên tục thúc mạnh khiến toàn thân cô dập đềnh như cưỡi trên con sóng dữ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không… đừng… cầu xin anh..”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Những tiếng rên tắc nghẹn, gãy đôi hòa lẫn với tiếng thở gấp gáp. Tống Thành đoạt lấy An Nhiên hết lần này đến lần khác, không một chút khoan nhượng. Hắn ở trên cô, dưới cô, bên trong cô, khiến cô thét lên bằng đủ mọi cách. Bên trong ấm áp của cô hút chặt hắn không buông, dù hẳn đã bản vào hai lần, lấp đầy mọi chỗ trống nhưng dường như vẫn chưa đủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lượng thuốc kích dục đã tan đi không ít nhưng Tống Thành vẫn không muốn ngừng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chưa bao giờ hẳn cuồng nhiệt đến thế. Hết lần này đến lần khác, hắn lại càng cứng hơn, lớn hơn, kiên trì không mệt mỏi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Một lần nữa, ngoan nào”, hẳn hổn hển dỗ dành, “Một lần nữa thôi”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên không còn sức để phản kháng nữa, cam chịu để mặc hản gặm cản khắp nơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cuộc xâm lược của Tống Thành sẽ kéo dài cả đêm nếu như chiếc xe bus không xuất hiện đúng lúc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh đèn xe từ xa cùng màu đỏ – vàng chói lọi như đánh thức An Nhiên đang sắp ngất đi vì đau đớn. Cô đồn hết sức lực, nghiến chặt quai hàm cản bắp tay Tống Thành đến bật máu rồi chạy một mạch về phía chiếc xe Tài xế thấy cô bé vừa lên xe váy áo xộc xệch, một bên giày còn bị rơi mất, cũng không nói năng gì. Chiếc xe bus chạy thẳng một mạch, bỏ lại Tống Thành đang thắn thờ ngồi bên cạnh chiếc Hummer đã vỡ nát một bên đèn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên ngồi khóc rấm rứt nửa tiếng thì xe bus về đến nhà. Thật may người lớn trong nhà đã đi ngủ hết, cô vội lết tấm thân đau đớn về phòng thì gặp ngay Vũ Như cũng vừa mở cửa phòng đi ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chị..”, An Nhiên hốt hoảng dùng cặp che dấu cơ thể rách nát của mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vũ Như liếc mắt một cái lập tức hiếu ngay vấn đề. Váy bị xé rách, áo sơmi đứt cúc, cần cổ trắng nốn đầy vết hôn đỏ ửng, khắp nơi trên dưới cơ thế Nguyễn An Nhiên đều tỏa ra hương vị tình dục.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn Vũ Như cười: “Em đi học thêm thế nào mà cổ đầy dấu hôn vậy?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên sợ hãi lắc đầu: “Không c‹ rồi cô vội chui vào phòng mình nhưng Vũ Như đã vào theo.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta thích thú nhìn em gái kế của mình run rẩy. Từ nhỏ, Vũ Như đã thích châm chọc An Nhiên. Ngay từ giây phút cha dượng nhặt được đứa con gái này trong rừng, cô ta đã thấy ngứa mắt. Bàn tay thô lỗ giật tung một bên cổ áo sơ-mi An Nhiên, làm lộ ra vô số vết đỏ trên khắp ngực, cổ và xương quai xanh. Cô ta nheo mắt: “Hẳn là làm cũng dữ dội đi”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên cảm thấy nhục nhã vô cùng, uất ức hỏi: “Chị, chiếc xe kia đúng là chị lái. Vì sao lúc đó lại bỏ mặc em?”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn Vũ Như “A” lên một tiếng. Người đó quả thực đúng là em gái cô thật! Khi đó trời mưa, cô không muốn xuống xe nên đã phóng đi luôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dù sao chiếc Hummer kia bị tai nạn, đèn xe nát bét, cô mà dừng lại có khi lại rước thêm phiền phức. Mà khéo sao An Nhiên lại có mặt ở đó, còn đang ôm lấy một người đàn ông, tư thế mười phần mập mờ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Em à, chị Vũ Như của em đâu có độc ác như vậy, nếu lúc đó chị nhận ra em thì chắc chắn sẽ dừng xe”, Vũ Như cười thâm hiểm, “Chắc hẳn em sang đường tùy tiện, làm người ta bị tai nạn giao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thông đúng không? Cái xe đó cũng là loại đắt tiền, đèn xe vỡ toang như vậy, em có tiết kiệm tiền tiêu vặt cả năm cũng không đền nổi đâu”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong, cô ta lại lướt mắt một lượt từ đầu đến chân An Nhiên, mỉa mai: “Tiền thì đền không nối, nhưng vàng thì lại khác. Ngàn vàng chứ đâu có ít”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Những lời châm chọc của Vũ Như làm An Nhiên tái mặt. Chẳng phải đang ám chỉ cô làm người ta bị tai nạn, không có tiền nên phải mang thân thể ra đền bù cho tài xế sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một người chị lại bỏ rơi đứa em gái mới mười bảy tuổi của mình. Ở giữa đường. Trong đêm tối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Với gã đàn ông xa lạ. Nghĩ thế nào cũng quá tàn nhãn!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên tức giận đến ứa nước mắt, nhưng không để cô kịp nói gì, Vũ Như đã chặn họng trước: “Chị thấy em mang thân thể cho người ta chơi đùa như vậy thì rất đau lòng đó. Lại còn lấy trộm giày của chịt Loại phiên bản giới hạn này người ta không bán nữa, làm mất một chiếc rồi thì đền tiền cho chị đi”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giày này rõ ràng Vũ Như tự tay ném vào thùng rác, nói đĩ rất đau chân, dáng vẻ thô kệch khác hẳn hình ảnh quảng cáo, cô thấy tiếc nên mới nhặt lại để đi mà. Nhưng An Nhiên chưa kịp nói gì thì Vũ Như đã xách chiếc giày kia đi thẳng về phòng.</span></p>', 0, '2022-07-01 19:35:32', '2022-07-01 19:35:32');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(91, 68, 'Chương 270: Full', 'chuong-270-full', 270, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cánh cửa đầy xích sắt mở toang, kéo theo âm thanh loảng xoảng rợn người.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người đàn ông năm co quắp trên sàn, thân thể rách nát, đầy những vết máu ứ đọng loang lố, răng cửa rụng cả hai chiếc, môi và mắt hẳn sưng phù đến mức mặt mũi biến dạng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đi ra đii” Tiếng người quát chói tai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“May phước cho mày là cậu chủ khai ân vì mày khai báo thành thật đấy”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chí Vỹ ngấng mặt lên, thều thào hỏi “Còn Vũ Như thì sao?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cô ta à?” Người kia cười khẩy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tội của cô ta còn chưa hết đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Địa ngục này vẫn phải ở lại thêm mấy ngày nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mày có thích ở chung luôn không?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe thế, Chí Vỹ hoảng sợ lắc đầu, vội vã chạy theo hướng người kia chỉ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn còn sống mà thoát ra khỏi đây đã là mừng lắm rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đời này hắn đã được dạy dỗ một bài học thật đất giá: không thể tùy tiện vu oan giá họa cho người khác.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên đường chạy ra ngoài, hẳn còn nghe loáng thoáng tiếng trẻ con líu lo: “Bố ơi, hôm nay mình đi đón mẹ và em gái được chưa?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chí Vỹ đánh liều ngoái đầu lại nhìn, thấy phía xa là một người đàn ông dáng hình cao lớn, đĩnh đạc đang cúi xuống xoa đầu đứa trẻ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đứa bé lại nói “Con không đòi bế đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lưng bố còn đau”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dường như nhận ra có kẻ đang nhìn lén, Tống Thành quắc mắt ngẩng lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong mắt hắn, cái thứ dơ bẩn kia không khác gì một con nhặng xanh bẩn thỉu.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn hơn cả bẩn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ vì cái mồm của hắn mà An Nhiên đã có một thời học sinh thê thảm, tiếng xấu theo đến tận bây giờ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Truyện Cung Đấu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chờ một lát ông ngoại con đến, chúng ta cùng đi”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vâng” Bé con ngoan ngoãn chờ đợi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lát sau, dường như nhịn hết nổi, nó lại líu lo: “Bà ngoại cũng sẽ đến chứ ạ?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“ừ”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thế còn ông trẻ thì sao ạ? Ông cũng đến đúng không?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Ông trẻ nào?” Tống Thành ù ù cạc cạc hỏi lại “Ông trẻ là người yêu của bà ngoại, là người mà bố bảo là bạn thân của bố ấy” Cá Chép vui vẻ nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chú ấy nhìn rất trẻ, nhưng vì là người yêu của bà ngoại nên con phải gọi là ông trẻ thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ông Nhĩ dặn con thế ạ”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành muốn đập đầu vào tường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thì ra con trai hẳn không phải hỏi Nguyễn Chính Quốc mà đang hỏi đến Vương Cư, Tô Ái Phương và cả Cao Vĩnh Mạnh nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện này nói ra thì dài, nhưng xảy ra nhanh đến mức khó tin.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngày đó hắn được đưa vào viện cấp cứu xong, hai ngày sau, tình cờ thế nào lại đúng lúc Vương Cư ôm theo Cá Chép chạy vào đó theo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thì ra ông ta thực sự xuống tay bắt cóc thẳng bé.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng không ngờ đứa trẻ lại chống trả kịch liệt, đang ở trên ô tô cũng dám mở cửa lao xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kết quả là lăn ra đường, va phải cục đá, máu chảy đầm đìa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Cậu bé có nhóm máu hiếm” Tiếng y tá lo lắng vang lên bên ngoài, tình cờ lọt vào tai Tống, Thành.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Bệnh viện chúng ta lại vừa mới dùng hết nhóm máu đó cho bệnh nhân ở phòng VIP rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chắc phải chờ rất lâu đấy: ‘Vương Cư là người có tiền, liền vùi dập đối phương ngay tức khäc: “Bắt đứa bé ba tuổi nãm chờ máu, các người có lương tâm không? Tôi không cần biết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Các cô mau mua hết máu hiếm về đây, đừng để cái bệnh viện này bị giải thể.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành nghe thấy thì đi ra mở cửa, không ngần ngại nói “Tôi chính là người có nhóm máu hiếm đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai đang cần máu?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy con trai năm chỏng gọng ở đó, hắn suýt chút nữa lật tung cả mả tổ nhà Vương Cư lên!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">‘Vương Cư biết mình sai, bị bắt ngay tại trận, không thể cãi được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành thuận lợi cho máu, Cá Chép cũng tiếp nhận dễ dàng khiến hẳn sinh nghi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhóm máu này hiếm không phải vì ít người có mà vì khả năng đào thải rất lớn, không phải cứ hai người cùng nhóm máu cho nhau mà được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">‘Vậy mà bé con này lại nghiễm nhiên nhận của hẳn mấy trăm cc máu mà chẳng có tác dụng phụ hay biểu hiện nào chứng tỏ bị đào thải cả.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi chiều, Ân Lãm đến báo cáo tình hình thẩm vấn Chí Vỹ và Nguyễn Vũ Như, mang theo một tin tức động trời: “Năm xưa, An Nhiên bị người lạ cưỡng bức mà có thai, không phải vì ăn chơi trác táng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Toàn bộ đều là tin đồn thất thiệt do Vũ Như xúi bẩy Chí Vỹ dựng lên”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe đến đây, dường như có cái gì đó chạy rần rật trong huyết quản của Tống Thành.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn đăm chiêu nghĩ lại một đoạn quá khứ đen tối mà mình vẫn muốn quên đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần đó, trong cơn mưa tầm tã, hắn đã gặp một người con gái rất mềm mại, ngọt ngào.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô bị hẳn làm đến lăn lộn khổ sở, sau đó bỏ chạy mất dạng Đến giờ hẳn mới nhớ ra, cô gái đó đi xe bus, trong khi Vũ Như ở thời điểm đó có một chiếc xe đỏ chót.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn nữa, ngay từ lần gặp đầu tiên, Vũ Như tuy giữ lễ nhưng ánh mắt thèm khát vẫn lén lút quét khắp người hắn, chẳng bao lâu liền bộc lộ ham muốn tiền tài vô độ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu vậy, chắc chắn đêm đó, nếu xảy ra chuyện, cô ta sẽ không chọn cách bỏ chạy sau khi nhìn thấy hẳn cùng chiếc siêu xe của mình Quả nhiên,cho Vũ Như một liều thuốc kích dục, treo cô ta lên như một miếng mố trước đám đàn ông khiến cô ta mất hết trí.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cái bí mật giấu kín suốt bốn năm nay bị phun ra bằng sạch.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này Tống Thành mới giật mình hoảng hốt, hắn đã lơ đãng suốt bốn năm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì hẳn vẫn đinh ninh rằng người đó là Vũ Như, cho nên không bao giờ muốn nhớ lại đoạn kí ức đó, cứ thế mắt nhẫm mắt mở cho qua.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thế nhưng sự vô tâm đó lại đối thành bốn năm An Nhiên lăn lộn khổ sở cùng con trai hắn, kéo theo vô vàn hệ lụy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hẳn có chết cũng không bồi hết tội cho cô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cầm tờ giấy xét nghiệm DNA trên tay, Tống Thành run rẩy không nói lên lời Trong lúc hẳn còn đang mải xúc động, cửa phòng bị đẩy toang ra, Tô Ái Phương ào vào như cơn gi “Cậu em đẹp trai, nghe nói cậu nấm viện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đã chết chưa?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếp theo là Cao Vĩnh Mạnh phe phẩy cái quạt đi vào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không rõ giác quan thứ sau của hẳn nhạy đến đâu mà đến phút chót, khi Tô Ái Phương lên chuyên cơ, hẳn lại đổi ý bám theo cho bằng được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho nên mới có ngày hôm nay cho Tống Thành ngồi im trên giường bệnh chứng kiến, Tô Ái Phương tung tăng lao vào chào hỏi, Cao Vĩnh Mạnh lạnh lùng theo sau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến khi thấy Tô Ái Phương chết đứng giữa trời khi trông thấy Vương Cư ngồi đó, hẳn vội vàng dìu chị xuống cái ghế phía xa, khó chịu làm bình vôi nghe hai ông bà trung niên nhận nhau, tâm sự nỉ non sau mấy chục năm xa cách.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại đến lúc Tô Ái Phương đòi đến thăm cô vợ đang hôn mê của Tống Thành, mọi chuyện mới gọi là bung bét.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ở đâu ra người chị hẳn quen biết đã lâu lại biến thành mẹ vợ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kẻ thù truyền kiếp của bố mình thì biến thành cha vợ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chưa hết, Tống Thành đau đầu nhìn thắng bạn thân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ nó ngang nhiên theo đuổi Tô Ái Phương, nếu nó thành công, chẳng lẽ nó sẽ thành cha dượng của vợ mình sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có lẽ cảm giác bị nghiệp quật tới tấp chính là đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tạm gác lại mọi chuyện lùm xùm về vai vế gia đình này, hẳn quay lại vấn đề chính: Phải đối diện với sự thật về nhân duyên giữa hẳn và An Nhiên như thế nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu để cô biết chuyện này, chẳng phải là đả kích lớn hay sao? Nhất là khi bác sĩ lại vừa vui sướng chạy vào thông báo với hắn, An Nhiên quả thực đã có thai Hắn muốn hét lên vui sướng nhưng một phần khác trong hắn lại bảo: “Khôn hồn thì im mồm”.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đứng trước bố mẹ vợ thực sự vừa từ không khí mọc ra, nếu hắn để lộ chuyện năm xưa từng cưỡng bức con gái yêu quý của bọn họ, cho dù hắn không cố ý, hắn vẫn cầm chắc cái chết rồi!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vợ à, em mau tỉnh lại nhanh nhanh, nếu không sẽ thành bà góa đấy! Tống Thành não nuột thở dài, Khi An Nhiên mở mắt tỉnh lại lần thứ hai, xung quanh không có nhiều khuôn mặt như lần trước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ có hai khuôn mặt tuấn tú giống hệt nhau, một lớn một nhỏ đang nhìn cô chăm chẩm Cô nâng tay, lần này tay nâng lên được, nhéo hai cái má của bọn họ một cái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“A… đau quá mẹ ơi..” Cá Chép nhăn nhó kêu lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tống Thành thì cam chịu mặc cho cô nhéo, “Đau à?” An Nhiên lẩm bẩm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thế thì đây không phải là mơ rồi”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ ơi, không phải mơ đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con và bố tới đón mẹ và em gái về đấy ạ”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Em gái gì?” An Nhiên có cảm giác mình đã ngủ một giấc lâu lắm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cá Chép xoa xoa bụng mẹ, âu yếm nói: “Em gái trong này này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con yêu mẹ, yêu cả em nữa.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên ngỡ ngàng nhìn bụng mình, lại quay sang nhìn Tống Thành kinh ngạc.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hẳn thực sự đây, bằng xương bằng thịt, không phải mờ ảo như sương khói nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước mắt cô lại trào ra, không kìm lại được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô chỉ muốn nhào vào lòng hẳn mà thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Mẹ ơi, đừng khóc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tí nữa có cả ông ngoại, bà ngoại, ông trẻ sẽ đến thăm mẹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi có cả ông ngoại cũ cũng sẽ tới, cả cô Minh Châu, chú Diệp Phong, ông Nhĩ, bà Hai, mẹ đừng khóc nhè nữa nhé.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai cũng yêu mẹ mà.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">An Nhiên lại bị một phen ù ù cạc cạc, không hiểu cái gì ông ngoại cũ, bà ngoại mới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô lại nhìn Tống Thành.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ đầu đến cuối hẳn chưa nói một lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cuối cùng, hẳn run rẩy đưa tay xoa tóc cô, thì thầm: “Có nhiều chuyện đã xảy ra trong lúc em ngủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhiều thông tin mà đến anh khi biết được cũng mất mấy ngày mới tiêu hóa hết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho nên, vợ à, chúng mình về nhà rồi lần lượt anh kể em nghe được không; An Nhiên bị hắn dỗ ngọt, rất muốn gật đầu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng nhớ đến việc bọn họ đã li hôn, cô lại ngần ngừ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Về nhà nào?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ống Thành vuốt ve mái tóc đã dài ra của cô, cười nói: “Nhà của chúng mình”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thế còn đám cưới?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn bật cười: “Đến bây giờ mà em vẫn chưa sợ đám cưới à?” Thấy cô lắc đầu, ánh mắt buồn rầu không giấu giếm, hẳn lại nói “Cho em hay, bố mẹ anh đã đồng ý việc đến gặp bố mẹ em để hỏi cưới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thậm chí còn gặp tất cả các bậc trưởng bối trong nhà em, một người” Nghĩ đến việc vợ chồng Tống Sơn, Hạ Cẩm tới nói chuyện với Vương Cư và Tô Ái Phương, Tống Thành không nhịn nổi có chút mong chờ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói gì thì nói, danh tiếng của Vương Cư sánh ngang với Tống Quỳnh Như, thuộc cấp bậc toàn cầu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn của cải của Tô Ái Phương thì vô số núi vàng núi bạc, so với Hạ Cẩm chỉ có hơn, không có kém.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Đến lúc ấy, em không được vứt bỏ anh đâu đất An Nhiên gật đầu, mim cười âu yếm nhìn hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vứt bỏ người sẵn sàng hi sinh cả tính mạng đế bảo vệ mình ư? Cô đâu có ngốc như vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhận được đảm bảo của An Nhiên, Tống Thành hít một hơi, sau đó ôm lấy cả cô và con trai, nói “Chúng ta có rất nhiều chuyện để nói với nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nào, để anh đưa em về nhà, được không?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Được” An Nhiên mỉm cười ôm lấy hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Vê nhà của chúng ta”</span></p>', 0, '2022-07-01 19:36:27', '2022-07-01 19:36:27'),
(93, 70, 'Chương 1: Nhân phẩm có vấn đề (1)', 'chuong-1-nhan-pham-co-van-de-1', 1, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ưm, ta đang ở nơi nào thế này?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lắc lư cái đầu vừa tỉnh lại sau khi chìm vào hôn mê, Lục Thiếu Du cảm thấy xung quanh mình rất ẩm ướt, hơn nữa toàn thân đau nhức như muốn tê liệt, gió từ bốn phía thổi qua, tạt vào người hắn khiến hắn không nhịn được mà rùng mình một cái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đầu Lục Thiếu Du toát mồ hôi lạnh, hắn vội vàng mở choàng mắt. Không gian xung quanh chỉ là một mảnh đen kịt, trên không trung, một vầng trăng lưỡi liềm đang treo lơ lửng. Sau một lát, mượn ánh sáng yếu ớt phát ra từ mặt trăng hắn mới nhìn được cảnh vật rõ ràng hơn một chút. Hiện tại hắn đang ở trong một đầm nước, lúc này hẳn là ban đêm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- A...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cơn đau điếng từ đại não truyền đến khiến hắn phải kêu lên một tiếng thảm thiết. Lục Thiếu Du cảm nhận được có một lực lượng kỳ lạ đánh vào đầu mình khiến hắn lập tức ngất đi một lần nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi trời gần sáng Lục Thiếu Du mới từ từ tỉnh lại, nhưng khi nhận được tin tức mới từ đại não Lục Thiếu Du thiếu chút nữa lại ngất đi.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhân phẩm của ta bạo phát sao, xuyên việt rồi, đây là không phải là nằm mơ chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du mở to hai mắt chăm chú nhìn bốn phía, theo những tin tức nhận được từ đại não, cùng với việc đã từng xem qua mấy tiểu thuyết xuyên việt, Lục Thiếu Du khẳng định rằng mình đã bị xuyên việt rồi. Tình huống này tưởng chừng như không có thật, thậm chí trong trăm triệu hoàn cảnh cũng chưa chắc tìm được một, vậy mà lại xảy ra trên người hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du ngẫm lại, mình vừa mới tốt nghiệp đại học hạng ba, cũng tìm được công việc vặt vãnh ở trong một văn phòng nhỏ. Trong khi đang giúp cấp trên photo tài liệu thì không ngờ máy photocopy bị rò điện, hắn bị chết bất đắc kỳ tử mà xuyên qua.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiếp trước không nói làm gì, tiền đồ xa vời, xuyên qua cũng tốt, nói không chừng xuyên qua đến một thế giới khác sẽ đạt được thành tựu lớn hơn. Dựa theo những hiểu biết của bản thân hắn trong tiểu thuyết, người xuyên qua, không một người nào đạt được thành tựu quá thấp. Nguyên một đám không phải thê thiếp thành đàn thì cũng là bá chủ của thế giới mới, kém cỏi nhất cũng làm phú nhị gia, hẳn là mình cũng không quá xui xẻo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xác định mình bị xuyên qua, Lục Thiếu Du không có bất kỳ cảm giác đau khổ hay tiếc nuối gì mà ngược lại hắn cảm thấy mình như được giải thoát. Kiếp trước hắn không phải là người có chức có quyền, cũng không phải người giàu có, tiền đồ rất xa vời, tương lai mờ mịt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nơi này là chỗ nào, tại sao ta lại xuyên đến chỗ quỷ quái này?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du đánh giá xung quanh, bắt đầu lục lọi ký ức còn lưu lại trong đầu, một đoạn ký ức không thuộc về hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau một lát, Lục Thiếu Du có loại xúc động muốn tự sát để xem có thể xuyên qua lần nữa hay không. Người khác xuyên qua không phải thành thiếu gia thì cũng là Vương gia, không thì cũng anh tuấn tiêu sái, phong độ bức người, là công tử gia tộc hoặc thế lực lớn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà thân thể hắn xuyên vào mặc dù ở thế giới này cũng là một thiếu gia nhưng lại hữu danh vô thực. Vị thiếu gia này cũng tên là Lục Thiếu Du, năm nay vừa mới mười sáu tuổi, là một người con riêng, địa vị của hắn trong gia tộc ngay cả người hầu đều không bằng. Ngoài ra hắn còn có một mẫu thân, trước là người hầu trong gia tộc, sau khi sinh hạ vị thiếu gia này thì càng bị vợ cả ức hiếp, cùng trải qua ngày tháng so với nô tài còn không bằng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai mẫu thân con, ngày bình thường thì luôn bị người trong gia tộc ức hiếp, ngay cả nô bộc cũng không để họ vào mắt. Đặc biệt là mấy nha hoàn đi theo vợ cả, càng hay tìm cớ để làm nhục bọn họ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngay như ngày hôm qua, chỉ vì chống đối vài câu mà vị thiếu gia này đã bị bọn chúng đánh cho một trận, nhưng không ngờ tới bị đánh đến chết. Đoán chừng những người kia thấy người bị chết mà sợ hãi nên mới đem vị thiếu gia này vất xuống vách núi, thi thể rơi vào đầm nước dưới vách núi, còn mình thì vì nhân phẩm bạo phát mà xuyên qua nhập vào thân thể của hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ mình xuyên không lấy được thân thế như vậy, Lục Thiếu Du có chút khóc không ra nước mắt, rốt cuộc nhân phẩm của mình tốt hay không tốt đây? Kiếp trước mình cũng chưa làm chuyện tốt gì, xem ra nhân phẩm cũng không thể tốt được a.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhập gia thì tùy tục, Lục Thiếu Du cũng không có cách nào khác, may mắn lắm mới không chết, trên đời này làm gì có chuyện thập toàn thập mỹ, dù thế nào đi chăng nữa không chết chính là có phúc, gặp đại nạn mà không chết tất sau này sẽ có phúc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nể mặt ta và ngươi cùng tên là Lục Thiếu Du, ta lại dùng thân thể của ngươi, mối thù của ngươi sau này có cơ hội ta sẽ giúp ngươi báo, mẫu thân của ngươi ta cũng sẽ giúp ngươi chăm sóc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du thầm thì tự nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du vừa nói xong, đầu óc dường như thông suốt, một cỗ trọc khí thoát ra từ đầu rồi tiêu tán không không gian.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Xem ra oán khí của ngươi cũng sâu a, mối thù của ngươi, Lục Thiếu Du ta chỉ cần có cơ hội nhất định sẽ báo cho ngươi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du chăm chú nhìn lên trời cao nói lại một lần nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong, Lục Thiếu Du không thể không nghĩ đến một vấn đề nan giải lúc này. Bây giờ hắn đang nằm trong đầm nước, dưới lưng dường như tựa vào một thân cây khô, nước trong đầm rất lạnh, xem ra bây giờ hẳn là mùa đông. Mà hắn ngâm trong nước đã lạnh đã rất lâu rồi thì phải, cả người đã sớm tê cóng, không còn bao nhiêu tri giác.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giãy dụa cố bơi về hướng bên cạnh đầm nước, Lục Thiếu Du không khỏi thầm rủa một tiếng, hai chân của hắn dường như bị chặt đứt, hai tay thì ngay từ đầu còn không cảm thấy gì nhưng vừa cố di chuyển một chút thì phát hiện ra hai tay cũng bị gãy xương. Lúc này, hắn căn bản không thể dùng sức, đừng nói bơi đến bên cạnh đầm nước, ngay cả di chuyển nửa phân cũng khó có khả năng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ông trời a, ngươi đừng chơi ta như vậy mà.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du bất đắc dĩ nhìn trời mắng, bây giờ mình hoàn toàn không nhúc nhích được, không phải nằm chờ bị động lạnh đến chết sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vèo...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một hồi âm thanh truyền vào trong tai Lục Thiếu Du, giống như là có con vật gì đó di chuyển trong nước với tốc độ vô cùng nhanh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không phải là Yêu thú chứ?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong đầu Lục Thiếu Du hiện lên một từ đáng sợ, theo như tin tức trong đầu lưu lại, Lục Thiếu Du biết được thế giới này khác với thế giới trước kia của mình. Đây là thế giới lấy vũ vi tôn, toàn bộ thế giới bao la vô cùng, trong đó có Yêu thú, Linh thú, Vũ giả và Linh sư tồn tại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vũ giả và Linh sư trong thế giới này có địa vị vô cùng cao, đặc biệt là Linh sư, địa vị của họ có thể nói là tối thượng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà thân thể của vị thiếu gia hắn xuyên qua này, không có cách nào trở thành Vũ giả cũng vô pháp trở thành Linh sư, chính vì vậy địa vị trong gia tộc mới thấp kém không bằng cả nô bộc.</span></p>', 0, '2022-07-02 01:41:23', '2022-07-02 01:41:23');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(94, 70, 'Chương 2: Nhân phẩm có vấn đề (2)', 'chuong-2-nhan-pham-co-van-de-2', 2, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- G ri...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngay tại lúc Lục Thiếu Du đang suy nghĩ xem có phải yêu thú đang tiến về phía mình hay không thì từ trên không một chuỗi âm thanh rít gào truyền đến. Ngay lập tức trước mắt hắn như bị mây đen bao phủ. Một con chim khổng lồ dài hơn trăm thước xuất hiện trên không trung.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cách chim mở rộng, bay lượn ngay khoảng không trên đầu Lục Thiếu Du. Con chim này có điểm giống chim ưng nhưng phủ bên ngoài bộ lông màu xanh, dưới bụng có lông tơ màu trắng. Hai chân có móng vuốt sắc nhọn uốn cong, thoạt nhìn như lưỡi dao sắc bén, hai mắt Cự Ưng chăm chú nhìn xuống phía hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Yêu thú, chẳng lẽ ta vừa mới xuyên qua đã phải trở thành món ăn trong bụng yêu thú sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du vô cùng kinh ngạc, ở kiếp trước lấy đâu ra một con chim ưng lớn như vậy. Lập tức, Lục Thiếu Du bất đắc dĩ mà nở nụ cười khổ, bây giờ mình không thể di chuyển, yêu thú mà muốn ăn thịt mình thì cũng chỉ có thể trơ mắt nhìn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vèo vèo...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du cảm giác được phía sau mình tựa hồ đang có con vật gì đó bay đến, hai cánh nó vuốt trên mặt nước làm mặt nước cuộn sóng, mà hắn cũng bị sóng cuộn ra phía ngoài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dát dát...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên không trung, Cự Ưng kêu lên một tiếng, hung quang trong mắt vừa lộ, hai cánh chấn động, mang theo gió lốc lao thẳng xuống dưới, móng vuốt co lại, như xuyên qua không gian lao xuống tựa tia chớp.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mạng ta xong rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lòng Lục Thiếu Du chỉ còn một mảnh lạnh lẽo, vừa mới xuyên qua cuối cùng lại trở thành món ăn trong bụng của yêu thú, vận số của ta cũng quá kém đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vèo...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngay lúc Lục Thiếu Du tuyệt vọng, trước mắt hắn đột ngột xuất hiện một một mảnh tối đen, một cái bóng to lớn từ phía sau đầu hắn nhảy lên. Tốc độ quá nhanh, Lục Thiếu Du vội ngước mắt nhìn lên cũng chỉ có thể trông thấy là một con Yêu thú phi hành lớn hơn trăm thước có điểm giống con dơi, toàn thân màu trắng tinh, dưới bụng yêu thú có một lỗ hổng to, máu tươi từ đó không ngừng chảy ra, hẳn là con yêu thú này đang bị thương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phanh! Phanh!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ trong tích tắc, hai con yêu thú đã đụng độ nhau trên không, những tiếng rít gào xuyên qua không gian vang vọng xung quanh Lục Thiếu Du. Hắn nằm trong đầm nước, mặt nước không ngừng dao động nhưng bản thân hắn thì lại không thể nhúc nhích.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi va chạm mấy lần, Lục Thiếu Du thấy, Yêu thú Cự Ưng thu lại móng vuốt, hai cánh chấn động, mang theo gió lốc hung hăng chụp xuống Yêu thú dơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chi chi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con dơi khổng lồ rít lên, ngay lúc Lục Thiếu Du còn đang kinh ngạc, trong miệng yêu thú đã tràn ra một đoàn sương mù màu trắng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sương mù màu trắng phun ra, giữa không trung một cỗ hàn khí bắt đầu khuếch tán. Trong tích tắc, hàn khí quét qua khiến cả đầm nước bị đóng băng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên không, Cự Ưng cũng bị đông lạnh trở thành khối băng, thân hình khổng lồ lập tức rơi thẳng xuống đầm nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ầm...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cự Ưng đã rơi xuống trên mặt băng, mặt băng bị phá vỡ, xuất hiện vô số khe nứt. Cùng lúc này con yêu thú dơi dường như cũng trở nên suy yếu, nó hạ xuống mặt băng, từ từ tiến tới gần Cự Ưng Yêu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Rắc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du vừa nhìn lại đã thấy yêu thú dơi há miệng lộ ra hai chiếc răng nanh bén nhọn cắn xuống, một ngụm đem da cùng lông trên bụng Cự Ưng xé ra, một viên ngọc to cỡ nắm tay trẻ con, toàn thân phát ra ánh sáng màu xanh, bên trên còn dính máu lăn xuống mặt băng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Yêu đan? Yêu thú ít nhất phải đạt tứ giai mới có yêu đan.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du từ trí nhớ trong đầu biết được, loại yêu đan này, yêu thú ít nhất phải đạt đến tứ giai mới có, những yêu thú tam giai trở xuống sẽ không có yêu đan. Yêu đan hình như là rất giá trị.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Dát dát...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi Cự Ưng bị yêu thú dơi xé mở bụng lộ ra yêu đan. Trong nháy mắt, quanh thân Cự Ưng phát ra một ngọn lửa, ngay lập tức làm băng tan, cái mỏ sắc bén mổ thẳng vào vết thương trên bụng của yêu thú dơi. Ngay sau đó bụng của yêu thú dơi không ngừng chảy máu, một viên ngọc lớn chừng nắm tay trẻ con dính đầy tơ máu tràn ngập tia sáng nhu hòa bị đánh rơi trên mặt băng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đây cũng là yêu đan sao...?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du kinh ngạc nói, yêu thú tứ giai trở lên rất khó bắt gặp, không ngờ dưới vách núi lại này một lát mà thấy những hai con.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ô ô!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Yêu thú dơi bị đau, nó kêu lên thảm thiết rồi dùng cái răng nanh cắn mạnh vào cổ của Cự Ưng, trực tiếp cắn đứt cổ Cự Ưng, tia máu không ngừng phun ra, lần này con Cự Ưng hoàn toàn chết hẳn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ, mặt băng bắt đầu thay đổi lần thứ hai, Lục Thiếu Du lạnh run cả người, môi trắng bệch không có chút huyết sắc, mặt tái xanh, thân thể hầu như cứng đơ. Nhìn hai con yêu thú khổng lồ đại chiến như một ảo giác khi xem phim 3D, thật là làm cho người ta kinh hoàng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chi chi...</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên mặt nước, yêu thú dơi ngậm viên yêu đan của Cự Ưng trong miệng của mình, hai cánh xẹt qua trên mặt nước lạnh. Ngay sau đó, nó chậm rãi hướng tới gần Lục Thiếu Du.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phiền toái rồi đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du nhìn con yêu thú dơi đang hướng tới gần mình, trong lòng cảm thấy tuyệt vọng, hai mắt yêu thú kia đang nhìn hắn chằm chằm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chi chi!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Yêu thú dơi bay đến bên người Lục Thiếu Du, cổ họng của con dơi phát ra một tiếng vang, với thân thể khổng lồ, một con mắt của nó còn lớn hơn cả người Lục Thiếu Du. Trong mắt của nó không hề có ác ý, ngược lại ánh mắt rất chân thành nhìn chăm chú vào Lục Thiếu Du.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du tưởng rằng mình chết chắc rồi, hắn nhìn vào thân hình to lớn của yêu thú, dường như yêu thú này không có ác ý với mình:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngươi có chuyện gì muốn ta giúp sao?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du thử nói một câu để thăm dò, nghe nói yêu thú cao giai đều là linh vật của thiên địa, nhất định sẽ có linh trí.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chi chi!</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe được Lục Thiếu Du nói, con yêu thú dơi phát ra một âm thanh vui sướng, khẽ gật đầu, hình như đang đáp lại Lục Thiếu Du.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con dơi này quả thật hiểu được lời của mình, Lục Thiếu Du sửng sốt một chút. Nếu như là trước đây, nhất định Lục Thiếu Du sẽ bị dọa cho giật hết cả mình, nhưng bây giờ hắn lại cảm thấy không có gì là quá kinh ngạc, bản thân hắn có thể chuyển kiếp thì còn có cái gì không thể tiếp nhận đây?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ta nói yêu thú huynh này, ngươi nhìn ta đi, tự thân ta còn khó bảo toàn, làm sao có thể giúp huynh?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lục Thiếu Du nhìn yêu thú dơi cười khổ một cái rồi nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chi chi</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tựa như nghe hiểu Lục Thiếu Du nói, con dơi yêu thú cúi đầu chăm chú đánh giá hắn, sau đó phát ra một âm thanh trầm thấp, nhảy mạnh lên lấy hai móng vuốt ấn vào bụng Lục Thiếu Du, Lục Thiếu Du không tự chủ được há hốc miệng ra.</span></p>', 0, '2022-07-02 01:41:57', '2022-07-02 01:41:57'),
(95, 72, 'Chương 1: Kẻ thế thân', 'chuong-1-ke-the-than', 1, NULL, '<div class=\"chapter-c\" id=\"chapter-c\" style=\"box-sizing: border-box; margin: 0px; font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; line-height: 46.8px; color: rgb(43, 43, 43); font-size: 26px; overflow-wrap: break-word; background-color: rgb(244, 244, 244);\">\r\n<p>Thời tiết ở thành phố Nam Dương vào tháng mười hai rất rất lạnh.<br />\r\nTrận tuyết lớn phủ xuống cả thành phố khiến ban đêm lạnh lẽo lại càng thêm vắng vẻ, đìu hiu.<br />\r\nNhưng lúc này tại phòng VVIP trong một khách sạn bảy sao, bầu không khí lại vô cùng nóng bỏng.<br />\r\n“Đau…” Giọng nói nũng nịu của cô gái khiến người khác nghe mà thương tiếc, thân thể cô gái cong lên, trên trán toàn là mồ hôi.<br />\r\n“Ngoan, ráng chịu thêm một chút nữa là hết đau rồi, được không?” Bờ môi mỏng gợi cảm của người đàn ông dán vào tai cô gái, hơi thở ấm áp, giọng nói quyến rũ khiến thân thể căng thẳng của cô dần dần thả lỏng.<br />\r\n“Đừng sợ…”</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; display: flex;\">&nbsp;</div>\r\nCô gái cắn môi, gương mặt tái nhợt giàn giụa nước mắt, nắm tay siết chặt của cô từ từ buông ra.<br />\r\n“Tử Diễm…”<br />\r\nNgười đàn ông thấy cô đã thả lỏng thì dịu dàng gọi tên cô.<br />\r\nMột nụ hôn đầy mùi rượu kéo dài từ cổ cô xuống dưới.<br />\r\nCô gái từ từ ngước mắt lên, dựa vào ánh sáng yếu ớt ở đầu giường nhìn vào mắt người đàn ông.<br />\r\n“Đừng sợ, có anh ở đây, đừng sợ được không?” Người đàn ông nở nụ cười, đôi mắt sâu thẳm hấp dẫn người nhìn vào, đôi mắt ấy đang nhìn cô vô cùng chân thành.<br />\r\nCô gái nhìn anh đầy mơ màng, dần dần, ánh mắt cô cũng hiện lên vẻ quyến rũ say lòng người nhưng sau đó cô đã chìm trong nụ hôn của người đàn ông…<br />\r\nTuyết bên ngoài càng lúc càng lớn còn đêm của bọn họ chỉ vừa mới bắt đầu.<br />\r\nBa giờ rưỡi sáng.<br />\r\nĐiện thoại của cô gái sáng lên một cái, trên màn hình điện thoại là số 198, nội dung chỉ là một khoảng trống.<br />\r\nCô vội vàng ngồi dậy, nhẹ chân nhẹ tay mặc quần áo, từ từ bước xuống giường.<br />\r\nCô quay đầu nhìn lại người đàn ông đang ngủ trên chiếc giường lớn.<br />\r\nGương mặt hoàn mỹ dưới ngọn đèn vẫn đẹp trai như thế, dù đang ngủ thì cũng là bức tranh cực kỳ đẹp đẽ.<br />\r\nCô gái cắn môi, hít một hơi thật sâu rồi không lưu luyến gì nữa.<br />\r\nCô quay người, dứt khoát rời khỏi phòng, mở cửa.<br />\r\n“Được không?” Có một cô gái đội mũ và đeo kính râm đứng ngoài cửa, cô ta lén đứng một bên, cẩn thận hỏi.<br />\r\n“Ừ.”<br />\r\n“Thật không?” Cô gái đeo kính cười, lập tức đứng thẳng người.<br />\r\n“Ừ, anh ta ngủ rồi.”<br />\r\n“Được, mày đi đi!” Cô gái kia chuẩn bị đi vào.<br />\r\nCó điều, cô vẫn đứng nguyên ở cửa không đi: “Tiền đâu?”<br />\r\nCô gái đeo kính đen nghe thế thì nhíu mày lại, gỡ mũ và kính râm xuống.<br />\r\nCô ta giống hệt cô gái đang đứng ở cửa.<br />\r\nCó điều, cô gái đứng ở cửa lại mang đến cho người khác cảm giác thông minh và ưu tú hơn hẳn.<br />\r\n“Tao mà thèm lừa mày à, tiền thì mày cứ tới chỗ bố lấy, mày mau đi đi, tránh ra dùm, nếu làm hỏng chuyện tốt của tao thì mày chết chắc đó!” Cô gái kia hung dữ nói với cô.<br />\r\n“Được!” Cô nghe thế thì tránh đường.<br />\r\n“Mang mấy cái này rồi đi!” Cô gái kia nhét kính râm và mũ vào tay cô, vội vàng đi vào phòng đóng cửa lại.<br />\r\nCô mỉm cười, nụ cười chứa đầy sự cay đắng. Nhưng cô không hề có chút lưu luyến nào, đội mũ và kính râm, thẳng lưng rời khỏi đó.<br />\r\nTrên xe taxi, cô nhìn bầu trời đầy tuyết, lấy điện thoại nhấn nút gọi.<br />\r\n“Bác sĩ Vương, mọi người đừng dừng thuốc của mẹ tôi, tôi sẽ lập tức chuyển tiền qua, đúng thế, tôi đảm bảo!” Sau khi cô chắc chắn bệnh viện sẽ không dừng thuốc thì cúp điện thoại.<br />\r\nCô tên là Vân Tử Lăng, năm nay hai mươi tuổi.<br />\r\nCô gái lúc nãy tên là Vân Tử Diễm, cũng hai mươi tuổi.<br />\r\nCô là chị em song sinh với cô ta.<br />\r\nVân Tử Diễm sinh sớm hơn cô mười giây, là chị của cô.<br />\r\nCó điều, lúc hai người được ba tuổi thì bố mẹ cô ly hôn. Cô được mẹ nuôi còn Vân Tử Diễm theo bố.<br />\r\nTừ đó về sau, hai người chưa bao giờ xuất hiện cùng một lúc.\r\n<p>&nbsp;</p>\r\n</div>\r\n\r\n<hr />\r\n<p>&nbsp;</p>', 0, '2022-07-03 02:51:28', '2022-07-03 02:51:28');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(96, 75, 'Chương 1: 1: Tiểu Cường!', 'chuong-1-1-tieu-cuong', 1, NULL, '<ul>\r\n	<li><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Đồ phế vật, ta không thể tưởng tượng được ngươi còn có mặt mũi mà sống tới từng tuổi này!</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Đúng đấy! thật không hiểu da mặt của ngươi dày đến thế.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thật không biết nhục, phế vật.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một đám nhao nhao hùa theo.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên một gò đất cao một thiếu niên đang đứng nhìn xuống thanh niên mặt mũi, quần áo dính đầy bùn đất quần áo nhuốm nhem, tóc tai bù xù, vây xung quanh thiếu niên là bốn thiếu niên khác trên mặt đều mang theo vẻ trào phúng khinh thường nhao nhao mắng chửi thanh niên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thiếu niên dẫn đầu trên khuôn mặt còn mang theo vẻ ngây thơ dương dương tự đắc tại vì hắn chính là thiên tài trong lớp thiếu niên năm nay mới có mười 13 tuổi mà đã tiến vào cảnh giới linh sư đỉnh phong.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chúng bạn vây quanh hắn nịnh hót như chúng tinh củng nguyệt.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Độc Nhĩ Hàm, ngươi đừng khinh người quá đáng! Ta cũng chẳng trêu chọc gì các ngươi!</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ngươi không trêu chọc gì ta nhưng ngươi lại xuất hiện trước mặt ta, đó là lỗi của ngươi, hãy tự trách bản thân ngươi là một tên phế vật đi, đến tận mười bảy tuổi đầu rồi mà vẵn còn ở cảnh giới linh đồ hạ giai!</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Độc Nhĩ Hàm, cha con các ngươi đều là kẻ vong ân phụ nghĩa, ngươi không biết ơn ngày xưa khi cha ta còn sống chiếu cố hai cha con ngươi thế nào ư!</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Chiếu cố ? Ngươi tưởng cha con ta cần cha ngươi chiếu cố ư.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu người cha vô dụng của ngươi không phải là trưởng tử, Độc Nhĩ Kha, ngươi nghĩ cha ngươi có được chức vị Thiếu tộc tưởng ư? Ngươi nghĩ cha ta phải dưới tay cha ngươi nghe sai khiến ư? Cả hai cha con ngươi đều là phế vật nuôi tốn cơm.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cha đã vô dụng con thì càng vô dụng, đồ phế vật.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Độc Nhĩ Hàm, Ngươi mắng cha ta là gì? Độc Nhĩ Kha lồng lên, hai mắt trợn tròn nắm tay siết chặt!</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Mắng! Ha ha ha! Ta mắng cha ngươi là đồ vô dụng, đồ phế vật, súc sinh! Phế vật vô dụng ngươi muốn làm gì? Đánh ta? Ha ha ha! Độc Nhĩ Hàm miệng trào phúng cười to.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ha ha ha.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phế vật không biết lượng sức.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy vậy những thiếu niên xung quanh cũng hùa theo cười vang.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bịch.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phụt…</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">A a a a.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này đột nhiên nghe một tiếng bịch, phá vỡ tiếng cười trào phúng của đám thiếu niên! Tiếp sau đó mọi người nhìn thấy thiếu niên thiên tài Độc Nhĩ Hàm đột nhiên phụt ra một ngụm máu tươi làm cho khuôn mặt non nớt của hắn thật sự buồn cười.</span>\r\n	<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">A a a.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Không thể nào? Không thể nào! Hắn mới chỉ có là Linh đồ hạ giai mà đánh trúng Độc Nhĩ Hàm thiếu gia đã là Linh Sư đỉnh phong! Hắn làm sao làm được.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này toàn trường chết lặng! Một lúc sau Độc Nhĩ Hàm mới từ trong mộng tỉnh lại!</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-A a a.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phế vật súc sinh.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngươi dám đánh ta.</span><br />\r\n	<br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Hàm như nổi điên hắn lúc này trợn tròn mắt rống lên! Hắn không thể nào tưởng tượng được, hắn đường đường là một Linh Sư đỉnh phong vậy mà lại bị cái tên phế vật súc sinh vô dụng kinh đánh trúng, mà lại đánh trúng khuôn mặt hắn nữa.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này khuôn mặt hắn đầy máu me làm cho hắn trong thật dữ tợn không hợp lứa tuổi.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Đánh ngươi thì sao, ngươi dám mắng cha ta, ta đánh ngươi còn chưa đủ đâu.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tốt, tốt, tốt.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Súc sinh phế vật, ngươi là súc sinh là phế vật, cha ngươi cũng là súc sinh là phế vật, cả hai cha con ngươi đều là súc sinh là phế vật.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chát.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần này Độc Nhĩ Hàm đã có chuẩn bị, hắn vừa thấy Độc Nhĩ Kha điên cuồng xông lên hắn vận khởi linh khí, phát ra một chưởng đối lại với quyền của Độc Nhĩ Kha.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bịch.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rắc rắc</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này một thân ảnh màu đất bay ra đụng vào gò đất sau trang viên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phụt.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Kha phun ra một ngụm máu, bàn tay hắn đã có dấu hiệu bị tê liệt tiếng rắc vừa này có lẽ chính là tiếng xương cánh tay rạn vỡ.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng dù thế hắn lại không hề để tâm cơn đau, trong mắt hắn lúc này tràn đầy sự điên cuồng và thù hận.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn lại bắt đầu đứng đậy vận khởi chút linh khí yếu ớt đến đáng thương vào cánh tay trái còn lại xông thẳng về phía Độc Nhĩ Hàm.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Súc sinh phế vật không biết sống chết.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôm nay ta sẽ đánh cho ngươi thành tàn phế.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy đang nói nhưng thiếu niên cũng không ngừng tay, hắn vẫn vận khởi linh khí xuất ra một chưởng 5 thành công lực.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bịch, răc rắc.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thân ảnh màu đất lại bay ra tiếp tục đụng phải gò đất, cánh tay còn lại hẳn cũng đã bị đánh cho gãy nát rồi.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sao hả? phế vật vô dụng, chút linh khí yếu ớt vô dụng của ngươi mà cũng thi triển ra.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngươi tưởng ngươi là ai hả? ha ha ha… hôm nay ta sẽ cho ngươi biết thế nào là thiên tài.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói rồi hắn tiến lại dùng chân dẫm lên thân ảnh đang đang cố đè nén cơn đau tới tận tâm can kia cong người đứng lên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phịch.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Hàm tiến lại đạp mạnh một cái, thanh niên lại nằm rạp xuống đất.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ha ha ha.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tạp chủng, sao hả? cảm giác làm phế vật tốt chứ.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Kha cố gắng vặn người lăn qua để khiến cho bàn chân thiếu niên trượt qua một bên lúc này hắn mới từ từ đứng dậy.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trong mắt đỏ hồng rống lên đạp một cước về phía thiếu niên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bụp, rắc rắc.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thiếu niên đá ra một cước, một cước này không hề nhẹ hắn đã sử dụng tám thành công lực, hẳn là vì hắn biết xương chân bao giờ cũng cứng hắn xương tay.</span><br />\r\n	<br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn đã cố ý phế chân phải thanh niên này.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng khi thân ảnh thanh niên vừa mới phóng ra thì bỗng nhiên lại bị giật ngược lại bởi vì thiếu niên đã nắm lấy cánh tay phải bị phế của thanh niên giật lại.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rống.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thanh niên rống lên tiếng thê lương hai mắt đầy cừu hận trừng trừng thiếu niên Độc Nhĩ Hàm.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Hàm thiếu gia, dẫm nát hắn đi, phế hắn đi, ha ha ha.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đám thiếu niên xung quanh chen lời vào, bọn chúng nhìn thanh niên bị đánh cho tàn phế mà không hề có chút đồng cảm nào chỉ thấy vẻ vui sướng khi người gặp họa.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này Độc Nhĩ Kha đang cúi gằm người lại nghiễm nhiên là bởi vì mắt thăng bằng do cánh tay phải hắn đã bị gãy.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn ngẩng đầu lên sau đó làm động tác húc đầu vào ngực thiếu niên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hự.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thiếu niên hiển nhiên không thể nào ngờ Độc Nhĩ Kha lại có chiêu này lại bị đính thêm một thủ.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn hự lên một tiếng sắc mặt vặn vẹo hiển nhiên hắn bị húc trúng là chỗ bụng mỡ, nhưng sau đó vẻ mặt lại bình tĩnh ngay lại hiển nhiên đã vận nội lực chuyển qua áp chế cơn đau.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rắc.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rắc rắc.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tạp chủng , dám đánh bản thiếu gia, ta đạp chết ngươi.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thiếu niên Độc Nhĩ Hàm điên cuồng rống lên rồi giơ chân lên đạp xuống thanh niên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bịch bịch bịch</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Độc Nhĩ Hàm, nếu hôm nay ta không chết thì sau này ta sẽ bắt ngươi trả giá gấp trăm nghìn lần.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thanh niên Độc Nhĩ Kha lúc này không còn đứng dậy nổi hắn cố rướn người lên nhìn như con lật đật cong lên nhấc cái thân hình nặng nề của mình kia há miệng cắn vào bắp đùi của Độc Nhĩ Hàm, ngay lập tức một dòng máu tươi từ trong miệng hắn trào ra, đây không phải là máu từ miệng hắn mà là mà của Độc Nhĩ Hàm.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Á á.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Hàm đau đớn nhảy lùi lại hòng thoát khỏi miệng Độc Nhĩ Kha nhưng lại không ngờ lại kéo theo Độc Nhĩ Kha, điều này càng khiến hắn cảm thấy đau hơn, hắn ngay lặp tức chân trùng xuống sau đó huých thẳng vào đầu thanh niên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bịch.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Máu tươi ướt đẫm mái tóc bù xù của thanh niên làm cho hắn càng bết lại tiếc rằng mái tóc của hắn quá nhiều đất làm cho hắn trông tàn tạ dữ tợn rợn người.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng ngay khi đó thanh niên lại bò lại hắn nhích từng tí từng tí một tiến lại gần Độc Nhĩ Hàm hắn lại cong người lên hướng về bắp đùi kia của thiếu niên cắn tới.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bịch.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại một cước, Độc Nhĩ Hàm đập mạnh một cước vào ngực thanh niên, nhưng mà không nghe tiếng rắc, thiếu niên Độc Nhĩ Hàm ngạc nhiên, hắn không thể ngờ lại được hắn sử dụng chín thành công lực mà không thể đá nát xương sườn thanh niên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Thật cứng đầu, ta không tin ngươi là tiểu cường đánh mãi không chết.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói rồi Độc Nhĩ Hàm lại xuất ra một cước vào ngực thanh niên, nhưng lần này hắn vẫn bị thất vọng vẫn không hề có tiếng rắc vang lên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Hàm thấy thế khẽ nhăn mặt lại xuất liên tục hai cước vào ngực thanh niên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hự, hự!</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Kha rướn lên đau đớn.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hắn lại điên cuồng hai mắt huyết hồng xông lên lại muốn dùng đầu huých vào thiếu niên, nhưng lần này vẫn đáp trả hắn là một cước của thiếu niên Độc Nhĩ Hàm mười thành công lực vào ngực hắn.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thân ảnh của Độc Nhĩ Kha lại bay ra.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn cong người lại ức chế cơn đau đang hành hạ ruột gan phèo phổi của hắn.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Grao.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Kha rống lên lồng lộn, lại cong người nhích lại phía thiếu niên khuôn mặt vặn vẹo dữ tợn hắn lại rướn người lên há miệng cắn vào bắp đùi thiếu niên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phịch.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rắc!</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần này Độc Nhĩ Hàm không xuất cước đá vào ngực Độc Nhĩ Kha mà đá vào bắp chân trài còn lại chưa phế của hắn, hiển nhiên là hắn muốn ra tay phế tứ chi của Độc Nhĩ Kha.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">…</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thanh niên màu đất Độc Nhĩ Kha lúc này toàn thân rã rời nhưng sâu trong con mắt hắn là cừu hận vô biên, không chết không thôi.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn trừng mắt đỏ hoe nhìn cừu nhân Độc Nhĩ Hàm.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Hàm thấy ánh mắt sắc lạnh như dã lang kia của thanh niên làm cho hắn trong lòng dâng lên cảm giác sợ hãi, làm cho hắn không dám nhìn thẳng đành nghiêng đầu đi, lúc này trong lòng hắn cảm thấy bất an, hắn cảm giác nếu như người này không chết sau này sẽ uy hiếp lớn đến hắn, người này không chết sẽ là tâm ma của hắn.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“nhất định phải giết hắn, nếu không cảnh giới của ta sau này chỉ sợ không thể tiến triển, không thể nuôi hổ vi họa”</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến đây trong mắt hắn ánh lên sát khí tàn nhẫn.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn tiến lại gần Độc Nhĩ Kha sau đó lại vận khí đạp lên từng cước xuống ngực Độc Nhĩ Kha nhưng hiển nhiên Độc Nhĩ Kha cũng không yếu thế tuy hắn đau quằn quại không thể nói lên lời nhưng mà hắn vẫn điên cuồng há miệng cắn, huých đầu nhưng đáp lại hắn chỉ là cước lực mãnh liệt của Độc Nhĩ Hàm mà thôi.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bịch bịch bịch…</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Máu tươi tràn đầy miệng mũi tai Độc Nhĩ Kha làm cho hắn thê lương nhưng tuy thể sâu trong tâm can hắn là sự cố chấp báo cừu, hắn vẫn cứ vô thức huých đầu vào đối thủ, dù vậy ánh mắt hắn vẫn dại đi.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơi thở yếu dần.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy thế Độc Nhĩ Hàm tiến lại ghé vào tai hắn khuôn mặt dữ tợn nói nhỏ với hắn.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Trước khi tiễn ngươi lên đường ta nói cho ngươi biết cha ngươi chết là do Vô Ảnh Độc hại chết.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ngươi…… là cha con ngươi.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Phế vật, ngươi vậy mà cũng thông minh đó, nhưng có trách là trách do cha ngươi vô năng thôi.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ ngươi đã biết thì cũng đi được rồi! Nói xong Độc Nhĩ Hàm vận khới hóa chưởng thanh đao chém xuống khuôn mặt dữ tợn đôi mắt huyết hồng cừu hận tỉu thiên kia của thanh niên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“ta phải chết sao?” Độc Nhĩ Kha thầm nghĩ nhưng khuôn mặt kiên nghị kia vẫn trừng mắt như muốn khắc sâu hình ảnh cừu nhân kia cho dù xuống địa phủ cũng phải làm quỷ báo thù.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng lúc tưởng khi chưởng đao chém xuống thì bỗng nhiên có một chưởng phong đỡ lại.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bịch…</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngay khi đó một thân ảnh xuất hiện trước mặt đám đông, đó là một lão già gày gò râu tóc bạc phơ, hai má hóp lại, quầng mắt thâm tím, lưng hơi còng xuống hiển nhiên là gần đất xa trời.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già lúc này ngẩng đầu lên nhìn thiếu niên Độc Nhĩ Hàm nói:</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Hàm thiếu gia cần gì phải tuyệt tình như thế, hắn dù thế nào cũng là con của cố thiếu tộc trưởng, cũng là người của Độc Nhĩ Gia tộc.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này thân ảnh có chút chật vật hai tay run lên tê rần, thiếu niên Độc Nhĩ Hàm mới nhìn kĩ lại thân ảnh già gày gò kia.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn kinh ngạc không thôi, đây không phải lão già chết tiệt trong hậu sơn nhà kho gia tộc Độc Nhĩ hay sao.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già này cũng không có tên cũng không ai biết lão xuất thân từ đâu chỉ biết rằng lão già này được gia chủ đời trước cứu về từ một lần thập tử nhất sinh, đến khi sống sót lão mới xin gia chủ cho ở lại trong nhà kho hậu sơn gia tộc để báo đáp ơn cứu mạng.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Lão già bí ẩn này sao lúc này lại xuất hiện ở đây? Sao lại không ở trong nhà kho chết tiệt kia chứ?\"</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Lão già.</span><br />\r\n	<br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện riêng của thiếu gia ta không đến lượt ngươi xen vào.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôm nay ta quyết lấy mạng tên tạp chủng này.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu ngươi ngăn cản đừng trách ta độc ác.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Hàm thiếu chủ nếu tha được thì hãy tha dù gì hắn cũng đã là người tàn phế rồi không thể nào ảnh hưởng tới ngươi được.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Lão già, ta hôm nay quyết lấy mạng hắn, lão khôn hồn thì tránh sang một bên.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Nếu vậy thì thiếu chủ phải đánh bại lão già ta đã.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già cũng không nhiêu lời vận khởi linh khí, đột nhiên uy áp ập đến đè ép tới Độc Nhĩ Hàm và đám thiếu niên làm cho bọn họ cảm thấy khó thở.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Hàm biến sắc vận khởi linh lực chống lại uy áp.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“ Linh tướng hạ giai, thật không ngờ lão già này là linh tướng, không đúng, linh lực có chút không ổn định có lẽ lão già này đang bị thương.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cảnh giới không ổn định”</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Các ngươi đừng sợ, vận linh lực chống lại uy áp.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già này tuy rằng là linh tướng hạ giai nhưng đang bị trọng thương khí tức không ổn định, mọi người cùng xông lên hợp lực giết lão già này cho ta.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong hắn là người đầu tiên vận khởi linh lực hóa thành tầng tầng chưởng ảnh, xông về phía lão già, bốn tên thiếu niên còn lại thấy vậy cũng vận khởi linh lực hóa chưởng, quyền trảo phóng về phía lão già.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Khai sơn chưởng.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Phá nhạc quyền.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Âm Ma trảo.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đối với thế công của năm thiếu niên lão già chỉ cười khẩy, hai tay hoa lên hóa thành vô số chưởng ảnh đối lại thế công của năm người.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bịch, bịch, bịch, bịch, bich.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Năm tiếp kêu trầm đục vang lên.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếp sau đó thấy năm thân ảnh bay nhanh ra ngoài.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phụt phụt….</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Năm người đồng thời phun ra năm ngọn tiên huyết sắc mặt tái nhợt, trong mắt hiện lên sự sợ hãi.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Hàm thiếu gia, lão hủ có thể đưa người đi được chứ?</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Lão già, đừng có đắc ý quá sớm.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cha ta là thiếu tộc trưởng sẽ tìm ngươi tính sổ.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngươi hãy chờ đấy.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhắc đến thiếu tộc trưởng lão già hơi khựng người lại một chút, hơi do dự sau đó lại tiếp tục tiến lên bế Độc Nhĩ Kha đang nằm hôn mê lên rồi bước đi.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhìn bóng lưng lão già, Độc Nhĩ Hàm hai mắt đoán độc ánh lên vẻ ngoan lệ, hai tay xiết chặt.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Lão già, hãy đợi đấy, ta nhất định tìm cha giúp ta báo thù, ta sẽ cho lão sống không bằng chết.</span><br />\r\n	<br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ha ha ha.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn cười đầy dữ tợn, làm cho khuôn mặt ngây thơ của hắn trông vặn vẹo nhìn thật buồn cười.</span><br />\r\n	<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong hắn ra hiệu bọn người kia đứng lên phóng về phía gia tộc..</span><br />\r\n	&nbsp;</li>\r\n</ul>', 0, '2022-08-05 00:59:14', '2022-08-05 00:59:14');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(97, 75, 'Chương 2: 2: Đào Vong', 'chuong-2-2-dao-vong', 2, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già bế Độc Nhĩ Kha phóng đi như bay chỉ trong một tuần trà đã về tới nhà kho, lão mới đặt Độc Nhĩ Kha xuống cái giường đá cứng nhắc kia, trên giường còn phả mùi mốc meo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già quan sát tình cảnh Độc Nhĩ Kha một lượt từ trên xuống dưới, lúc này mới lắc đầu thở đài sau đó vận linh lực đưa một chút vào trong cơ thể Độc Nhĩ Kha duy trì hô hấp và nhịp tim cho hắn, lúc này làm xong mới đi tìm dụng cụ bó bột cho Độc Nhĩ Kha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ thấy lão già tay nhanh như thoát lấy ra mấy khúc gỗ và một số dải vải thô ra, nhanh chóng bó lại cho Độc Nhĩ Kha.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ trong phút chốc thân ảnh Độc Nhĩ Kha cứng ngắc như khúc cây khô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này lão già làm xong mới thở phào lấy từ trong nhẫn trữ vật ra một cuộn kim châm cứu ra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão xé áo trên thân Độc Nhĩ Kha ra, đang định châm cứu thì bỗng nhiên dừng lại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ồ…</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thì ra lão già nhìn thấy bên trong ngực Độc Nhĩ Kha có một cái bảo hạp màu đen, toàn thân đen thui, cực kỳ xấu xí, hơi mỏng chỉ cỡ một ngón tay cái.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cái hộp hình chữ nhật thật khó nhìn nhưng đụng vào thấy mát lạnh và rất cứng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thật cứng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chả trách tiểu tử kia đá bao nhiêu cước như vậy vào ngực mà hắn không bị sao, hiển nhiên là do bảo hạp này chặn lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già cũng không để ý, chỉ tùy ý cầm bảo hạp bỏ qua một bên, sau đó ngón tay thoăn thoát từng chiếc từng chiếc kim châm đính vào ngực Độc Nhĩ Kha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ chốc lát hắn đã như một con nhím cả người đều là kim châm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Làm xong tất cả lão già cảm thấy hơi mệt đầu có chút choáng váng, càng làm cho hai hốc mắt của lão thêm sâu, nhìn như già thêm nhiều tuổi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này lão già mới ngẩng đầu nhìn ra ngoài, ánh mặt trời đã ngả chiều, lúc này mới thì thào:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“phải nhanh chóng thôi, nếu không không còn kịp”.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lời này nghe ra khó hiểu nhưng lão già biết phải nhanh chóng trốn đi đem người thanh niên này giấu đi để thoát khỏi sự trả thù của thiếu tộc trưởng Độc Nhĩ Long cha của Độc Nhĩ Hàm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão biết Độc Nhĩ Long là người trừng mắt tất báo, hắn là con người nham hiểm lão vẫn hoài nghi cái chết của cố thiếu tộc trưởng Độc Nhĩ Hà cũng là cha của Độc Nhĩ Kha có liên quan đến hắn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiển nhiên lão hoài nghi không sai, bởi vì ngày đó vì cái chức thiếu tộc trưởng mà Độc Nhĩ Long mới bỏ Vô Ảnh Độc vào canh của Độc Nhĩ Hà, Vô Ảnh Độc là loại độc hiếm có rất rất độc, vô ảnh vô tung, không mùi không vị, rất khó phát hiện, quả nhiên sau khi Độc Nhĩ Hà chết đi không ai tra ra được nguyên nhân cái chết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già không chút do dự chui tọt vào kho lấy ra một số gốc linh dược bỏ vào nhẫn trữ vật sau đó vác Độc Nhĩ Kha lên vai phóng ra khỏi hậu sơn Độc Nhĩ Gia tộc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">------</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cùng lúc đó Độc Nhĩ Hàm đang quỳ trước mặt Độc Nhĩ Long khóc thút thít.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người phải báo thù rửa hận cho hài nhi, cái tên súc sinh Độc Nhĩ Kha ấy dám sỉ nhục cha bị con đánh cho tàn phế thì cái lào già canh giữ nhà kho không biết từ đâu đến cứu hắn và đả thương hài nhi, cha, hắn nói cho dù cha đến hắn cũng không nể mặt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long lúc này ngồi trên ghế thái sư trừng mắt nhìn nhi tử, tuy rằng lão biết nhi tử lão thương yêu bảo vệ nói thêm mắm thêm muối vào, tính tình của Độc Nhĩ Hàm như thế nào hắn biết rõ, tuy vậy hắn là người bao che khuyết điểm trừng mắt tất báo, có người dám đả thương nhi tử hắn hắn quyết không tha.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Đứng dậy đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta sẽ cho người bắt hai người bọn họ lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long uy nghiêm nói.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Cha, cha nhất định phải tự tay diệt sát bọn họ, bởi vì… vì… Nói đến đây Độc Nhĩ Hàm trong lòng không khỏi sợ hãi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Vì cái gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Vì… cha, hài nhi bất hiếu, hài nhi đã nói với Độc Nhĩ Kha là cha hắn chết bởi vì Vô Ảnh Độc.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tên phế vật đó đã biết hung thủ là cha rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nói đến đây hắn có chút sợ hãi lẫn yếu ớt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Cái gì…….</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long đứng phắt dậy quát lớn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khuôn mặt đỏ bừng vì giận dữ hắn tung một chưởng tát lên khuôn mặt nhi tử mình khiến cho Độc Nhĩ Hàm phun ra một ngụm máu, nhưng hắn vẫn không dám kêu lên đau đớn bởi vì hắn rất sợ lúc này cha hắn đang bạo lộ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Súc sinh vô dụng, phạt vào linh động bế quan ba tháng cho ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không có lệnh của ta không được ai vào thăm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói xong hắn nhìn ra ngoài, hô:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Người đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bỗng nhiên có một bóng đên tử bên góc cửa bay ra đứng trước mặt Độc Nhĩ Long, hiển nhiên người này tu vi không thấp, cũng là Linh Tướng trung giai.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Truyền lệnh, tập hợp mọi người vây bắt hai người Độc Nhĩ Kha và lão già trong coi nhà kho, ta sẽ đích thân đuổi giết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Còn nữa cho người phong tỏa cửa thành Thanh Đàm cho ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bất cứ ai cũng phải kiểm tra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">----</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rừng U Minh là khu rừng ven thành Thanh Đàm, khu rừng có rất nhiều linh được và ma thú hung hãn, hàng ngày dong binh đoàn lườm lượp tổ chức vào rừng liệp sát ma thú lấy tinh hạch và da thú về bán để đổi lấy linh thach, kim tệ,,,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôm nay vẫn như mọi ngày chỉ có điều lúc này đây có một lão già thoáng ẩn thoáng hiện đang vác một thân ảnh cứng ngắc trắng xóa trên vai tận tình phóng đi tránh chỗ đông người tiến về phía ven rừng U Minh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thân ảnh đó chính là lão già gầy gò trông coi nhà kho và Độc Nhĩ Kha.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cùng lúc này toàn thành Thanh Đàm đang nháo loạn cả lên bởi vì gia tộc Độc Nhĩ vừa phát lệnh truy nã hai phản đồ của gia tộc là một lão già lưng còng và một thanh niên, hai người này hiển nhiên là hai thân ảnh đang tiến vào ven rừng kia.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người dân trong thành đang nháo lên vì giá thưởng người cung cấp thông tin là 1000 kim tệ và người giết được hai người này sẽ được thưởng 100 linh thạch hạ phẩm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đối với người trong thành số tiền kia là rất lớn, nhưng hấp dẫn hơn cả là số 100 linh thạch hạ phẩm kia, một linh thạch hạ phẩm bằng 100 kim tệ, với lại linh thạch hạ phẩm trợ giúp người tu luyện hấp thu linh khí tinh thuần năng cao cảnh giới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy chỉ là linh thạch hạ phẩm nhưng có kim tệ cũng không mua được.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong nhất thời toàn thành sôi trào mà người gia tộc Độc Nhĩ chính là muốn thấy cảnh này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vô số người điên cuồng lên, hàng loạt bồ câu đưa thư bay lên, chim đưa tin bay về bốn phía.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">----</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này hai bóng ảnh một đen một trắng đã tiến vào trong rừng U Minh năm dặm..</span></p>', 0, '2022-08-05 00:59:49', '2022-08-05 00:59:49');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(98, 75, 'Chương 3: 3: Độc Nhãn Hổ Mang Độc', 'chuong-3-3-doc-nhan-ho-mang-doc', 3, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này người thành Thanh Đàm đang nháo nhào lên, người của gia tộc Độc Nhĩ cũng không rảnh, chạy đôn chạy đáo lùng xục khắp chốn hòng tìm ra hai người Độc Nhĩ Kha.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bành.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long một chưởng mạnh vỗ lên mặt bàn gỗ lim, làm cho cái bàn rắn chắc này cũng phải in hằn lên một chưởng tay.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vẫn chưa có tin tức gì sao, các người đều là vô dụng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bẩm thiếu tộc trưởng, trong thành đã tìm đên lật từng viên gạch lên rồi mà không có tin tức gì? Thuộc hạ nghĩ có lẽ hai người họ đã trốn vào rừng U Minh rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc ảnh bên góc lại hiện ra trước mặt Độc Nhĩ Long bẩm báo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Cho người tăng thêm phần thưởng, mang người chuyển cáo các thành phụ cận giúp bắt phản đồ nếu thấy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tuân lệnh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc ảnh nói xong bỗng nhoáng lên cái biến mất.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">----</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này đây thành Thanh Đàm lại lần nữa điên cuồng vì mức thưởng cho người cung cấp thông tin lên tới 1 vạn kim tệ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">linh thạch hạ phẩm 100 khối, 1 khối linh thạch trung phẩm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">----</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Đã có tin tức của hai người! Hắc ảnh lại xuất hiện trước mặt Độc Nhĩ Long bẩm báo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Nói!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tin tức của một dong binh đoàn Thiểm Điện nói đã nhìn thấy hai người bọn họ sâu trong rừng U Minh mười dặm hướng Tây Bắc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rừng U Minh!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long cau mày, hắn biết sự nguy hiểm của rừng U Minh, U Minh ma thú trùng trùng, mãnh thú vô số.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu bọn họ trốn vào đó thực sự khó giải quyết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ngươi phải đi một chuyến, làm cho sạch sẽ rồi nhanh chóng trở về, còn nữa báo cho Dong binh đoàn tin tức hai người, có lẽ họ cũng nên ra sức một chút.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long phân phó hắc ảnh rồi nâng chum trà húp một ngụm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này lão già gày gò đang ngày một tiến vào sâu trong rừng, cứ cách một đoạn thỉnh thoảng có một số mãnh thú đụng phải, lão cũng không dây dưa lựa chọn đường vòng tránh đi, có hai lần gặp phải ma thú nhưng đều là ma thú cấp một, lão già cũng không tấn công mà phát ra uy áp đuổi chúng đi.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ma thú đã mở linh trí tất nhiên biết lợi hại, đành phải lủi đi, lúc này đây đã tới 50 dặm sâu phía tây Bắc rừng U Minh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sâu trong rừng vô số tiếng thú rống, giờ đây đã gần xế chiều.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một tin tức liên quan tới hai người Độc Nhĩ Kha phát ra vô số võ giả và dong binh đoàn tinh tiến về phía Tây Bắc U Minh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiển nhiên muốn phát tài một phen.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong số đó có một hắc ảnh đã bỏ xa đám người một mình phóng như bay tiến vào khu Tây Bắc U Minh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vèo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già vác Độc Nhĩ Kha phóng như bay xuyên qua tầng tầng lớp cây cối rậm rạp, xung quang chỉ lốm đốm những tia nẵng hoàng hôn, còn đâu chỉ còn lại bóng tối bao phủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khu rừng rậm tầng tầng lá cây, che chắn ánh nắng xuyên qua, dưới những tán lá này nguy hiểm trùng trùng, bây giờ hai người cũng đã tiến vào khu vực có nhiều ma thú cấp cao ngự trị.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già đang phóng đi cũng nhạy cảm cảm nhận nguy hiểm, bất chợt dừng lại quan sát xung quanh tìm một nơi đặt chân để điều trị cho Độc Nhĩ Kha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng tiếc thay cho lão xung quanh chỉ cây với cây, chỉ có một gốc cây cổ thụ khá to khoảng ba người ôm, gốc cây cố một cái hốc có vẻ đủ một người trong đó.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão quan sát nhận xung quanh, rồi cẩn thận đặt Độc Nhĩ Kha vào hốc cây, từ trong nhẫn trữ vật lấy ra một bình sứ, bên trong chứa một loại bột.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lão cẩn thận rắc xung quanh phạm vi hai mươi trượng hốc cây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây là loại bột chuyên đuổi côn trùng rắn rết, nó phát ra mùi hương rất thơm nhưng đối với rắn rết con trùng thì thực sự khó ngửi, chúng nhanh dời xa nơi phát ra cái mùi mà chúng chán ghét đó.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Loại bột này chính là Triệt Trùng Hương.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già rất nhanh ra tay bắt về một con thỏ rừng, đặt lên nướng thơm phức, làm xong lão lấy ra một cái cối và một cái chày bằng sắt, lão xé thịt thỏ ra từng mảnh nhỏ sau đó tay thoăn thoát dùng chày nghiền nát thịt thỏ ép nó thành bột xệt xệt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">làm xong tất cả lão bắt đầu bón cho Độc Nhĩ Kha bởi vì hắn lúc này còn đang hôn mê trong thống khổ không thể ăn được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhìn khuôn mặt kiên nghị người thanh niên này lão chỉ biết thở dài.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bỗng dưng nét mặt lão già khựng lại, quay phắt người nhìn thẳng vào một góc đen bên một gốc cây to quát:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Lấp la lấp ló, các hạ hãy ra mặt đi, cần gì phải giấu mặt như thế.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vèo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một hắc ảnh phóng ra từ góc tối đáp xuống các lão già hai mươi trượng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này mới nhìn rõ hắc ảnh hiển nhiên là Ảnh Vệ của Độc Nhĩ Long phái ra truy sát hai người.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sở dĩ hắn tới nhanh và chính xác tìm được hai người là vì hắn có tu vi cao và ngửi được mùi hương của Triệt Trùng Hương.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Các hạ là ai?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già gằn lên hỏi, tư thế chuẩn bị đầy đề phòng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Lão già, ta là ai lão không cần biết, lão chỉ cần biết hôm nay ta đến để lấy mạng lão và tiểu tử kia.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hừ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già hừ một tiếng nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tuy rằng lão đã mất nửa cái mạng nhưng mà hôm nay bất cứ ai cũng không mang hắn đi được.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hừ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc Ảnh cũng không thèm nói nhiều, nhiệm vụ của hắn trước lúc đi Độc Nhĩ Long đã dặn dò là làm nhanh và sạch sẽ.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tính cách của hắn cũng không muốn dây dưa, thế giới này cường giả vi tôn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc ảnh vận linh lực, một luồng uy áp phát ra làm cho không khí thêm nặng nề ép tới lão già, hiển nhiên đang súc lực chuẩn bị ra tay.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Linh tướng trung giai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già hít một hơi lãnh khí, sắc mặt trở nên ngưng trọng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vèo, hắc ảnh phóng về phía lão già, vô số hỏa chưởng ảnh phát ra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Bát Cực chưởng, đệ nhất thức.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bạch bạch.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già cũng không kém cạnh đánh ra vô số chưởng đón đỡ chưởng phong của đối phương, tiếng vỗ kêu lên bạch bạch.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hự.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già bị chấn động lui về phía sau năm bước sau đó mới ổn định lại thân hình, khí huyết có phần nhộn nhạo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xẹt, hắc ảnh cũng bị kéo lê trên mặt đất hai bước mới dừng lại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Bát Cực chưởng, đệ nhị thức.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc ảnh lại phóng về phía lão già phát ra đệ nhị thức.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần này lão già không trực tiếp đón đỡ khi chưởng phong chỉ còn cách một thước, lão bước qua bên trái một bước nghiêng người đánh ra một chưởng làm lệch quỹ đạo chưởng lực của đối phương lấy yếu đối mạnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dù vậy lão già vẫn bị ép cho lùi về phía sau hai bước hiển nhiên đệ nhị thức Bát Cực chưởng đã mạnh hơn chưởng trước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhìn lại gốc cây cổ thụ bị chưởng ảnh thế đi không giảm đánh vào đã có một lỗ thủng màu đen bị cháy xém, trên mặt còn khói đen lập lờ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“ Cứ như thế này thì không thể thoát thân, xem ra phải dùng chiêu đó thôi”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già nghĩ thầm, đang định chuẩn bị lấy từ trong nhẫn trữ vật ra thì đột nhiên ‘grao” một tiếng làm cho lão giật mình.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thì ra không biết từ lúc nào khi hai người giao thủ đã hấp dẫn một con Hắc báo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Hắc Vân Báo – ma thú cấp 4 thượng giai.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai người đồng thanh réo lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không khói hít vào một ngụm lãnh khí, hiển nhiên vì kiêng kị ma thú này bởi vì nó tương đương với Linh Tướng thượng giai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngoài ra ma thú da dày thịt chắc khó lòng đối phó.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con Hắc Vân bào toàn thân đen sì, cao khoảng một thước dài ba thước, trên làn da lốm đốm còn có những vân ảnh màu xám nhìn như hình đám mây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cái đuôi nó thật dài đen xì đang quẹt qua quẹt lại dưới nền lá cây nghe xoạt xoạt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con hắc báo cũng không tấn công ngay nó dường như đang kiêng kỵ hai người chỉ đứng một bên dửng dưng xem trò vui.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già ngưng trọng từ trong nhẫn trữ vật lấy ra một cây Lưỡi hái toàn thân màu đen thân dài một thước ba, mũi mâu cong như hình lưỡi liềm dài một thước.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiển nhiên cây Lưỡi hái có bôi độc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Không biết các hạ có nguyện ý liên thủ cùng ta giết chết con Hắc Vân Báo này trước không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc Ảnh trên mặt hơi ngưng trọng, vẻ mặt hơi lưỡng lự nhưng rất nhanh được thay thế bởi sự quyết đoán, hắn lắc đầu, từ trong nhẫn trữ vật lấy ra một thanh đại đao đỏ rực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiển nhiên thanh đao thuộc tính hỏa, trên thân đao có khảm một viên ma thú hạch hỏa thuộc tính.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc Ảnh vận hỏa linh lực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hỏa linh lực làm thanh đại đao sáng rực sau đó nhanh như chớp xong về phía lão già lưng còng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Liệt Vân trảm tam thức, thức thứ nhất Liệt Vân Đoạt Mạng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đao mang đỏ rực làm không khí nóng bức ép tới khuôn mặt già nua càng làm rõ nét khuôn mặt hốc hác của lão già.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già vận thủy linh lực làm cho thanh Lưỡi hái càng thêm đen nhánh, ánh sáng yếu ớt hoàng hôn chiếu vào càng làm cho nó thêm nguy hiểm như diêm vương dòi mạng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chát.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đao, xà va chạm chát một tiếng không khí chấn động làm cho hai người bị bật lại, nhưng đúng lúc Hắc Ảnh muốn thu hồi lại Hỏa Đao biến chiêu mới thì bỗng nhiên bị thanh Lưỡi hái cong kéo lại, bởi vì Lưỡi hái có hình cong còn có công năng móc kéo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lực kéo không giảm làm cho Hắc Vệ Ảnh hơi hoảng hốt nhưng chỉ giây lát hắn làm ra quyết định đẩy ra một chưởng vào chuôi đao nhằm phóng đao về phía lão già.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già cũng không vừa, tay hoa lên xoay Lưỡi hái nửa vòng làm cho nó lệch khỏi quỹ tích, cùng lúc đó Hắc Vân Báo phóng tới phía Hắc Vệ Ảnh bởi vì nó thấy được người này mất đi vũ khí uy hiếp với nó không đáng kể.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Súc sinh cút mau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc Vệ Ảnh giận dữ hóa chưởng thành Đao chém tới Hắc Vân Báo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cùng lúc đó thế tới Lưỡi hái của lão già lưng còng cũng tiến tới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chát.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già nện vào móng vuốt Hắc Vân báo nhưng nó lại không bị gì ngược lại Hắc Vệ Ảnh bay ngược lại miệng thổ huyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xẹt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lưỡi hái quét qua cánh tay phát chưởng Hắc Vệ Ảnh, nhất thời trên không trung một cánh tay bay lên máu tươi phun ra bắn lên miệng con Hắc Vân Báo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">A.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc Vệ Ảnh kêu lên đau đớn cơ mặt co giật gằn lên, gân xanh nổi lên thật dữ tợn, lúc này hắn không chần chừ dùng tay trái điểm lên huyệt vị bả vai khiến cho máu ngừng chảy ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này mới dừng lại khuôn mặt tái nhợt, đau đớn khôn xiết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hắn nghiến răng nghiến lợi oán hận nhìn lão già lưng còng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ta liều mạng với ngươi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn nhanh bay ra phía thanh đao, dùng chân đá thanh đao lên tay trái nắm lấy chuôi đao vận hỏa linh lực bay về phía lão già.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tình cảnh lão già cũng không khá gì, lão đã bị thương còn nửa cái mạng, lại còn liều mạng với Hắc Vệ Ảnh, khóe miệng rướm máu linh lực rối loạn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cùng lúc đó Hắc Vân Báo nếm mùi máu tươi càng thêm điên cuồng, nó càng hưng phấn xông thẳng về phía hai người.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Liệt Vân Trảm tam thức, thức thứ hai Liệt Vân Tiêu Hồn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đao mang rực rỡ thế đến cuồng bạo, lão già sắc mặt ngưng trọng vận toàn lực đón đỡ thế đao.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Binh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại một tiếng vang lên, nhưng lần này lão già bị bắn ngược lại va vào gốc cây cổ thụ chỗ Độc Nhĩ Kha nằm.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão phun ra một ngụm máu, cả người co giật đau đớn vô cùng, sắc mặt tái nhợt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Grao.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc Vân Báo cũng phóng tới móng vuốt sắc bén cắm sâu vào lưng Hắc Vệ Ảnh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Máu tươi phun ra nhuốm đỏ cả lưng hắn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">A.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc Vệ Ảnh điên cuồng rống lên hắn xoay người bắt chấp đau đớn phang ra một đao vào bụng con Hắc Vân Báo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bịch.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con báo bị bắn ra 3 thước, trước khi bắn ra nó còn kịp phun ra một quả cầu màu đen về phía Hắc Vệ Ảnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bụp.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên Hắc Ảnh đang định vận linh lực phòng vệ thì phát hiện linh lực không thể vận chuyển, cả người cảm giác đau đớn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đến lúc này hắn mới hiểu ra trên Lưỡi hái có độc, độc tố đã tràn vào máu huyết của hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc này có tên chính là Độc nhãn Hổ mang độc, một loại kịch độc lấy từ răng nanh của Hổ mang một mắt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ngươi…</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên Hắc Ảnh không cam lòng chỉ về phía lão già, ánh mắt oán hận.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hắn chỉ trừng trừng mắt nhìn hắc cầu ngày một tiến lại gần ngực mình.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bụp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắc cầu bắn xuyên qua ngực hắn làm hắn đứng như trời trồng, sau mấy hô hấp liền rầm một tiếng ngã xuống tắt thở.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng lúc tưởng con báo ngã xuống thì nó lại vặn vẹo một vòng xoay người đáp xuống mượn phản lực phóng về phía lão già lưng còng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già thấy thế không chần chừ vác Độc Nhĩ Kha lên phóng về phía Hắc Vân Báo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng không phải lão già liều mạng mà là khi lão phóng đến thi thể Hắc Vệ Ảnh liền xuất ra một cước thật mạnh đá thi thể văng về phía Hắc Vân Báo, làm xong lão vặn người nửa vòng quay trở lại phóng về phía rừng rậm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con Hắc Vân Báo bị thi thể bay về phía nó nó xuất ra một trảo xé toạc thi thể ra nhưng đúng là nó vẫn bị khựng lại một lát.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng chỉ một lát này thôi đã không còn tung tích lão già lưng còng đâu, nó không cam lòng điên cuồng rống lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó hưởng thụ thành quả cuộc chiến là thi thể Hắc Vệ ẢNh kia.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này đây lão già đã phóng đi rất nhanh bất chấp thương thế lão không hề dừng lại điều tức trị thương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Máu tươi ngày một nhiều nơi khóe miệng, đi được một lát thì trời vừa tối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già cũng vừa nhìn thấy phía trước là một sơn cốc, bên trong cốc khá nhỏ hẹp, tối tăm vừa vặn có một cái hang, lão không do dự phóng vệ phía cái hang nhỏ hẹp khoảng chừng hai trượng sâu năm trượng đó.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này mới yên tâm phần nào đặt Độc Nhĩ Kha xuống đất và móc ra từ trong nhẫn trữ vật một viên đan màu trắng, hiển nhiên là thủy thuộc tính, viên đan tỏa ra khí tức mát lạnh hiển nhiên phẩm chất cũng rất cao.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão hơi do dự, nhìn viên đan luyến tiếc nhưng cũng nuốt nó vào bụng ngồi xuống điều tức.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phục hồi thương thế..</span></p>', 0, '2022-08-05 01:00:35', '2022-08-05 01:00:35');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(99, 75, 'Chương 4: 4: Ngâm Thuốc', 'chuong-4-4-ngam-thuoc', 4, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bành.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long ngồi trong thư phòng đứng ngồi không yên, đã gần 6 canh giờ rồi mà không có tin tức của Hắc Ảnh Vệ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hắn đành trút giận lên cái bàn gỗ lim màu đen kia.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Người đâu, thông báo mọi người toàn lực tiến về phía rừng U Minh truy bắt phản đồ cho ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn nữa thông báo nếu ai giết được hai người họ sẽ được thưởng thêm một viên ma thú hạch cấp 7 hạ giai.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Rõ, thưa thiếu tộc trưởng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dứt tiếng lại một hắc ảnh bay ra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Tên phế vật Độc Nhĩ Kha này không chết kế hoạch của ta sẽ bại lộ, xem ra ta phải đích thân đi một chuyến rồi.”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">----</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phụt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già lưng còng đang điều tức chợt mở bừng mắt phun ra một ngụm máu đen, hiển nhiên là máu độc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thương thế năm đó lại tái phát thêm nghiêm trọng, cộng với lần này liều mạng làm cho cái mạng già của ta không sống được bao lâu nữa”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ rồi lão ngẩng đầu nhìn về phía Độc Nhĩ Kha đang hôn mê trong đau đớn kia, suốt quãng đường dài chạy như bay càng làm thương thế hắn thêm nghiêm trọng, lão thở dài:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“có lẽ phải xem số mệnh của hắn rồi, nên phải dùng phương pháp đó thôi!”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đột nhiên lão phóng ra ngoài.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">----</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một phút sau lão già mang về một khúc gỗ to hai người ôm cao một thước sáu và một con thỏ rừng một bó củi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa về tới hang lão liệt bận rộn lấy ra một thanh chủy thủ màu trắng, tay nhanh thoăn thoắt khoát lên ruột khúc gỗ làm thành một cái thùng chứa nước.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xong rồi lão đốt lửa lấy ra một cái nồi sau đó ra ngoài múc đầy nước bỏ lên cái bếp củi, tiếp đó lão lấy ra một đống dược vật bỏ vào cái nồi nước trong đó có một bông hoa ngũ sắc đặc biệt rực rỡ - Ngũ hành hoa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây là một loại hoa cực độc, nhưng cũng cực quý bởi vì nó có thể làm vị thuốc quý nếu dùng đúng cách.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Làm xong tất thảy lão già lưng còng mới nhìn về phía con thỏ sau đó lại một phen thịt thỏ rồi nướng thỏ, tiếp theo lại nghiền thành dịch sệt bón cho Độc Nhĩ Kha.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Gần nửa canh giờ nước trong nồi đã sôi, lão già mới ra ngoài lấy một ít nước lạnh, sau đó vác Độc Nhĩ Kha bỏ vào thùng gỗ mới tạo một cách cẩn thận, xong rồi lão già mới cẩn thận pha nước sôi dược vật với nước lạnh theo tỉ lệ nhất định rồi đổ vào thùng gỗ.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước được vật vừa được đổ vào ngập cổ Độc Nhĩ Kha chỉ chừa cho hắn cái đầu để thở, một khắc sâu thân thể Độc Nhĩ Kha trở nên co giật, vặn vẹo trông rất dữ tợn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rống.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn bừng tỉnh hét lên đau đớn, con ngươi lồi ra nhìn thảm thiết vô cùng, cơn đau hành hạ do được vật thấm vào từng lỗ chân lông.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này hắn mới nhìn loáng thoáng thấy trước mặt mình một lão già lưng còng gần đất xa trời đang bịt tai lại, lúc này mới hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ngươi là ai? Đây là đâu? Ta chưa chết sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tiểu tử, ta là ai không quan trọng, quan trọng là ta cứu người khỏi tay Hàm thiếu gia, ngươi còn chưa chết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-A.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tại sao đau như vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngươi đã làm gì ta?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn hét lên một cách đau đớn hai mắt trắng dã như tôm luộc, nhìn thật hoạt kê.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Cả người ngươi tứ chi đều bị đánh nát, kinh mạch hỗn loạn tắc nghẽn, không thể hồi phục hoàn toàn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta chỉ còn cách giúp ngươi ngâm vào nước dược vật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây là phương thuốc bí truyền của gia tộc ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngươi nếu như muốn trả thù thì phải cố gắng chịu đựng sống vượt qua lần này, nếu không suốt đời ngươi sẽ tàn phế.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Là phế vật suốt đời người ta khinh thường.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Là người hay là phế vật thì phải dựa vào ngươi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">A.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn hét lên đau đớn, được vật lại thấm vào làm cho cơ mặt hắn vặn vẹo, gân xanh nổi ra, hai mắt hốc hác đỏ hồng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn muốn nói gì nhưng không thành lời.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lúc qua đi, hắn mới gượng từng chữ nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ta…Không…Muốn…Làm…Phế Vật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta phải sống để báo thù.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già lưng còng thấy thế mới hài lòng, hắn cũng không nói gì thêm chỉ còn cách chờ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">---</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Lão thiên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta sẽ không khuất phục, ta sẽ đấu với ngươi tới cùng, ta muốn báo thù, ta sẽ không cho bất cứ ai khinh thường ta, sỉ nhục cha mẹ ta, người thân của ta.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Xin hỏi tiền bối quý danh là gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói rồi hắn lại gào lên một cách thảm thiết bởi vì đoàn dược vật mới đã ngấm vào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một hồi lão già mới buông tay bịt tai ra rồi mới nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ta tên gì ngươi không cần biết.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta cứu ngươi cũng là vì không đành lòng thấy ngươi như vậy, hoàn cảnh của ngươi và ta có điểm tương đồng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già lưng còng nói đến đây hai mắt như hồi tưởng lại nhưng ngày tháng trước, khuôn mặt sầu thảm đau khổ ngay sau đó là nỗi hận thù bất cộng đái thiên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Kha thấy vậy cũng không hỏi gì thêm bởi hắn biết hoàn cảnh này của hắn còn chưa xong.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn chỉ còn cách ghi tạc ân tình này vào tận đáy lòng khi nào có cơ hội hỏi lão, nếu như qua được giai đoạn này hắn sẽ báo thù cho lão dù cho bỏ cái mạng nhỏ này.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khụ khụ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này lão già bỗng ho sặc sục, sắc mặt tái nhợt hiển nhiên vết thương chưa lành.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tiền bối ngài bị thương? Vì cứu ta?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Chỉ một chút nội thương mà thôi, không đáng kể.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già lãnh đạm nói.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe thế nhưng trong lòng Độc Nhĩ Kha cảm thấy chua xót, hắn hiểu lão già nói có vẻ nhẹ nhàng như vậy nhưng mà để có thể cứu hắn ra hiển nhiên phải trả giá lớn cỡ nào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tiền…</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lời vừa ra miệng đã bị lão già ngắt lời.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngươi không cần nói thêm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ dược vật hãy còn thiếu, lão cũng phải ra ngoài tìm một ít dược vật dự trữ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói rồi lão phóng ra ngoài để mặc Độc Nhĩ Kha vẫn bị cơn đau hành hạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-À quên nói cho ngươi biết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không nên kêu hoảng lên, bây giơ có rất nhiều người đang truy sát ngươi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngậm miệng lại cho ta.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiền bối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">có thể cho ta một cái khăn bịt miệng không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn cắn răng nhịn đau gằn lên nói.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già cũng không nói gì thêm lấy ra một cái khăn đút vào miệng hắn rồi phóng ra ngoài.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">---</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này màn đêm đã buông xuống, ánh trăng chiếu sáng cả khu rừng, bầu trời trong veo, thú rừng ăn đêm cũng ra khỏi hang, nhưng hôm nay lại khác, khu rừng còn rất nhiều khách không mời mà đến.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trong số đó có rất nhiều hậu vệ Độc Nhĩ gia và nhiều dong binh đoàn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lý do? Rất đơn giản bọn họ đến vì thủ cấp của hai người.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hai người này chính là Độc Nhĩ Kha và lão già lưng còng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Nhanh tìm bên kia, khốn kiếp không biết lão già chết tiệt kia trốn đi đâu, hầu như đã lật tung cả 100 dặm rừng phía Tây Bắc mà còn chưa thấy tung tích.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Bọn khốn kiếp, đêm hôm khuya khoát bắt đại gia phải từ trên giường mĩ nữ mà dậy chịu khổ, nếu bắt được bọn chúng ta sẽ cho chúng sống không bằng chết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một tên trường đoàn một tiểu dong binh đoàn gắt lên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tiểu đội trưởng không cần phải giận dữ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nếu lấy được thủ cấp của hai người bọn họ thì chúng ta giàu to rồi, chậc chậc đó là ma thú hạch cấp 7 hạ giai đó, bán ra cũng được mấy vạn linh thạch hạ phẩm đó.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên tiểu dong binh đi bên cạnh nói chen vào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngươi nói cũng đúng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến phần thưởng ta cảm thấy thoải mái một chút.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">---</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này một đạo bạch quang loáng cái hiện ra, đây là chiến trường của cuộc giao chiến giữa hai người một thú.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đạo thân ảnh này không phải ai khác chính là Độc Nhĩ Long.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn nhìn thanh đại khảm đao màu đỏ rực của Hắc Ảnh Vệ trong lòng phẫn nộ gầm lên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Súc sinh, ta phải đích thân bầm thây ngươi thành vạn mảnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phẫn nộ xong hắn quan sát một chút rồi phóng về một hướng nơi rừng sâu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">---</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời gian qua bình minh đã chiếu những tia nắng đầu tiên làm cho khu rừng thêm rực rõ, sơn cốc phản chiếu qua khe hẹp chiếu vào hang động, lúc này lão già lưng còng có chút chật vật trở về.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vừa về nhìn thấy bộ dạng Độc Nhĩ Kha đang ngất đi vì qua đau khổ, khuôn mặt thỉnh thoảng lại vặn vẹo co giật một hổi lão yên tâm phần nào, còn tốt là hắn vẫn còn sống.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần này lão mang về rất nhiều thỏ rừng ra và dược vật từ trong nhẫn trữ vật lấy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói rồi lão cũng không rảnh tiếp tục công việc như lúc tối nấu dược vật, nướng thịt thỏ bón cho Độc Nhĩ Kha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó lại thay nước dược vật vào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">---</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nước vừa đổ vào thì Độc Nhĩ Kha tỉnh lại hắn vặn vẹo thống khổ mở mắt ra nhìn lão già lưng còng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khụ khụ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già lại ho sắng lên liên hồi mới dần bình tĩnh lại nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tiểu tử, dược vật này có rất nhiều thảo dược quý hiếm có tác dụng cường thân kiện thể, bách độc bất xâm nếu như ngươi có thể qua được giai đoạn này thì sau này ngươi sẽ có thành tựu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc nhĩ kha vẻ mặt kiên nghị gật đầu, hắn muốn nói cũng không nói được bởi vì đang bị bịt miệng, hắn sợ nếu bỏ ra hắn sẽ hét toáng lên, bởi vì được vật hôm nay ngày một mạnh hơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già cũng không nói gì quay người lại, trở lại móc từ trong nhẫn trữ vật ra một đống thảo dược rồi lão lại nấu tiếp được vật, lần này lão nấu rất nhiều, cho vào từng cái thùng gỗ đã chuẩn bị sẵn rồi bịt lại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão lấy một viên nham thạch đánh dấu số thứ tự rồi mới ngẩng đầu lên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tiểu tử, ta ở đây đã nấu 6 thùng dược vật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đánh dấu thứ tự từ một tới bảy, trong một tuần ngươi dùng một thùng theo thứ tự.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Hiện giờ ngươi đang ngâm dược vật bảy ngày sau phải thay nước dược vật khác.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Kha ánh mắt đỏ ngầu nhìn lão già đầy cảm kích.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tiểu tử, ngươi không vội cảm kích ta, ngươi qua được bốn chín ngày này rồi ngày sau cảm kích ta cũng không muộn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Tiểu tử, lão già ta phải đi giải quyết một số việc, đây là nhẫn trữ vật của ta bên trong đã xóa ấn kí, ngươi chỉ cẩn trích vào một giọt máu là dùng được, bên trong có thịt thỏ ta đã chuẩn bị sẵn, khi nào đói ngươi hay lấy ra ăn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngoài ra còn một ít đồ dùng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Kha ngơ ngác nhìn lão già dặn dò như đang để lại di ngôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lòng hắn hỗn loạn, hắn nhất thời không biết phải làm gì.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy hắn ngơ ra lão già ài một tiếng thở dài lấy ra một cái kim châm chích một giọt máu của hắn rót vào nhẫn trữ vật, nhất thời Nhẫn Trữ Vật lóa lên, sau đó trong đầu Độc Nhĩ Kha hiện lên một thứ gì đó, hắn cảm nhận được liên kết với cái nhẫn kia.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến đây lão già đeo cái Giới Chỉ kia vào tay hắn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Bảy ngày sau ngươi có thể đi lại được một chút, nên ra ngoài thay nước dược vật được rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lão già ta còn có chút việc rắc rối cần giải quyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta đi đây.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già lưng còng không đành lòng bước đi, nhìn bóng lưng lão Độc Nhĩ Kha cảm giác bất an trong lòng, hắn cắn chặt răng để mặc cơn đau hành hạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già ra khỏi hang thi triển một vài thủ đoạn lấp bớt miệng hang và dùng cây cỏ rậm rạp che lại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thời gian còn lại của ta không còn nhiều”.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khụ Khụ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Việc gì nên tới cũng sẽ tới, cũng nên giải quyết một ít rắc rối rồi” Lão ngẩng đầu nhìn về phía bầu trời Tây bắc xa xăm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nơi đó không phải là quê hương đầy đau khổ của lão sao, không phải ngày đó lão vượt vạn dặm đào thoát vượt rừng U Minh chạy tới Thanh Đàm Thành sao.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão có chút không cam lòng, không cam lòng vì thù còn chưa báo mà mạng già này đã sắp lìa thế gian.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão Thiên, Thuận Thiên Môn – Ta hận ngươi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói rồi hai mắt lão đỏ hoe, lão điên cuồng xông ra khỏi sơn cốc nhanh như bay..</span></p>', 0, '2022-08-05 01:01:16', '2022-08-05 01:01:16');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(100, 75, 'Chương 5: 5: Tuyệt Cảnh', 'chuong-5-5-tuyet-canh', 5, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một đường điên cuồng phóng ra năm dặm ngoài sơn cốc thì bỗng nhiên một bóng trắng loáng lên chắn trước mặt lão già.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ngả lão bá.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bá bá khiến tiểu điệt tìm thật cực khổ à!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ha ha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long ngươi bớt giả nhân giả nghĩa đi, cần gì phải nhiều lời, ngươi là người như thế nào sao ta không biết chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy thế Độc Nhĩ Long biến sắc hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ngươi biết những gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ha ha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long cái gì của ngươi ta đều biết việc xấu của ngươi người khác không biết nhưng sao qua được mắt lão già ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Những việc thương thiên hại lí của ngươi ta đều rõ hết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhãn thần Độc Nhĩ Long ánh lên vẻ tàn nhẫn nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Vậy thì hôm nay ta sẽ cho ngươi chết, người chết thì cái gì cũng không biết hết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ha ha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khụ Khụ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão già lại ho lên sặc sụa nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Độc Nhĩ Long ngươi giết chết Độc Nhĩ Hà, nhi tử ngươi phế bỏ Độc Nhĩ Kha đến nỗi hắn không qua khỏi nguy kịch phải bỏ mạng nơi rừng sâu nước thiêng, bị dã thú ăn thịt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngươi không bằng cầm thú.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Sao ngươi biết chuyện này?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long biến sắc quát lên uy áp linh tướng đỉnh phong của hắn ép tới làm cho Ngả lão bá càng thêm thống khổ.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lão gằn lên vận công lúc này mới nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Lúc trước ta không biết nhưng bây giờ ta đã biết!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Ngươi… Ngươi lừa ta! Ta giết ngươi!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long chợt tỉnh ngộ đại nộ, hắn vận thổ linh lực hóa thành một chưởng ảnh to lớn lao về phía Ngả lão bá.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Đại Hùng Chưởng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thổ khắc thủy, Ngả lão bá biết không qua khỏi nhưng vẫn toàn lực chống cự lấy chưởng đối chưởng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Uỳnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phụt, phụt phụt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thân hình Ngả lão bá như diều dứt dây</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thân hình lão co giật một hổi rồi nằm bất động.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hiển nhiên lão không thể chống lại một chưởng toàn lực của một linh tướng đại viên mãn trong cơn cuồng nộ đạt mười hai thành thực lực, và lại thổ khắc thủy, tu vi của lão già chỉ có Linh Tướng Hạ Giai làm sao là đối thủ của Độc Nhĩ Long, hơn nữa lão cũng đã bị trọng thương đã bước một chân vào quan tài.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Cứ chết như vậy sao? Ta không để yên cho ngươi đâu!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Long cuồng nộ phóng ra vô số chưởng phong về phía thi thể Ngả lão bá.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhất thời thi thể tan thành vô số mảnh vụn, huyết nhục đầy trời.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một khắc sâu cơn cuồng nộ, Độc Nhĩ Long mới dần tỉnh táo lại, hắn giữ lại bình tĩnh, lẩm bẩm:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Lão già này lúc trước có nói Độc Nhĩ Kha bị phế toàn thân không qua khỏi chết nơi rừng thiêng nước độc bị dã thú ăn thịt, không đáng tin lắm vẫn nên tìm tòi một phen.\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Lão già kia lúc trước phóng ra từ phía kia.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói rồi hắn phóng về phía Ngả lão bá vừa mới lướt qua.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">----</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này Độc Nhĩ Kha vẫn không biết chuyện gì xảy ra bên ngoài, hắn vẫn đang kiên cường chống đỡ cơn đau làm cho hắn chết đi sống lại, khuôn mặt không ngừng vặn vẹo, cơn đau hành hắn cứ tỉnh lại hôn mê, hôn mê rồi lại tỉnh tuần hoàn, mỗi lần tỉnh hắn đều nghiến răng nghiến lợi, hôn mê thì khuôn mặt vặn vẹo trông thật dữ tợn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">…..</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này Độc Nhĩ Long đang đứng trước sơn cốc cẩn thận quan sát.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn quan sát một hồi cũng không thu hoạch được gì, bỗng nhiên hắn chú ý tới một bụi rậm đầy lá cây bên sâu phía trong.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bỗng nhiên hắn mừng rỡ, phóng về phía đó, tới bên cạnh tảng đá chỗ bụi cây gần cái cửa hang của Độc Nhĩ Kha hắn đưa tay ngắt lấy một cây thảo được màu nâu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Thật không ngờ đi chuyến này lại có thu hoạch bất ngờ, ha ha ha, trời cũng giúp ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ha ha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng Thổ Linh thảo, thượng phẩm linh thảo, ha ha, thật không ngờ linh thảo này không có ma thú thủ hộ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thật ra không phải ngẫu nhiên mà là hắn may mắn vì sơn cốc này nhỏ hẹp không có gió lùa vào, ma thú không thể ngửi được mùi được thảo nên cũng không thể thủ hộ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Điên cuồng một hồi, Độc Nhĩ Long mới xoáy người rời đi, nhờ thế mà Độc Nhĩ Kha mới thoát khỏi một mạng, nhưng hiển nhiên hắn không biết điều này, hắn vẫn hôn hô mê mê trong thùng nước dược vật.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">….</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thời gian dần trôi đi, thấm thoát đã tới buổi chiều, lúc này Độc Nhĩ Kha cũng đã tỉnh lại chỉ là rụng hắn réo lên ột ột, hắn nhăn mặt, cảm giác bất lực, hắn cố nâng cánh tay rã rời của mình lên, cơn đau lại kéo ra khiến hắn quằn quại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">từng chút từng chút, hắn không bỏ cuộc, nhưng được một nửa đoạn đường thì cánh tay buông xuống, hắn vẫn không từ bỏ lại tiếp tục nâng lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vẫn như cũ một nửa đoạn đường thì nó lại không nghe điều khiển của hắn buông thõng xuống.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Gân xanh trên mặt Độc Nhĩ Kha hằn lên, hắn quyết không từ bỏ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“ta phải sống sót, ta phải kiên cường, ta phải chiến đấu tới cùng, ta phải báo thù, phải báo ân, ta quyết không khuất phục lão thiên.” Nghĩ rồi hai mắt hắn đỏ hoe lại tiếp tục nâng cánh tay đeo giới chỉ lên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">… một lần, hai lần… mười lần… hai mươi ba lần… ba mươi sáu lần.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tới lần thứ ba mươi sáu hắn rốt cuộc cũng thành công.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn cố gắng rướn ngón tay khô gầy của mình ra kéo cái khăn bịt miệng ra, bàn tay co lại làm cho con đau lại thêm đau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn cắn răng cuối cùng cũng kéo được ra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hộc, hà hà.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thở ra một hổi hắn mới nhìn chiếc giới chỉ trên ngòn tay giữa của mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện hắn muốn làm bây giờ là phải lấy đồ ăn trong nhẫn trữ vật ra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tâm thần chìm vào trong giới chỉ hắn đưa tay bắt lấy một miếng thịt thỏ nướng đưa lên miệng cắn một miếng, nhưng không chờ hắn nhai thì cơn đau ập tới làm cho hắn không khỏi đơ miệng, miếng thịt cũng phải rơi ra ngoài, chìm vào trong thùng nước.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lòng sầu não không thôi, hắn cố gắng giơ tay giữ nguyên cho nó không bị nhúng nước, cơn đau qua đi hắn lại tranh thủ ăn một miếng thịt thỏ, vừa mới nuốt vào bụng thì cơn đau lại ập tới hắn đành cố gắng cưỡng lại cái bụng ham muốn, chờ cơn đau qua đi mới cắn miếng nữa, cứ như vậy lặp lại tận khi mất nguyên gần một canh giờ hắn mới ăn xong cái đùi thỏ nướng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn còn đang thỏa mãn thì những con đau dược vật thấm vào lại hành hạ hắn chết đi sống lại, hắn vẫn cứ kiên cường cắn răng chịu đựng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">….</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại một buổi chiều qua đi, khi trời tối thì tiếng rống cú hung thú rừng U Minh vang lên từng hồi, tiếng sói tru vọng vào làm cho Độc Nhĩ Kha tỉnh lại, cái bụng hắn lại ột oạt, hắn cười khổ tiếp tục gắng gượng nâng cánh tay giới chỉ lấy ra một miếng thịt thỏ nướng ra ăn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngày lại qua ngày thấp thoáng đã qua ba ngày cứ thế buồn tẻ lặp đi lặp lại, nhưng thời gian này đối với Độc Nhĩ Kha không khác gì địa ngục.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thương thế trên người hắn dần lành lại, kinh mạc đã ổn định lại, một chút linh khí yếu ớt đã phục hồi một chút.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Da dẻ hắn bây giờ ngăm đen nhìn như màu cổ đồng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhìn rất là khỏe khoắn, khổ nỗi nhìn dáng vẻ vặn vẹo đau khổ của hắn thật thương cảm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi trưa nay con đói lại đánh thức hắn tỉnh dậy, đầu tiên hắn lấy ra từ trong nhẫn trữ vật ra một ra một cái đùi thỏ, ngấu nghiến ăn, cảm nhận tinh tế thương thế đang chuyển biến tốt đẹp hắn mừng thầm, nhưng mừng thì mừng tứ chi của hắn hoạt động còn rất gắng gượng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng khi thỏa mãn cơn đói thì một vấn đề phát sinh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cái vấn đề này ai cũng vội, đó là hắn rất ‘buồn’ đi tiểu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng khổ nỗi cái thùng gỗ chỉ vừa một người lại cao những thước sáu hắn lại bất tiện làm sao mà ra nổi mà giải quyết nỗi buồn đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn tự giễu mình không thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không lẽ phải đi ngay ở đây sao? Nghĩ đến đây hắn thấy sầu khổ không thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại vấn đề khác phát sinh là khi hắn còn buồn hơn thì sao không thể cứ vậy mà tống ra ngoài như thế được, thật là tội lỗi mà”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Thôi mặc kệ! ta đã chịu bao nhiêu thống khổ có thêm chút muộn phiền này thì đã có sao”</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ vậy hắn quyết định thì một dòng nước nóng chảy ra, lướt qua háng hắn làm cho hắn đỏ mặt không khỏi nín thở một hồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng khi nín rồi thì sao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn vẫn phải thở, bởi vậy hắn phải hít vào, nhưng mà vẫn không có gì ngoài mùi thuốc nồng nặc như trước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có lẽ hắn tống ra ít qua nên so với cả một cái thùng đầy dược vật này chẳng thấm vào đâu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn cười khổ không thôi!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thực ra Độc Nhĩ Kha hắn cũng không cần lo điều này là bởi vì trong dược vật Ngả lão đã sớm dự tính chuyện này nên đã chế ra một ít dược vật khử trùng tiêu uế rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho dù hắn có đại tiện đi nữa thì cũng chẳng có mùi thối đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chẳng qua nó chỉ nổi lên một chút trước mặt hắn rồi dần dần bị dược vật khử tiêu đi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">…</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấm thoát đã tới ngày thứ năm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đúng là ngày mà hắn buồn đại tiện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hắn luống cuống không thôi, tình cảnh xấu hổ này còn hơn nỗi xấu hổ trước kia bị người ta cười nhạo phế vật.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng mà tình cảnh thế hắn cũng đành phải chấp nhận thôi, không tống nó ra thì hắn không chịu nổi, đó là biểu hiện sinh lý, hắn đã năm ngày không ra thì cũng khá lắm rồi, người thường không luyện công thì một hai ngày là phải cho ra rồi, hắn thân thể được rèn luyện, luyện được tới những “Linh Đồ Hạ Giai ” rồi đó chứ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thế là ục, ự.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn có rặn thì một cục màu vàng vàng nổi lên trước mặt hắn, hắn không dám nhìn thẳng nhắm mắt lại bế khí, ự, lại một cục nữa nổi lên, nhưng trước mặt hắn chỉ có một cục mới này, cục trước đã bị dược vật khử tiêu rồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ự ự ự, liên tiếp ba phát bắn đại bác, hắn xấu hổ không thôi, ăn nhiều chi vậy để đến tình cảnh xấu hổ này.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn thở mạnh một phát hít vào một hơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai mũi nheo lại khuôn mặt hiện lên vẻ kinh khủng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đây là tâm lý hắn chuẩn bị đón nhận “thành quả” của hắn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng để Độc Nhĩ Kha thất vọng là hắn không ngửi được cái mùi kinh khủng đó, vẫn chỉ là cái mùi dược vật mà thôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Kha mạnh dạn mở mắt to lên nhìn, hắn chỉ nhìn lướt rồi quay phắt ra chỗ khác.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không có?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không phải chứ?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Kha quay đầu lại nhìn lại thực sự không có cục nào, hắn quay đầu lại tìm kiếm cũng không thấy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mùi cũng không thấy vậy thì nó đi đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Chẳng nhẽ dược vật này tiêu uế ư?”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vẫn còn may là lão tiền bối đã nghĩ sẵn chuyện xấu hổ này cho ta”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không biết lão tiền bối lúc này thế nào rồi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Lão tiền bối thần bí này không biết có lai lịch gì mà chế ra những được vật hiệu nghiệm với thương thế của ta vậy.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chỉ còn hai ngày nữa là ta có khả năng hồi phục tứ chi, có thể chậm rãi đi lại được rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiền bối ơn này ghi tạc trong lòng nguyện dùng tính mạng này báo đáp công ơn!”</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">…</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại là hai ngày qua đi, khu rừng này hàng ngày cũng có những dong binh đoàn nườm nượp vào săn bắt ma thú, người thì tìm kiếm linh thảo, tìm kiếm báu vật, cơ duyên… nhưng mà vào chỗ sâu sơn cốc này rất ít ai tiến tới.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôm nay là ngày Độc Nhĩ Kha phải thay nước dược vật, hắn tứ chi đã hồi phục được rồi, có thể bước đi chậm rãi rồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc Nhĩ Kha chậm rãi khéo chân phải lên, hắn chủ ý muốn chậm rãi bước ra khỏi cái thùng gỗ này nhưng mà cái thùng gỗ này cao lắm chỉ có một người vừa thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn làm được sao, chân truyền lại cảm giác tê dại, có vẻ lâu không hoạt động rồi nên hắn vẫn chưa điểu khiển hẳn nổi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy rằng hắn không thể bước qua thùng gỗ nhưng hắn vẫn cứ luân phiên nhắc chân lên để tập dần thích ứng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một canh giờ qua đi hắn đã lấy được phần nào cảm giác với đôi chân.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc này hắn lại tốn một canh giờ làm cho đôi tay thích ứng như trước.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xong rồi, hắn mới xịn tay vào khối nham thạch sát vách, rất may là cái thùng này để gần vách đá.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn dùng lục kéo cho cái thùng gỗ nghiêng ra nhất thời dịch thủy dược vật trào ra làm cho nền động nhanh ướt nhẹp, cũng nhờ thế hắn mới bước được ra khói cái thùng gỗ này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cái thùng gỗ này dài thước sáu so với chiều cao của hắn là thước tám thì chỉ thấp hơn cái đầu, Ngả lão cũng đóng một thanh gỗ dẹp chặn lại cổ hắn để chống đỡ thân thể tàn tạ của hắn để hắn mãi đứng thẳng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">…</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hít hà.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cố gắng hít một hơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn cố gắng tận hưởng không khí trong động, tuy chẳng khác gì trong thùng thuốc bởi vì mùi nước thuốc tràn ra đầy động nhưng mà khi thân thể giải thoát hắn không thể không mừng như điên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Lão thiên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta cuối cùng cũng không chết, lão hãy chống mắt lên mà xem, ta sẽ trở nên cường đại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn ngửa lên trời gào thét nửa ngày lúc này mới phất hiện thân thể còn suy yếu, bộ dạng trần truồng, không khỏi xấu hổ không thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng xấu hổ thế mà cái tên Độc Nhĩ Kha chết tiệt này cũng không buồn chịu mặc quần áo, bởi vì hắn lúc này tràn đầy cảm giác hưởng thụ cái thân thể khỏe mạnh của mình, tràn đầy lực lượng, da dày thịt chắc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">“Không biết một tuần ngâm dược thủy tiếp theo sẽ cho ta lợi ích gì”.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến đây Độc Nhĩ Kha nhìn quanh hang động một chút, hang động lúc này chỉ lồm đốm tia sáng lách qua mày khe hở của lá cây chiếu vào, còn lại toàn màu tối.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn cũng nhìn thấy bên cạnh cái thùng gỗ có một cái thùng gỗ thấp nữa chứa nước suối.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn biết đây là vị tiền bối kia đã chuẩn bị sẵn cho hắn ngâm do lo ngại hắn đi lại khó khăn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ đến đây hắn cảm động không thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiền bối, không biết ngài bây giờ ra sao rồi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Độc Nhĩ Hàm, Độc Nhĩ Long hai cha con súc sinh ngươi, thủ này ta sẽ trả ngươi gấp trăm ngàn lần.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hãy đợi đấy!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai nắm tay siết chặt hai mắt đỏ lừ, hắn hung hăng oán hận một hồi..</span></p>', 0, '2022-08-05 01:02:44', '2022-08-05 01:02:44');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(101, 76, 'Chương 1: Người Khách Lạ', 'chuong-1-nguoi-khach-la', 1, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy mới vào khoảng đầu giờ Dậu, nhưng về tiết mùa đông, trời đã nhá nhem</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tối Các nhà, các hàng xén ở phố Từ Sơn đều đóng cửa. Chỉ trừ một hàng cơm là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">có ánh sáng. Và luôn luôn ở trong đưa ra tiếng cười nói ầm ỹ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một trang thiếu niên ky sĩ, từ phía Nam tới, kìm ghì cương ngựa trước cửa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hàng. Chàng y phục nai nịt gọn gàng, đầu đội nón lông đen, chân đi hia chẽn, cỗ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quấn một cái khăn lụa trắng thắt lỏng, mối bỏ rủ sau lưng. Con ngựa của chàng sắc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nâu xẫm, và tuy nhỏ thon, nhưng có dáng mạnh mẽ và khỏe dai. Trời lạnh buốt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">căm căm mà mình nó ướt đẫm mồ hôi, xem đó đủ đoán biết chủ nó đi từ xa lại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thế mà mũi nó hục hặc thở ra hai luồng hơi khói, bốn gió nó cuốc xuống đất như</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gõ nhịp, để tỏ ý muốn là còn thừa sức chạy một thôi dài nữa. Ky sĩ lấy tay vỗ khẽ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vào cổ ngựa nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hãy thong thả, tuấn mã, đi đâu mà vội thế? Thầy trò ta nghỉ chân ăn lót dạ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng vừa toan xuống ngựa, thì mắt chàng ngẫu nhiên để tới một cái bảng gỗ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vuông quét vôi trắng, trên viết những chữ nôm thật to, to đến nỗi trời đã gần tối</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hẳn mà chàng còn lỗ mỗ đọc được...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng giữa lúc đó, sau một tiếng hét của con ngựa thì tiếng ồn ào cười nói</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trong hàng cơm im bặt. Rồi cánh cửa hé mở, một người thò đầu ra hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ai?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không trả lời, thanh nhiên võ sĩ nhanh nhẹn nhảy ngoắt xuống đất, kéo và vuốt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">áo cho hết nếp răn vì lúc ngồi trên yên, chàng đã giắt vạt cả vào trong chiếc giây</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lưng điều. Đoạn, thong thả, dõng dạc, chàng bảo người vừa hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chú còn đợi đến bao giờ mới ra giắt ngựa của ta vào tàu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ý chừng cho là lời nói đó hỗn xược, người kia mở mạnh cánh cửa bước ra</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đường đứng phưỡn ngực chống tay vào sườn, gật gù hỏi lại:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Anh có biết tôi là ai không mà dám nói xách mé như thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người khách mới đến mỉm nụ cười khinh bỉ thản nhiên đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- à, ra tôi lầm? ý chừng chú không phải là chủ quán hay người nhà hàng cơm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi, sau khi đã buộc giây cương vào đầu một cái kèo ở mái hiên, chàng đi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thẳng vào trong hàng, lớn tiếng gọi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chủ quán?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe tiếng chàng sang sảng, ngắm thân thể chàng cao lớn, mạnh mẽ, nhất lại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nghe lách cách cái vỏ kiếm chàng đeo bên sườn đập vào ngưỡng cửa, người chủ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hàng cơm hốt hoảng chạy ra khúm núm chào và lễ phép hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- B ẩm quan lớn truyền . . . ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sắp rượn làm một con gà nhắm. Đoạn ra giắt ngựa vào tầu cho nó ăn thóc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bốn người ngồi quây quần đánh tam cúc ở một cái phản bên dương mắt ngạc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhiên, yên lặng nhìn. Người ở ngoài đường quay vào nói to bảo bọn họ, có lẽ cốt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">để võ sĩ vừa tới nghe rõ :</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lệnh trên ban xuống cho anh em mình canh phòng ở đây, các chú phải cẩn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mật, nếu vô ý để phạm nhân trốn thoát thì đầu anh em mình chẳng còn đâu!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dứt lời, bác cai - vì bọn đó là một viên cai và bốn tên lính - trèo lên ngồi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chểm chệ trên một cái phản cao kê liền bên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người trẻ tuổi mới đến tò mò đưa mắt quanh phòng rồi mỉm cười hỏi bọn lính:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Này các chú, có xảy ra việc gì mà việc canh phòng cẩn mật thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một người lính trỏ lên cái bảng cũng giống cái bảng treo ở ngoài cửa mà nói</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">răng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đọc đó sẽ hiểu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong khi người khách lạ đứng lên cái ghế đẩu vuông để xem bảng, thì viên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cai và mấy chú lính nháy nhau thì thầm:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Khả nghi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đề phòng?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi hỏi tín bài hắn nhé?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hãy thong thả. Nhưng phải luôn luôn để ý đến hắn. Mà khí giới để đâu cả?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Để cả dưới gầm phản.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đeo dao ngay vào thắt lưng. Còn mã tấu, đoản kích thì của người nào, người</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ấy đặt ngay bên mìn để phòng biến. Mà se sẽ chứ chẳng hắn biết. Nghe chừng hắn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ta cũng không phải tay vừa đâu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người trẻ tuổi vẫn đứng trên ghế lẩm nhẩm đọc:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Quan trấn thủ trấn Kinh Bắc Nguyễn Mỗ có lời yết thị như sau này:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Từ khi đức Thái tổ Võ Hoàng đế đánh đuổi quân Tôn SĩNghị chạy như đàn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chuột về Tàu, và trừ tiệt giặc giã quấy nhiễu trong nước, thì trăm họ đều đươc an</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cư lạc nghiệp. Nhưng gần đây một tên ngông cuồng dấy loạn để làm rối cuộc thái</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bình, nhân dân đầu ghét, đều oán coi như kẻ thù chung. Tên ấy là Nguyễn Đoàn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người hạt Từ Sơn. Than ôi! Lưới trời tránh sao cho thoát, nhờ oai hoàng đế, bản</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chức đã bắt sống đươc tên phản quốc và đã hành hình nó rồi. Song dư đảng bọn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">giặc còn luẩn quẩn vùng này, mà trong bọn có một tên rất nguy hiể ưm ấy là tên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái tức Phạm Phụng con tên nghịch tặc Thạch Trung Hầu, quán thôn Yên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thi xã Yên Thượng, huyện Đông Ngàn, phủ Từ Sơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Phạm Thái vào trạc hai mươi tuổi, người vừa tầm, nhưng rất khỏe, rất giỏi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">võ Dưới đây có phác bức hình, hoạ theo những lời trình bày của các thám tử. . .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ai bắt sống đươc Phạm Thái sẽ thưởng tiền một nghìn quan. Ai giết chết hắn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sẽ thưởng tiền năm trăm quan.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Nhận đươc giấy yết thị nầy, các viên phân tri phải dán ở các ngã ba cùng cá</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tửu quán có đông người lai vãng. Lại cho lính ngày đêm canh phóng ở các nơi mà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quân gian có thể tụ họp đươc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Niên hiệu Cảnh Thịnh, Năm thứ năm, tháng chạp, . ngày mồng ba \".</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ở một góc bảng, có vẽ rất sơ sài diện mạo một người đàn ông và chua mấy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hàng chữ sau này:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Mặt trái soan, miệng rộng, mắt hơi xếch lông mi rậm, tai to, có một nốt ruồi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ở sau tai bên phải \"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng trẻ tuổi bỗng nhiên cất tiếng cười vang. Bọn lính cơ ngạc nhiên nhìn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhau, rồi một người mạnh bạo hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cớ sao đọc tờ yết thị của quan trấn thủ, nhà ngươi lại dám chế nhạo?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng kia quay lại đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không, ta có chế nhạo ai đâu? Ta chỉ tức cười chết đi vì câu: \"Có một nốt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ruồi sau tai phải\". Thiết tưởng nếu tráng sĩ Phạm Thái là người rất khoẻ, rất giỏi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">võ như tờ yết thị đã nói, thì còn ai dám đến gần vạch tai hắn ra mà xem nốt ruồi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thành thử câu chỉ thị đó thực là thừa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dứt lời, chàng lại cười, lấy làm thích chí lắm:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trời ơi? Sao không chua: Phạm Thái có cái mũi ở giữa mặt, có cái cằm ở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dưới mũi, có phải dễ nhận hơn không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Viên cai đứng dậy quát:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Anh không được hỗn xược với quan trấn thủ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng trẻ tuổi vờ nhún nhường đấu dịu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao chú vội giận thế? Nào tôi có hỗn xược gì đâu? chẳng qua bàn một câu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cho vui đấy thôi chứ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa nói chàng vừa bước xuống đất, rồi như quên hẳn câu chuyện vừa xảy ra,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chàng gọi:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chủ quán?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một người to béo mặc áo cánh bông, đầu chít chéo vuông khăn nhuộm nâu,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nặng nề đi lại gần, chắp tay chờ lệnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chủ mi đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm quan lớn? Chủ con đương làm nhắm hầu quan lớn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao rượn ta gọi từ nãy vẫn chưa đem ra?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm, con, tưởng còn chờ nhắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cứ đem rượn ra trước đã.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm có hâm nóng không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không cần. Mau lên?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ? Nhưng ngài sơi rượn gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Rượn Cúc? Mau?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tức thì tên hầu rượn vào buồng lấy ra đặt lên bàn một cái nậm và một cái chén</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vại mà hỏi :</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm công tử ngổi giường hay ngồi bàn?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngồi bàn được rồi. Mà sao đèn lù mù thế nầy. Chặp ba sợi bấc vào, rồi đi lấy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thêm dầu rót cho đầy bát.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng trẻ tuổi uống luôn ba chén rượn đầy, chép miệng liếm môi chau mày</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nói lớn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hừ? Rượn thằng cha pha nước lã nhiều quá, uống chẳng mùi mằn gì? .. Quán?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên hầu sợ hãi bước tới, vì hắn đã nghe rõ lời chê bai của ông khách. Nhưng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ông khách ôn tồn hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có mực không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên kia vui mừng đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm công tử, có mực Bắc Hải ngon lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy đi sắp cho ta cái hỏa lò than hồng với lại đem lên đây mươi con mực.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lát sau, mùi mực nướng thơm phức bay khắp gian phòng. Và ông khách</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đã dùng đến nậm rượn thứ ba.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Viên cai bắm bọn lính ra hiệu thì thầm:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thằng nầy tôi xem chừng có lẽ là Phạm Thái. Mặt trái soan thì đích rồi. Lông</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mày tuy không rậm nhưng không thưa. Còn miệng hắn như thế cũng có thể cho là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rộng được.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vâng, có lẽ đích rồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Giá bây giờ ai lén đến sau lưng lật trái tai hắn ta lên xem có nốt ruồi không,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thì mới biết chắc chắn được.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người im lặng nhìn nhau, có ý lo sợ. Viên cai ngẫm nghĩ rồi lại nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Các chú ạ, ta nói phải dùng mưu... Thế này này: Bây giờ ta nói phỉnh cho nó</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">uống thật nhiều rượn. Khi nào nó say mềm, ta chỉ việc trói gô lại là xong.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhỡ nó không phải Phạm Thái.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thì thả nói ra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy được rồi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người trẻ tuổi thấy bọn kia ở ngoài đi vào, nét mặt ai nấy có vẽ bí mật, thì vui</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cười hỏi:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Các chú đi tìm Phạm Thái về đấy à?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Viên cai cơ đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phạm Thái hắn ẩn núp ở nơi rừng rú, chứ dám bén mảng về đây.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phải, hắn ta dại gì mà về đây để người ta bắt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nói dút câu, chàng thích chí cười khanh khách. Rồi chàng nâng chén rượn nốc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một hơi cạn. Viên cai tấm tắc khen:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Giỏi thực? Tửu lượng công tử ít người sánh kịp. Thưa công tử, công tử uống</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nổi mấy nậm?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cái đó tùy? Rượn ngon thì mười nậm cũng là ít. Chứ rượn thằng cha này nhạt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thếch uống chán phè. Nhưng kìa, sao các chú không uống rượn với tôi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe người ta gọi mãi mình bằng chú, viên cai căm tức muốn sinh sự ngay,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhưng lại nghĩ đến mưu sâu đã sắp thành, nên cố nén lòng nhẫn nại và phỉnh thêm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một câu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vâng, rượn này chỉ bọn anh em chúng tôi uống, chứ công tử thì phải sơi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">những thứ rượn hoàng cúc, thanh mai, hay ngũ da bì chính hiệu kia.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng tuổi trẻ cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng thôi, các chú ạ, người quân tử không nên nghĩ đến cái ăn, cái uống tha</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thiết quá Tối nay ta có thứ rượn cúc khổ này của anh quán khổ thì ta hãy tạm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thích nó vậy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ, ngài nói phải lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- vậy bây giờ tôi mừng các chú mỗi người một chén để các chú tỉnh ngủ mà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">canh phòng nhé?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ, đa tạ công tử.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng tuổi trẻ liền đập mạnh cái nậm thiếc xuống bàn gọi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bớ quán?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lấy thật nhiều rượn ra đây để ta thết lính nhà vua.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tức thì chủ quán mang ra đặt lên bàn năm cái chén lớn và một cái bình thiếc to</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gần bằng cái hũ... Chàng trẻ tuổi quay về phía bọn lính:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Các chú lại cả đây cùng uống cho vui.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ, chúng tôi không dám.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thì cứ dám đi mà?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa nói, chàng vừa rót sáu chén rượn đầy. Lúc bấy giờ có lẽ đã cuối giờ Tuất.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phố phủ im vắng. Chỉ thỉnh thoảng có tiếng trống trong phủ và tiếng mõ ở các</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">điếm cầm canh mà thôi. Bọn lính thì thầm bảo nhau:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ta cứ lại xem sao.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhớ mời nó uống thật say nhé?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mà đừng quên dao đấy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng trẻ tuổi cười, nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có thế mà cứ bàn tính mãi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ, chúng tôi xin lại đây.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sáu người vừa uống cạn chén thì phía ngoài có tiếng gọi. Chủ quán ra mở cửa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một nhà sư khoác tấm mền ướt màu nâu cúi đầu chào:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- A-di đà phật?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- A-di đà phật?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà sư đi thẳng vào một góc phòng ngồi xếp bằng trê phản, miệng lâm râm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">như đọc kinh. Viên cai hỏi chàng tuổi trẻ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chẳng hay nhà sư có biết uống rượn không nhỉ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao lại không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng chúng tôi chưa chúc mừng công tử chén nào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa nói vừa rót đầy chén rượn năng lên mời:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- xin chúc công tử vạn sự như ý.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần lượt năm người mời chàng tuổi trẻ luôn năm chén. Thấy chàng uống rượn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">như uống nước lã, bọn lính đưa mắt nhìn nhau. Rồi một người nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bây giờ ta chúc thọ thiên tử một chén.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng tuổi trẻ đáp:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ư, thì chúc thọ thiên tử. Nhưng con gà của ta nó đã mang lên kia.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Uống cạn chén rượn chúc thọ thiên tử, chàng tuổi trẻ lại bàn uống một chén đẻ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mừng cho linh hồn con gà thiến đã được siêu tục qui tiên. Rồi lần lần họ chúc thọ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quan Thái sư Bùi đắc Tuyên, quan Thái uý Phan Côn Hưng, quan Đại tư khấu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trần Quang Riệu, quan Đại tư mã Ngô Văn Sở, quan trấn thủ, quan phân tri. Khi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chúc thọ đén chủ quán thì chàng tuổi trẻ đã say mèm, mặt đỏ gay, đầu lảo đảo lười</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cứng đờ, nói díu lại không ra câu nữa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Này các... chú... nhắm đi chứ... rồi ta còn chúc thọ... nhiều... ử? Sao các chú</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không . . . chúc thọ tôi . . .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ, vậy xin công tử cho biết quý tính phương danh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Quý... tính phương danh à? Tên ta... à... ? Ta là Ngang... tàng công tử... Lê</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Báo... người trấn Sơn Nam.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bọn lính đưa mắt phân trần thầm với nhau rằng đó là một câu nói dối. Nhưng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">viên cai cũng vờ nâng chén rượn chúc tụng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngang tàng công tử Lê Báo thiên tuế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cả sáu người giốc cạn chén rồi cùng cất tiếng cười vang. Ngó thấy nhà sư vẫn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngồi ở cái phản đầu bên kia, co ro, ủ rủ trong chiếc mền nâu, có vẻ rét mướt ốm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">yếu lắm, một chú lính đã hơi chuếnh choáng mời đùa:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sư cụ sơi rượn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà sư có dáng sợ hãi, chắp tay se sẽ đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Xin mời các thầy, bần tăng không dám.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng lúc bấy giờ, chàng tuổi trẻ càng say lắm, không nghĩ đến giữ gìn nữa,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bạ câu gì nói câu ấy. Nhân bàn chuyện đến quan trấn thủ và quan phân tri, chàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vừa cười vừa mắng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dốt? dốt tệ!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chừng đã muốn dở mặt, người cai sừng sộ hỏi lại:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Công tử bảo ai dốt?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dốt? Dốt... cả... lủ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng ai dốt mới được chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Như không nghe rõ câu hỏi, chàng kia nói luôn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hừ? Không dốt... mà lại phải dùng.. chữ nôm. Đời thuở nhà ai... lại yết... thị...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bằng chữ nôm... bao giờ không... Cha mách qué... Đồ mách qué?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người cai sấn đến tận mặt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Anh này bảo ai là đồ máchy qué? Anh phải biết tiên đế đã xuống chỉ cấm tiệt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chữ Ngô... Thi cữ, tờ bối dùng toàn tiếng, toàn chữ nước nhà... Tiên đế làm thế là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">để gây lấy một thứ chữ riêng cho người mình, sao lại dám ngạo mạn, khi quân...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng trẻ tuổi cười phì cả rượn ra:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tiên đế... các... chú à?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Của cả nước Nam, chứ của riêng gì ai?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng kia lại cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không... phải... của... ta... Ta chỉ biết... có nhà Lê.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tức thì viên cai hô lớn một tiếng, bốn tên lính rút cả dao ra chĩa vào mặt chàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tuổi trẻ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng chợt hiểu, đứng dậy lùi lại mấy bước, rút gươm ra đối địch. Nhưng vì</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chàng say quá, chân tay run lẩy bẩy, vừa bị viên cai đẩy mạnh cái bàn vào người</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đã ngã quay ra. Bọn lính cười ầm lên xúm lại toan trói, thì một tiếng hét sau lưng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khiến mọi người kinh hoảng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bay? Không được vô lễ với công tử.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà sư lù rù ban nãy tung cái mền nâu xuống phản đã vụt trở nên võ sĩ lẫm liệt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">oai phong, tay cầm kiếm xông vào. Bọn lính liền bỏ chàng say rượn quay lại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chống cự với nhà sư. Nhưng chống cự sao nổi? Lưỡi kiếm đi đến đâu, máu chảy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đến đấy, chỉ trong khoảnh khắc, năm cái thây đã nằm ngổn ngang dưới đất.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà sư liền giơ kiếm vụt mạnh một cái vào cây đèn dầu lạc cho tắt, rồi xốc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chàng trẻ tuổi sau rượn cắp nách ra sân sau vào tàu giắt ngựa, nhảy vót lên yên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trông thẳng phía tây bắc, phi nước đại.</span></p>', 0, '2022-08-05 01:05:23', '2022-08-05 01:05:23');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(102, 76, 'Chương 2: Bến Lo', 'chuong-2-ben-lo', 2, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trên con đường từ thành Kinh Bắc đến huyện Đa Phúc, buổi sáng hôm ấy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Những người lái buôn nói với nhau câu chuyện sởn gáy, rùng mình.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong bọn có bác gánh một gánh nồi đồng nặng là nhát gan hơn cả. Mới tới</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cầu Dọi, bác đã hoãng hốt hết vía vì một câu chuyện vừa được nghe và nhất định</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đòi trở lại trấn lỵ, không đi nữa. Một cô hàng nồi đất, người bé nhỏ xinh xắn đưa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">con mắt sắc sảo liếc chàng và mỉm cười nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ví bằng bác chẳng chịu hộ vệ chúng em qua khỏi bến đò Lo thì cũng xin bác</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đưa chúng em đến chợ Chờ để chúng em bán mấy gánh nồi đất này chứ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một cô khác cũng chêm một câu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đàn ông các ông mà sợ hãi thế, thì bọn đàn bà con gái chúng em còn đi buôn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bán làm sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một anh chàng trẻ tuổi làm nghề hoạn lợn, tay vác cái cần dài đầu có thòng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lọng, nghe chừng cảm động và mạnh bạo vì những đôi mắt đưa tình, những cặp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">môi tươi thắm. Chàng bàn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thì ta hãy vào trong cầu ngồi nghỉ mệt đã nào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phải đấy, trời mưa bay cũng có vẻ đẹp, nhưng chúng ta chẳng khỏi rét buốt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đến xương. Vậy còn gì hơn là vào ẩn dưới mái cầu, trú mưa một lát.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng bình tĩnh của cô hàng nồi đất trẻ tuổi khiến mọi người phải kính phục</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">và lấy làm thẹn cho cái tính nhút nhát của mình. Vì thế, ai nấy vui vẻ cười nói đi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vào trong cầu, đặt gánh hai bên lan can, kẻ ngồi người đứng, góp câu chuyện gẫu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh chàng hoạn lợn đứng phải chỗ mái cầu giột liền nói đùa:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Rõ may? Đương khát lại với được chỗ có nước.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi anh ta thọc tay vào gánh hàng của cô xinh xắn kia lấy ra một cái niêu nhỏ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hứng nước mưa. CÔ hàng cũng chẳng phải tay vừa:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Se sẽ chứ không vỡ cả của em đấy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người cất tiếng cười vang. CÔ hàng lại tiếp luôn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cười vừa chứ, chẳng lỡ sụt mái cầu thì chết cả lũ bây giờ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc bấy giờ nàng mới kịp lưu ý đến một người đàn ông vào trạc ba mươi ngồi</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bên hai cái tay nải nây to kếch, vẻ mặt lo lắng, sợ hãi. Nàng liền quay lại hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bác gánh vải sợi đi đâu đấy?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người kia se sẽ đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">-Không, cô ạ, tôi gánh tơ sang bán bên chợ Phù Lỗ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- o thế thì hay quá nhỉ, em củng đi chợ Phù Lỗ. Vậy ta phải lên đường thôi,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chẳng trễ quá thì bán cho ai.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi đợi trời sáng hẳn mới dám đi. Nghe nói vùng này loạn lạc ghê gớm lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">CÔ kia cười:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bác cả lo quá, từ khi nhà Nguyễn ta nối nghiệp nhà Lê thì giặc giã còn đâu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cơ chứ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh hoạn lợn gia sản mang theo có mỗi một cái thòng lọng. Vì thế, anh ta</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chẳng lo ngại gì cho tính mệnh anh ta, dù có giữa đường gặp cướp đi nữa. Nhưng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">anh ta càng bình tĩnh bao nhiêu, anh ta lại muốn mọi người lo sợ bấy nhiêu, nên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gặp bác hàng tơ và vài người hàng vải, hàng xén đứng trong cầu, anh ta rắp kể cho</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nghe câu chuyện mà anh ta đã thuật với mọi người trên quãng đường từ trấn lỵ tới</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đó Anh ta liền vờ lên giọng đạo dức bảo cô hàng nồi đất:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- CÔ ác lắm. Nói thế nhỡ bác ta tưởng thực thì sao? Không đâu, bác ạ, vùng ta</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhộn lắm kia đấy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bác hàng tơ run lập cập:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vâng, tôi cũng nghe thấy người ta đồn thế. Hình như tối hôm qua, quân của</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn Đoàn hạ... mất Phủ Từ Sơn rồi thì phải.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">CÔ hàng nồi đất cười ròn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nguyễn Đoàn bị xử tử rồi còn đâu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh hoạn lợn bĩu môi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Xử tử hắn, nhưng dư đảng của hắn liệu có xử tử hết được không? Phạm Thái</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">còn đáng ghê sợ gấp mấy Nguyễn Đoàn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">CÔ hàng, giọng khinh bỉ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng đảng họ chống cự với vua Tây Sơn, chứ có phải bọn giặc cướp tầm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thường đâu mà đi bóc lột bọn con buôn như chúng mình.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không? Cứ một việc xảy ra ở một tửu quán phố Từ Sơn tồi hôm qua cũng đủ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hiểu bọn họ chỉ là một bọn giết người lấy của.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người hàng tơ nhớn nhác hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy à? Câu chuyện thế nào, bác cho chúng tôi nghe với.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh hoạn lợn ra bộ ta đây thành thạo việc đời, hắn giặng hai, ba tiếng lấy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">giọng rồi kể:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nguyên tôi có người anh con nhà bác đóng đội cơ tại dinh quan Hiệp trấn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kinh Bắc. Tối hôm qua, tôi ở chơi đằng nhà bác đội. Cơm nước xong đi ngủ như</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thường...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">CÔ hàng nồi đất cười và ngắt lời:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bác nên kể tóm tắt thì hơn. Chúng ta còn phải lên đường chớ. Kìa bác coi, đã</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sáng rõ rồi, phương đông đã đỏ ửng ánh sáng mặt trời. Mưa thì cũng đã tạnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh chàng kể chuyện liếc cô một cái rất tình:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao mà cô táo cấp thế? Vâng thì kể tóm tắt. Chúng tôi đi ngủ... Vào khoảng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cuối giờ Sửu sang đầu giờ Dần, có tin cầp báo ở Từ Sơn đưa lên. Một người lính</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cưỡi ngựa truy phong phi thẳng vào dinh quan Hiệp trấn báo có giặc ở Từ Sơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên giặc ấy chẳng phải ai xa lạ, chính là Phạm Thái tức Phạm Phụng mà quan</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quân vẫn tróc nã bấy lâu nay. Đầu đuôi việc ấy như thế này: Chập tối, một nhà sư</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vào một hàng cơm ở phố Từ Sơn ngủ trọ để sáng hôm sau lên chùa Phật tích sớm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà sư đem theo nhiều đồ thờ quý giá lắm, ấy là không kể tiền bạc gói chặt trong</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một chiếc mền nâu. Tức thì Phạm Thái đã nấp sẵn đâu đó tiến theo vào liền sau.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn ăn vận chững chạc, nai nịt gọn gàng; khi lính canh trong tửu quán hỏi tín bài</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thì hắn giơ ra một cái tín bài giả cũng có khắc bốn chữ \"thiên hạ đại tín\" y như</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trong những tín bài của triều đình. Nhưng tên tuổi quê quán thì hắn chu man là Lê</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Báo, người trấn Sơn Nam. Tuy thế lính vẫn chú ý đề phòng vì thấy hắn ta có dáng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khả nghi lắm. Quả không sai. Vào khoảng gần nửa đêm, tên Lê Báo giả tứ Phạm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thái, chém giết hết bọn lính đông đến ngót ba chục, rồi cướp nhà sư với các bảo</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vật kim ngân nhảy phắt lên ngựa trốn mất. Bây giờ nghe đâu hắn ta vẫn còn lẩn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quất ở vùng ta thì phải.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bác bán tơ run như cầy sấy, hỏi lại:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hắn ta còn lẩn quất ở vùng nầy?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">CÔ hàng nồi đất cất tiếng cười khanh khách nói bông:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bác hoạn lợn kể chuyện hay nhỉ, nghe như truyện Tam Quốc vậy. Nhưng anh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">em chị em đừng tin bác ta, cứ mạnh bạo lên đường là hơn hết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dút lời, nàng nhanh nhẹn quẩy gánh và giục mọi người ra đi. Muốn ai nấy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quên câu truyện ghê gớm nàng cất tiếng hát một bài quan họ có ý tứ bỡn cợt, trêu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ghẹo, lẳng lơ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Đêm qua em mơ gặp chàng,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đôi ta tạc nghĩa đá vàng cùng nhau.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Uớc gì anh trước em sau,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đi chợ Phú Lỗ trao cầu kết duyên \".</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hát xong, nàng lại cười ròn như nắc nẻ, anh hoạn lợn nghe nàng nói đùa bác</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bán tơ thì tỏ ra vẻ tức tối, hậm hực. ý chừng nàng cũng biết vậy, nên lại hát luôn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">câu nữa để lấy lòng anh ta.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh thợ hoạn ơi,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Anh về gánh đất nặn nồi,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Để em đem bón cho người nấi cơm \".</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Những người đàn ông ôm bụng cười ngất. Mấy cô bán hàng nồi đất xấu hổ đỏ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mặt. Nhưng ai nấy hầu như đã quên bẵng câu chuyện cướp bóc và đề cất gánh</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lững thững đi theo cô hàng láu lỉnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc bấy giờ, mưa bay đã tạnh hẳn và mặt trời đã ló trên phía thành quách,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chiếu xuống một làn ánh sáng dịu dàng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bọn lái buôn vừa trai vừa gái, vừa nhà quê vừa thành thị độ hơn mười người,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gồng nánh, đội vác đi trên bờ đê nhỏ hẹp, bên con sông Dọi, nước về mùa đông,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hầu cạn hẳn. Họ xúm xít đi sát vào nhau, hình như để đỡ lo sợ. Lòng lo sợ gầy nên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quang cảnh quanh vùng, tiêu điều, xơ xác, với những cây trơ trụi, khẳng khiu, trên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">những mồ đất rải rác trong một cánh đồng rộng đầu nước, bát ngát, mênh mông</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trắng xóa tới tận rặng tre xanh xa tắp.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đi nửa giờ tới huyện lỵ Đông Ngàn. Viên phân tri đã nhận được giấy sức tróc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nã phạm nhân ngay từ buổi sớm tinh sương, nên sự canh phòng ở đây rất cẩn mật.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Viên phân xuất thân ra cổng huyện xem xét dỹ lưỡng tín bài. Song chỉ một mình</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">anh hoạn lợn là phải giữ lại, vì tín bài của anh thiếu điểm chỉ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trước khi đến chợ Chờ, ai nấy còn phải dùng bước ở một cái quán con lợp cói,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mà người ta gọi là cầu Chờ, để làm việc thiện: Nghĩa là lần lượt mỗi người kính</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cẩn đến trước cái nong, để trên mặt đất, trong có cắm mấy nén hương, và bỏ vào</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đó từ mười tới ba mươi đồng tiền trinh, tuỳ theo tài sản từng người mang theo. Ai</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dáng cúng hai mươi đồng mà chỉ cúng mười đồng thì sẽ gặp sự nguy biến ngay.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì đâu có sự cúng tiền như thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyên ở vùng ấy, trong vòng ba, bốn năm, một bọn cướp nhũng hiếp tróc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhân dân hai bên ven sông Cà Lồ, nhất là ở gần bến đò Kim Lũ, Quan hiệp trấn đã</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cho quân lính về tuần phòng nhưng vẫn vô công hiệu. Đến nỗi bến đò Kim Lũ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khách vãng lai đã đặt cho cái tên \"bến Lo\". Mà cũng vì thế mới tên \"cầu Chờ\": Ai</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ai đi đến đấy đều phải nghỉ lại để chờ cho thực đông người mới dám cùng nhau</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sang sông.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một đêm, một nhà sư qua đò. Bọn cướp đến vây bắt, bị nhà sư đánh một trận</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chạy tán loạn. Rồi luôn mấy đêm liền, nhà sư vô danh kia đem đồ đảng đến Kim</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lũ trừ tiệt bọn cướp. Từ đó, nhân dân đi lại được như thường và sự thương mại đã</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">kém sút lại trở nên phồn thịnh như xưa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng đảng cướp vừa trừ xong thì một tục lệ bỗng thành lập, không rõ do một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tay kỳ dị nào. Người ta chỉ biết rằng ai đi đến cầu Chờ mà không lễ phép bỏ vào</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cái nong một số tiền để cúng nhà chùa thì sẽ bị bóc lột một cách chắc chắn khi qua</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bến đò. Cúng vào chùa nào, và bị ai bóc lột? Còn ai biết. Hình như có một dảng bí</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mật nã tiền để làm việc gì to tát... Vả thấy được yên ổn làm ăn buôn bán, ai ai cũng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vui lòng nộp số tiền phải nộp, không hề ta oán, kêu ca hay thóc mắch báo quan,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hay tò mò tìm biết tung tích người đứng thu thuế một cách trái phép như thế.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người ta đồn rằng - nhưng đó chỉ là một lời đồn phỏng - quan trấn thủ, quan</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hiệp trấn và viên phân tri Đông Ngàn đã rõ có sự nã tiền ấy, song vì không ai tố</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cáo, nên cũng làm ngơ để khỏi lôi thôi đến mình: Các quan cũng thừa biết trong</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hạt có một đảng bí mật rất đáng sợ, kiêng nể, nên các quan định sẽ dùng mưu kế,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chứ không thể dùng sức mạnh mà trị được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bọn lái buôn trên kia khi đến cầu Chờ đã biết sự lệ. Ai nấy đặt gánh cởi hầu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bao, khi thấy cô hàng nồi đất vui vẻ bỏ vào nong năm mươi đồng tiền trinh mà nói</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">răng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Anh em ạ, việc nghĩa ta nên rộng rãi một chút.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cố nhiên nhiều người lấy làm khó chịu về cử chỉ của nàng, nhưng không ai</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dám cúng ít, vì phần sợ xấu hổ với cô hàng nồi đẹp đẽ, phần lo đảng bí mật trị tội.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ở khắp vùng, họ đồn đại rằng tuy chung quanh nong tiền không có người trông</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">coi, nhưng chẳng một cử chỉ, chẳng một lời nói của khách qua đường là không bị</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một người nấp đó ghi chép.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Làm xong việc thiện - việc thiện miễn cưỡng - bọn lái buôn vội vã gánh gồng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hoặc đến chợ Phú Mẫn bán hàng, hoặc đến Kim Lũ để qua đò sang sông. Một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mình cô hàng nồi đất còn ngồi lại nghỉ mệt một lát.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bỗng nhớn nhác trông trước trông sau không thấy ai, nàng hắng dặng một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tiếng. Tức thì ở cửa buồng nhỏ bên cạnh, tường vách xiêu đổ như một nơi bỏ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hoang, một chú tiểu thò đầu ra giơ một ngón tay trỏ lên trời rồi lại biến mất.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">CÔ hàng liền rẽ bờ ruộng vào làng Ngô Xá, vừa đi vừa rao: \"Có ai mua nồi đất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ra mua?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hình như nàng cũng không thiết gì bán, và chỉ rao lấy lệ mà thôi, vì nàng đi rất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mau. Tới chùa Tiểu Linh Quang, nơi tu hành của các ni cô, nàng đã ngần ngừ toan</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vào, nhưng lại đi thẳng, coi bộ như có việc gì cần kíp lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lát sau, nàng dừng bước ở cổng chùa Liên Đài làng Nghiêm Xá, một ngôi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chùa nhỏ không có chi khiến ai lưu ý tới. Cỗng chùa đóng, nàng ghé mồm vào bên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">giậu rau lớn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Ai nồi đất ra mua? Trong chùa có mua nồi đất không?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đáp lại lời rao hàng, một đàn chó vừa sủa vừa chạy ùa ra. Đi liền theo sau, một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chú tiểu y phục tuy màu nâu sồng, nhưng hẹp chẽn gọn gàng. Vuông khăn nâu chú</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chít chéo trên đầu tựa cái đầu mâu của chiến tướng đời cổ khi ra trận càng làm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tăng vẻ mảnh liệt của chú lên lắm. Chú cầm cái roi tre đuổi đàn chó to lớn, dữ tợn,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rồi ra mở cổng mà hỏi rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cái gì thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- A di đà Phật. Nà chùa có mua nồi không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chú tiểu nhìn cô hàng nồi, nói khẽ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có, vào nhà oản ngồi chờ một lát.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">CÔ hàng có dáng lo lắng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không thể ngồi chờ được, việc cần kíp lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng ông sư đã truyền không ai được lên chùa trên trong khi sư ông làm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">việc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">CÔ hàng giọng quả quyết nhắc lại:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không thể chờ được.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy để tôi lên chùa xem.</span></p>', 0, '2022-08-05 01:06:26', '2022-08-05 01:06:26');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(103, 76, 'Chương 3: Sư Ông', 'chuong-3-su-ong', 3, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đã luôn ba hôm nay, sư ông đóng cửa chùa trên, cả ngay cặm cụi làm việc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Làm việc gì? Trong chùa không một ai hay. Chỉ biết rằng đã sáu bữa nhà sư bỏ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cơm, và mỗi lần có ai vào khiến đàn chó sủa ầm ĩ, thì trên chùa lại nghe có tiếng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mõ đều đều và tiếng tụng kinh sang sảng. Mãi cho đến lúc một chú tiểu thân cận</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đến gõ cửa và báo cho nhà sư biết người mới đến là ai.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần này cũng vậy, đàn chó vừa im sủa thì người ta nghe thấy tiếng tụng kinh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">du dương huyền bí ở trong chùa đưa ra, lẫn trong mùi trầm, mùi hương phảng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phất. Nhưng chú tiễu vừa bạch có cô hàng nồi đất ở Kinh Bắc đến, thì nhà sư vội</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vã mở cửa bảo đưa cô ta lên ngay.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Công việc của cô hàng chỉ có giao tận tay cho sư ông một bức mật thư. Đoạn,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nàng hầp tấp xin đi ngay. Muốn tránh sự ngờ vực, nhà sự lớn tiếng bảo chú tiểu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mua hai cái niêu thổi cơm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đóng cửa cải then xong, nhà sư đến chỗ bệ gạch bày tượng. Tức thì hở ra một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khe. Khi ấy lại mảnh tường để lấp cái khe đi, không ai có thể nhận thấy dấu vết</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được, nhất là chỗ đó lúc nào cũng tối như ban đêm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cố nhiên cái bệ ấy rỗng, và tuy ở trong có thắp đèn, thở cũng vẫn dễ, vì có</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhiều lỗ thông hơi đục qua thân bụt gỗ khỗng lồ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ở một góc buồng nhỏ hẹp ấy có một bản gỗ trên khắc chữ nôm. Bên cạnh, một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tập giấy vừa in xong. Thì ra luôn ba hôm nay, sư ông bận khắc bản gỗ ấy. Chẳng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nói thì độc giã cũng thừa biết công việc bí hiểm của nhà sư quan trọng là chừng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nao .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng có lẽ nhà sư cho bức thư kia còn quan trọng hơn nhiều, nên mới bỏ dở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">việc in giấy mà vội vàng xé phong bì ra xem.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bức thư vắn tắt như sau này:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm quý hữu nhã giám.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bọn đồng chí chúng ta mới thêm đươc một người văn võ hêm toàn. Người ấy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">là Lê Báo công tử con cả quan Thiên thư khu mật viện sự Lê Ban. Lê Báo hiện ẩn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">núp ở bản am, khao khát đươc gặp mặt quý hữu lắm. Vậy quý hữu nên sang ngay</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiêu Sơn hội viện để bàn một điều rất là cẩn trọng, cần hp.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phổ T nh tiền sư hnh thư.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Niên hiệu Chiêu thống (Đảng tiêu Sơn vẫn giữ niên hiệu cũ của nhà Lê không</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chịu dùng niên hiệu Cảnh Thịnh nhà Nguyễn Tây Sơn) thứ mười một năm Bính</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thìn tháng chạp ~ờ~ mồng bốn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xêm xong thư, Phạm Thái - vì sư ông chính là Phạm Thái tức Phạm Phụng -</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vội vàng thu xếp bản in giấy má gọn gàng, rồi ra ngoài đóng cửa phòng bí mật lại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sau khi đã cặn kẽ dặn các tiểu trông nam chùa, chàng buộc hành lý vào trong một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cái tay nải nâu, vắt lên vai ra đi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì cớ gì Phạm Thái vội vàng thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có lẽ cũng vì một phần việc đảng bộ, nhưng nhất vì tính tò mò muốn biết Lê</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Báo là ai, là người thế nào mà võ nghệ cao siêu lạ thường như thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyên sáng sớm hôm nay, thám tử của chàng về báo cho biết tối hôm trước</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">có một người trẻ tự xưng là Lê Báo vào uống rượn ở một tửu quán phố Từ Sơn rồi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trong khi bất ngờ giết bọn lính canh trong quán và cướp một nhà sư trốn biệt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái lấy làm kinh ngạc. Trong thư Phổ T nh nói sang hội diện cùng Lê</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Báo. Vậy Lê Báo ấy chẳng hay có phải Lê Báo trong tửu quán không. Nếu phải thì</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhà sư bị cướp đi chẳng là Phổ tĩnh còn là ai?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái vừa rảo bước trên con đường Từ Sơn vừa lẩm bẩm: \"Khó hiểu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chẳng nhẽ nhà sư ấy lại là Phổ T nh? VÔ lý? Trí dũng như anh Trần Quang Ngọc,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">còn ai bắt cóc nổi. Mà dù bắt cóc, dù cướp đi nữa, sao lại đến Tiêu Sơn?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Câu truyện xẩy ra năm trước, chàng còn nhớ rành mạch, và khiến chàng không</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thể nào tin được rần sự bắt cóc kia là có thực.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hồi ấy Nguyễn Đoàn vừa bị giết, đồ đảng vỡ lở tán loạn, sào huyện bị phá tan</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hoang. Phạm Thái phải lang thang, lẩn lút quanh vùng, luôn luôn bị quân lính của</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quan hiệp trấn Kinh Bắc và viên phân phủ Từ Sơn tróc nã.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một đêm chàng đến chùa Tiêu sơn ngủ trọ. Thấy cảnh chùa hùng vĩ độc chiếm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một trái đồi, chàng mừng thầm rằng chốn ấy có thể nương thân được để chờ vận</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mà đi tìm kiếm, tụ hội các nhân tài trong nước. Lòng sốt sắng báo thù cho cha và</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cho chủ đảng bị hại bởi tay Tây Sơn làm cho chàng trở nên tàn ác. Đối với chàng,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chỉ một mục đích chàng đương theo đuổi là đáng kể. Chàng không kiêng nể một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sự gì để đi tới mục đích ấy, dù phải quả quyết hung bạo cũng đành.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bởi vậy chàng có manh tâm muốn giết nhà sư Tiêu Sơn để chiếm giữ ngôi</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chùa làm một nơi sào huyệt. Chàng cho đó là một việc rất dễ dàng và giản dị. Sau</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khi chàng cạo trọc đầu và thắng bộ quần áo nâu vào thì còn ai nhận được chàng là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái nữa; người ta sẽ gọi chàng là sư Tiêu Sơn và không bao giờ dám ngờ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rần chàng đã kế nghiệp nhà sư chân tu kia một cách võ đoán.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng Phổ T nh thiền sư trụ trì ở chùa Tiêu Sơn há phải là một thầy tu tầm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thường mà chàng có thể giết như giết co gà mái non? vì thế vào khoảng giờ Tý,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái vừa lẻn vào nhà trai, nơi túc phòng của Phổ T nh, định giở tay, thì dưới</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ánh lù mù một ngọn đèn dầu, hai người liền đánh nhau một trận kịch liệt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ý chừng đều biết sức nhau, nên sau khi đã giở mấy miếng võ ra, đôi bên cùng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lùi lại và cất tiếng cả cười Nhà sư khinh bỉ hỏi Phạm Thái:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cớ sao ta đã cho nhà ngươi ngủ trọ, nhà ngươi lại trả ơn ta một cách xứng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đáng thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái đáp vắn tắt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không lôi thôi? Trong hai ta chỉ một người được sống.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi chàng lại sấn tới giơ dao chém. Nhà sư né người sang một bên mà nói</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">răng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Khoan? Phòng này chật hẹp quá, không phải là nơi đấu võ. Vả như ngươi có</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dao mà ta tay không. Chẳng lẽ nhà ngươi lại hèn nhát đến thế. Có giỏi hãy đi ngủ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một giấc cho khoẻ khoắn đã rồi sáng mai lên ngọn đồi sau chùa, cùng ta tỉ thí.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngươi có ưng thế không, Phạm Thái?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái, nghe nhà sư đọc đến tên mình thì giật mình kinh hãi. Phổ T nh liếc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mắt thấy vậy liền bất thình lình nhẩy lại giật phắt lấy con dao rồi mỉm cười bảo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phạm Thái, chú có dao còn không làm gì nổi ta, bây giờ con dao ấy đã sang</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tay ta, vậy chú nghĩ sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái cũng mỉm cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Điều đó không cần biết vội, hãy hỏi nhà sư điều này: Sao nhà sư nhận được</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tôi là Phạm Thái?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hỏ dở, một nghìn quan tiền thưởng của triều đình, ngươi bảo dễ không đáng</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khiến ta lưu ý đến diện mạo nhà ngươi chăng?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái không nén được lòng tức giận, thét mắng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thằnng sư hổ mang kia? Tao cho phép mày bắt tao đem nộp mà l~nh thưởng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhà sư lạnh lùng bỉu môi, đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng ta đã trót hứa với nhà ngươi rằng mai lên đồi đấu võ mất rồi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ta không thèm đấu võ với quân khốn nạn đội lốt sư để đi do thám cho giặc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">của nhà Lê !</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Khá đấy? Phạm Thái anh hùng đấy? Còn kẻ đang đêm lẻn vào phòng này để</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">giết trộm thì Phạm Thái liệt vào hạng gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái phần xấu hổ, phần căm tức, sấn lại đánh nhà sư, nhưng nhà sư</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhanh nhện tránh sang một bên mà lớn tiếng hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thong thả, Phạm Thái có biết ông Trần Quang Châu là ai không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao ta không biết? Bậc trung thần ấy là bạn của thân phụ ta khi xưa, sao ta lại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không biết? Nhưng ta cấm thằng ác tăng kia không được đọc đến tên ngài.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái bỗng kêu rú lên:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trời ơi ? Anh Trần Quang Ngọc ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chính ta là Trần Quang Ngọc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái vội sụp lạy xuống đất, tạ tội:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Anh ta chết cho em, vì em thật đáng chết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trần Quang Ngọc đỡ Phạm Thái dậy mà nói rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chúng ta quen biết nhau từ thuở nhỏ. Ngày nay anh còn nhận sao được mặt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tôi, nhất tôi lại ẩn núp trong bộ thiền phục.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai người mừng rỡ đem truyện riêng ra kể cho nhau nghe. Phạm Thái nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chẳng hay sau khi bác đốc trấn bị mắc mẹo lừa ở Chí Linh rồi ra sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trần Quang Ngọc ứa nước mắt trả lời:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Khi ấy thân phụ bị bắt giải về Phú Xuân, Quang Trung dụ thân phụ quy hàng,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thân phụ nhất định không chịu nghe, nên đã bị hắn hại rồi, còn đâu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thằng giặc già? Cha chúng mình cùng chết về tay nó. Nhưng đại huynh còn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cặm cụi chống chọi mãi với quân Tây sơn kia mà?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phải, được gần một năm. Vả hiện giờ đồ đảng của tôi cũng còn tới hơn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nghìn, tản mạn ở khắp các trấn, nếu cần dùng đến, có thể có ngay. Nhưng còn hiều</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hữu, nay định đi đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi cũng chẳng biết đi đâu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngày hiền hữu ở bên Nguyễn Đoàn, tôi vẫn có ý muốn sang theo, nhưng sau</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">biết Nguyễn Đoàn không phải tay làm nổi việc lớn, nên lại thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế thì đại huynh có con mắt tinh đời lắm. Quả thực, Nguyễn Đoàn là một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tay chủ trại tầm thường, chẳng qua chỉ sai khiến nổi một bọn lâu la Ô hợp mà thôi,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hiểu làm sao được chí lớn của anh em mình. Nếu hắn biết theo bài quân yếu về</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mấy thế chiến công của tôi thì đâu đến nổi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cười, hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi nghe nói bài quân yếu của hiền hữu có thể so sánh với những binh thư có</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tiếng của cổ nhân như Lục Thao Tam Lược của Thái công, Thập Tam Thiên của</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tôn Tử. . .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái gạt đi mà rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đại huynh nói quá. Tôi tài hèn đâu dám ví với các bậc đại tướng xưa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai người chuyện trò với nhau mãi đến sáng về binh lược, võ nghệ. Hôm sau</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trần Quang Ngọc khuyên Phạm Thái thế phát quy y đễ dễ trốn tránh, rồi nhân</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chùa Liên Đài ở xã Nghiêm Xá chưa có ai trụ trì (vì vùng ấy loạn lạc không nhà sư</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nào dám ở tu) Phổ T nh thiền sư liền cắt Phạm Thái về đó lấy đạo hiệu là Phổ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chiêu thiền sư.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái vừa đi vừa ôn lại trong trí quãng đời dĩ vãng. Chàng lấy làm lạ rằng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phổ tỉnh thiền sư tức Trần Quang Ngọc là một tay võ nghễ cao cường mà còn bị</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo bắt thì không hiểu Lê Báo sức khoẻ đến bực nào. Mà nếu Phổ Tĩnh đã bị</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bắt cóc, sao lại còn có bức thư gọi mình sang hội diện. Hay là bức thư man trá?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng chín tay Nhị Nương đưa cho ta kia mà. Vả xét tự dạng thì đích là thư của</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phổ T nh rồi, Chắc có điều gì bí mật chi đây.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái mải suy nghỉ đến nỗi tới đò Thọ Khê mà vẫn không biết. Mãi lúc bị</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lính giữ lại hỏi, chàng mới giụt mình như choàng thức dậy:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhà sư kia đi đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một viên đội cười bảo tên lính của mình:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sư ông Phổ Chiêu ở chùa Nghiêm Xá mà chú không biết hay sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Liền vẫy tay cho phép nhà sư xuống đò.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy sự canh phòng nghiêm mật, Phạm Thái càng nóng ruột về câu chuyện</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xảy ra ở tửu quán... Vì thế, trước khi đến Tiêu Sơn, chàng đi thẳng tới hàng nhà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngỗng (bạn đồng chí của chàng thường hội họp ở đấy) để dò tin tức.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy cửa quán đóng im ỉm, chàng liền gõ năm tiếng, đó là hiệu riêng của</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đảng Người chủ quán thì thầm mấy câu rồi đóng sập cửa lại. Trong lòng mừng rỡ,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái thong rong bước lên chùa Tiêu Sơn.</span></p>', 0, '2022-08-05 01:06:50', '2022-08-05 01:06:50');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(104, 76, 'Chương 4: Tiêu Sơn Kết Nghĩa', 'chuong-4-tieu-son-ket-nghia', 4, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đã lâu nay cửa tam quan chùa Tiêu Sơn rào kén hẳn hàng ba, bốn lần tre, hóp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">và chông chà.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khách thập phương phải đi qua một con đường vòng chạy theo chu vi trái đồi,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rồi rẽ ngoặt ra phía bên. ở đó có một cái cổng nhỏ hẹp nhưng xây rất kiên cố. Qua</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lần cổng, một hàng bậc gạch cao và giốc đưa đến nhà trai. Như thế , đứng trên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngọn đồi hay trong lầu Tiêu Lĩnh nhìn xuống có thể biết ai sắp đến chùa, nhất lại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">có một cái lạch nước rất sâu ngăn chận đồi ra với con đường vòng. Cái lạch ấy, sư</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phổ T nh cho đào để lấy đất đắp bức tường dài bao bọc quanh đồi. Và cũng nhờ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">việc to tát ấy mà nhà sư đã được dân làng nức nở ca tụng công đức. Họ cho nhà sư</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đắp tường đào hào như thế không những chỉ có một mục đích giữ chùa, mà còn có</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mục đích che chở cho dân quanh vùng trong khi nhiễu loạn, vì hạt ấy, họ sợ hãi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bọn Nguyễn Đoàn, Phạm Thái lắm, tuy chỉ sợ bóng sợ gió.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ý chừng Phổ T nh thiền sư cũng biết vậy, nên ngay ở cửa tam quan, có dán</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một tờ yết thị nói cửa từ bi không hẹp, ai sơ quân cường đạo cướp bóc cứ vào chùa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nương náu ít ngày, nhà chùa sẵn lòng dung nạp.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kỳ thực chỉ có đồ đảng của Phổ Tĩnh là hay lui tới cửa chùa và tờ yết thị kia</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không có mục đích gì khác là để che mắt quan quân. Chẳng thế có khi trong chùa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tụ họp đến hàng trăm người mà viên phân phủ Từ Sơn vẫn không lưu ý tới, cho</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rằng đó toàn là lulung bọn quê mùa yếu hèn, nhút nhát đến ẩn núp. Không những</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thế, viên phân phủ còn nhân tờ chiếu của vua Quang Trung bắt bỏ hết chùa nhỏ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trong các làng đệ dựng một ngôi chùa lớn ở mỗi phủ, mỗi huyện, mà đệ tờ bẩm lên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quan trấn thủ xin lấy chùa Tiêu Sơn làm chùa chính thức được trụ trì, ở chùa ấy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy việc tư xin bị đình bãi, vì từ khi vua Quang Trung thăng hà, vua Quang Toản</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">và thái sư Bùi Đắc Tuyên không còn lưu tâm gì đến công việc cải cách thiền học</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nữa, nhưng lòng tín nhiệm của quan quân hạt Kinh Bắc đối với Phổ T nh thiền sư,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhờ việc đó mà ngày một thêm vững chặt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lòng tính nhiệm hầu hoàn toàn ấy đã giúp đồ đảng bí mật của Quang Ngọc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hoành hành dễ dàng ở vùng Kinh Bắc, vì những viên kiện tướng của chàng đều là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">các sư ông, sư bác mà tay quan trọng nhất là Phạm Thái tức sư ông Phổ Chiêu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chùa Linh Đài, làng Nghiêm Xá.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chiều hôm trước nhân sư bác chùa Đình Bảng đến báo có một bà hoàng phi bị</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bắt giải về giam ở phủ Từ Sơn, Quang Ngọc liền hốt hoảng chít vội cái khăn</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vuông xuống tận mắt và khoác vội vào mình cái mền nâu cũ, cho người ta không</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhận được ra đi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyên chàng vẫn biết rằng từ khi thành Thăng Long mới vỡ, người em thứ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ba vua Lê là Lan quận công Duy Chí đem bà hoàng phi họ Nguyễn chạy lên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuyên Quang rồi chiêu dụ những người thổ hào cùng nhau lo toan việc hưng phục.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng Duy Chí mới chống chọi với quân Tây Sơn được vài tháng ở Bắc Lạc thì bị</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bắt bỏ cũi giải về Phú Xuân hàng hình cùng với hết thảy các tướng tá. Hoàng Phi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">liền rời Tuyên Quang trở về hạt Kinh Bắc ẩn núp ở trong các nhà bình dân. Quân</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tây Sơn thường đi lùng bắt mà không được, vì người Kinh Bắc vẫn còn mến tiếc</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhà Lê, không ai chịu tố cáo nơi hoàng phi trú ẩn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi đã dò biết đích xác rằng hoàng phi bị bắt, Quang Ngọc vào hàng cơm nhà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngỗng ở phố phủ, định sai chủ quán, một đảng viên của đảng Tiêu Sơn, đưa ngay</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tin đến Nghiêm xá cho Phạm Thái. Chẳng ngờ gặp giữa lúc Lê Báo đang uống</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rượn và nói nhiều câu khảng khái. Chàng liền dốn ngồi lại để xem ông khách trẻ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tuổi kia là người thế nào, nhất chàng lại như bị cái sức vóc vạm vỡ và nét mặt tươi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">như hoa của kẻ kia lưu luyến.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Việc cần kíp thứ nhút của Quang Ngọc khi đã đưa Lê Báo về tới chùa là viết</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thư sai người tức tốc đến Kinh Bắc giao Nhị Nương đem về Nghiêm xá cho Phạm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thái. Chàng biết tất có binh mã đuỗi theo con đường Từ Sơn - Kim Lũ, nên chàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không cho người mang thư đi lối ấy. Chàng lại biết đàn bà, con gái ít khi bị ngờ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vực, khám xét, nên việc thông tin tức chàng thường giao cho bọn họ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vào khoảng cuối giờ Tỵ, Phạm Thái tới chùa Tiêu Sơn. Quang Ngọc đã dứng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chờ ở chân đồi. Hai người lớn tiếng chào nhau: \"A di đà phật?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lê Báo đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cũng khe khẽ đáp lại:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trong chùa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có việc gì quan hệ nữa không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có, chốc nói chuyện.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lên đến đầu bực thang gạch, nghe có tiếng mõ lớn thưa thớt rời rạc. Phạm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thái mỉm cười, theo Quang Ngọc qua cái cửa nách bước vào chùa trên. Một nhà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sư đầu mới cạo nhẵn thín, khoác áo cà sa ngồi ở cái bục gỗ trước bàn thờ, miệng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lẩm nhẩm đọc kinh, tay uể oải gõ mõ. Hình như nhà sư chú hết tinh thần vào sự</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tụng niệm, nên không biết có hai người vừa vào, tuy họ đã cất tiếng chào: \"A di đà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phật?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy người kia không nhúc nhích, Quang Ngọc đưa mắt liếc Phạm Thái, mỉm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cười rồi lại gần bàn thờ gọi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lê Báo?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo vờ không nghe rõ, vẫn ngồi đọc kinh, mắt chăm chăm để vào quyển</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sách chữ lớn mở đặt trên giá. Quang Ngọc cáu tiết, đến sau lưng ghé vào tai nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mới tu được một buổi mà đã mộ đạo thế ư?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bấy giờ Lê Báo mới rời quyển kinh, ngước mắt nhìn lên, nhoẻn miệng cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không, đệ có đọc kinh đâu, đệ ngâm thơ đó chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cả ba người cùng cười ồ. Bỗng một chú tiểu ở ngoài đi vào để thắp hương.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Các nhà sư lại im bặt, nét mặt người nào người ấy đều có vẻ thành kính, nhu mì,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">kín đáo. Phổ Tĩnh vờ hỏi Lê Báo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sư cụ bên ấy vẫn được mạnh đấy chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo hấp tấp đáp lại:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa ngài...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phổ Chiêu vẻ mặt trang nghiêm vội đỡ lời:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bạch sư ông, cụ Phổ Mịch nhờ ơn Phật tổ vẫn được mạnh như thường.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phổ tĩnh mỉm cười rồi quay ra bảo chú tiểu, ý chừng mới tu ở chùa này:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Gọi chú Mộc?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lát sau, bước vào một người to lớn, gân cốt nở nang, cặp mắt tròn xoe, da</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dẻ hồng hào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phổ Tĩnh hất hàm hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nó mới đến, chú đã biết tâm địa ra sao mà dám cho lên chùa trông nom việc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đèn nhang?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bạch sư ông, nó ở trong bọn thủ túc chân thành của đệ tử. Đệ tử xin cam</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đoan chịu hết trách nhiệm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phổ T nh hơi gắt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đành vậy, nhưng cứ phòng bị trước thì vẫn hơn. Tiệc đã sửa soạn xong</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chưa?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bạch sư ông đã.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có nhiều rượn ngon đấy chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bạch sư ông, đủ cả. Đệ tử đã cho xong đâu đấy ở trên lầu Tiêu Lĩnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Được, ta không cần đến chú nữa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chú tiểu lễ phép cúi đầu chào, đi ra. Phổ T nh đóng cửa cẩn thận mà nói rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thôi, bây giờ chúng ta không còn lo sợ điều gì, cú việc bình tĩnh mà đánh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chén, vì muốn lên Tiêu Lĩnh tất phải qua chùa, mà cửa chùa thì đóng khóa kiên cố</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy Phạm Thái thì thầm nói chuyện với Lê Báo, Quang Ngọc quay lại hỏi hai</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người :</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chỗ quen biết cả đấy. Mà dù chưa quen biết thì rồi cũng phải quen biết. Anh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hùng trong thiên hạ phỏng được bao người, sao không cùng nhau làm việc đại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nghĩa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngu đệ vẫn được nghe đại danh của quan Thiên thơ khu mật viện sự. Nay</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được gặp ngài thực lấy làm hân hạnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc thẳng thắn cười lớn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngài? Cái tiếng xưng hô ấy không được ổn bỏ nó đi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo cũng nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phải, chỗ anh em sao lại gọi thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc bàn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Muốn chính kỳ danh, trước hết phải chính kỳ vị. Ngày xưa anh em Lưu,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quan, Trương kết nghĩa tại vườn đào, rồi lập nên cơ nghiệp kinh thiên động địa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vậy ngày nay, sao chúng ta không theo gương ấy mà cũng kết làm anh em?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo vỗ tay, thét vang như tiếng lệnh:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- ồ? Phải đấy? Hay? Hay? ý đại huynh hay lắm!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái mỉm cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trong ba anh em mình chẳng biết có ai giống Lưu Huyền Đức, Quan vân</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trường không, như giống Trương Dực Đức thì Lê hiền hữu thực là giống như đúc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy đệ xin làm em út chứ sao.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hiền hữu niên canh bao nhiêu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mười chín tuổi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế thì hiền hữu là em út hẳn đi rồi, vì Phạm quân hơn hiền hữu một tuỗi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm thái khiêm tốn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng Lê hiền hữu giòng dõi tôn thất nhà Lê, ngu đệ xin nhường là anh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phổ T nh vội gạt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không được, chỉ có một điều đáng kể: Ai hơn tuổi là anh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hiền huynh đã dạy như thế, thì hai em hẳn phải vâng theo. Vậy bây giờ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chúng ta phải thề ra sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- ở chùa này cũng có thờ Quan Công đấy chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chùa nào lại chẳng thờ đức Thánh Quan.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế thì hay lắm. Chúng ta cứ đến trước bàn thờ ông ấy mà thề.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phải đấy, phải đấy?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ba người liền cùng nhau lại bàn thờ Quan Clông. Lê Báo bảo hai bạn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trông Quan vân Trường lẫm liệt oai phong lắm nhỉ? Có lẽ vẻ lẫm liệt oai</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phong ấy là nhờ ở bộ mặt đỏ, mà muốn có một bộ mặt đỏ tất phải uống nhiều</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rượn Vậy trước khi phát thệ, sao ta không đem rượn lên dâng ngài rồi cùng nhau</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">uống thực say đã?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vì say rượn hiền hữu suýt bị thiệt mạng ở tửu quán, thế mà vẫn không chừa?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo lấy làm xấu hổ với Phạm Thái, nói chữa thẹn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hiền huynh tưởng ngu đệ say à? Ngu đệ uốn gấp năm, gấp mười thế cũng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chẳng thấm vào đâu. Chẳng qua giữa lúc bất ngờ bị chúng nó đẩy cái bàn vào</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người, nên ngu đệ ngã đó mà thôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái vốn thích rưỡn mà uống bao nhiêu cũng không say, liền đỡ lời bạn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lê hiền hữu nói rất đúng. Lễ phát thệ long trọng này không có rượn sao</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dút lời, Quang Ngọc đi thẳng lên lầu Tiêu L~nh. ở lại trước bàn thờ Quan</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Công, Phạm Thái sẽ bảo Lê Báo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nghe nói tửu lượng hiền hữu khá lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vâng, cũng khá. Hôm nay xin uống thi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thi cái gì chứ thi uống rượn thì không bao giờ ngu đệ dám nhận lời.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao vậy?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vì kẻ tu hành phải giới tửu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế thì buồn lắm nhỉ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc bê xuống một bình rượn lớn và hỏi hai người:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngần này đã đủ chưa?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cũng tiềm tiệm. Nhưng rượn có ngon không đấy? Chứ rượn của thằng cha</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chủ quán, ngu đệ uống hôm qua không thể nuốt được.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- ấy là không thể nuốt được đấy, chứ nuốt được thì không biết hiền hữu say</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tới đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cũng cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hai chú không ngại. Rượn đây tôi thửa mãi tận ở Thủ Khối chính hiệu hoàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cúc Nhưng ta làm lễ đã rồi hãy hay.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bây giờ mỗi người thề một câu. Tôi hơn tuổi được hai hiền đệ tôn làm anh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xin thề trước.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng liền quỳ trước bàn thờ Quan Công và dõng dạc khấn rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngày xưa Quan thánh đế có kết nghĩa với Lưu Huyền Đức, Trương Dực Đức</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tai vườn đào, thề cùng sống cùng chết để cùng nhau phò nhà Hán, cứu giúp muôn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dân. Ngày nay ở nước Việt Nam chúng tôi trăm họ loạn lạc, bị lầm than chẳng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">kém đời hậu hán, hai anh em lũ giặc Tây Sơn đem quân đi ăn cướp phá đánh đuổi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vua chúng tôi phải chạy trốn sang bên quý quốc, giết cha chúng tôi, giết họ hàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chúng tôi, vậy trước bàn thờ ngài ba chúng tôi là Trần Quang Ngọc, hai mươi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhăm tuổi, Phạm Thái hai mươi tuổi, Lê Báo mười chín tuổi, xin theo gương ngài</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cùng họ Lưu, họ Trương, kết nghĩa anh em, trước là để phò nhà Lê, sau nữa là để</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rửa thù cho cha chúng tôi. Tôi thề rằng tôi coi Phạm Thái, Lê Báo như hai anh em</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ruột, cùng hai người sống chết có nhau. Nếu tôi ở không đúng lời thề xin chết như</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thế nầy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dút lời chàng bẻ hương ra làm hai đoạn mà vứt xuống đất. Phạm Thái thề</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cũng như Quang Ngọc. Đến lượt Lê Báo. Chàng rót thêm vào chén rượn thờ, rồi tự</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rót cho mình một chén đầy giốc một hơi cạn. Đoạn chàng bảo hai người:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hai hiền huynh thề như thế không được, nghe ngu đệ đây nè.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tức thì chàng vào quì trước bàn thờ và lớn tiếng khấn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi đây Lê Báo tuy mới mười chín tuổi đầu nhưng trong ba năm nay tôi đã đi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chu du khắp nước, vì vậy tôi biết dân tình rất khổ sở. Tôi nhớ sách Mạnh Tử có</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">câu: Dân vì quý, xã tắc thứ chi, quân vi khinh. Thế mà họ coi nước như tư sản của</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">họ, không tưởng gì đến dân, đến xã tắc nữa. Vì vậy ba chúng tôi là Trần Quang</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngọc, Phạm Thái, Lê Báo cùng nhau kết anh để ra tay tôn phò một vị nhân quân</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">biết coi dân làm trọng. Tôi thề rằng (vừa nói vừa rót rượn vào chén) tôi xin giữ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trọn đạo làm em út dốc lòng tuân theo mệnh lệnh của đảng trưởng là ông anh cả</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trần Quang Ngọc của tôi, nếu tôi trái lời thề ấy thì nguồn ngày xanh của tôi cũng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cạn như chén rượn này.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cùng với câu thề, chén rượn cùng hết. Quang Ngọc và Phạm Thái đưa mắt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhìn nhau mỉm cười. Rồi Quang Ngọc rót ba chén rượn đầy mà nói rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Xin uống cạn chén rượn để chúc cho tình huynh đệ chúng ta ngày thêm bền</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chặt với non sông Nam Việt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Uống xong Phạm Thái khen:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Rượn của hiền huynh ngon lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo nghĩ thầm: \"Đã giới tửu mà còn biết rượn ngon?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc mời hai bạn lên lầu Tiêu Lĩnh dự tiệc. Lê Báo sợ bỏ quên mất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bình rượn, vội vàng bê theo.</span></p>', 0, '2022-08-05 01:07:35', '2022-08-05 01:07:35');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(105, 76, 'Chương 5: Trên Lầu Tiêu Lĩnh', 'chuong-5-tren-lau-tieu-linh', 5, NULL, '<p>&nbsp;</p>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có phải nhị vị đại huynh về bè với nhau để công kích ta chăng?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái vẫn tươi cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lê hiền đệ có lẽ say rượn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy Lê Báo có tính lỗ mãng, Quang Ngọc liền giải hoà:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thôi xin hai hiền đệ, lỗi tại ngu huynh cả. Vậy cố nhiên là ngu huynh phải kể</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">câu truyện đã hứa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo hết giận, vỗ tay reo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ư, có thế chứ? Nếu không, ta băt đấu võ liền, mà đấu võ thì ta chấp hai anh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một bên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc biết Lê Báo say lắm rồi, liền vui vẻ cười vang nói đùa:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lê hiền đệ nên để dành lực lượng với võ nghệ, có lẽ tối hôm nay phải dùng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đến. Bây giờ hãy xin lắng tai nghe câu truyện khôi hài của ngu huynh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Truyện khôi hài chắc là nhạt thếch.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái đỡ lời:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thì cứ để Trần đại huynh kể đã nào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc giốc cạn chén rượn đầy, rồi nói rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ba năm trước đây, một khách chinh phu niên thiếu lang thang trên con đường</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gió bụi. Chàng ta đi xa cửa xa nhà tìm chốn trú thân.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Cha chàng vừa qua đời, mà lúc qua đời, chàng không gặp mặt, qua đời một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cách thảm khốc đầu bị rời mình nơi pháp trường...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo ngắt lời:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thì cứ nói ngay là bị chém có giản dị hơn không!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng thấy Quang Ngọc ngồi yên, cặp mắt đỏ ngầu dữ tợn, đăm đăm nhìn nơi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chân trời xa tắp, thì chàng lấy làm sợ hãi im ngay. Bỗng Quang Ngọc cười sằng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sặc một hồi, rồi kể tiếp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Một buổi chiều, tâm hồn ngây ngất, chàng thiếu niên bỏ lỏng dây cương để</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mặc ngựa theo con đường hẻm, cỏ rậm, muốn mang đi đâu tuỳ ý. Chàng đưa cặp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mắt mỏi mệt nhìn sắc trời tà đỏ ửng mà đoái tưởng lại thời oanh liệt theo cha tung</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hoành trong hai trấn Đông, Bắc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Cái vỏ kiếm lách cách đập vào yên ngựa lại nhắc tới những bài ca chàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thường hát để tự phấn khởi tâm hồn trong khi thất vọng. Chàng liền kìm cương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rút thanh kiếm báu giơ lên múa. Toan cất lời ca thì xa xa có tiếng chuông rời rạc,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">buồn tẻ, như gieo vào lòng chàng sự hư vô chán nản. Chàng thong thả tra kiếm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vào vỏ rồi theo tìm nơi có tiếng chuông.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Trời nhá nhem tối, thiếu niên tới chân một quả đồi. ở lưng chừng có một ngôi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chùa. Tiếng chuông đổ hồi từ trên cao gieo xuống, gieo vào lòng khách chính phu,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">làm cho tắt hẳn ngọn lửa đương bùng bùng cháy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Thiếu niên xuống yên, buộc ngựa ở cửa tam quan. Chờ lâu vẫn không thấy có</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người ra, mà gọi cửa cũng không ai thưa. Mãi sau, khi chàng quay đi mới có một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhà sư ở gian phòng trai bước tới. Người ấy nói mình chỉ là sư bác, còn sư cụ thì</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chơi vắng phương xa. Chàng liền nhờ sư bác cho ở trú một đêm, sáng hôm sau lại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xin đi sớm, nhưng sư bác nhất định không thuận, nói không có phép sư cụ thì</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không thể tự tiện để khách thập phương ngủ trọ trong chùa được. Ngôn ngữ, cử</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chỉ, nhất là sức vóc của nhà sư khiến chàng trẻ tuổi phải nghĩ thầm: \"Quái? Sao đi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tu mà hỗn xược dữ tợn đến thế?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Chẳng muốn nói khó, chàng tuổi trẻ lẳng lặng xuống đồi, ra cửa tam quan.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng, ôi thôi? Con ngựa buộc đó đã biến đâu mất, mà lạ nữa, nghe xa xa có tiếng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngựa phi nước đại về phía làng Nỗi duệ. Chàng biết dẻ trộm vừa tốn thoát. Bực tức</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">uất người, nhất là từ trên chùa lại ném xuống những tiếng cười mai mỉa. Chàng lộn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tiết chạy một mạch lên đồi, lại gần sư bác sừng xộ hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Sao ngươi biết ta mất ngựa lại cười?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Ta cười thì có can dự gì đến ai?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Chàng tuổi trẻ mắm môi trợn mắt, giọng đe dọa:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Có can dự đến ta. Nếu ngươi không bảo cho ta biết đứa nào bắt trộm ngựa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">của ta thì ta thề xin đưa linh hồn người lên Nát bàn ngay lập tức.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Nhà sư cười ha hả đáp lại:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Mi làm như Nát bàn của nhà mi gần lắm? Dẫu sao, xuống địa ngục vẫn đễ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dàng hơn, vậy nếu mi muốn xuống thì cứ việc ỡm ờ đứng lại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Chẳng nói chẳng rằng, chàng tuổi trẻ tuốt ngay kiếm ra. Nhà sư ý chừng đã</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phòng bị trước, cũng giơ ngay cây búa dấu sẵn trong vạy áo ra. Hai người đánh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhau được một hồi, thì nhà sư biết sức không chống nổi quay đầu chạy. Chẳng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">may chân vướng cỏ khô, vấp ngã bổ chửng. Chàng trẻ tuổi liền nhanh nhẹn dẫm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chân lên ngực hắn rồi dí mũi kiếm vào ngực hắn mà dọa rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Ai lấy trộm ngựa ta?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Bất giác nhà sư kêu rống lên. Tức thì ba chú tiểu lực lưỡng ở nhà trai chạy ra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng tuổi trẻ cả tiếng thét lớn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Nếu chúng mày lại gần hay kêu cầu cứu thì trước hết tao hãy thí cho thày</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chúng mày một mũi kiếm đã, rồi tao giết chết hết chúng mày như giết một đàn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngoé.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Bọn tiểu thất kinh quỳ cả xuống lạy van xin \"công tử\" xá cho sư bác. Còn lão</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sư thì luôn mồm kêu: \"Nam vô a di đà phật? Lạy ngài tha tội cho bần tăng, bần</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tăng thứ hết, Nam vo a di đà phập ? \"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Chàng tuổi trẻ lộn ruột vì mấy tiến Nam vo a di đà phập của lão ác tăng, đã</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">toan đưa lưỡi kiếm vào cổ hắn, nhưng nghĩ đến con ngựa bị mất trộm chàng lại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thôi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo vui vẻ giốc cạn chén rượn rồi vỗ tay reo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- ồ ? Ngộ lắm nhỉ ? Ngu để tưởng như trông thấy ở trước mắt một tráng sĩ dẫm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chân lên ngực một nhà sư, gần đấy, ba chú tiểu quỳ gối chắp tay van lơn. Thực là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một bức tranh linh hoạt của một họa sĩ đời Chiến quốc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái thì lâm râm cầu nguyện, có vẻ cảm động xót thương. Mãi sau,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chàng mới ôn tồn bảo Quang Ngọc:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nếu trang thiếu niên kia không là hiền huynh thì ngu đệ xin bảo chàng ta là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một người lỗ mãng, dám quý con ngựa hơn mạng một nhà sư, dù là một nhà sư ăn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trộm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cười đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng hằn ta có lấy trộm ngựa của chàng tuổi trẻ đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy ai lấy?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không ai lấy cả. Nhà sư sợ hãi thú thật với chàng tuổi trẻ rằng giữa lúc chàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lên chùa thì sư cụ đi ra cửa sau vòng ra tam quan thấy có con ngựa tốt, liền mượn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tạm vì có chút việc cần. Nhà sư lại mời chàng tuổi trẻ hãy vaò nghĩ trong phòng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quan cư để chờ một lát nữa sư cụ về.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Chàng tuổi trẻ đoán rằng nhà sư muốn cạm bẫy mình để trả thù cho bỏ ghét,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhưng đấng trượng phu ngang tàng há sợ chì? Vả chàng cũng muốn dò xét ngôi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chùa còn giấu diếm những sự bí mật ghê gớm gì nữa chăng, vì cứ ngắm cái cử chỉ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bất chíng của vị sự cụ mượn ngựa bắng một cách hơi khác thường để đi chơi đêm,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chàng cũng đoán biết rằng chùa này không phải là một nơi tu hàng của các bậc đồ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đệ tôn sùng đức Thích Ca.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Chàng bèn theo sư bác vào phòng trai. Tức thì các chú tiểu xúm xít hầu hạ, kẻ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lấy thau, người pha nước. Nhưng chàng không dám uống nước, sợ trong đó có</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thuốc mê. Và lúc nào chàng cũng nhăm nhăm cầm thanh kiếm tuốt trần ở tay để</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phòng ngừa sự phản trắc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Chờ mãi tới cuối giờ Tuất cũng không thấy gì, chàng liền đóng cửa phòng tắt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đèn đi ngủ. Kỳ thực chàng vẫn thức, nằm nghe ngóng...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Bỗng vào khoảng nửa đêm, nghe có tiếng ngựa hí. Chàng rón rén đứng dậy,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ghé mắt nhòm ra cửa, thấy dưới ánh trăng suông lờ mờ hiện ra hai cái bóng đen từ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chân đồi đi lên: một bóng nhà sư lực lưỡng to lớn và một bóng người thiếu nữ rất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">yểu điệu, thướt tha...\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo vỗ tay cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sư cụ hổ mang, tối rước gái về chủa rồi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái buồn rầu chắp tay nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nam VÔ a di đà phật, nhưng rồi sau nữa, thưa hiền huynh?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc kể tiếp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Chàng tuổi trẻ toan cầm kiếm xông ra thì lại nghe có tiếng - tiếng sự cự -</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hỏi một người thứ ba vừa đi tới:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Hắn ta ra sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Tiếng trả lời của sư bác:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Bạch cụ, hắn ta nằm trong buồng quan cư. Xin cụ nên đề phòng, hắn ta giỏi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">võ lắm kia đấy ? \"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Hai người còn nói nhiều, chàng trẻ tuổi không nghe rõ, vì họ nói nhỏ, nhưng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ý chừng sư bác thuật lại cuộc đánh nhau vừa xảy ra, vì thấy người thiếu nữ khúc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khích cười có dáng chế nhạo. Bỗng sư cụ lớn tiếng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Được ? Để nó đấy, ta đây sẵn lòng đưa nó về cực lạc thế giới.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Bấy giờ ba người đi ngay sát cửa phòng chàng thiếu niên. Sư bác thì thầm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bảo sư cụ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Hắn ta ở trong này, nhưng chắc đương ngủ say.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Thiếu nữ không biết thích chí điều gì, vỗ tay cười vang, cười ngã cả vào cánh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cửa phòng đến rầm một tiếng. Sư bác vội vàng xua tay bảo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Se sẽ chứ? Háng ta thức dậy thì nguy bây giờ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Thiếu nữ vẫn cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Sư cụ sợ gì thằng nhãi ranh ấy, phải không bạch sư cụ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Sư cụ được gái khen, phỗng mũi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Phải, ái khanh nói phải. Ngữ ấy vị tất đã chịu nổi nửa chùy này.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Vừa nói vừa giơ ra một cái chùy đồng nặng. Sư cụ lại nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Nhưng ái khanh ạ, bây giờ ta hãy cùng nhau hưởng cuộc ái ân đã...\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Thiếu nữ nũng nịu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Không. Bao giờ sư cụ giết được tên hỗn xược ấy, em mới chịu nghe lời.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Tức thì cánh cửa phòng mở toang, chàng trẻ xông ra, tay múa kiếm, miệng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thét:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Có ta đây?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Sư cụ cũng khoa chùy lên đối địch. Trong khi ấy thì, lạ lùng xiết bao, thiếu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nữ nhân lúc bất ngờ rút ngay dao dấu ở trong bọc ra thí cho sư bác một nhát trúng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngay cửa họng, nằm vật xuống đất chết tươi. Rồi nàng quay lại giúp sức chàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tuổi trẻ Thấy nàng trong tay chỉ có một cây đoản đao, không đỡ nổi cái chùy nặng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">của sư cụ, chàng tuổi trẻ vội kêu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Cô lùi ra, cứ một mình tôi cũng đủ giết nổi thằng sư hổ mang này rồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Nhân lúc chàng để ý đến thiếu nữ, giữ mình không được kín, nhà sư nhằm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trúng thanh kiếm chàng cầm, giáng xuống một chùy hết sức mạnh, khiến kiếm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">văng ra. Tính mệnh chàng sắp bị nguy thì thiếu nữ đã múa tít lưỡi đoản đao xông</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vào cứu viện. Cử chỉ hào hiệp ấy giúp chàng trẻ tuổi đủ thời giờ nhặt thanh kiếm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sấn lại đánh nhà sư Chàng tức vì đã bị nhà sư làm mất thể diện ở trước mặt má</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hồng, nên chàng rán hết sức bình sinh, giở hết võ nghệ ra đối địch. Bởi thế, chẳng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bao lâu thiếu nữ nghe thấy chàng thét lên một tiếng rất lớn, rồi nhảy bổ vào đâm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhà sư một nhát xiên từ ngực sang lưng\".</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo cười the thé:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế là cả sư cụ, lẫn sư bác cùng được lên Nát bàn chầu Phật.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cũng cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hoạ chăng xuống địa ngục chầu vua Diêm vương.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái mơ màng đăm đăm nhìn, hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Còn người thiếu nữ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc thản nhiên đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tức Nhị Nương.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái kinh ngạc:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhị Nương?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phải, Nhị Nương. Mà người thiếu nữa là Nhị Nương thì có chi lạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo ngơ ngác hỏi:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhị Nương là ai?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Rồi hiền đệ ắt biết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy bây giờ ta hãy uống mỗi người hai chén rượn để chúc thọ Nhị Nương</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đã ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lê hiền đệ nói rất phải.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ba người cùng vui vẻ nâng chén. Lê Báo lại hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế rồi sao nữa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc mỉm cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Rồi chàng trẻ tuổi trở nên sư ông...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái nói tiếp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đạo hiệu là Phổ T nh thiền sư, phải không bạch sư ông?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trần quang Ngọc vẫn mỉm cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- ý thế, giết xong hai tên ác tăng, Nhị Nương cùng ngu hữu xục xạo đi tìm bọn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tiểu, thì chúng, - tất cả năm tên - đều ra quỳ xuống van lạy xin tha. Ngu hữu liền</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sai chúng khiêng hai cái thây đem chôn ở phía bên kia đồi. Đoạn ai nấy lại đi ngủ,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tuy lúc đó gà đã bắt đầu gáy sáng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Từ hôm sau, Ngọc đã nghiễm nhiên dùng đạo hiệu của nhà sư đã tịch một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cách phi thường, nghĩa là hiệu Phổ T nh thiền sư. ở vùng này, nhiều người không</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngờ rằng có xẩy ra sự biến gì hết. Một ít người biết truyện thì lại làm ngơ, vì họ rất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ghét, rất sợ lão ác tăng kia, nên biết hắn bị giết, họ rất đỗi vui mừng. Bởi vậy, ngu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">huynh đây, nhờ trời, nhờ phật tổ được yên lành mà tu đạo nhiệm mầu...\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái từ nãy vẫn theo đuổi ý nghĩ về Nhị Nương, tò mò hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế còn Nhị Nương? Nàng không nói chuyện vì sao nàng lại để tên ác tăng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bắt về chùa? Hình như nàng cũng thông võ lược kia mà?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phải, nàng rất tinh thông võ nghệ. Cứ kể tay đôi đánh nhau, nhà sư hổ mang</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">kia cũng chưa dễ làm gì nổi, nhưng vì nhiều lẽ, nàng muốn dùng mưu giết tên ác</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tăng một cách kín đáo, không để tiết lộ cho ai biết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Nguyên nàng là con gái quan binh bộ thượng thư Nguyễn Đình Giản. Khi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn Huệ lên chiếm ngôi nhà Lê, quan thượng thư vì theo không kịp đức Chiêu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thống, đành phải quay về ẩn núp ở vùng huyện Lập thạch, tỉnh Sơn Tây. Bắc bình</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vương dụ mãi ngài ra làm quan không được, liền sai người đến Thanh Hoá bắt Nhị</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nương - khi ấy mới mười lăm tuổi - cho vào hậu cung, tưởng làm thế thì thương</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">con, thể nào ngài cũng phải ra hàng. Chẳng dè được tin, ngài rửng rưng như</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không, cười gằn mà nói rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- \"Con ta không biết chết đi để làm nhục môn hộ nhà ta, ta chẳng vì tình phụ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tử nhỏ mọn mà bỏ đại nghĩa đâư .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Nguyễn Huệ biết không dụ nổi, mới dùng mẹo bắt sống đem về. Nguyễn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thượng thư không chịu khuất phục mà chết. Nhị Nương, bên thây cha không kêu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khóc nửa lời, chỉ thì thầm khấn thề với linh hồn cha rằng: \"Thù này xin trả\".</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Nhân xin được phép đi đưa đám cha, nàng thừa lúc bối rối trốn thoát, rồi đi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chu du khắp đó đây, tìm đến những chỗ quen thuộc để cầu học nghề võ. Ba, bốn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">năm trời, nàng lẩn lút trong vùng quê các trấn Nghệ An, Thanh Hoá, Sơn Nam,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kinh Bắc, cho đến hôm gặp nhà sư hổ mang, mê cái sắc đẹp của nàng, thả lời</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chòng ghẹo. Nàng đã sắp ra tay cự địch, nhưng khốn nỗi một sự ngẫu nhiên lạ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lùng? Tên sư kia lại là người đồng hương với nàng: Sáu năm trước hắn trụ trì ở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chùa làng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Hắn dọa nếu không bằng lòng hắn, thì hắn tố cáo tung tích, nàng với quan</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phân phủ Từ Sơn. Bất đắc d~ nàng phải giả ưng thuận, cùng định chờ đêm khuya</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ra tay trừ khử tên cường bạo hoang dâm cho bỏ ghét...\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngừng một lát Quang Ngọc lại nói tiếp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chính nàng cùng ngu huynh đã lập nên đảng Tiêu Sơn. Nàng đã giúp đảng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được nhiều việc lắm, ấy là không kể cái công cứu sống cho Ngọc đây.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo cười sằng sặc:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế mà hai người không yêu nhau thì cũng lạ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc buồn rầu đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nói kính trọng nhau thì đúng hơn. Đương lúc theo đuổi công cuộc lớn lao,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">còn bụng đâu nghĩ tới ái tình. vả lại xin nhị đệ hiểu cho rằng, mình tuy không thực</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bụng tu hành, song đã nhờ cái áo cà sa để trốn tránh thì cũng không nên làm nhem</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhọ nó .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái mỉm nụ cười ngờ vực, ngồi im. Quang Ngọc lại nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nàng cũng bảo ngu hữu rằng khi nào việc lớn xong xuôi đã, hai người mới</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cùng nhau đi chu du khắp nước Nam như Phạm Lãi, Tây Thi đời xưa chứ nay còn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ở thời kỳ nằm gai nếm mật, nghĩ chi tới hạnh phúc êm đềm của tình ái ? . . . Lời vàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ấy, ngu hữu đây đã khắc ở bên lòng, thề không bao giờ dám sai.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo bông đùa một câu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nghĩa là hai anh chị còn tạm là đôi bạn cầm kỳ để chờ ngày trở nên đôi bạn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cầm sắt chứ gì.</span><br />\r\n&nbsp;</p>', 0, '2022-08-05 01:10:28', '2022-08-05 01:10:28');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(106, 76, 'Chương 6: Quan Quân', 'chuong-6-quan-quan', 6, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chiều hôm ấy, trước trại lính phủ Từ Sơn, người lính, nón sơn, quần áo chẽn,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mỗi người cầm một cây tre dài bịt vải đứng xếp hàng chữ nhất. Khi đã tập một lúc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">về đủ các miếng đâm trên, đánh dưới, phạt ngang thì người đội chọn từng cặp sức</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tương đương cho ra dấu với nhau.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Biết rằng có viên phân suất đứng trên mặt thành nhìn xuống, thầy đội, làm ra</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bộ ta đây giỏi võ, nắm tay người này, kéo chân kẻ khác hò hét, dạy bảo, mắng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhiếc luôn miệng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chú đứng tấn trống quá. Mũi roi chúc xuống thế này thì người ta khẽ bẩy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một cái cũng băng cả roi đi, còn đánh chác gì...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Viên phân suất từ trên thành đi xuống, lại gần đội cơ ban lời khen ngợi. Được</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thể, đội ta càng lên mặt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm ông lớn, với năm mươi tên lính giỏi võ, tôi có thể chống nổi mấy trăm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quân Ô hợp, chẳng nói đâu xa, giá tối hôm qua năm người canh phòng trong tửu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quán đều là lính tôi luyện tập thì có đâu đến nỗi bị giặc giết như ngoé thế? Đấy,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ông lớn cứ tin lính trấn giỏi ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngày mai bản chức thân giải Thị Kim lên trấn lỵ, vậy anh tính nên cho bao</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhiêu lính hộ tống cũi tội nhân?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm chỉ cho mười tên đi là chắc chắn lắm rồi - mười tên với tôi nữa là mười</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một thì dẫu giặc mang trăm binh đến đánh giải vây cũng không lo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất gật đầu, mỉm cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Anh nên hết lòng làm việc quan. Thế nào ta cũng tự thăng thưởng cho... à?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai mươi tên lính mới mộ này đã biết gì chưa?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm ông lớn, khá lắm rồi, xin ông lớn đứng coi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Liền hô cho bọn lính tập các miếng trông rất đều và ngoạn mục. Phân xuất vẫy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tay cho bọn lính vào trại nghỉ, rồi hỏi đội cơ:</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Anh có dạy tập đoản côn, đoản đao đấy chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm, có cả. Cả bắn cung nữa. Còn bắn súng hỏa mai thì vì ở phủ này quan</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hiệp trấn phát cho có năm cây, nên tôi đã chọn mười người giỏi nhất, nhanh nhẹn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhất mà dạy tập thôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất mỉm cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ta cũng không tin gì ở hiệu quả súng hỏa mai. Trong tận Tiên đế đánh nhau</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">với Tôn Sĩ Nghị, bên địch có tới hàng nghìn cây súng còn bên ta chỉ dùng toàn dáo</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dài, mã tấu, thế mà quân kia thua chạy không còn một mảnh giáp thì đủ biết đoản</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">binh vẫn lợi hại hơn trường binh nhiều.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm ông lớn, nhưng giữ thành thì súng hỏa mai được cái lợi bắn xa lại trúng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hơn cung, nỏ nhiều. Còn như bấn cây súng thần công đặt bốn góc thành, thì thực là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vô ích, xoay xở đã chậm mà bắn lại không trúng. Chỉ được cái tiếng to để dương</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">oai với bên địch.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Kể ra khi có quân giặc kéo đến hàng nghìn hàng vạn thì súng thần công cũng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">có lợi: Bắn bừa vào đám đông người, thế nào chẵng trúng. Nhưng phủ này thì trừ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khi trấn Kinh Bắc có thất thủ, địch quân mới kéo binh đến vây. Mà nếu Kinh Bắc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đã thất thủ, thì Từ Sơn cũng chẳng cố thủ với ai được. Thành thử súng thần công</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">có đó cũng như không. Còn như súng hỏa mai thì một trận Ngọc hồi đủ chúng tỏ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rằng đó là những binh khí vô dụng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm, nghe đâu ông lớn cũng có dụ chiến trận ấy?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có Ta theo Tiên đế ngay sau trận Cẩm Thủy. Bấy giờ, ta ở trong toán nghĩa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quân của nhà Lê. Tiên đế vừa sang sông thì quân nhà Lê vỡ lở ngay. Ta cùng vài</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bạn đồng chí đến xin hàng Tiền đế mà bấy lâu nay ta vẫn mộ tiếng anh hùng, ao</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ước được gập long nhan, nhất từ ngày ta đem lòng khinh bỉ Chiêu Thống đã rước</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quân ngoại quốc về giầy xéo người đồng bang. Thế là ta theo Tiên đế đến đánh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phú Xuyên, đến vây Hà Hồi. Trận Hà Hồi cũng như trận Phú Xuyên không có chi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">là đáng kể. Tiên đế đến, giặc trông thấy bóng cờ là đem hết quân lương, binh khí</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ra hàng răm rắp, chẳng phải đánh chác gì.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm, còn trận Ngọc Hồi? Nghe nói trận ấy đánh hăng hái lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phải, hăng hái lắm là vì quân Tàu có rất nhiều súng hỏa mai.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đôi cơ mỉm cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đó, ông lớn coi, súng hỏa mai vẫn lợi hại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất cau mày:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ta đã bảo không ăn thua gì mà lại. Sáng tờ mờ ngày mồng năm, quân ta tiến</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lên tới làng Ngọc Hồi, quân tàu bắn súng ra như mưa. Đến đây, anh mới nhận thấy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mưu lược Tiên đế. Ngài sai lấy những mảnh ván, ghép ba bốn mảnh vào làm một,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quấn một lần rơm ướt ở phía ngoài rồi truyền một toán quân kiêu dõng, cú hai</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mươi người khiên một bức, mỗi người dắt một con dao nhọn, lại hai mươi người</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cầm khí giới theo sau. Ta cũng tình nguyện nhập bọn này nên mới biết rất tường</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tận Đạn bên địch bắn ra vẫn vun vút rào rào, nhưng chỉ trúng bồm bộp vào ván</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quấn rơm chứ không thiệt một mạng người. Anh coi đó, súng có ích lợi gì đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi đến gần cửa đốn toán quân ta liền bỏ ván xuống đất, rút dao ra, xong lại chém</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">giết, quân đi sau kéo ùa vào trợ lực, còn vua ta thì cười voi theo sau đốc chiến.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quân Tàu địch không nổi, vút cả súng mà chạy tán loạn. Quân ta thừa thế đánh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tràn đi, lấy được đồn, giết quân Thanh, thây rải khắp đồng. Sau điểm binh khí bắt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được thì số súng hỏa mai có trên năm trăm cây.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc đó, một tên lính lệ hầu trà chạy ra nói với phân suất:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm, phủ đường cho ra mời đại nhân vào phòng khách xơi nước.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất lật đật theo tên lính đi vào nhà trong. Sau khi vui vẻ chào mời, hỏi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thăm qua loa về cách việc binh lương, phân phủ truyền cho hết cả người nhà ra</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngoài rồi thì thầm bảo phân suất:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Về việc bắt được Lê hoàng phi họ Nguyễn, ngài có ý kiến gì không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất ngẫm nghĩ, trả lời:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa ngài, không. Vả ta chỉ việc giải tù nhân lên trấn lỵ là xong.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ mỉm cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế là ngài thiển cận quá. Ngài nên biết tù nhân nào phải người tầm thường.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nàng Nguyễn thị Kim này xưa kia được Chiêu Thống sủng ái vì có tấm nhan sắc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nghiêng nước nghiêng thành. Buổi sinh thời đức Tiên đế ta nóng đánh trấn Tuyên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang làm loạn biết đâu không phải vì ở đó có bực mỹ nữ ấy ẩn núp. Nhưng khi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngài bắt được Duy Chí thì Lê hoàng phi lại trốn được. Nay viên ngọc quý ấy lọt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vào tay ta, mà ta không biết lợi dụng thì thực cũng uỗng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy ý ngài tính ra sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi thiết tưởng chúng ta đã trải qua bao khó nhọc mới tới được chức này, tôi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thì nhờ có Bình phái hầu Ngô Thị Lang tiến cử, còn ngài thì nhờ sự xông pha mũi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tên hòn đạn tại trận tiền. Thế mà dịp này còn bằng mấy lời tiến cử cùng là những</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sự hiểm nghèo ở nơi chiến địa, có thể nhảy ngay đến chức trấn thủ, hiệp trấn cũng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chưa biết chừng. Nhưng...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng sao nữa, thưa ngài?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng nếu chúng mình chỉ sơ ý một tí là làm cỗ sẵn cho kẻ khác ăn mà thôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thí dụ bây giờ chúng mình nộp tù nhân lên trấn lỵ để quan trấn thủ áp giải về</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">kinh, thì thế nào họ chẳng nhận hão rằng chính họ đã bắt được Lê hoàng phi mà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bao nhiêu công trạng họ nghiễm nhiên toạ hưởng kỳ thành. Chi bằng một mặt ta</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hãy bí mật giam Lê hoàng phi vào một nơi, một mặt ta sai một tên cận tính tức tốc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mang tờ sớ về kinh tâu rằng hai chúng ta đã lao tâm khổ tứ bày mưu lập mẹo trong</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nửa năm trời mới bắt nổi Lê hoàng phi Nguyễn thị Kim. Việc này là việc rất quan</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trọng nên có sớ về triều để hỏi xem phải giải Thị Kim tới trấn Lỵ hay về kinh đô,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">như thế thì huân nghiệp của mình không ai làm mai một nổi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất cả mừng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngài thực có mưu trí hơn người.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lại còn điều này nữa: Tạ đệ một bức mật thư lên thái sư, ca tụng cái nhan sắc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tuyệt thế của Lê hoàng phi. Thái sư vốn...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi hiểu rồi. Vậy ngài nên thảo ngay sớ viết kíp thư đi mới được.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Việc đó tôi xin cáng đáng, nhưng việc giải Lê hoàng phi ngài chưa tiết lộ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cho ai hay đấy?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi mới ngỏ với tên đội Nhất, nhưng hắn là tay tùy tòng trung thành của tôi,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tôi bảo được hắn giữ bí mật.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế thì được rồi. Vả lại không cứ người ngoài mà ngay trong phủ cũng ít</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người biết ràng ta bắt được hoàng phi, vì lúc giải nàng về đây, đêm đã khuya lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà tôi lại giam nàng ở một cái buồng nhỏ trong tư thất, chẳng ai biết hết... Kể</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nàng đẹp lắm đấy chứ, ngài nhỉ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai người đương nói chuyện, bỗng một tên lính ở ngoài chạy vào có vẻ hấp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tấp, kinh hãi. Viên phân suất đứng dậy hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có việc gì thế bay?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên lính cất giọng run run nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm... Yên Phụ đến báo có giặc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất tỏ vẻ lo sợ, chau mày gắt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao nó không báo ở trấn ly? Đâu? gọi nó vào đây.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lát sau, tên lính đưa tối một người nhà quê hiền lành, thực thà, run như</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cầy sấy và nói lắp bắp không ra tiếng, lí nhí không ra hơi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất thét lớn hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mày ở đâu? ở đâu? ở làng nào? Sao hỏi lại không nói?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên kia chớp mắt luôn, đưa tay lên gãi mang tai, luống cuống đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm... Bẩm quan lớn, con người làng Yên Phụ ạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tín bài đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người nhà quê quay ra phía ngoài, vắt vạt áo nâu lên vai, rồi thong thả cởi hầu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bao lần mãi mới lấy ra được một cái bìa nhỏ bằng bàn tay và cuộn tròn. Trong khi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ấy phân suất thì thầm bảo phân phủ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phải cẩn thận lắm mới được. Biết đâu nó không là một tên trong bọn cướp.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ phì cười:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cướp? Thằng ốm đói kia mà là cướp được. Ngài đa nghi quá đỗi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người nhà quê cúi đầu, hai tay dâng tín bài, phân suất đỡ lấy ngắm nghía, xem</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xét từng ly, từng tí, rồi khi áp ngón tay người kia thấy đúng vạch điểm chỉ, liền</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">giao trả cái thẻ mà nói rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mày trình gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm... Bẩm hai quan lớn, có một bọn cướp đông lắm đến đóng ở bến đò Kim</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lũ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đông độ bao nhiêu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm con không đếm, nhưng đông lắm, mà hình như tên đầu đảng là một...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhà sư</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất kinh hãi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Một nhà sư?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm... Vâng... Một nhà sư khỏe lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất nói khẽ với phân phủ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi nghe dân sự đồn đại rằng ở vùng bến đò Kim Lũ vốn có một tên cường</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đạo mặc giả sư đến quấy nhiễu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế bây giờ ngài định sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Để bọn nó hoành hành mãi vùng này, nhỡ đến tai Thái sư thì chúng mình</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cũng khó lòng ngồi yên mà hưởng phú quý. Chi bằng ta đánh rát cho chúng nó</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một trận để chúng nó lẩn đi địa hạt khác mà bóc lột. Hiện phủ ta có hơn hai trăm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lính, tôi đã luyện tập trong luôn mấy tháng nay cũng chỉ vì một mục đích ấy. Vậy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đêm nay xinh lưu lại năm tên giữ súng hỏa mai lòn bao nhiêu binh lính, tôi đem</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">theo hết để trị cho bọn giặc kia một mẽ. Phen này mà tóm được tên sư bí mật giả</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mạo kia thì thực tiếng tăm chúng ta lừng lẫy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất quay ra bảo người nhà quê:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mày dẫn đường cho quan quân, nghe!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên kia chừng hoàn hồn, trả lời trơn chu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ, nhưng con sợ bọn giặc báo thù đốt nhà giết vợ con con mất. Bẩm chúng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nó ghê gớm lắm cơ, ở vùng chúng con ai cũng phải kiêng nể... Vì sáng hôm nay</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chúng dốt nhà con, lại bắt mất trâu, mất lợn, gà của con mổ ăn thịt nên tình con</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">oan ức con mới liều đến kêu quan lớn, xin quan lớn thương tình... Chẳng nhẽ giữa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đời thái bình mà chúng nó cứ hiếp tróc, lấn áp, bóc lột mãi lương dân, chẳng coi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phép nước vào đâu như thế.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ nghe người nhà quê nói lý, mà lại hơi xúc phạm tới oai quyền vua,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quan thì cả tiếng mắng át:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Không được hỗn? Tên kia hãy xuống tại ngồi chờ lệnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi bàn nhau với phân suất đi kiểm điểm cơ đội để tức khắc cất quân.</span></p>', 0, '2022-08-05 01:11:15', '2022-08-05 01:11:15');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(107, 76, 'Chương 7: Bà Hoàng Phi Họ Nguyễn', 'chuong-7-ba-hoang-phi-ho-nguyen', 7, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc bấy giờ chùng vào cuối giờ Dậu. Những tiếng huyên náo nhộn nhịp cất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quân hồi nửa giờ trước đã im bẵng. Mấy toà nhà trong phủ như ngủ say dưới lớp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">màn đen tối yên lặng. Vì đêm hôm ấy là một đêm thượng tuần tháng chạp, mưa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phùn gió bấc, rét buốt đến xương.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong một gian phòng tư thất, phòng chứa lương thực và khí cụ và sửa sang</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thành nơi ngục thất tạm thời, bà Lê hoàng phi họ Nguyễn ngồi ủ rũ nghĩ ngợi ở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bên một ngọn đèn dầu lạc lù mù.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà bình tĩnh nhớ lại ngững sự xảy ra từ hôm trước. Bà không khỏi lấy làm lạ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong vòng bảy tám năm, bà trốn tránh ở hạt Từ Sơn, trừ những bực cựu thần</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trung nghĩa nhà lê thường bí mật lui tới thăm bà, thì không một ai trong đám dân</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gian để ý dò la tung tích bà hết. Họ coi bà như những người sinh trưởng ở vùng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">này, hay ít ra cũng như một người đến ngụ cư không biết từ bao giờ. Bà lại khéo</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">theo phong tục ngôn ngữ, y phục dân quê và đi lại chơi bời với ngững bà chánh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tổng, lý trưởng để làm thân với họ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ở cùng nhà với bà chỉ có một viên quan già mà bà nhận là cha, và người con</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trai viên quan ấy mà bà nhận là em. Người ta cho rằng và goá bụa về ở cùng với</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cha mẹ, thủ tiết thờ chồng, vì gia đình chồng bà trong thời loạn lạc bị giặc giết</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chết sạch và đốt phá mất cả cửa nhà.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Thế mà hôm qua sao bỗng dưng...?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà thở dài, ngước nhìn ngọn đèn sắp tắt đặt trên cái giá cao. Thong thả, bà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đứng dậy cầm cái que khêu bấc, rồi đưa mắt ngắm người thị tì theo hầu nằm co ro</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gối đầu lên hai viên gạch, tiếng ngáy nghe se sẽ, đều đều.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi vừa buồn rầu vừa kinh hãi, vẩn vơ, rón rén lại gần lay thị tì dậy:</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Này em sao ngủ nhiều thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thị tỳ choàng thức giấc, mở mắt nhìn ngơ ngác rồi nhớ lại cảnh bị tù, oà lên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khóc, Hoàng Phi dỗ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lan ơi, khóc lóc như thế có ích chi? Chẳng qua số mệnh thầy trò ta đến lúc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gặp nạn, nên nó xui ra như vậy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lan nức nở:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm bà... Số mệnh gì? ... Con biết... Con biết đứa nào... Tố giác rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi vội gạt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đừng ngờ oan cho ai hết, tội nghiệp con ạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Còn ngờ gì nữa, bẩm bà... Chính... Thằng...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bỗng một người lính mở cửa vào bảo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Quan truyền im ngay? Phải biết, người ta cho phép cùng ở một buồng với</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhau như thế không phải để mà than vãn. . . Nếu không tuân lệnh, thì ta tống cổ con</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bé kia ra ngoài tức khắc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dút lời, hắn bước ra, đóng sập cửa phòng lại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi buông một tiếng thở dài nhìn theo, Lan thì ngồi xuống ôm lấy chân</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chủ mà nức nở. Hoàng phi ghé vào tai thì thầm:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Em Lan, em nín đi kẻo chúng nó vào chia rẽ thầy trò mình ra bây giờ... Em</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">có đói không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lan sụt sịt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Khốn nạn ? . . . Con còn. . . Bụng đâu. . . Tưởng tới ăn?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng em cũng cố gượng mà ăn một tí chẳng lẽ.. Còn nắm cơm với ít muối</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vừng ta để phần em đấy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thôi, mới bà sơi... Con không đói.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Từ tối hôm qua đến bây giờ chưa có một hột cơm lót dạ lại còn không đói?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếng người lính canh ở ngoài thét:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Im ngay?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dưới ánh đèn lờ mờ, hoàng phi và người thị tỳ nhìn nhau, ứa hai hàng lệ. Rồi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">muốn giữ không nói chuyện nữa để khỏi bị xa chủ, Lan ra chỗ cũ nằm ngủ, kéo</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chiếu đắp chùm kín đầu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi ngồi lại một mình trên cái giường tồi tàn xiêu vẹo, không dám động</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đậy, sợ tiếng lát tre kêu lạch cạch, khiến tên lính canh nghe thấy lại mở cửa vào</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chăng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi yên lặng, bà để tư tưởng lặng lẽ theo giòng. Sự ngờ vực của thị tỳ Lan</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khiến bà không thể không nghĩ tới lòng phản trắc của những người sống quanh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mình bà. Bà tự nhủ thầm:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nguyễn gnụ sử thì chắc không phải rồi. Bậc lão thần ấy, ta kính trọng như</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người cha già, khi nào lại nỡ hại ta. Hay con trai ngự sử? Ư, biết đâu? Bấy lâu ta</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thấy tính nết hắn biến cải đi nhiều lắm. Mà con người hay nhìn trộm thì cũng khả</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nghi lắm đấy. Ngoài hai cha con hắn ra thì chẳng còn ai. Hay Đào Phùng ở Phù</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lưu?... Có lẽ nó chăng? Trời ơi, ta à một món hàng cao giá lắm kia mà? Bắt được</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ta đem nộp đem bán cho bọn Nguyễn Quang Toản, Bùi Đắc Tuyên thì làm gì</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không được dăm nghìn quan tiền hay ít ra cũng được bổ đi phân tri, phân suất ở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một hạt béo bở... Chúng nó chỉ vì lợi tuốt, chứ trung nghĩa gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà chép miệng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thôi? Mỗi cái ta chẳng nên oán trách nghi hoạc ai là hơn hết: Cớ sao ta</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không đủ can đảm mà yên lặng chờ chết?...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi lim dim cặp mắt nhìn vào xó tối như thấy hiện ra thanh mã tấu của</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tên đao phủ. Bà mỉm nụ cười khinh bỉ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chẳng lẽ chúng nó lại xử trảm một người đàn bà, một vị hoàng phi? Chà?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu chúng nó giết cho ta chết? Ta cũng chẳng còn mong sống ? . . . Nhưng ta sợ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chúng nó không giết, mà lại chỉ làm nhơ nhuốc tấm thân ta...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà nghiến răng nguyền rủa:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ba đời bọn giặc cỏ...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi bà cúi xuống ngắm cái dây lưng nghĩ đến sự tự ải. Gian phòng bỗng tồi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sầm lại. Thì ra mãi tư lự, bà không để ý đến bát dầu cạn từ bao giờ. Bà sợ hãi đưa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mắt cố nhìn bốn phía, nhưng chẳng thấy gì hết, lắng tai nghe tiếng dế kêu ngoài</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hiên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhớ rằng ở một phía tường có cái cửa sổ, rào chắn song sắt, bà rón rén đứng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dậy lần mò ra mở. Nhưng vừa khẽ hé thì theo luồng gió lạnh và giọt mưa nhọn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hoắt tạt vào mặt, tiếng quát lớn của tên lính đứng canh bên ngoài:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ai?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi vội đóng sập cửa lại lên giường cuộn thân trong tấm chăn màu nâu</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mốc, sặc mùi hôi hám, và vờ nằm ngủ say, vì bà chắc thế nào tên lính kia cũng sắp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sửa vào phòng để thốt lời quở mắng hỗn xược. Chờ mãi không thấy gì, bà mới</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hoàn hồn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếng trống, tiếng mõ, tiếng kiểng mỗi lúc một thưa. Hình như vì đêm lạnh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quá, các chú lính không buồn cầm canh nữa...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sang canh hai được một lúc lâu, thốt có tiếng ầm ầm ở ngoài cổng phủ. Tiếp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tiếng ngựa hí, người kêu. Hoàng phi hoảng hốt lo sợ, đoán chắc rằng đội binh kéo</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đi chinh phạt buổi chiều đã trở về. Xót cảnh mình, bà nghĩ ngay tới số phận ngững</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người đàn bà nào đó có lẽ vừa bị toán quân kia bắt giam như bà.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng đội binh về thì sao lại huyên náo, rối loạn đến thế được? Mà hình như</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">họ đánh nhau chí mạng thì phải, vì nghe có tiếng loảng xoảng của gươm, giáo</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đụng chạm nhau, và súng hỏa mai nổ đến hơn mười phát.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếng hò hét mỗi lúc một ần chỗ bà nằm. Thị tỳ Lan thức giấc thấy tối mịt thì</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sợ hãi, vừa khóc vừa hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cái gì thế bà?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi cố tự trấn tĩnh, trã lời:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ta cũng không rõ em ạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lan lại bên giường:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bà đâu? Bà đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ta đây, em đừng lo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bỗng bà thấy rùng mình, vì nghe có kẻ thét ngay ở gian nhà bên cạnh, nơi tu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thất của viên phân phủ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đừng để nó trốn thoát? Trói gò lấy nó ? Còn vợ con nó thì không được đụng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tới</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lan ghé mắt nhòm qua khe cửa, rồi quay lại cất giọng run run bảo Hoàng phi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm bà, có lẽ cướp ? Họ đất đuốc sáng trưng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại có tiếng thét:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nó trốn rồi à? Hãy để nó đấy? Ta đến phá cửa phòng chứa lương thực kia đã.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Việc ấy cần kíp hơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lan tru lên khóc:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bà ơi? Bà có nghe thấy không? Họ sắp đến phá buồng này bây giờ... Tính</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mạng bà... Con lo lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lan định đi tìm bàn ghế, đồ đạc để chặn cửa, nhưng trong phòng tối đen như</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mực không trông rõ một vật gì. Mà ở ngoài thì người ta đã bắt đầu phá cửa, mỗi</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tiếng đập như đâm mạnh vào trái tim hai người bị giam. Xen lẫn với tiếng phá</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phách có tiếng đàn bà, trẻ con khóc như gì và tiếng một tên lính van lơn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lạy các quan, tha cho con, con xin nộp chìa khoá buồng để các quan khỏi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phải phá.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy chìa khóa đâu? Đưa mau.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi biết rằng sắp có sự biến cố xảy ra. Song bà cố giữ hết can đảm ngồi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">im, chờ đợi. Lan thì lăn ra đất khóc thảm thiết...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cánh cửa mở tung. ánh sáng hai cây đuốc ùa vào trong phòng. Hoàng phi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đứng phắt dậy, đăm đăm nhìn ra phía ngoài. Một võ tướng chạy thẳng lại gần nói</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lớn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bà đi ngay cho.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi trợn mắt, dõng dạc hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đi đâu? Chúng mày định bắt ta đi đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Võ tướng có vẻ lo lắng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trời ơi, xin bà đừng trùng trình nữa, đi ngay cho kẻo lỡ việc bây giờ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi vẫn cương quyết:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ta thà chết ở trong phòng này, chứ không đi đâu hết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lan thì vừa níu lấy vạt áo chủ khóc vừa nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lạy các ông, các ông tha cho bà tôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">võ tướng lại gần. Lan tưởng chàng sắp ra tay làm ác, kêu hét lên. Nhưng võ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tướng chỉ nắm lấy cánh tay nàng và ghé vào tai thì thầm mấy câu. Nghe dứt lời</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngàng vui mừng quay lại nói với Hoàng phi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Xin mời bà đi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi cũng chẳng hiểu ra sao, song nghĩ bụng thử cứ liều xem, chết với</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bọn lạ mặt này, hay chết ở trong phủ thì phỏng có khác gì nhau. Bà liền lạnh lùng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đi theo bọn kia. Khi mọi người đã ra ngoài, võ tướng đẩy cả gia đình viên phân</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phủ cùng người lính canh vào trong phòng, khóa trái cửa lại. Đoạn chàng lớn tiếng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dặn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hễ kêu, ta giết chết, nghe?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ở sân phủ đã có một toán binh đứng tề chỉnh sắp hàng. Võ tướng mời Hoàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phi lên ngựa, rồi cùng ba võ tướng khác đi kèm chung quanh kéo quân lên đường.</span></p>', 0, '2022-08-05 01:11:40', '2022-08-05 01:11:40');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(108, 76, 'Chương 8: Cửa Bắc', 'chuong-8-cua-bac', 8, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng chiều hôm ấy, vùng Vĩnh Kiều, Tiêu Niệm, nhân dân nhớn nhác nhìn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhau kinh ngạc. Họ vừa nghe thấy chuông chùa Tiêu Sơn gióng giả oang oang từ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trên cao gieo xuống. Trước đỉnh xóm Dương một ông già bảo mọi người đứng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xúm xít chung quanh:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có tiếng chuông.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người trả lời:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vâng, chùa Tiêu Sơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- ử Lạ nhỉ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vâng, lạ thật. Chẳng nhẻ vô có thỉnh chuông chiều hai lần.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thôi chắc chú tiểu Mộc lú ruột rồi, đã thỉnh chuông mà còn tưởng là chưa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thỉnh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuông đổ hồi, rồi im bẵng. Ai nấy cất tiếng cười. Có người nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Biết ngay mà? Đích thực chú Mộc lú lấp rồi. Chẳng thế lại thỉnh có một hồi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chuông.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thì ra dứt hồi chú mới nhớ ra rằng nhầm nên thôi không thỉnh nữa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kỳ thực chú Mộc không lầm, mà ở chùa Tiêu Sơn cũng không ai lú ruột cả.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi chiều hôm ấy có hồi chuông thứ hai, hồi chuông bất thường, là chỉ vì sư Phổ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tĩnh dùng cách báo hiệu ước định ấy để gọi đồng đảng đến tụ hội ở Tiêu Sơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyên sau bữa tiệc trên lầu Tiêu Lĩnh, Quang Ngọc, Phạm Thái, Lê Báo đều</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đi ngủ, vì cứ lời Quang Ngọc thì tối hôm ấy bọn họ cần phải ra tay làm một việc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khó khăn gì đó Chẳng nói thì chúng ta cũng đoán được là việc đi cứu bà Lê hoàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phi, mà tối hôm trước nhà sư Phổ T nh đã lần mò tới phủ nha dò xem tung tích rất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tường tận.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc lập chiến lược như thế này:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Buổi trưa hôm ấy cho Nhị Nương quẩy gánh nồi đất đến phố phủ bán và lảng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vảng qua phía cửa Bắc rao to ba câu liền: \"Nồi đất ai mua?\" Tức thì sẽ có người</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trong phủ đi ra. Người ấy chính là cai lệ Tảo, một thám tử mà đảnh Tiêu Sơn đã</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cho vào phủ làm lính. Nhị Nương sẽ đưa cho Tảo một phong thơ trong đó Quang</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngọc dặn đêm nay phải xoay hết cách để canh hai được coi giữ phòng bà hoàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phi. Vào khoảng cuối canh hai, ba tráng sĩ họ Trần, họ Phạm, họ Lê, cùng hiệp nữ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">họ Nguyễn sẽ do cữa Bắc vào phủ, vì cửa Bắc đổ nát đã lâu ngày mà vẫn chưa sửa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sang lại, khiến người nào biết rõ, chỉ lấy cái gậy bẩy mạnh lên là cánh cổng mở ra</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tức khắc Vào trong phủ rồi, công việc giết tên lính canh bên cửa sổ cố nhiên sẽ rất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dễ dàng lặng lẽ, vì đêm hôm ấy trời tối đen như mực, hai người giáp mặt mới</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trông thấy nhau. Bấy giờ Phạm Thái sẽ bắt chước tiếng mèo để báo cho Tảo biết</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mà mở cửa phòng ngục. Chỉ còn một việc mời hoàng phi ra cửa Bắc lên ngựa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phóng nước đại. Có điều này Quang Ngọc hơi ngại, là sợ hoàng phi không chịu đi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trốn, vì bà chưa biết mặt ba tráng sĩ. Bởi vậy chàng mới nghĩ đến đem Nhị Nương</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đi theo: Nhị Nương thường vẫn lại thăm hoàng phi và hai người chơi với nhau</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thân mật lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chiến thuật xếp đặt xong đâu đấy, Quang Ngọc đi ngủ để lấy sức, một nụ cười</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sung sướng nở trên cặp môi khi chàng thức giấc, vì chàng biết chắc rằng thế nào</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chàng cũng sẽ hoàn toàn đắc thắng. Chàng liền gọi Phạm Thái và Lê Báo dậy bàn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">việc Lê Báo hậm hực, trách:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao bậy giờ đại huynh mới bảo cho hai em biết?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc nghiêm sắc mặt dõng dạc hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lê hiền đệ còn nhớ lời thề không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giọng rắn rỏi, quả quyết làm cho Lê Báo sợ hãi đứng im. Muốn nhân thể luyện</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cho tính nết của Lê Báo bớt cương ngạnh, Quang Ngọc rút kiếm chém xuống bàn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nol:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Quân lệnh truyền ra, ai không tuân, ta sẽ chặt đầu ngay lập tức.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái và Lê Báo chắp tay cúi đầu chờ lệnh. Quang Ngọc cặp mắt tròn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xoe, có vẻ oai phong lẫm liệt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc bấy giờ vào khoảng giờ Dậu, vì chùa vừa thỉnh chuong chiều xong, mà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chuông chiều bao giờ cũng thỉnh vào cuối giờ Thân. Bỗng một chú tiểu hấp tấp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chạy lại thưa:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bạch sư ông, có một công tử cười ngựa cùng đi với ba ky sĩ theo hầu, nói xin</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lễ phật. Quang Ngọc tra kiếm vào vỏ, mỉm cười đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Được, cứ mời công tử lên nói có ta chờ đây.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái vội giữ chú tiểu lại bảo Quang Ngọc:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đại huynh nên hãy cho mời người ta ngồi lại phòng trai đã.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng khách đã bước tới, lễ phép chấp tay chào. Lê Báo trừng mắt đăm đăm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhìn, vì khách là một trang thiếu niên, mắt sắc như nước, miệng tươi như hoa, mặt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trái xoan, xinh xắn và da trắng phớt hồng hồng. Thực là một trang diễm lệ công tử.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo thì thầm bảo Phạm Thái:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hắn đẹp như con gái đẹp?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc nói với khách:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mời công tử vào lễ phật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khách yên lặng bước thẳng tới lầu Tiêu Lĩnh, Lê Báo kinh ngạc nhìn Phạm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thái. Nhưng thầy Quang Ngọc đi theo, nên hai chàng không dám hỏi, cũng bước</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">theo lên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tới lầu, Quang Ngọc hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khách đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Xong.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lành, dữ ra sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tốt, tốt lắm?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc vui mừng, qua;y lại giới thiệu với Lê Báo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hiền đệ khao khát được biết mặt Nhị Nương. Vậy Nhị Nương đấy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái sửng sốt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhị Nương?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi chàng nhìn kỹ lại và bật cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trời ơi? Quý nương khéo cải trang quá, chính Thái này cũng không còn nhận</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ra được .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo làm ra bộ thạo xem tướng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thảo nào tôi ngờ ngợ. Nam tử gì mà cặp mi lại thanh như lá liễu thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cười lớn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nói hậu thì ai nói không được. Nhưng ta hãy đễ Nhị Nương thuật lại cuộc do</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thám đã.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhị Nương trỏ Lê Báo hỏi Quang Ngọc:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đây là công tử mắc nạn ở tửu quán tối hôm qua?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa hiền muội chính đó. Công tử đã làm lễ tuyên thệ nhận Ngọc này với</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái là anh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ý chừng để đáp lại cái nụ cười chế nhạo của Nhị Nương, Lê Báo hỗn xược nối</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lời Quang Ngọc :</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy cố nhiên Báo đây phải nhận tiểu thư là chị.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhị Nương chẳng chút e lệ, ung dung nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhị Nương xin vui lòng nhận Lê Báo là em.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi nàng cười nói tiếp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bọn ta kết làm anh em, chị em là phải lắm. Có thứ giây liên lạc thiêng liêng</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hơn tình huyết giàng buộc chúng ta lại với nhau. . . Giây liên lạc ấy là sự phục thù.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vâng chúng ta tuy không cùng một huyết thống, nhưng giòng máu ở bốn cái đầu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">của bốn người cha bị chém đã cùng phung vào mặt chúng ta khiến cho chúng ta</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phải trở nên ruột thịt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo nghe mấy lời khảng khái ở miệng xinh xắn một thiếu nữ thốt ra mà lấy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">làm cảm động. Nhị Nương lại nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trần đại huynh cùng nhị vị công tử đã làm lễ tuyên thệ kết làm anh em, chớ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">như thôn nữ này thì chỉ xin nhận miệng mà thôi, thiết tưởng như thế cũng đủ lắm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rồi, lọ là còn phải thề thốt nặng lời.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi nàng mỉm cười nói bông đùa:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa Trần hiền huynh, năm nay em hai mươi ba tuổi, vậy tùy hiền huynh đặt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đâu em xin ngồi đấy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cũng cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Còn phải đặt đâu nữa. Nguyễn hiền muội hai mươi ba tuổi thì cố nhiên phải</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">là em hai, vì nhờ trời Ngọc này ra trước hiền muội hai năm, còn hai nhị đệ đây lại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ra đời sau hiền muội một người ba năm, một người bốn năm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo hậm hực, tức tối, cố nói châm chọc một câu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa hiền huynh, đối với hai em, Nguyễn Nhị Nương là chị, thì đã hẳn đi rồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chứ sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhị Nương quắc mắt đáp lại giọng đùa rỡn của Lê Báo, rồi nàng quay về phía</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc nói tiếp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa hiền huynh, ban nãy ngu muội có nói kết quả công việc của ta sẽ được</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tốt đẹp hoàn toàn là vì ngu muội biết một sự bất ngờ vừa xảy ra. . . Thực có trời</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phật phù hộ tính mệnh hoàng phi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc nóng ruột vội hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sự gì xảy ra mà may mắng đến thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phủ Từ Sơn chỉ còn độ một chục tên lính ở lại canh giữ, mà trong số đó có</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cai Tảo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngọc kinh ngạc:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy hơn hai trăm lính đi đâu cả?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngu muội gặp trên con đường Thọ Khê... Chừng họ đi Kim Lũ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế thì hồng phúc nhà Lê còn to.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng hiền huynh đừng vội mừng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao vậy?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- vì ta chưa biết hơn hai trăm quân đó kéo đi đâu Nghiêm Xá, Ngô Xá, Phú</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mẫn, Yên Phụ, Kim Lũ, đều là lulung nơi sào huyệt của đảng ta. Thế mà bọn kia</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sắp sửa sang đò Thọ Khê... Hiền huynh thử nghĩ xem ta có đáng lo không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cũng có lý.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dút lời, Quang Ngọc ra lịnh thỉnh một hồi chuông. Vì thế mà nhân dân vùng</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vĩnh Kiều, Tiêu Niệm đã hai lần nghe thấy tiếng chuông.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lát sau, đồ đảng Tiêu Sơn lục tục kéo đến có tới vài chục. Quang Ngọc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phân phát cho mỗi người đi một ngả để do thám sự hành động của đội quân Từ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sơn, và cấp báo cho các nơi căn cứ của đảng biết mà phòng bị.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái cũng xin đi, nói mạn Kim Lũ, Nghiêm xá thuộc quyền hộ vệ của</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mình. Nhưng Quang Ngọc giữ chàng lại để cùng đi cứu hoàng phi đã, rồi đến giờ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tý xong công việc, chàng sẽ về Nghiêm xá cũng kịp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vì quan quân đi chinh phạt bao giờ cũng chậm như sên, ta không vội lo ngại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giá ở thời Quang trung thì cũng sợ đấy. Nhưng ngày nay chỉ là thời Bùi Đắc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuyên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người cười rộ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bàn định, cắt đặt xong đâu đấy, thì đêm đã khuya. Vào khoảng nửa giờ Hợi,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ba tráng sĩ nai nịt gọn gàng, đội mũ đầu mâu để che cái đầu trọc, rồi cùng Nhị</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nương lên ngựa (bốn con ngựa ấy chính Nhị Nương đã đem đến chùa).</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tới cửa Bắc phủ Từ Sơn, bốn người kìm giây cương đứng lại nghe ngóng, và</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lấy làm lạ rằng trong phủ không có một tiếng động đưa ra. Trống, mõ cầm canh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đều im phăng phắc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng bọn họ cũng vượt qua hào và bẩy cánh cổng mà vào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc bảo ba người hãy ở lại sau để một mình chàng đi do thám động</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tĩnh ra sao đã. Chàng rón rén đến bên cửa sổ: Tên lính canh không có ở đấy. chàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">liền bắt chước tiếng mèo: Vẫn không ai ra. Mấy nếp nhà như bỏ hoang đã lâu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngày.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc quay ra thuật lại sự thể với mới người rồi bàn cùng vào cả trong</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tư thất xem sao.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo hăng hái xin đi trước dẫn đường, tay nhăm nhăm cầm thanh kiếm tuốt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trần</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai địch thử thứ nhất mà chàng gặp là hai người bị trới gò vào hai cột nhà. ở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">giữa hai cột ấy có cắm một ngọn đuốc cháy đã gần tàn, và chiếu ánh sáng le lói lên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mặt hai người khốn nạn. Quang Ngọc cởi giây cho họ mà hỏi rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vì sao hai người lại bị trói?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một người - người lính - thuật lại những sự đã xảy ra. Quang Ngọc kinh hãi,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sửng sốt hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- vậy ra chúng nó cướp mât bà hoàng phi đi rồi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vâng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chúng nó đi về ngã nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa, tôi không được biết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dút lời, tên lính ôm đầu chạy mất. Quang Ngọc tức thì ra lịnh đuổi theo bọn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cướp</span></p>', 0, '2022-08-05 01:12:11', '2022-08-05 01:12:11');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(109, 76, 'Chương 9: Trong Rừng Đình Bảng', 'chuong-9-trong-rung-dinh-bang', 9, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc đi trước dẫn đường, rẽ cương lượn qua cửa Tây, tức cửa chính</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phủ Từ Sơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo cho ngựa chạy ngang hàng và hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao đại huynh lại cho ngựa chạy về phía này?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng Quang Ngọc không trả lời, im lặng cho ngựa phi thật nhanh mãi khi tới</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chỗ ngã ba mới kìm cương nhảy xuống đất. Nhị Nương cũng nhanh nhẹn nhảy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngoắt theo xuống, hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đại huynh dừng lại làm gì vậy?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hiền muội không biết đây là ngã ba ư?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy ở bên đường có một người bắt ếch giơ cao bó đuốc nứa đứng nhìn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc gọi lại bảo soi ánh lửa xuống đường xem. Chàng chau mày chép</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">miệng lẩm bẩm nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế thì lạ quá nhĩ Cả hai ngã cùng có vết móng ngựa... Hay họ đến một lối</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mà đi một lối... Cũng vô lý, vì xét kỹ thì vết chân ngựa đều đi tản ra hai phía.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo thấy Quang Ngọc loay hoay tìm đường thì cười bảo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao hiền huynh không hỏi ngay tên bắt ếch xem họ chạy đường nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người bắt ếch ngơ ngác:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa ai cơ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng Quang Ngọc đã theo ý Lê Báo và lớn tiếng hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tên kia, vừa giờ có một bọn qua đây. Vậy họ chạy về ngã nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người bắt ếch chỉ con dường Kim Lũ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có hơn mười người cười ngựa đi về ngã này.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đã bao lâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Họ đi chưa xa đâu. Nhưng các ông làm gì mà cưỡi ngựa đi chơi khuya thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngững ông ban nãy cũng vậy, làm ếch của tôi sợ trốn biệt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái chú mục nhìn thẳng vào mặt người bắt ếch thấp thoáng dưới ánh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lửa. Thì thầm chàng bảo Nhị Nương:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thằng này không phải làm nghề bắt ếch đâu, trông dữ tợn lắm, mà ngôn ngữ,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cử chỉ của nó có vẻ bướng bỉnh, tuy nó cố làm ra bộ rù rờ, ngớ ngẩn, sợ hãi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có lẽ Quang Ngọc cũng cùng một ý nghĩ, nên chàng vặn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Anh nói láo...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng ngừng bặt, rồi nhảy phắt lên mình ngựa vẫy mọi người theo đường Phù</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lưu, Đình Bảng thẳng tiến. Lê Báo hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao người ta nói ngã kia, hiền huynh lại đi ngã này?</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cười đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế thì hiền đệ thật thà quá. Hiền đệ phải biết ban chiều khi viên phân suất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rầm rộ dẫn quân đi, thế nào bọn kia chẳng rõ. Nhất họ lại đã rắp định đêm nay đến</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phủ cướp hoàng phi thì họ càng phải xem xét binh thế trong phủ lắm. Thế mà họ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">còn đi cùng một đường với quan quân, thì họa chăng họ không óc mà ngu huynh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thì chắc rằng họ có óc. Đó là một lẽ. Lẽ nữa là trước đây một lát ở trong phủ có</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đánh nhau. Vậy thì tên bắt ếch kia, nếu là một tên bắt ếch, sao còn đủ can đảm ở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lại đây bắt ếch. Nó đứng ngay chỗ ngã ba, há không phải chỉ cốt để trỏ đường láo</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cho quan quân đuổi theo. Nó có ngờ đâu quân đuổi theo lại chính là bọn ta, nghĩa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">là bao giờ cũng khôn hơn quan quân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng thích chí cười ha hả. Mọi người cũng cất tiếng cười theo. Lê Báo hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Còn vết chân ngựa sao lại có ở cả hai ngã đường?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Điều ấy thỉ phỏng khó gì. Cho ngựa phi về đường kia, rồi khi trở lại thì rẽ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xuống ruộng. Hiền đệ không thấy ruộng khô à?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bọn ky sĩ vượt qua làng Đình Bảng, Quang Ngọc kìm cương lại bảo mọi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người :</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bây giờ cho ngựa đi bước một, vì đây gần đến nơi rồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái kinh ngạc hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao đại huynh biết?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Làm một ông tướng phải biết địa thế khắp vùng mình hoạt động. Ta xét ra</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gần đây có hai nơi hiểm trở, nếu ban ngày cũng ít người lai vãng: một là rừng Sặt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ở mạn Tràng Liệt, hai là rừng Bát Đế ở sau làng Đình Bảng. Rừng Sặt ở gần</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đường Thọ Khê, chắc họ không đến. Còn rừng Bát Đế thì họ có thể giấu người</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trong đó được. Thực là một nơi thâm u. Nguyên cùng vua chúa đời Lý đến nghỉ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mát, bỏ hoang lâu ngày cây cối mọc um tùm thành rừng. Lại thêm có hào chung</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quanh khiến khó ai tìm được lối vào, chắc thế nào bọn kia chẳng có thuyền chờ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sẳn ở trước cửa đền Lý Bát Đế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người đều phục tài xét đoán của Quang Ngọc. Nhưng Lê Báo cười thầm,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chỉ mong Quang Ngọc đoán sai để sau này chế riễu chơi. Quang Ngọc lại nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Còn một điều ngu huynh nghĩ mãi không ra. Là bọn kia cướp hoàng phi làm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gì? Hay là lũ trung thần nhà Lê đến cứu bà? Phải chờ khi nào gặp nhau mới biết rõ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bấy giờ chỉ còn cách đến Lý Bát Đế độ vài trăm bước, Phạm Thái bàn buộc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngựa một nơi, rồi đi bộ lại đền. Chàng nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngày theo Nguyễn Đoàn, ngu đệ cũng đã nhiều lần trốn ở đó. Quả thực là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một nơi bí hiểm nhưng ngu đệ thuộc đường lối trong rừng như đường lối chùa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiêu Sơn, vậy xin đến dọ thám trước đã, rồi hãy kéo đại quân đến sau.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đại quân có tất cả bốn người.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhị Nương khảng khái nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bốn người này lại không địch nổi một trăm quân ư? Vậy thì Phạm hiền đệ cứ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đi trước xem binh thế họ ra sao, rồi về báo, dẫu họ đông đến đâu ta cũng không sợ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái tuân lời đi thẳng. Biết chắc thếo nào bên địch cũng có quân canh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trên con đường tới rừng, chàng rẽ xuống ruộng đi vòng về phía sau đền rồi quay</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lên mạn Nam. Chàng biết ở đó có một quãng hào vừa hẹp vừa nông, nước chưa tới</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thắt lưng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến đlo Phạm Thái cởi quần áo ra lội qua để vào rừng. Trời rét, nước giá</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">buốt, nhưng chàng chẳng coi vào đâu. Cái thân chiến sĩ phiêu lưu đã từng xông</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">pha tên đạn, đã từng làm quen với cái chết giữa đám can qua, có quản gì một dòng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nước lạnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tới rừng, chàng se sẽ mặc lạiquần áo vào, rồi cúi lam khom, tay cầm kiếm, rón</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rén đi quanh bờ hào một vòng để dò xem trong rừng có ánh lửa không. Khi đến</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mạn Bắc khu rừng, và nghe có tiếng sột soạt răng rắc như tiếng bàn chân đi lên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đám cành lá khô, chàng vội nằm rạp xuống, vì chàng sợ ở trong rừng nhìn ra, bên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">địch sẽ thấy bóng chàng thấp thoáng in trên nền trời.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Đích là họ núp ở đây rồi, vì rõ ràng có tiếng nhiều người nói chuyện. Trần đại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">huynh thực đoán việc như thần\", Phạm Thái vừa nghĩ vậy, vừa men bờ hào, vòng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sang phía đông rừng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bỗng chàng mừng quýnh, suýt buột miệng kêu lên. Một chiếc thuyền buộc ở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gốc cây si cỗi, dưới đám lá rườm rà phủ xuống che lấp.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng bước vào thuyền cầm bơi chèo nhẹ nhàng chở sang bờ bên kia rồi hấp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tấp về chỗ cũ báo tin cho anh em biết. Tức thì cả bọn kéo đến thuyền bơi sang bên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rừng. Vừa bước chân lên đất thì một bọn đông ẩn sau khóm cây xồ ra ai nấy tay</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cầm khí giới. Một người hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Các ngươi đến đây tìm cái chết, phải không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chẳng nói chẳng rằng anh em Quang Ngọc xông vào múa kiếm đánh liền. Bên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">địch, có ai thét:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Khoan? Đánh nhau phải có cớ. Vậy vì cớ gì chúng ta đâm chém nhau?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo hùng hổ cầm thanh kiếm dài vẫn đứng giữ miếng. Nghe bọn kia hỏi,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chàng liền trả lời:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chẳng vì cớ gì hết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người kia cười, ôn tồn nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Xin tiểu tướng đừng vội giận. Ta hỏi thế là vì trước ta vẫn tưởng chư tướng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">là quân trong phủ đi đuổi bắt chúng ta Nhưng thiết tưởng đội binh trong phủ khi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nào lùng biết mà đến đây. Mà dù có đến đây nữa cũng chẳng có đủ can đảm dám</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lội qua hào để vào tới rừng. Vậy ta hỏi, sao chư tướng lại lần mò theo chúng ta?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo nóng nẩy thét:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vì bọn ngươi cướp bà hoàng phi đem đi. ta hãy hỏi: Có phải các người định</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đem bà giải nộp để lĩnh thưởng chăng?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người kia mắng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đồ hỗn xược? Nếu ta không thương ngươi còn nhỏ dại, thì ta đã thí cho một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mũi kiếm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo nổi thịnh nộ xông vào đánh, Quang Ngọc phải lôi bạn mà bảo rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao hiền đệ hấp tấp thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi chàng nói với bên địch:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi xem ra các ông đều là tay khảng khái có dõng cảm. Chắc không phải là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bọn cướp tầm thường. Vậy sao chúng ta không đem lễ nhượng ra đối đãi nhau, dù</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vì việc nghĩa phải đánh nhau đi nữa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một người bên địch đáp lại:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ư, ông này biết điều đấy, chớ như cái ông kia thì hung hăng quá. Các ông</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phải biết, đánh nhau thì chúng tôi cũng vui lòng đánh nhau với các ông. Nhưng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bên các ông có bốn người mà bên chúng tôi những hơn hai chục, chẵng lẽ lấy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhiều lấn ít, e không tiện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo thét:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- anh em ta không sợ nhiều đâu. Đánh thì đánh ngay, không cần phải trì hoãn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người kia giọng chế nhạo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hãy khoan? Can gì mà vội thế? Để đến sáng cũng không muộn kia mà... Bây</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">giờ các ông đã đến đây, chúng tôi hãy xin lấy địa vị chủ nhân mời các ông lại đằng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhà xơi chén rượu với chúng tôi cho vui.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy anh em Quang Ngọc do dự, người kia cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Các ông đừng ngại, chúng tôi không coi các ông như một bọn tù binh vô giá</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trị đâu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lê Báo hầm hầm nổi giận:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- à bọn này láo, dám bảo chúng ta là tù binh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người kia giọng bình tĩnh ôn tồn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Gớm? ông này sao mà nóng như Trương Phi thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái từ nãy vẫn đứng im. Chàng bỗng thủng thỉnh tiến đến gần bên địch,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dõng dạc nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Các ông đã lấy lễ độ chủ nhân mà đón tiếp chúng tôi lẽ nào chúng tôi lại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không lấy lễ độ tân khách mà nhận lời. Vậy thanh kiếm của tôi đây tôi xin gửi các</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ông (vừa nói chàng vừa théo kiếm đưa cho bọn kia). Sau khi cùng nhau hội ẩm,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mà chúng tôi cần phải đấu gươm, thì lại xin các ông trả kiếm tôi. Còn như nếu các</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ông không phải là tay hảo hán mà hất định chiếm đoạt thanh kiếm của tôi, thì lúc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đlo ta hãy nói chuyện. Xin các ông kíp dẫn đường cho chúng tôi theo về nhà,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chẳng trời sáng thì lỡ mất cả công việc của chúng tôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cảm động vì khí phách anh hùng, vì sự thành thực và nhã nhặn của Phạm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thái, bên địch lễ phép đưa anh em chàng tới một nếp nhá tranh làm ở giữa mấy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khóm cây đầy lá che kín mít tứ phía, sau khi đã đi quanh co trong rừng rậm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa bước chân vào trong nhà, Nhị Nương kinh hoảng kêu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trời ơi? Bà hoàng phi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quả thực, trên một cái ổ rươm giải chiếu, bên ngọn đèn dầu ánh sáng lờ mờ,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hoàng phi đương ngồi hơ hai bàn tay lên trên than hồng đựng trong cái nồi đất. Bà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngước mắt đăm đăm nhìn mấy người lạ mặt có vẻ lo lắng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ai đấy? nghe tiếng hình như quen quen.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Em đây mà? Em Nhị Nương mà bà không nhận ra sao? (Trong khi trốn tránh,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hoàng phi thường xưng chị với Nhị Nương, và gọi Nhị Nương bằng em).</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi mừng quýnh, đứng dậy ôm lấy Nhị Nương vừa khóc nức nở vừa kể</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Em Nhị Nương ơi... Chị đã tưởng không bao giờ còn nhìn thấy mặt em nữa...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai ngờ trời chưa nỡ chia rẽ chị em ta.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhị Nương thuật lại cho bà nghe công việc xếp đặt từ hôm trước để tối nay</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đến phủ phá ngục cứu bà ra, ngờ đâu khi đến nơi thì được tin một bọn đã cướp bà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đem đi . . .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một người đứng gần đấy cười có vẻ tự phụ nói riễu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hừ ? Trâu chậm uống nước đục ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi trỏ người ấy bảo Nhị Nương:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đây là Đào Phùng, người làng Phù Lưu. Chính Đào quân đã họp anh em đến</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cứu chị. ơn ấy thực không bao giờ chị dám quên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tâu lệnh bà, nhà kẻ hạ thần đời đời đội ơn thánh đến, nay kẻ hạ thần dẫu chết</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cũng chưa đủ báo đền, lệnh bà nói đến ơn huệ làm chi, khiến hạ thần thêm xấu hổ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Để lệnh bà bị quân giặc bắt được, tội kẻ hạ thần cũng đã nặng lắm rồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi rót một chén rượu đầy đưa cho Đào Phùng mà rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đào anh hùng trung nghĩa ai bì kịp? Xin tặng anh hùng một chén rượu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng đỡ lấy nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lệnh bà ban, hạ thần xin bái lĩnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi đưa mắt nhìn Quang Ngọc, Phạm Thái, và Lê Báo đứng chắp tay ở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một bên và thì thầm hỏi Nhị Nương:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ai thế em?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tâu lệnh bà, đó là ba tráng sĩ, bạn thân của em, đã cùng em kết nghĩa anh em.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi rót ba chén rượu nữa rồi bảo Nhị Nương bưng mời ba chàng. Đoạn,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bà hỏi Đào Phùng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Công tử đã cứu tôi thoát nạn, bây giờ công tử bảo nên đi ẩn lánh ở đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bà rơm rớm nước mắt, thở dài nói tiếp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bệ hạ có rõ cảnh lưu lạc này cho thiếp không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc bàn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tâu lệnh bà, bây giờ người ta đã nhận được dung nhan lệnh bà, thì lệnh bà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đến nương náu cửa thiền, thiết tưởng có phần chắc chắn hơn nhiều.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoàng phi vui mừng đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phải, tướng quân bàn rất phải. Vả lại ta cũng nên thế phát quy y thôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quang Ngọc lại nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tâu lệnh bà, ở làng Ngô Xá có một ngôi chùa sư nữ, vậy mai mời lệnh bà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đến ẩn ở đó.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quay ra, chàng bảo Nhị Nương:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngu huynh giao cho hiền muội việc đó. Sáng mai...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhị Nương ngắt lời:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sáng mai không bằng đêm nay. Ngu muội xin phò giá lên đường ngay bây</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">giờ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế thì càng hay lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người đều lấy làm phải, liền chở thuyền đưa hoàng phi và Nhị Nương qua</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hào. Hai người lên ngưạ đi thẳng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái cũng xin đi theo. Quang Ngọc giữ lại mà rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Một mình Nhị Nương cũng đủ rồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng thưa hiền huynh, ngu đệ phải về xem hạt Kim Lũ ra sao. Hiền huynh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hẳn chưa quên rằng có hơn hai trăm binh lính đang hoành hành ở đó.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cphải hai trăm binh lính Từ Sơn không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chính.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy thì không lo.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng kể cho anh em Quang Ngọc nghe cái mưu kế của chàng. Chàng cho</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một thám tử giả làm người làng Yên Phụ đến phủ báo ở vùng ấy có bọn giặc rất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đông, mà người đi đầu là nhà sư. Chàng bịa ra điều đó, là vì nghe người ta tuyên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">truyền có một nhà sư rất hung tợn thường đem đồ đảng đi tống tiền bọn nhà giàu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng chắc báo có một nhà sư, mà lại nói khích thì thế nào viên phủ chỉ còn một ít</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lính ở lại canh giữ và việc phá ngục cứu hoàng phi dễ như trở bàn tay.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phạm Thái nghe truyện mỉm cười đưa mắt nhìn Quang Ngọc rồi phàn nàn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tội nghiệp? Người ta đã xuất gia tu hành mà công tử còn đổ cho người ta cái</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tội tầy trời ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chẳng qua cũng là một sự bất đắc dĩ Không thế, sao cứu được hoàng phi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người cùng cười, rồi mời nhau uống rượu cho mãi tới gần sáng mới chịu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chia tay giải tán, hẹn thỉnh thoảng lại đến rừng hội họp uống rượu múa gươm.</span></p>', 0, '2022-08-05 01:12:48', '2022-08-05 01:12:48');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(110, 76, 'Chương 10: Chín Và Sống', 'chuong-10-chin-va-song', 10, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ hôm đánh hụt trận Kim Lũ thì hai viên phân phủ, phân suất đem lòng thù</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">oán bọn cựu thần nhà Lê lắm, vì họ chắc rằng cánh quân đến phá ngục cứu Lê</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hoàng phi chỉ có thể là bầy tôi nhà Lê. Phân phủ bàn với phân suất:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Việc nầy ta nên giữ bí mật. Quan trên với triều đình đều chưa biết tin ta bắt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được Thị Kim. May mà chưa kịp cho chạy giấy về kinh đấy!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất hậm hực tức tối:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi thề không đội trời chung với bọn này. Không nhưng chúng nó giết mất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">viện đội nhất của chúng ta mà chúng nó còn làm cho tôi lặn lội đêm khuya cất</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quân đi, rồi lại đem quân về, thực là mình làm trò múa rối cho dân vùng Kim Lũ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">coi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất cười khà nói tiếp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Rõ đen cho mấy tên lái thuyền vô tội bị chặt đầu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngài chặt đầu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vâng, Bực mình về nỗi bị lừa nên gặp mấy thằng lái đò đương họp nhau ở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dưới thuyền đánh bài phu, tôi cầm dao khoa tay một lượt, bốn, năm cái đầu rơi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xuống ván? Như thế cũng hả lòng được đôi chút.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ ngồi trầm ngâm suy nghĩ. Cái bực tức của ngài, ngài cho còn gắp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">năm gắp mười cái bực tức của phân suất. Đêm hôm ấy còn bao giờ ngài quên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được? Trốn vào chuồng ngựa nằm ở máng ăn, lấy rơm phủ chùm kín người, rất là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khó thở, lại thêm mùi phân ngựa khai quá đỗi, thế mà ngài phải nằm dí ở đấy cho</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tới lúc sáng rõ, trong mấy canh dài đằng đẳng lắng tai nghe động tĩnh ra sao. Giữa</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lúc ấy thì gia quyến ngài đang bị nhốt ở trong buồng chứa, lo sợ im hơi lặng tiếng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhớ lại những sự đau đớn đáng kỷ niệm ấy phân phủ mỉm cười. Đối với con</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhà võ như phân suất thì cái tức giận hiện ra nét mặt, thốt ra lời chửi rủa thề</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nguyền. Nhưng đối với con nhà văn, đối với nhà thâm nho như phân phủ thì cái</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tức giận dẫu dữ dội đến đâu cũng chỉ ngụ trong một khoé mỉm cười chua chát.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chẳng thế ngài lại thường mỉa tính võ đoán của phân suất bằng những câu thơ đại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ý nói: Trăm quân hùng tráng không mạnh bằng một lời văn mát mẻ của nhà cầm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bút.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần này lại là một dịp để ngài phô cái sức mạnh của sự giận đương yên lặng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngấm ngầm cháy trong lòng ngài. Ngài không làm thơ nữa. Ngài chỉ mở tráp lấy ra</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một cái bảng mà theo sự dò xét cẩn mật của bọn thám tử, ngài đã làm ra. Đó là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bảng chua đủ tên những bực trung thần nhà Lê trốn trong địa hạt Từ Sơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ gõ xuống cái bảng ấy bảo phân suất:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bấy lâu tôi để bọn chúng ăn ngon ngũ yên là cũng tưởng họ hiểu thời thế,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không hoạt động nữa. Ai ngờ ngày nay lại chính họ gây sự. Vậy bất đắc dĩ ta phải</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mời ngững ông tướng đến chơi ít bữa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lời ngài thật ngọt như mía lùi. Ngài lại mỉm cười nhìn phân suất nói tiếp, ý</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chừng để được lòng ông bạn đồng thành:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Việc đó phải nhờ đến bàn tay sắt của tướng quân.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tướng quân phổng mũi đáp liền:</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đại nhân cứ truyền, việc dẫu khó đến đâu tôi cũng xin đảm nhận.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy chúng ta cùng bàn xem nên bắt những tay nào trước.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tức thì phân phủ mở rộng cái bảng đẹp đẽ của ngài ra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đây đại nhân coi, tất cả có bãy mươi ba tên. Nhưng xét ra chỉ có bảy tên thực</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lợi hại. Bảy tên đó là cho con ngự sử Nguyễn Bặc, người chứa chấp Thị Kim. Hai</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tên này ta hãy để đ 1 o vì hiện chúng còn trốn tránh, mà trốn thì hẳn trốn ở nơi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khác Nếu ta bẩm tỉnh sức giấy đi lùng thì cũng bắt được, nhưng là thế là làm cỗ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sẳn cho kẻ khác ăn. Chi bằng ta cứ để yên, ít lâu thế nào chim chẳng bay về tổ, lúc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ấy ta hãy mời nhẹ hai ngài vào cũi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất cười lớn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hay? mưu hay lắm? Còn ai nữa, thêu đại nhân?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Kế đến Đào Phùng. Tên này tuy còn trẻ, nhưng đáng lo ngại lắm đây. Nhiều</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bài thơ hắn ngâm vịnh có ý nghĩa cuồng bột, phạm thượng. Mà hắng tụ họp anh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">em ĩ chè luôn luôn. Trong một bữa tiệc hắn ngà ngà say có nói một câu hỗn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xược mà thám tử đã chép lại đem nộp tôi. Câu ấy đây.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ vừa nói vừa đưa cho phân suất một mảnh giấy. Phân suất vốn không</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">biết chữ, cười gượng nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đại nhân đọc cho tôi nghe xem nào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Xin vâng. Hắn ta nói thế này: Kẻ sĩ cúi cổ khom lưng làm tôi bọn Bùi Đắc</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuyên, Nguyễn Quang Toản thì khác gì bán rẻ linh hồn cho bọn đồ tể.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất đập mạnh tay xuống ghế ngựa quát tháo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nó bảo thế? Thực nó bảo thế? Được đễ nó đấy rồi ta bán rẻ linh hồn nó cho</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Diêm vương. Nhưng còn ai nữa?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Còn Hoàng An ở Phù Đổng, Nguyễn Tiết ở Phù Ninh, Nguyễn Đắc ở Đồn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kỹ. Trần Xá ở Phú Xuân. Đó toàn là những tay ghê gớm cả?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Được, đại nhân cũng nên cẩn thận, vì chúng không phải là những tay tầm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thường. Mà biết đâu chúng nó lại không mật thông với đồ đảng Phạm Thái?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất lớn tiếng cười, toan nói câu gì để tỏ lòng khinh bỉ đối với bọn kia.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bỗng ngài ngừng bặt, hoảng hốt quay ra hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cái gì thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một tên lính mặt tái mét như gà cắt tiết chạy xồng xộc vào trong phòng đứng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thở không ra hơi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm... hai ông lớn... có người... chết ở cổng phủ...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Người chết?... Ai?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên lính đứng thở một hồi nữa rồi mới thuật lại rằng một võ sĩ trông rất mạnh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mẽ, dữ tợn, phi ngựa qua cổng phủ và ném xuống đó một cái xác người chết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ và phân suất vội vàng theo lính ra cổng. Một người trần truồng nằm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sấp ở giữa đường, ngay bên cầu treo. Phân suất cúi xuống lật ngửa cái thây lên thì</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thấy một con dao sáng loáng cắm trúng chỗ trái tim. Thốt nhiên phân phủ hét lớn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trời ơi? Nguyễn Kha? Nguyễn Kha bị giết rồi?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất kinh hải vội hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nguyễn Kha là ai vậy?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ vẫn đứng yên lặng nhìn tròng trọc vào cập mắt trắng đã mở to của</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người chết và nói một mình hai, ba lần:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trời ơi? Nguyễn kha bị giết ? Ai giết Nguyễn Kha? Nguyễn Kha? Ai giết?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vâng, tôi cũng hỏi đại nhân: Ai giết người này, mà người này là ai?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ như chợt tỉnh, quay lại bảo phân suất:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- ngài cho lính cời ngựa đuổi theo lùng bắt ngay lấy nó... Mau chẵng nó trốn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thoát</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nhưng biết nó chạy ngả nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cho mỗi người đuổi một ngả.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất ra lệnh, tức thì hai chục lính ky mã chia làm bốn bọn, đem theo bốn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cây súng hỏa mai rầm rộ kéo đi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất theo phân phủ vào công đường và nhắc lại câu hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nguyễn Kha là ai vậy, thưa đại nhân?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ có vẻ lo sơ, nhìn trước nhìn sau, rồi nói khẽ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Người đến báo tối hôm ấy, ngài đã quên? Hắn là một viên thám tử rất có tài</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">của tôi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- à? Tôi nhớ ra rồi. Chính hắn đã đến báo cho ta biết người con gái ở nhà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn Bặc làng Hà Vị là Thị Kim, vợ Lê chiêu Thống.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chính hắn, Nguyên tôi sai hắn giả danh là một bậc trung thần nhà Lê đi lại</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chơi bời với bọn kia để dò tin tức. Nhưng không rõ sao bọn kia biết là thám tử của</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ta.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ một lát, phân phủ lại nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Việc này ta phải ra tay mới xong... Phiền ngài đem quân áp đến bắt ngay năm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tên đầu sỏ, hiềm nghi phạm điệu về phủ cho.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất tuân lời, xem lại bảng kê tên tuổi vả chỗ ở của những người kia rồi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">điểm binh mã ra đi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chiều hôm ấy, quân lính khiêng về phủ ba cái củi đóng sơ sài, trong mổi cái có</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một người bị trói ngồi lom khom như con khỉ lớn. Phân suất xuống ngựa vào công</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đường hớn hở bảo phân phủ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tuy không bắt được đủ năm tên, nhưng trong ba tên này có tên Đào Phùng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mời ngài ra coi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ vui mừng vừa theo ra sân vừa hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có Đào Phùng? Làm thế nào mà ngài bắt được hắn?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngài tính đã bắt thì được chứ!</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có tiến cười the thé trong một cái cũi đưa ra. Phân suất tức giận vì giọng cười</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chế nhạo, đạp mạnh vào cái cũi làm nó đổ lăn ra. Tiếng cười càng ròn, càng to, rồi</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người bị nhốt dằn từng tiếng hỏi phân suất:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- ông bảo ông bắt được tôi. Vậy dám hỏi ông: ông bắt được tôi ở đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau một cái đạp thứ hai của phân suất, người kia khẳng khái nói tiếp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Than ôi? Kẻ chiến sĩ anh hùng mong ước được bỏ mạng ở nơi trận địa, da</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngựa bọc thây, nhưng tôi đây vô tội được người anh hùng bắt trói giữa lúc tôi yên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">giấc ở nơi buồng tối, nhưng tôi đây vô tội được người anh hùng giơ chân đạp một</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cái mạnh bạo, trong khi tôi bị nhốt trong cũi hẹp. Dáng kính thay cái dõng cảm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">của người anh hùng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy phân suất rút gươm hầm hầm xông lại, phân phủ vội ngăn cản và ung</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dung nói :</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngài cần gì phải nóng thế, cứ để Đào quân đấy cho tôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi trỏ hai cái cũi khác hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Còn hai ông này?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa ngài, đó là Nguyễn Tiết với Trần Xá. Còn Hoàng An, Nguyễn Đắc đi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đâu mất từ tháng trước.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ truyền lệnh tháo cũ lôi ba người ra, rồi lại gần ôn tồn hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa ba ngài, nếu ba ngài làm ơn cho tôi biết ba ngài dấu bà hoàng phi ở</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đâu, thì tôi xin cho lính đem kiệu rước ba ngài về nhà ngay.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn Tiết và Trần Xá cùng cãi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hai ngài không biết Lê hoàng phi là ai, vậy chắc ngài Đào Phùng thì hẳn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">biết?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng thản nhiên đáp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi ấy à? Có, tôi có biết Lê hoàng phi, khi ngài còn ở trong cung điện kinh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thành Thăng Long. Ngài thật là bậc quốc sắc, khuynh thành.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vâng, ngài cũng là bậc quốc sắc khuynh thành? Bữa nọ tôi đã được hân hạnh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gặp long nhan. Nhưng tôi muốn biết Đào quân giấu ngài ở nơi nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng vờ ngơ ngác:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Giấu ngài? Chết ai giấu được ngài?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đào quân? Nhà tôi có quen Đào tướng công, vì thế tôi không muốn để Đào</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quân bị hình phạt đau đớn. Vậy Đào quân chẳng nên chối cãi. Việc này tôi biết</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tường tận lắm rồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng mỉm cười:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa Nguyễn đại nhân, tôi biết tường tận hơn nhiều, vì tôi biết đích xác rằng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tôi không giấu hoàng phi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Có lẽ Đào quân không giấu thực, nhưng Đào quân biết nơi ẩn núp của Lê</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ho àng phi .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi nhìn thẳng vào mặt Đào Phùng, phân phủ đột ngột hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phạm Thái vẫn được mạnh đấy chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng chau mày hỏi lại:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Phạm Thái? Tôi tưởng Phạm Thái bị giết với Nguyễn Đoàn rồi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ cáu tiết gắt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chú đừng trách tôi ác nhé? Vì bổn phận, tôi không thể không trừng phạt chú</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Liền ra lệnh đánh mỗi người hai chục tĩg. Một tên lính lực lưỡng giơ roi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">song ráng sức quật vào mông, vào lưng phạm nhân, khiến Nguyễn Tiết, người</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chịu hình phạt thứ nhất, kêu la ầm ỹ và Trần Xá một nhà văn yếu đuối mới đến roi</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thú mười lăm đã chết ngất đi rồi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến lượt Đào Phùng, chàng chỉ cười và cắn chặt hàm răng trên xuống môi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">dưới, không thốt một tiếng kêu ca, đến nỗi chú lính cáu tiết, đánh thêm cho ba roi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rất mạnh. Đào Phùng giơ hai tay bị trói lên lau mồ hôi trán, bảo tên kia:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chú rõ quá cẩn thận? Quan truyền đánh có hai mươi roi, chú lại ra thêm cho</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ba roi nữa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân suất căm tức trừng mắt nhìn, còn phân phủ thì có ý khen thầm.Trong khi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ấy Trần Xá được một tên lính lấy nước phun vào mặt, đã tỉnh dậy, lim dim cặp</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mắt và nằm thở hổn hển. Phân suất hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Trần Xá? Ngươi đã chịu cung khai chưa?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm đại nhân... Đoái thương tôi... già yếu. Tôi không biết một tí gì thì cung</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khai... làm sao được?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe lời nói có vẻ thành thực, phân phủ xuống lệnh hãy giam Xá vào ngục, rồi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">quay ra bảo Nguyễn Tiết:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Còn ông này nghĩ sao?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nguyễn Tiết lại gần nói thầm mấy câu. Phân phủ vui mừng truyền cởi trói và</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mời vào trong nhà, Đào Phùng vội kêu:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hắn sợ đòn nên đã khai láo, ngài chớ nghe.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ tức giận thét:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Im ngay?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi sai lính đi lấy một cái hỏa lò than hồng và hai cái kìm. Một lát sau thịt đùi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng cháy xèo xèo, xông lên mùi giẻ khét. Đào Phùng bảo phân phủ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Xin ngài miển cho cái hình phạt này.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ hớn hở:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy ông chịu cung khai?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng nói luôn:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vì khó ngửi lắm. Tôi đến lợm nôn vì mùi thịt cháy mất thôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bản chức bằng lòng chuẩn lời xin của Đào quân.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lền quay ra gọi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lính đâu dúng ngay kìm và nước lạnh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếng dạ ran... Họ lại bắt đầu kẹp dùi bên kia của Đào Phùng, hẳn kìm sống</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đau hơn kìm chín nhiều, vì kẻ bị hình phạt nghiến hai hàm răng vào nhau, tiếng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">kêu ken két, và tuy chàng cố cười gượng, nước mắt chảy ràn rụa ướt dẫm hai bên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">má. Phân phủ bỡn cợt hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thế nào, bây giờ thì đỡ khét chứ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng muốn trả lời một câu chua chát, nhưng vì phần đau quá, phần hai</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hàm răng nghiến mạnh như đã sai khớp, chàng nói không ra tiếng nữa. Đưa hai tay</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bị trói lên, nắn lại hàm dưới, rồi khi đã hơi hoàn hồn, ôn tồn bảo phân phủ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa ngài, tôi còn biết phân trần sao cho ngài tin được, vì ngài yên trí rằng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tôi là đồ đảng của Phạm Thái mà tôi không biết mặt, lại buộc cho tôi cái tội tàng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nặc Lê hoàng phi mà tôi tưởng đã chết. Nay đối với ngài tôi như con cá đối với</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người hỏa đầu, sống chết ở tay ngài. Vậy ngài muốn dùng cách hình phạt nào mà</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">kẻ vôi tội này chẳng phải chịu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đào quân nói có lý lắm... Lính đâu hãy mời Đào quân vào gnhỉ tạm trong</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngục thất mấy bửa cho lại sức đã, rồi ta sẽ nói chuyện sau.</span></p>', 0, '2022-08-05 01:13:26', '2022-08-05 01:13:26');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(111, 76, 'Chương 11: Cái Võng Thịt', 'chuong-11-cai-vong-thit', 11, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng bị giam đã được hơn một tháng. Trần Xá vì tuổi tác không chịu nổi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hình phạt quá dã man nên đã bỏ mạng trong ngục thất. Nguyễn Tiết thì được tha</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngay hôm bị bắt. Chàng hứa với phân phủ sẽ đi dọ thám bọn cựu thần nhà Lê. Đó</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">là câu nói thầm đã khiến phân phủ vui mừng truyền cởi trói cho chàng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn Đào Phùng, trong thời kỳ ấy, đã năm phen bị hành phạt roi song và kìm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cặp mà vẫn không chịu cung khai, một mực nói mình chỉ phạm có một tội: tội làm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">con bậc trung thần nhà Lê.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một buổi sáng, tiết xuân ấm áp, Đào Phùng hồi tưởng tới hồi tự do đi chơi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ngoạn cảnh, cùng anh em uống rượu làm thơ, mà trong lòng sinh ra mối hoài xuân,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">man mác. Mấy ngày tết nguyên đán, tiếng pháo nổ ran, chàng nghe như lời trêu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ghẹo, nhưng chàng chỉ căm tức mà không buồn rầu. Hôm nay, trong phòng ngục</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chật hẹp, sự yên lặng đã khiến chàng buồn rầu mà quên hẳn lòng căm tức.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Qua hàng chấn song sắt cửa sổ, màn mưa bay lờ mờ làm tăng vẻ dịu của cảnh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trời muộn, Đào Phùng cúi xuống nhìn hai chân sỏ trong cùm rồi lắc đầu, thốt ra</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một tiếng thở dài đau đớn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bỗng chàng nghe có tiếng lao xao ở phòng binh lính giáp vách với ngục</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phòng. Chàng lắng tai nghe được câu chuyện của lý trưởng sở tại với người cai lệ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lý trưởng đem biếu quan cái thủ lợn và chú cai vòi lễ mới chịu trình quan.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng liền lên tiếng nói đổng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lâu nay bị giam them rượu quá. Giá được chén một bữa thỏa thích thì cung</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khai hết mọi điều.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người cai nghe rõ, hấp tấp chạy lên cung đường trình bày cùng phân phủ. Tức</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">thì có lệnh tháo cùm cho phạm nhân Đào Phùng và cho phép y được lên nhà trè</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xơi rượu</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ thân đến mời Đào Phùng một chén đầy mà nói rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Nếu Đào quân cung khai bốn điều ấy thì không những được uống rượu mãi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mãi, mà còn được cất nhắc lên làm quan nữa kia. Bốn điều ấy bản chức ôn lại để</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào quân nhớ cho. Là: Ai giết Nguyễn Kha? Lê hoàng phi hiện trốn ở đâu? Phạm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thái hiện ẩn ở đâu? Nơi sào huyệt của bọn Phạm Thái ở đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ, đại nhân để tôi uống rượu đã.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Được, xin mời Đào quân uống cho thỏa thích. Hết, xin lại gọi thêm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hơn một giờ sau nghe tiếng ngâm thơ sang sảng ở nhà trên, phân phủ liền đi</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xuống, vui vẻ hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đào quân sơi rượu rồi?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm đã. Đa tạ đại nhân thết đãi một tên tù quá hậu... ước gì bữa nào đại nhân</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cũng cho ăn uống no say như thế.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ cười hả hả:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cái đó tùy ngài cả. Cung khai sự thực xong thì muốn gì mà bản chức chẳng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tâu lên thiên tử ban cho.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cung khai thì cố nhiên là tôi cung khai, nhưng thưa đại nhân, tôi có một tính</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rất xấu: cơm rượu xong cứ phải tiêu khiển một ván cờ, hoặc một hội tổ tôm. Giá</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đại nhân chuẩn cho sự nguyện vọng ấy thì tôi xin thú nhận hết các tội.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ ngẫm nghĩ. \"Hay thằng này lại giở quẻ đây? ... Được ngươi cứ gan...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta đến phải cho ngươi nếm mùi võng thịt mới xong ? \"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng cũng nghĩ thầm: \"Thú nhận? Rồi mày biết tay tao thử cung khai</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chính mày là thủ phạm, vợ mày là đồng đãng xem mày còn giở ra thứ hình phạt gì</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">có thễ dã man hơn được nữa?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đào quân nói gì tôi quên bẵng mất... Đào quân miễn chấp. Tôi đãng trí lắm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Dạ tôi đâu dám. Vừa rồi tôi xin đại nhân cho phép tôi hầu đại nhân một ván</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cờ hay một hội tổ tôm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ mỉm một nụ cười ghê gớm:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bản chức cờ còn thấp lắm, địch sao lại Đào quân lắm mưu nhiều trí. Còn như</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">tổ tôm thì không đủ quân. Vả, thiết tưởng hai cách tiêu khiển ấy nhu nhược lắm,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">buồn tẻ lắm. Tôi xin hiến Đào quân một cách tiêu khiển khác hùng tráng mà cảm</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">động hơn nhi ều. . .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Liền gọi viên đội trưởng mà bảo thầm mấy câu. Lát sau, mấy tên lính dẫn đến</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trước mật hai người một tội nhân cỗ mang gông nặng, tay bị trói quặt về sau lưng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ trỏ tên tù, nói với Đào Phùng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đây là một thằng tướng cướp rất lợi hại ở vùng này quan phân suất vừa bắt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được tối hôm qua. Tôi sai nó làm trò để Đào quân coi cho đỡ buồn nhé?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi quay hỏi tên kia:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mày đã thú nhận chưa?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tên cướp vờ khóc:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm ông lớn, ông lớn thương con phận nào, con được nhờ phận ấy. Quả thực</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">con là người lương thiện.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ quát tháo:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lương thiện à? Đốt nhà giết người, lấy của mà là lương thiện thì thế nào mới</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">là tàn ác hở tên...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một dịp cười làm cho phân phủ ngừng bặt:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cái gì mà Đào quân cười dữ dội thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Thưa đại nhân, tôi nghĩ đến bọn giết người lấy của mà vẫn tưởng mình là</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lương thiện, thì tôi tức cười đến chết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng lại cười. Phân phủ cố nén giận, nghọt ngào bảo chàng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đấy ngài coi, thằng tướng cướp này giết biết bao nhiêu lương dân mà dám tự</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cho mình là lương thiện thì còn trời đất nào nữa? ... Nhưng tôi đã có cách trừng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trị nhân tiện hiến ngài một cuộc vui hiếm có.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi bảo tên cướp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Mày không xưng, quả thực mày không xưng?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm ông lớn, con biết điều gì mà xưng?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Lính đâu?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếng dạ ran. Mấy người lính chạy lại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sắp sửa khí cụ hình phạt để làm võng thịt hầu Đào quân coi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tức thì kẻ đi lấy thừng, lấy giây đàn, kẻ bê đá, bê gạch đến, rồi dùng giây đàn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">buộc hai ngón tay cái hai ngón chân cái tên tướng cướp lại với nhau. Trong khi ấy,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một người lính khác đã leo lên cây bàng mắc vào một cành hai đoạn giây thừng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">lớn Xong, họ buộc hai đầu thừng vào đầu ngón chân và ngón tay người bị trừng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phạt mà kéo lên cao, trông như cái cõng vậy: \"Võng thịt\". Tên tướng cướp đã gan,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cố cắn răng không kêu khóc. Nhưng khi người ta lạnh lùng đặt một phiến đá lên</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bụng hắn mà đưa người hắn như đưa võng thì hét lớn lên một tiếng: \"ối?\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng lại cất tiếng cười the thé.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Ngài cười gì vậy?</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tôi cười để khen ngợi đại nhân đó mà thôi, xin đại nhân đừng vội giận. Cách</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">xử án của đại nhân khiến tôi nhớ tới một người hiền đời xưa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ, trong lòng căm tức nhưng vờ vui tươi hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Người hiền ấy là ai thế?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Người ấy là Triệu Công. Ngày xưa Triệu Công xử án ở gốc cây bàng. Lòng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">công bằng của ngài đã khiến người ta phổ vào bản đàn câu hát: \"Tế thế cam</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đường, vật tiễn vật phạt...\" Ngày nay đại nhân cũng xử án ở một cành bàng. Cành</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bàng tuy có cao hơn gốc bàng nhưng cũng thế thôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Được, lát nữa xin sẵn lòng treo anh lên một cành cao hơn.\" Phân phủ nghĩ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vậy rồi hầm hầm truyền lính lật sắp tên tướng cướp xuống và đặt trên lưng hắn ta</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một phiến đá nặng hơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giữa lúc ấy có tiếng hát ngoài cổng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ lắng tai nghe và bảo một tên lính:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đứa nào hát nghêu ngao thế? Mày ra xem...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lát, tên lính trở về nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm ông lớn, đó là một bọn mãi võ, đi múa hát kiếm ăn. Chúng xin vào hầu</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ông lớn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Đuổi cổ nó ra?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng vội can thiệp:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Sao đại nhân không cho phép chúng nó vào múa gươm để tôi được coi nhờ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">với Thưa đại nhân trong bọn biết đâu không có kẻ đại tài mà đại nhân có thể dùng</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">làm thủ túc được !</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ gật gù:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Cũng có lẽ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Liền truyền lệnh gọi bọn kia vào. Tức thì bốn người nai nịt gọn gàng, mạnh</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mẽ dõng dạc bước tới. Người đi đầu là một tráng sĩ vào trạc ba mươi tuổi, hai</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người đi kèm hai bên ý chừng là em trai và em gái tráng sĩ, vì cùng một nét mặt</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">rắn rỏi, xương xương với cặp mắt xếch ngược và trong sáng. Theo sau, một người</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">gánh đôi hòm lớn, trông càng lực lưỡng hơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cả bốn người kính cẩn dập đầu xuống sân làm lễ. Phân phủ hách dịch hỏi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Chúng mày biết làm những trò gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một tiếng \"ái\" rất lớn trả lời lại, đó là tiếng kêu của tội nhân bị treo trên cành</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bàng. Phân phủ quát:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hãy cho phép nó nằm yên đấy: Đừng đặt đá lên lưng nó vội, để ta coi mấy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đứa này làm trò cùng múa võ đã.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi quay về bọn mãi võ:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Tao hỏi: chúng mày biết làm những trò gì?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người trùm lễ phép thưa:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Kính bẩm đại nhân chúng tôi biết đủ các món võ, biết làm, biết hát những bài</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ca múa gươm. . .</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vừa múa gươm vừa hát có được không?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Bẩm được lắm chứ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Vậy múa hát ta coi thử nào?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chàng liền vừa múa vừa ca rằng:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta, tráng sĩ hề, gặp thời loạn lạc,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Như cá gặp nước hề? ta vẫy vùng,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bõ bút nghiên hề? Toàn đồ vô dụng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một ngựa một gươm hề ? một cây cung,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Với lòng dõng cảm hề? Với chí lớn,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta xông xáo hề? trong đám mông lung,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chiếc chiến bào của ta hề? Đẫm máu,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bên tai ta hề? Súng nỗ đì đùng,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta, tráng sĩ hề? Vào trong trận địa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Như cá trong nước hề ? Ta vẫy vùng ?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đào Phùng vỗ đùi khen ngợi:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- Hay? Hay lắm?</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần lượt bốn ngưòi kế tiếp nhau múa gươm, múa dáo, múa đại đao. Sau hết,</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">người trùm xin chia ra làm hai cánh, giả đánh nhau để hiến phân phủ một cuộc vui</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hiếm có. Đào Phùng lấy làm thích chí, cười nói:</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">- ồ, thế thì đẹp lắm nhỉ. Giá đại nhân cũng cho phép tôi múa may với họ thì</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vui biết chừng nào.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phân phủ chưa kịp trả lời thì bốn người mại võ đã kẻ đao, kiếm, kể dáo, kích</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đanth nhau loạn sạ. Bỗng huych một tiến, giây thừng treo tội nhân đã đút và</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhanh như cắt, một người đã cổi trói cho va, trong khi ba người sấn vào đâm chém</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phân phủ. Lính tráng hoảng hồn vớ lấy ghế giơ lên đỡ. Nhưng bọn kia cũng chỉ</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đánh dọa để mở đường mà thôi. Vụt một cái cả năm người đã ra khỏi cổng chạy</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">miết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhân lúc phân phủ và binh lính chạy hỗn loạn và kêu la ầm ĩ, Đào Phùng lẻn</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">được ra cổng, trốn thoát.</span></p>', 0, '2022-08-05 01:14:01', '2022-08-05 01:14:01');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(112, 77, 'Chương 1: 1: Lạc Tinh', 'chuong-1-1-lac-tinh', 1, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*Kim qua ảnh lý đan tâm khổ,Đồng cổ thanh trung bạch phát sinh…Thơ xưa nhưng vẫn có tìnhNgàn năm còn đó bóng hình tổ tiên.*...Trần Lạc Tinh là một sinh viên đại học Cần Thơ năm thứ tư.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tư chất bình thường, bình thường gia cảnh.Nếu không nói là nghèo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà nghèo nên không thể nào thuộc dạng tiền đầy túi, tình đầy tim được.Sở thích lớn nhất, là đọc truyện tiên hiệp các kiểu, mấy món này thì không tốn kém bao nhiêu.Nói chung, sở thích và cách giải trí thì theo hoàn cảnh mà, \"Giàu thì đi bar, nghèo uống trà đá \" Mỗi tầng lớp thì sẽ có cách giải trí khác nhau, nên vẫn cứ vui vẻ sống.Nhưng đó chỉ là lời nói tự an ủi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai không có giấc mộng có thể :” Tỉnh chưởng quyền thiên hạ, túy ngọa mỹ nhân đùi” Lạc Tinh cũng thế.Một ngày đẹp trời, Lạc Tinh đi bộ vào tiệm đồ cổ gần bến Ninh Kiều.Định tìm xem thử có mua được món đồ nào đặc biệt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì tối hôm qua Lạc Tinh đọc báo, thấy có người vô tình mua món đồ cổ vài trăm ngàn.Nhưng sao này lại phát hiện là đổ cổ thời Minh, trở thành tỷ phú trong một đêm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên bản thân mình cũng muốn thử thời vận.Khu bán đồ cổ dọc theo hai bên đường.Bình cổ,chén cổ, radio, tivi, điện thoại, tranh ảnh, nói chung ko biết phải cổ hay không.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng có một thứ chắc chắn : toàn là đồ cũ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hình như, ông chủ chúng chẳng thèm lao chùi.Đi dạo một dòng, rồi lại một dòng.Cái muốn mua thì không đủ tiền.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cái đủ tiền thì toàn là hàng xấu xí hoặc ko thấy có tác dụng gì.Lạc Tinh lục trong túi mình, xem hai tờ tiền cuối cùng trị giá 550 k và khấn trong lòng :\"-Thiên linh linh địa linh linh, cầu cho con mua một món gì giúp con đổi đời.\"Sau đó Lạc Tinh vào một phòng nhỏ, trưng bày bán đồ lưu niệm nhỏ tên là Đại Hà, ở đây có ít vật phẩm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng đồ đạc được xếp ngay ngắn và có sẵn bảng giá.Dò theo từng hàng từ cao tới thấp, giá hai triệu, một triệu rưỡi, một triệu, tám trăm ngàn, bảy trăm ngàn...Tới hàng năm trăm ngàn có tất cả ba sản phẩm.Cái đầu tiên là một tấm giương bát quái bằng gỗ, loại hình hay treo trước nhà để trừ tà.Cái thứ hai là một quả cầu tròn bằng pha lê, nhưng trên đó khắc hình sông núi, biển cả, chim cá.Cái thứ ba là tấm khiên tròn, không biết bằng đồng hay sắt, hình hoa văn đứt khúc đã khá cũ, không nhìn ra hình dạng gì.Lạc Tinh cầm từng loại lên xem xét.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi cầm đến mặt khiên đồng, cảm giác mát lạnh lan tỏa và có cảm giác quen thuộc như thấy ở đâu rồi.Có thể là trong giấc mơ...!giống như cảm giác Déjà vu hay đề cập.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi thấy một hình ảnh hay một sự việc nào đó, mà cảm giác như quen lắm mình đã từng trải qua rồi.Dù gì chỉ là cầu vận may thôi và có lẽ cái mát lạnh là do chất liệu kim loại mang lại nhưng thôi kệ.“- Ta chọn ngươi.”Lạc Tinh nhận gói đồ từ chủ tiệm và trả nốt số tiền trong túi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tâm trạng hồi hộp, hi vọng đây là món đồ cổ có giá trị, có thể bán lại một cái giá cao hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không thì coi như toi cơm, xem như mười bữa nữa tháng ăn mì gói​\"- Thôi nếu không bán được, thì biết đâu nó mang lại may mắn cho mình cũng nên.\" Trần Lạc Tinh tự nhủ.Vừa lâng lâng ra khỏi quán, huýt gió một bản tình ca mùa đông.\"-Rầm \".Trời đất quay cuồng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc định thần lại thấy một cô gái xinh đẹp, mặt bộ đồ thun thể thao đang ngồi quan tâm và ríu rít :\"- Em xin lỗi anh có sao không ! Để em đền tiền thuốc cho anh\"Lúc lắc đầu vài cái cho tỉnh, thì mới biết do mình lo suy nghĩ miên man, ko để ý đường.Bị cô nàng chạy chiếc SH mất lái chạy lên phần đường dành cho người đi bộ, rồi hun cho Lạc Tinh một phát bay vào lề.\"-Khỏi đi, tôi cũng không sao\" Thôi dù sao cũng do mình một phần lỗi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không để ý đường đi, Lạc Tinh vội vã đi về.\"- Mới nói hy vọng mua món đồ sẽ có may mắn, ai ngờ đã bị xe hun.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Gặp người đẹp đụng nên cũng không sao, chứ gặp tên nào đụng chắc cũng phải đòi bồi thường rồi.Ài, xưa nay anh hùng khó qua ải mỹ nhân mà.không biết sao lại có người xinh thế nhỉ..</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*\"\"- Tính ra ta cũng may mắn, vì lúc nãy ko mua viên cầu thủy tinh, hay gương bát quái nếu không chắc nó tan nát...Mà cũng nhờ cái khiên, nên xe đụng trúng khiên trước, bản thân mình mới bị bay hai mét mà không có gì \"Đi về tới phòng trọ, cố quên bàn tay đau nhói vì bị trầy sướt, Lạc Tinh lật đật mở cái khiên mình mua ra, xem có hư không.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu hư chắc tiếc đứt ruột.Tấm khiên vẫn còn nguyên, nhưng có cái khác lạ, Lạc Tinh không biết là do cái gì.Ngồi nhìn một lúc, mới chợt nhớ hình như hoa văn khác lúc mình mua.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xem xét kĩ lại hình như vòng ngoài của khiên đã lệch.Lạc Tinh lật phía trong của khiên nhìn, thì ra do khiên cấu trúc như mấy vòng tròn đồng tâm có thể xê dịch được\"- Chắc đo đụng xe, nên phần khớp nó hở ra, thấy rõ hơn..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thử đùng tay xoay tròn lại.Lạc Tinh phát hiện, nó giống như một Bộ xếp hình theo hình tròn răng cưa, mày mò ba mươi phút, xoay năm vòng tròn tới lui cho hoa văn khít nhau, xong nhìn kỹ lại hình ảnh cũng khá đẹp.Ở giữa là ngôi sao, ở ngoài chia từng vòng tròn vẽ người có, chim có, nai có mỗi vòng là một hoa văn\"-Khoan đã !\" Lạc Tinh bỗng chợt nhớ tới một hình mình đã từng xem, là hoa văn khắc trên trống đồng Đông Sơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Biết đâu đây là cổ vật bốn ngàn năm tuổi.Mừng như bắt được bảo vật, Lạc Tinh cầm khiên quơ lên \"OHM YEAH\"Bỗng nhiên :\" -Bụp..\" Có tiếng nổ.Thì ra tấm khiên quơ trúng bóng đèn treo trên tường, rồi đường đây điện trên tường chập điện, đèn điện tắt ngấm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đồng thời cảm giác tê dại truyền đến.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh chỉ còn chợt nghĩ đến điện giựt.\"-Toi thật rồi, cha mẹ ơi..\" Sau đó thì không biết gì nữa.Nếu Lạc Tinh còn tỉnh, sẽ thấy Khiên đồng phát sáng lên, bao quây Lạc Tinh rồi cả hai biến mất, như chưa từng xuất hiện.​\"Hùng Thiên đại lục \"Một đại lục hoang sơ không có máy bay không có xe ô tô, không có ti vi,..Chỉ có loài người thời kì ăn lông ở lổ, à không đến nỗi vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà loài người đã biết nướng thức ăn bằng lửa không còn ăn thịt sống, biết ngủ bằng lều làm bằng da thú, thay vì chỉ ngủ trong hang đá.Bên cạnh nhân loại, còn các con vật to lớn chỉ có trong phim ảnh như khủng Long mà ở đây gọi là Giao Long sống dưới nước và Địa Long sống trên cạn..và nhiều loại thú to lớn khác.Thực vật cũng nhiều loại cây to lớn, nhiều loại ăn thịt người và chưa ai thống kê hết được có bao nhiêu loài động vật bao nhiêu thực vật.Vì chưa ai biết Hùng Thiên Đại lục lớn bao nhiêu...!Một vùng đất chưa khai phá...Loài người ở đây sinh trưởng nhanh, mười sáu tuổi đã thành niên và có thể cưới vợ.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thọ nguyên trung bình một trăm hai mươi tuổi.Tuy cuộc sống nhân loại không tốt đẹp lắm, nhưng để sinh tồn trong môi trường khắc nghiệt này, nhân loại cũng rất mạnh mẽ.Sức mạnh của người ở đây mạnh hơn nhân loại hiện tại gấp vài lần.Đặc biệt có loại ấn ký hình xăm giúp tăng sức mạnh bản thân lên nhiều lần gọi là Thần Ấn.Khi thiếu niên mười lăm, mười sáu tuổi sẽ được tổ chức một buổi cầu nguyện cúng tế thần linh để Khai mở ấn ký với tỷ lệ rất thấp, trăm người được mộtVà người được thần linh ban khắc ấn hình xăm bao gồm Thần ấn Thiên Cấp (Thiên ấn) và Thần ấn Địa Cấp (Địa ấn).Ngoài hai loại trên thì Thần ấn còn một loại nữa đó là Nhân ấn.Loại này thì được một người có Thần ấn dùng thủ pháp đặt biệt để cấp cho.và loại Nhân ấn này thì không có không gian phát triển nhiều,phụ thuộc vào người Thi pháp cho.​Ba ngày trước Lạc Tinh tỉnh dậy, và phát hiện một sự thật là bản thân Xuyên việt và nhập hồn vàocháu tộc trưởng Việt tộc tên Lạc Tinh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhưng là họ Lạc không phải Trần, trong lúc cậu này trèo lên cây để canh gác, thì bị một luồng lừa từ trên trên bắn xuống trúng.Khi mọi người tìm được thì đã hôn mê bất tỉnh,quần áo cháy sạch, đầu tóc cuốn như mì gói, và được đem về Việt tộc.trong thời gian đó Lạc Tinh như chìm giấc mộng.nhìn thấy tình cảnh từ nhỏ tới lớn của Lạc Tinh chính hiệuLại qua tiếp ba ngày sau, quá trình dung hợp ký ức giữa hai người hoàn thành.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh tỉnh dậy, dần chấp nhận số phận Xuyên Việt của mình.Tuy thân phận hiện tại là cháu trưởng tộc nhưng tình hình cũng không khả quan vì thế giới này là thế giới mạnh được yếu thua.Cái chức tộc trưởng không phải cha truyền con nối, mà là ai là người có năng lực nhất sẽ được làm.Tất cả mọi người trong làng đều mang họ Lạclà một chi nhánh của Đại Lạc tộc hùng mạnh..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng đã qua thời gian dài không liên hệ được với dòng chính.Các bộ lạc không ở cố định, do lối sống săn bắn hái lượm qua nhiều thế hệ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại không có bản đồ, nên không còn ai biết dòng chính của mình ở nơi nào.Muốn về lại cũng khó.Hiện tại Việt Tộc họ Lạc cũng đang ở thời kì nguy hiểm, lúc nào cũng bị Mãnh Thú tộc tiến đánh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phải trốn đông trốn tây.Từ ban đầu dân trong tộc hơn một vạn người, sau mười năm chạy trốn, di chuyển qua nhiều điểm trú ẩn, thì dân trong tộc chỉ còn hơn ngàn người.và đã không còn sức di chuyển một lần nữa vì người già nhiều, thời tiết không thuận lợi thức ăn cạn kiệt...Nhóm Việt tộc này sinh sống kiểu rày đây mai đó.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đi săn bắn hái lượm hầu như chưa có khái niệm ở một chỗ và trồng lương thực, chăn nuôi.Lạc Tinh cũng dần chấp nhận cuộc sống mới, cũng như khát vọng có sức mạnh trong truyền thuyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhớ lại các truyện tiên hiệp mình đọc thời sinh viên.Có khi nào thời đại của mình tới rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu các nhân vật đó sống cuộc sống oanh liệt, thì biết đâu Lạc Tinh ta cũng có thế, vì tiền đề đầu tiên mình đã làm được.Đó là xuyên việt.....Hùng Thiên đại lục ơi ta tới đây.\"*- Mà khoan đã..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*\"\"- Đói quá...-\" Tìm gì ăn vậy \" ba ngày ba đêm, chỉ ngủ rồi giờ cả người rả rời vì đói..​Hiên tại ở bên Lều tộc trưởng của Việt Tộc, không khí đang gấp ráp và bi thương, vì lương thực cạn kiệt kẻ thù dòm ngó.Dân làng đã thương lượng xong, quyết định dành hi vọng sống cho đám trẻ.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên tiến hành nhờ Tổ Linh ban Thần Ấn lần cuối, cho lứa tuổi mươi lăm tới mười bảy rồi sẽ chia nhỏ ra để chạy trốn tránh khỏi bị diệt vong toàn bộ.Do khan hiếm lễ tế, nên toàn Việt Tộc đã dùng máu tươi mình thay thế.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hi vọng cóthể mang may mắn lại cho lứa con cháu cuối cùng.Mỗi thành viên điều góp một chén máu bỏ vào khay đá lớn đựng đồ tế lễ.Tầm chín giờ sáng, lúc mặt trời nhô lên ngay cổng làng chiếu vào Tổ Linh của Việt Tộc.Tộc trưởng Lạc Việt tiến lên cầm gậy tộc trưởng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hai tay đưa lên trước Tổ Linh.Là tượng một con chim giống phượng hoàng lửa đỏ rực toàn thân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhìn trông rất sống động.Nhưng Lạc Tinh biết, không phải là chim phượng hoàng mà chim Lạc Hoàng, Tổ Linh của Việt Tộc.Giọng tộc trưởng vang xa :\" *-Con lạy chín phương Trời, thập phương Địa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con lại đức Lạc Hoàng, Con lạy chư thần.Người đã mang ánh sáng và hơi ấm Giúp cho dân Việt tộc chúng con xóa tan dêm tối lãnh lẽo, tìm được sự ấm áp.**Giúp cho chúng con có thịt ăn, có nới trú ẩn, giúp chúng con có sức mạnh chống lại thú dữ, kẻ thùHôm nay chúng con dùng máu của mình tế Thiên,Tế Địa, tế đấng Lạc Hoàng.* \"\"- Xin ngài ban cho lũ trẻ trong Việt tộc được ban Thần Ấn, để có thể bảo vệ dân làng phát triển nòi giống.Con hứa Việt Tộc sẽ thờ phụng Lạc Hoàng mãi mãi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu bất tuân cả Việt tộc sẽ làm mồi cho thú dữ..\"Sau khi cúng bái xong, hơn một trăm hai mươi Thiếu Niên Việt Tộc được tắm rửa sạch sẽ và bôi lên người một lớp dầu bóng loáng, lần lượt đi lên.Ai cũng mặt Khố và ở trần cả nam lẫn Nữ.Riêng Nữ thì ai cũng tóc chẻ hai bên vai để che đi hai cái ai cũng biết là hai cái gì đó.Đương nhiên bình thường nữ giới không phải trang phục này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng khi tế bái nghi thức thì không thể mặc áo.Lần lượt mỗi người dùng dao tế lễ chích lấy máu mình nhỏ vào một chén ngọc phía trước tượng Lạc Hoàng.Mười người đầu tiên không có chuyện gì sảy ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người thứ hai mươi, ba mươi.Người thứ 33 là một cô gái tiến đến, nhỏ máu người này là trong ấn tượng Lạc Tinh sâu nhất, có thể nói thanh mai trúc mã của Lạc Tinh tên là Lạc Dương, bỗng Tượng Lạc Hoàng tỏa sáng màu vàng đất.Máu từ trong chén thánh như sợi chỉ dâng lên bay vào ngực của Lạc Dương cả người cô tỏa sáng hoa văn như ẩn như hiện.Khi ánh sáng dần tan đi, thì nhìn rõ ràng là hình chim Lạc đang uống lượn, có con mắt chính ngay huyệt Đản Trung của cô gái.Mọi người trong làng ai cũng ôm nhau mừng rỡ, sau đó quỳ xuống cảm tạ Lạc Hoàng.Tộc trưởng chảy nước mắt, mừng vì Lạc Hoàng còn thương xót Việt Tộc ban cho Thần ấn.Trong liên tục tế bái 50 năm gần đây, chỉ có mười hai người được ban Thần Ấn và người cuối cùng được Thần ấn là Cha của Lạc Tinh nhưng đã bị mất vì chiến đấu với thú dữ bảo vệ dân làng cách đây mười năm.Sau khi mừng rỡ qua đi thì lễ tế tiếp tục cho những người còn lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai cũng hi vọng mình được chư thần nhìn ngó.Người thứ năm mươi, sáu mươi, một trăm không có ai có được Thần Ấn..</span></p>', 0, '2022-08-05 01:20:33', '2022-08-05 01:20:33'),
(113, 77, 'Chương 2: 2: Thần Ấn', 'chuong-2-2-than-an', 2, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Ấn thần rực rỡ trời cao,Lạc Tinh Thiên Ấn cớ sao lạ lùngSức mạnh đâu phải dễ dùngPhải chờ thích ứng, vẫy vùng thân trai.\"...Tới phiên Lạc Tinh đi lên, tay cầm thanh dao bằng răng nanh một loại thú mài nhỏ lại.Lạc Tinh run run, cắt tay nhỏ máu vào chén.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần đầu tiên trong đời, cảm giác tự cắt tay mình thật là khó chịu, nhưng phải ráng.Tí tách từng giọt nhỏ xuống, im lặng ba giây, bỗng Tổ Linh Lạc Hoàng như sống dậy, cặp mắt sinh động như thật, nhìn chằm chằm Lạc Tinh.Tâm thần Lạc Tinh bỗng như rơi vào hầm băng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không lẽ Lạc Hoàng biết mình là kẻ ngoại lai, nên định trừng phạt mình...\"- Không lẽ mình là kẻ xuyên Việt xui xẻo nhất, vừa qua chưa đầy một tuần đã chết.\"Mọi người bỗng òa lên ngạc nhiên, vì Tổ Linh toàn thân xuất hiện hào quang màu Tím vàchén thánh cũng nhảy lên một sợi như chỉ, bắt đầu xuyên qua huyệt Đản Trung của Lạc Tinh và thiêu dệt từng nét hoa văn.Một phút, hai phút, ba phút...!hoa văn tiếp tục xuyên ra phía sau, đâm vào huyệt Linh Đài và lại thiêu dệt khắp mặt lưng.Cuối cùng cảm giác nóng cháy cũng qua.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh thấy mình vẫn còn sống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cảm giác lâng lâng, trong người như có sức mạnh lan tràn toàn thân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ánh sáng màu tím từ thân thể Lạc Tinh xông thẳng lên trời cao vạn trượng, sao đó mới dần tiêu thất.Lạc Tinh chợt nghĩ \"-Không ngờ người ta nói tuổi mười bảy bẻ gãy sừng trâu là thật...\" Bản thân mình xuyên việt vào thiếu niên tuổi mười sáu thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng giờ có mười con Trâu chạy tới, Mình cũng dễ dàng làm món trâu nhúng mẻ toàn bộ...Hưng phấn quá độ, Lạc Tinh nhảy lên vui mừng.Nào ngờ bình thường nhảy cao hơn một thước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng hôm nay làm cái vèo đã nhảy cao hai trượng, ba trượng sao đó thì trút đầu rơi xuống.\"- Nguy quá \"Cố gắng xoay người vòng lại, cho chân chạm đất.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng lực xoay mạnh quá, nên thành raxoay tiếp một vòng, rồi xoay tiếp một vòng nữa, một pha tiếp đất bằng đầu.\"-Rầm \"Đất đào một hố to...Cả Việt tộc đang xôn xao, cảm giác như mơ, vì đã có hai người được Thần Ấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thì lại thấy tình cảnh Lạc Tinh như vậy, ai cũng bất ngờ\"- Này đây là chuyện không thật tý nào, sao lại tự mình đâm đầu xuống đất ?\" Đám đông bàn tán.\"- Mau dẫn cậu ta về phòng \"Tộc trưởng nén lại tâm tình, tiếp tục tế lễ người thứ một trăm, một trăm hai mươi.Buổi lễ kết thúc mà không có người nào đạt được Thần Ấn nữa.\"-Mọi người giải tán..</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">các Trưởng lão ở lại chúng ta bàn việc.\"Về phần Lạc Tinh sau khi được người đưa về phòng thì hơi hoang mang, rùi bình tĩnh lại, có lẽ là mới đạt được sức mạnh mới nên chưa thích nghi kịp thôi.Một lúc sau có người vào liều và hô :\"- Thiếu tộc trưởng, tộc trưởng cho gọi\"Lạc Tinh bước từng bước đi theo vì sợ té.\"- Đúng là có sức mạnh thích thật, hôm trước ai cũng kiu là Tiểu Tinh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôm nay đã kêu Thiếu tộc trưởng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Vào Lều tộc trưởng thì đã thấy có cô gái đang đứng sẵn, cùng là người đạt được Thần Ấn.\" - Lạc Tinh cháu bình thường chưa? \"\"- Dạ cháu đã bình thường rồi ông ! \"\"- Tốt lắm để ta giảng giải cho hai cháu hiểu về sức mạnh Thần Xăm, dù ta cũng không hiểu nhiều lắm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng sẽ cố giảng giải những gì mình hiểu.Đã lâu rồi tộc ta mới có người có Thần Xăm, mà cùng lúc hai người.Điều đó chứng tỏ bộ lạc ta được Thần Hoàng phù hộ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhất là Lạc Dương, cháu có Thần Ấn mang hình dáng gần giống Lạc Hoàng, đó niềm vinh hạnh của cả tộc ta.Cháu hãy nhớ cố gắng vì tộc ta mà cống hiến.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Từ đây, ta ban cho cháu danh Thần Sứ Việt Tộc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quyền hạn chỉ dưới Tộc Trưởng, tương lai đãi ngộ sẽ như tộc Trưởng.Lạc Dương qùy xuống\"- Thưa Tộc Trưởng xin yên tâm, cháu sẽ luôn vì Lạc Hoàng mà ra sức bảo vệ tộc ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Lạc Tinh đứng nhìn Lạc Dương đang quỳ, làn da tuy ngăm ngăm hơn các cô gái thời hiện đạitoàn dùng sữa tắm trắng hằng ngày.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng so trong bộ lạc thì là trắng không tỳ vết rồi.Nhất là vừa nhận được Thần Ấn, cả người bỗng như cao hơn phổng phao hơn, b.ầu ng.ực như ẩn hiện dưới tấm da cáo được quấn ngang lưng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phần dưới thì cũng quấn một vòng da thú màu vàng nhạt, tôn lên cặp đùi thẳng tấp...Tộc trưởng thấy Lạc Tinh nhìn chăm chăm Lạc Dương thì hỏi :\"- Lạc Tinh cháu chưa khỏe hả ?\"Lạc Tinh mới nhớ mình hình như thất thố.Trong lòng mặt niệm, biết bản thân mình bị ảnh hưởng bởi linh hồn Lạc Tinh cũ.\"- Dạ thưa ông không ạ ! Cháu đang nghĩ sao Thần ấn của Lạc Dương màu vàng mà của cháu màu Tím.\"Lạc Việt nghĩ một chút rồi nói :\"- Theo lời tổ tiên truyền lại thìThần Xăm hay gọi là Thần Ấn có ba loại cấp độ Thiên, Địa, Nhân gọi là Thiên Ấn, Địa Ấn và Nhân Ấn, của Lạc Dương là Địa Ấn của cháu có thể là Thiên Ấn.Ta cũng không chắc nữa, vì suốt đời ta cũng chưa từng gặp ai có Thiên Ấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"\"- Nếu thật là Thiên Ấn, thì ta cũng không biết là phúc hay họa, vì cháu không tiến bộ thêm được nữa, do tộc ta không có công pháp tăng cường linh lực của Thiên ấn...mà Thiên ấn thì cần nhiều linh lực để tấn cấp hơn địa ấn.nên chuyện hôm nay cháu không nên nói cho bất kỳ ai biết, ai hỏi thì cứ nói là có Địa ấn.Thôi, để ta giảng giải cho hai cháu về năng lực Thần Ấn...\"Sau một canh giờ vừa hỏi vừa đáp, thì Lạc Tinh cũng mơ hồ hiểu một chút.Thần Ấn lấy sức mạnh từ Tổ Linh ban cho những người ưu tú nhất trong dòng tộc.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tùy theo hình thù của Hình Xăm, mà người đó có năng lực khác nhau.Địa ấn thường là khắc vẽ sinh vật, lợi dụng đặt tính của sinh vật, để gia tăng sức mạnh cho bản thân.Hình xăm hình Hổ khi bộc phát năng lực, sẽ lấy Sức Mạnh làm chính, Nhanh Nhẹn, Bền Bỉ làm phụ.Hình Xăm hình Sói sẽ lấy bốn chỉ số Sức Mạnh, Nhanh Nhẹn, Bền Bỉ tương đương nhau, cân bằng và có thể kết hợp năng lực nếu có nhiều người cùng hình sói.Dù sao đặc tính của Sói là chiến đấu theo bầy đàn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hình Xăm hình chim cũng lấy Nhanh Nhẹn làm chính..Hình Xăm hình thực vật thì hiếm gặp và có năng lực đặc thù riêng.Riêng Hình Xăm của Lạc Tinh thì phải chờ Lạc Tinh tự Khám phá, vì bản thân Tộc Trưởng cũng không hiểu.Không phải mỗi người chỉ có một Thần ấn duy nhất, mà còn nhiều lần khác nhau.Lần đầu Thần ấn gọi là Đản Trung Kỳ, Lần hai là Linh Đài Kỳ, Lần ba là Hợp Cốc Kỳ còn cao hơn thì bản thân lão cũng không biết.Vì ông ta cũng chỉ có năng lực Đản Trung Kỳ đỉnh phong.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng như suốt đời ở trong tông tộc, nên không biết được nhiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thần ấn của Lạc Việt cũng chỉ là Nhân cấp , là được tộc trưởng đời trước khắc ấn cho.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì thế khả năng ông tấn cấp hầu như rất khó.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Do sức mạnh to lớn của hình Xăm mang đến cao hơn cơ thể chịu đựng.Nên nếu cùng cấp bậc thì Thiên ấn, Địa ấn, Nhân ấn cũng không chênh lệch gì nhau.Nhưng càng tăng cấp độ lên, thì chênh lệch giữa các loại Thần ấn mới hiện rõ ra.Một người bình thường có thể một tay không nâng tảng đá trăm mươi cân lên, với những chiến sĩ luyện tập thường xuyên và mạnh mẽ nhất của tộc, thì thậm chí tiếp cận tám trăm cân.Khi đạt được Thần ấn lần đầu tiên gọi là Đản Trung Kỳ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc đó lực nâng sẽ tăng lên Ngàn cân hay còn gọi là một Ngưu Lực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi tập luyện thành thục tới đỉnh phong thì tối đa sẽ đạt hai Ngưu Lực.Nếu muốn tăng sức mạnh lên nữa, thì phải đạt được lần Thần ấn thứ hai là Linh Đài Kỳ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi đó lực nâng sẽ tăng lên bốn Ngưu Lực.Linh Đài Kỳ đỉnh phong sẽ tiếp cận tám Ngưu Lực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau Linh Đài Kỳ sẽ là Hợp cốc kỳ và chắc là Hợp Cốc Kỳ chưa là điểm dừng.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng thử nghĩ một người có thể dể dàng nâng lên ba vạn cân là khái niệm gì..Thêm nữa, mỗi khi đạt một cấp độ sẽ có thể được một Thiên phú Thần kỹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy không duy trì lâu được, nhưng thường rất mạnh.Lạc Tinh chỉ có thể nghĩ tới một chữ\"-Siêu Nhân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Ai trong đời không từng có mộng tưởng làm một lần siêu nhân oanh oanh liệt liệt.Hiện tại trời xui đất khiến lạc vào thế giới này, Lạc Tinh cảm thấy mình không nên làm một người bình thường.Sau khi giảng giải xong, ông bắt Lạc Tinh và Lạc Dương học thuộc lòng bộ một Võ kỹ.Là bộ duy nhất của Lạc Tộc còn lại : Lạc Lôi Thuật.Do ở Hùng Thiên đại lục chưa có chữ viết, hoặc là do bộ lạc Việt tộc lạc hậu quá không có chữ viết, nên hầu như các kinh nghiệm hay võ kỹ toàn truyền thụ bằng miệng.Rất dễ sai xót nên thường khi dạy gì thì người ta cũng dạy ít nhất hai người cùng lúc, để có thể đối chiếu nhau.Tuy vậy qua thời gian dài thì vẫn rất dễ mất đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng như sai xót không còn như nguyên bản.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đâu phải ai học một lần là nhớ mãi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếp tục thêm một canh giờ trôi qua, hai người cũng học thuộc lòng và đối chiếu cùng nhau..Sau khi hoàn thành.Tộc trưởng Lạc Việt thở dài nói :Lạc Lôi Thuật này theo lời tổ tiên thì nó đạt Tam Tinh, nhưng theo thời gian dài đã cósai xót, hay vì không người đủ điều kiện đễ học.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà chỉ còn đạt dược Nhất tinh năng lực.Thật hi vọng có người có thể bổ xung được về nguyên bản, phát huy sức mạnh vốn có của nó.Lạc Dương hỏi : \"- Thưa tộc trưởng Tam Tinh võ kỹ so với Nhất Tinh là mạnh hơn bao nhiêu ? \"\"- Cái gọi là Nhất Tinh tức là có thể đề thăng gấp đôi năng lực bản thân, là gấp đôi lúc bản thân chưa mở thần ấn lên.Như con hiện tại vừa vào Đản Trung Kỳ nếu mở Thần ấn thì một hơi thở chạy được ba trượng.....( gần bằng 10m/s nhưng thời này không ai biết mét là gì giây là gì nên họ dùng trượng để so sánh trong khoảng thời gian một hơi thở.Chú ý tốt độ này là bộc phát lập tức như vận động viên quốc tế 100 m trong 10s thì giây đầu tiên của họ chạy cũng chỉ tầm hai mét rưỡi thôi, vì thế tốc độ bộc phát 10m/s là đã gấp bốn lần người hiện đại.)...\" Nếu dùng võ kỹ Nhất Tinh, sẽ đạt tới 6 trượng một hơi thở kết hợp với thần ấn thuộc hệ tốc độ như của Lạc Dương sẽ ra 10 trượng một hơi thở là bình thường.tương ứng võ kỹ Nhị tinh là có thể tăng lên 15 trượng chẳng hạn.Còn võ kỹ đạt tối đa bao nhiêu tinh thì ta không biết.\"- Còn phát động thần kỹ, thì tùy theo loại thần ấn mà có tác dụng khác nhau, nhưng cũng là con dao hai lưỡi.Ví dụ thời gian phát động Thần ấn kéo dài, chỉ tầm khoảng 1000 hơi thở với người mới đạt Đản Trung sơ kỳ, nếu phát động Thần Kỹ chỉ kéo dài từ hai tới bốn hơi thở, là người đó thoát khỏi trạng thái Thần ấn luôn, vì không đủ năng lượng duy trì.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đương nhiên có cái lợi là tăng lên khả năng trong diện rộng nếu kết hợp thần ấn, võ kỹ,thần kỹ...\"\"- Thế giới rộng lớn không gì không có.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không phải võ kỹ nào cũng chỉ tăng một thuộc tính.Như Mãnh Thú là tộc kẻ thù chúng ta.Họ có Võ Kỹ tên là Thiết Ngưu Thuật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi mở ra tăng hai thuộc tính Sức Mạnh và Bền bỉ.Lúc kích hoạt có thể tăng sức mạnh thêm tám phần.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ lực nâng 1000 cân tăng lên khoảng 1800 cân.Đồng thời lực bền bỉ chịu đựng lực tấn công và thời gian kéo dài thần ấn cũng tăng lên.Chúng ta bình thường chỉ mở thần ấn được 1000 hơi thở nhưng họ có thể kéo dài tới gần 1800 hơi thở.Đồng thời khả năng chịu đòn cũng mạnh hơn ta.Cho nên chúng ta không chống lại được họ, Ta đánh trúng một, hai lần họ không sao.Ngược họ mạnh, nên đánh trúng ta một lần đã đã khó có thể gượng dậy, nhất là nếu trong 1000 hơi thở ta không đánh thắng được, thì đồng nghĩa với bị bọn họ giết chết....Phải chi, phải chi cho thời gian 3 năm, 4 năm nữa thì đã không lo.\"Lạc Dương đang đứng kế bên, hai tay nắm chắc run run.Trong trí nhớ của Lạc Tinh, thì cha mẹ của Lạc Dương đều bị Mãnh Thú tộc giết hại cách đây ba năm.Cha của Lạc Tinh cũng mất cách đây mười năm do Mãnh Thú tộc gây nên.Nhưng một phần là người xuyên việt chỉ kế thừa chung trí nhớ, một phần Cha mất lúc Lạc Tinh còn nhỏ nên cũng ko ấn tượng nhiều.Vị tộc trưởng vỗ vai Lạc Dương...!\"- Con đừng buồn con có Lạc Hoàng hình xăm, tương lai sẽ mạnh hơn Thú tộc nhiều lần.Cái con cần là thời gian thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thần kỹ của con là gì.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc con được Thần ấn có âm thanh gì vang lên trong lòng con đó.\"\"- Dạ là Lạc Hoàng Ấn ạ! \"\"- Còn Lạc Tinh ?\"\"- Dạ con cũng không biết, lúc đó không nghe tiếng nói nào vang lên.\" Lạc Tinh thành thật đáp.Lạc Việt trầm ngâm ! \"- Kỳ lạ thật, thường Địa ấn thì 100 người sẽ có một người đạt được Thần Kỹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Dương có địa ấn là Lạc Hoàng cao quí có thần kỹ là đúng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng còn Thần ấn thường là 100% được Thần kỹ sao Lạc Tinh lại không có.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" Giấu nỗi thất vọng vào lòngSau đó quay qua Lạc Dương :\"- Bây giờ ta hỏi con ? Con có đồng ý lấy Lạc Tinh không ?\"Đoàng Tâm thần Lạc Tinh như bị sấm đánh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tự nhiên được ông nội hỏi cưới.Nếu là Lạc Tinh đã mất kia, chắc sẽ mừng lắm vì nó thầm thương cô nàng này lâu rùi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng hiện tại đã dung hợp hồn hai người lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nêntrong lòng Lạc Tinh hiện tại có thể Lạc Dương chỉ là cô gái bình thường, không có tình cảm gì.Lạc Dương dáng người đẹp, lại có Thần ấn nên lại càng toát vẻ cao quí so trong bộ lạc đương nhiên không ai sánh bằng.Tuy nhiên tính tình lạnh nhạt, ít cười, từ nhỏ đả như con trai chỉ thích luyện tập sức mạnh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không khéo cưới về bị cô nàng quản lý gắt gao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì có thể bản thân không thắng được Nàng..Tuy nhiên Lạc Tinh đang lăn tăn thì Lạc Dương đã lên tiếng :\"- Thưa tộc trưởng mạng của con là do cha Lạc Tinh năm xưa cứu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con gả cho Lạc Tinh cũng đúng coi như báo ân.Nhưng con lỡ thề với Lạc Hoàng là ai thay con giết Mãnh Hùng con mới chấp nhận làm vợ.\"Mãnh Hùng hiện là tộc trưởng của Mãnh Thú tộc Thực Lực đã Đản Trung Kỳ Đỉnh Phong.Thên mấy chục năm chinh chiến cùng với võ kỹ Thiết Ngưu thuật, không phải Lạc Tinh bây giờ có thể chống lại.Nhất là vũ khí của tên tộc trưởng, nghe đâu đạt tới vũ khí Nhất Tinh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không gì không phá được...Cái này coi như là cự tuyệt còn gì....</span></p>', 0, '2022-08-05 01:21:03', '2022-08-05 01:21:03');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(114, 77, 'Chương 3: 3: Di Chuyển', 'chuong-3-3-di-chuyen', 3, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*\"Kẻ thù đã đuổi tới đây,Chia ly tử biệt đợi ngày thành công.Thân trai dù có đau lòngNhưng mà cố nén, để không phụ lòng.\"...Tộc trưởng cười nói :\"*- Ta chỉ hỏi con thôi, chứ cũng không ép.Có đều con nên suy nghĩ lại, vì kết duyên vợ chồng cùng người có Thần ấn, thì sinh con ra mới có xác xuất đạt Thần ấn cao nhất.Mà bộ tộc ta chỉ có con và Lạc Tinh đạt được điều kiện này *\"\"- Bây giờ con về đi chuẩn bị đồ đạc, rồi nghỉ một chút sáng mai sẽ có phân công.\"\"- Riêng Lạc Tinh ở lại ta còn chuyện quan trọng.\"\"- Vâng Thưa tộc trưởng !\"Sau khi Lạc Dương đi rồi còn Lạc Tinh ở lại.\"* Tinh nhi có chuyện quan trọng này ta muốn nói với cháu.*\"\" *Cháu tuyệt đối không được nói với ai, kể cả Lạc Dương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cháu biết tại sao Thần ấn lại chia Thiên ấn và Địa ấn không.Nó khác nhau ở chỗ Địa Ấn Đản Trung kỳ chỉ có thể giúp cho tối đa chín người đạt được Nhân Ấn.Nhưng Thiên Ấn lại có thể giúp cho mười tám người đạt được Nhân Ấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhân ấn do Thiên ấn tạo ra có không gian phát triển và tiến bộ cao hơn nhân ấn do một địa ấn tạo ra.Tức là con tạo ra nhân ấn có số lượng, cũng như chất lượng nhiều gấp đôi so với Lạc Dương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc con Linh Đài kỳ tức là tộc ta có ba mươi sáu người nhân ấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thêm Lạc Dương có thể có mười tám người Nhân ấn nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một con số rất lơn với tộc nhỏ như chúng ta.Tiếc là Thiên Ấn cũa cháu bị dị biến, Là Đản Trung kỳ lại có Thần Xăm phủ kín cả hai huyệt Đản Trung và Linh Đài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Điều này ta không biết tương lai cháu trùng kích Linh Đài Kỳ bằng cách nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chính vì điều này nên trong tộc mới đang phân vân không biết chọn cháu và Lạc Dương ai làm tộc trưởng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cháu hơn ở được Thiên ấn, Nhưng Lạc Dương lại tốt hơn, vì tuy là Địa ấn nhưng lại là Lạc Hoàng Thần ấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên có thể có tốc độ tiến nhanh và phù hợp với Lạc Lôi Thuật nhất, nên sẽ dễ dạt Linh Đài kì nhất.Nếu lúc nãy Lạc Dương nó chịu lấy cháu, thì ta đã ban chức tộc trưởng cho nó.Nhưng nó không chịu, nên dù sao cháu cũng là cháu ta.Ta đành lạm quyền lần cuối, truyền lại chức tộc trưởng cho cháu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lấy lý do là Cháu được Thiên ấn tốt hơn Địa ấn..Cái này khiến ta đau đầu, nhưng chỉ cần cháu có thể đạt Đản Trung kỳ viên mãn thì cũng cực kỳ tốt , sẽ không ai có dị nghị gì.Nếu Mãnh Thú tộc mà biết cháu có Thiên Ấn, nó sẽ tìm mọi cách giết cháu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiện tại ta đã thương lượng với các trưởng lão trong tộc.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mai cháu và Lạc Dương, sẽ dẫn hơn 230 người là số người trẻ tuổi được lựa chọn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chia hai hướng dọc theo sông Lô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn lại số người khác cùng với ta, sẽ ở lại đánh lạc hướng Mãnh Thú tộc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chúng ta đã già rồi.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">không đi được nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ hi vọng tuổi trẻ các c cháu có thể đánh ra một mảnh sinh tồn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tộc còn nhiều nhóm riêng lẽ rải rác khắp nơi hi vọng sao này cháu tìm được họ.Nếu có khả năng thì bảo bộc họ.Ta truyền cho cháu Lạc Hoàng Trượng, tượng trưng cho tộc trưởng.Nghe truyền thuyết, thì nó là từ xương sườn của Lạc Hoàng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tổng cộng có bảy cây thần Trượng,tộc ta được một cây, nhưng qua nhiều đời rồi, cũng không ai biết có quyền năng gì khác không.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng đây là bí mật to lớn của tộc ta.Nó Có thể câu thông Lạc Hoàng ban tặng thần ấn cho Lạc tộc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cháu phải giữ gìn kỹ.Làm tộc trưởng, không phải là vinh vang mà là trách nhiệm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cháu quỳ xuống đi và đọc theo ta.* \"Lạc Tinh Qùy xuống, hai tay tiếp nhận trượng :\"- Con Lạc Tinh là Tân Tộc Trưởng đời tiếp theo của Việt Tộc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con hứa sẽ luôn tín ngưỡng Lạc Hoàng và sẽ dùng hết sức mình đưa con dân Việt tộc phát triển không ngừng, ngày càng lớn mạnh ! con sẽ gìn giữ trượng thần để truyền cho thế hệ kế tiếp, trượng còn người còn, trượng mất người mất\"Tộc trưởng tiếp tục dùng dao chích một đầu ngón tay của Lạc Tinh và nhỏ vào Trượng Lạc Hoàng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cây trượng dần sáng lên và biến mất.Trên ngón tay giữa của Lạc Tinh có một chiếc nhẫn nhìn như bằng xương, tạo cảm giác cỗ lão như có từ vạn năm trước, nhưng có khắc triện những hoa văn không thể nhìn rõ bằng mắt thường, mới đầu nhìn rất rối mắt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng càng nhìn càng như có cảm giác hài hòa nào đó, cứ như theo lý nó phải như vậy mới là đúng.-Thôi con về đi, ta phải làm một số việc dự phòng nữa..Về lều của mình, Lạc Tinh sắp xếp lại xuy nghĩ , hôm nay là ngày có nhiều điều mới mẽ sảy ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhất trong hai kiếp làm người của mình.Đầu tiên được tiếp xúc với lĩnh vực Thần ấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vui mừng vì được Thần ấn cao cấp nhất..Lại bị hụt hẫng vì là thần ấn khiếm khuyết và chưa có cách tăng Linh lực, cũng như chưa biết Thần ấn mình tăng gì, tuyệt kỹ Thần ấn là gì.Trong khi Địa ấn của Lạc Dương rất mạnh khi kích hoạt chỉ số, sức mạnh, tốc độ tăng gấp đôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thêm khi học xong Lạc Lôi Thuật nữa thìtốc độ nàng lại có thể tăng gấp đôi tiếp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy không phải hai nhân hai là bốn, nhưng cũng được gấp ba lần.Sau đó, nếu kết hợp thần kỹ, thì sẽ ra được bốn lần tốc độ,mặc dù chỉ duy trì được khoảng vài giây.Lạc Tinh là người mê tiểu thuyết Cổ Long, Kim Dung và tôn thờ trường phái Thiên hạ Võ Công Duy Tốc Bất Phá.Nhưng mà có một bí mật mà Lạc Tinh không nói cho ai biết kể cả ông mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đó là cảm giác mình tăng năng lực lên cao hơn so với ông nói về Đản Trung kỳ.Khi một người đạt Đản Trung kì thì ở trạng thái bình thường sức mạnh đạt Ngàn cân (một Ngưu Lực) và tốc độ bộc phát lên ba trượng.còn bản thân Lạc Tinh ở trạng thái bình thường tăng bao nhiêu thì không biết đượcNhưng ít nhất gấp ba, bốn lần Đản Trung sơ kỳ.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì lý do này mà Lạc Tinh trải qua gần một ngày vẫn chưa thích ứng được,trong khi Lạc Dương chỉ cần một buổi.Đến nỗi khẩu phần cả ngày hôm nay là một con chim nướng nhỏ bằng nắm tay, mà khi Lạc Tinh cầm đến nó bị nghiền nát như bột.Làm cho Lạc Tinh buồn đi tiểu, cũng không dám đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lỡ tay cầm mạnh một phát là nát đời trai.Vì đề phòng bắt trắc, thay vì nghỉ một giấc để lên đường, thì Lạc Tinh ra ngoài chạy một vòng để cảm nhận năng lực mới.khi thì nhảy cao, chạy nhanh đứng lại, ngồi xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cảm giác một người từ không có gì, trở thành một cao thủ võ công thật tuyệt,không thể diễn tả bằng lời.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh thầm nghĩ có lẽ đây là cuộc sống mình hằng mơ ước, tuy đầy cảm giác nguy cơ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng lại có thể có năng lực hơn người thường, tại sao không sảng khoái một lần..Cốc Keng Cốc..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">âm thanh gõ vang lên là tiếng báo hiệu dân làng tập hợp...Khi tập hợp lại xong vị, Tộc Trưởng lên tiếng- *Theo tin tức nhận được, Mãnh Thú Tộc còn cách chúng ta một ngày đường nữa.Nên giờ chúng ta phải tách ra để có thể bảo tồn được một phần bộ tộc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Toàn bộ tộc ta đã phân ra làm bảy nhóm, và mọi người đã biết mình được phân nhóm nào.Lạc Dương con được phong là Thần sứ của Việt tộc ta, con dẫn nhóm 7.Lạc Tinh con được phong là Thiếu tộc trưởng con dẫn nhóm 6.Tứ đại Hộ Pháp thì Lạc Võ dẫn nhóm 5, Lạc Văn dẫn nhóm 4.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Vong dẫn nhóm 3, Lạc Vỹ dẫn nhóm 2.Nhóm một theo ta ở lại tại chỗ cầm chân.Nhóm 2,3,4,5 theo thứ tự tiến đến các cứ điểm đã định trước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người còn một canh giờ để tập hợp chỉnh đốn đội ngũ.*Vì tránh cho bị bắt và tra tấn mà người khác chỉ hướng đi của các nhóm nên ta phải nói riêng hướng đi cho từng nhóm.\"-Lạc Dương lên đây ta nói hướng đi..\" Sau khi nghe chỉ dẫn hướng đi Lạc Dương rời đi.\"-Lạc Tinh lên đây \", Lạc Tinh lên bên tộc trưởng nói.\"*-Cháu dẫn theo nhóm mình đưa người dọc theo hướng Tây Nam, sau đó quay ngoặc lại mentheo Sông Lô nhánh bên trái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cứ đi thẳng sau đó tùy theo tình hình mà cháu chọn hướng đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đi ít nhất mười ngày mới được dừng lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cháu nhớ ở giữa Sông Lô, cách nới đây ngàn dặm có một đảo nhỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu nhóm mình bình an thì cần lấy Khối đá này khắc vị trí dựng trại, để đối phương biết đường tìm kiếm rồi mang lên đảo đó nhé.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhóm của Lạc Dương ta cũng đưa một nữa khối đá này..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">hi vọng cả hai cháu đều bình an.Các nhóm còn lại ta không nói họ biết vị trí này.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">và các nhóm đó thủ ở các vị trí mà hai năm nay chúng ta đã định sẵn, làm nơi đánh du kích với Mãnh Thú tộc.Chúng ta đã lớn tuổi, không còn tiềm năng phát triển nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cháu và Lạc Dương là niềm hi vọng của chúng ta.*\"Lạc Tinh đã nghe phong phanh dự định của Việt tộc lâu rùi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng giờ chính thức bắt đầu cảm giác tim mình nhói đau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dù là người xuyên việt, nhưng cảm giác Việt tộc không hề xa lạ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ những người xa lạ chuẩn bị liều mình bảo vệ mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà bản thân chỉ biết trốn chạy.Nghĩ tới đây hai mắt Lạc Tinh đỏ hồng lên, Cảm giác thật thất bại.Mới vui mừng vì mình có sức mạnh, đúng ra có năng lực bảo vệ người thân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thì giờ đây lại phải nhờ người khác bảo vệ mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chưa bao giờ Lạc Tinh thấy mình vô dụng đến thế !Đừng nên bi thương nhé, dũng cảm lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trách nhiệm của cháu rất nặng nề.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thôi cháu đi đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có gì cứ nhờ đội trưởng Lạc Hồng giúp.Nói xong ông ôm Lạc Tinh lần cuối rùi đi vào Lều.Lạc Tinh trong lầm thầm trách mình, có khi bản thân lúc đạt Thần ấn quá oanh động, nên mới khiến vị trí của tộc đàn bị lộ ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cố nén thất lạc đi về vị trí nhóm 6 đang đứng và kiểm tra lại lần cuối..Nhóm 6 tổng cộng có một trăm mười lăm người trừ Lạc Tinh ra, trong đó chỉ có tầm hơn ba mươi người là trên ba mươi tuổi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong đó có năm nữ.Mỗi người đều tay cầm gậy ở đầu có gắn một viên đá nhọn như mũi giáo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn lại phần lớn người tuổi dưới hai mươi chín.Số lượng nữ khá nhiều khoảng bốn mươi lăm người.Trong đó có hơn mười trẻ em từ ba tới mười tuổi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Do cuộc sống khó khăn, lại thêm phải di chuyển nhiều,nên trẻ em nhỏ hơn 3 tuổi gần như chết hết.một Người đứng trước chào Lạc Tinh là người trung niên tầm bốn mươi tên Lạc Hồng, là chú ruột của Lạc Tinh.Râu Lạc Hồng che khắp mặt nên trông như sáu mươi tuổi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng thân hình rắn chắc, to cao tay cầm hai cây gậy ngắn gắn hai hòn đá to như hai cây chùy.Báo cáo Thiếu tộc trưởng Nhóm 6 đã đầy đủ người có thể xuất phát.Xin hãy ra lệnh hướng đi.Lạc Tinh đang thất thần, nên gật đầu cho phép đi và chỉ hướng đi cho Đội Trưởng Lạc Hồng.Cả đoàn người đi theo thỉnh thoảng có tiếng khóc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng phần lớn là nghiêm nghị môi mím chặt, vì thực tế người yếu đuối đã không thể sống sót ở thời kỳ này được.Đi đường một ngày Lạc Tinh đã dần bình tĩnh lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy kiếp trước chỉ là sinh viên, nhưng ít ra Lạc Tinh cũng đam mê tiểu thuyết.đọc nhiều sách về quân sự, hành quân, đánh trận.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy phần lớn không dùng được, nhưng cũng là rất giỏi trong thời kỳ hiện tại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không có sách vở phim ảnh muốn biết gì phải tự trải qua..Lạc Tinh cho mọi người ở lại tại chỗ nghĩ ngơi.Bốn người nhanh nhẹn nhất đội đi phía sau xóa dấu vết và tạo dấu chân hướng ngược lại so với đoàn người.Riêng Lạc Hồng thì đi xem xét gần đó xem thử có vết tích thú dữ không, mọi người còn lại thì tranh thủ ăn lương khô đem theo.Chủ yếu là thịt Thiết Nha Thử, cùng tên chuột nhưng không phải loài nhỏ bé như ngày naymà là một loại yêu thú giống con chồn nhưng lớn tầm ba mươi cân.Với hàm răng to hay gặm nhấm các loại cây.Là lương thực chủ yếu của Việt tộc vì loài này khá chậm nên được bắt và phơi khô để dành làm lương khô.Lạc Tinh ăn vội vài cục thịt vừa cứng vừa dai và có mùi thum thủm xong, thì tranh thủ tu luyện Lạc Lôi Thuật.Môn võ kỹ này cũng không phải quá khó chỉ cần tập luyện nhiều lần.Lạc Lôi Thuật bao gồm hai phần.Cách vận chuyển linh lực gọi là Lạc Lôi Quyết và bộ pháp gọi là Lạc Lôi Bộ.Lạc Lôi Quyết là cách vận chuyển linh lực, giúp tăng tốc độ cơ bản lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu kết hợp theo bộ pháp của Lạc Lôi Bộ là ba bước chuyển hướng nhỏ một lần, năm bước có thể đảo chiều một lần.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Eo kết hợp theo chân, tay kết hợp theo eo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tốc độ tay cũng từ đó tăng lên, sức mạnh cũng tăng theo nhưng không đáng kể.Do Lạc Lôi Quyết có Linh khí chỉ tập trung vận chuyển xuống chân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên lực tấn công cũng ko tăng lên đáng kể.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cái tăng chủ yếu do tộc độ tăng nên lực tăng theo.Nói chung là Lạc Lôi Quyết phạm vi gia tốc nhỏ, chỉ tồn tại bên trong cơ thể mà thôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngược lại Lạc Lôi Bộ phạm vi tăng tốc lớn, phụ thuộc theo bước chân của người sử dụng.Lạc Lôi quyết hơn ở tăng tốc độ trực tiếp, không cần thời gian gia tốc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Lôi bộ tốn thời gian nhiều hơn.Tính ra từ Lạc Lôi bộ Nhất chuyển tăng lên Ngũ chuyễn cần mười lăm bước chân...!Thí dụ đại thành Lạc Lôi quyết khi thi triển ban đầu từ vận tốc là 13 trượng / một hơi thở, tăng lên 19.5 trượng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó bắt đầu Lạc Lôi bộ, bước qua ba bước đầu tiên tăng lên gần 21 trượng.Khi bước tới bước 15 mới tăng lên 26 trượng một hơi thở.Nhớ lại lời ông nói, Lạc Lôi Thuật tăng gấp đôi tốc độ là thua Thiết Ngưu Thuật.Nhưng Lạc Tinh nghĩ ngược lại, Vì Lạc Lôi Thuật quá khó học thôi và tăng tốc lên chậm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh tin là tốc độ tăng thì lực công kích cũng sẽ tăng ít nhiều.Nếu cho Lạc Tinh đánh giá, thì môn võ kỹ này hoàn toàn xứng đáng bỏ thời gian và công sức học.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì sức mạnh có bao lớn đi nữa mà không đánh trúng kẻ địch cũng vô dụng.Lạc Tinh tập vận chuyển linh lực học Lạc Lôi Quyết trước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đầu tiên tập trung kích hoạt năng lượng Thần ấn từ huyệt Đản Trung, nương theo vòng xoáy sản sinh ra Linh Khí.Sao đó làm cho hai luồng linh khí xoắn lại nhau từng đôi một và truyền xuống huyệt Quan Nguyên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại chia hai luồng theo hai huyệt Túc Tam Lý ở hai bên chân, kéo dài xuống hai huyệt Dũng Tuyền.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sao đó kích hoạt Linh lực ra hai mu bàn chân, rồi bật lên giống như đệm không khí.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi đó tốc độ chạy sẽ tăng lên, đồng thời nhảy cũng cao hơn...Là sinh viên năm cuối, thì Lạc Tinh cũng từng học hiểu vài điều cơ bản về vật lý mượn lực, trợ lực, Lực ly tâm...!nên học khá nhanh Lạc Lôi Thuật, cái khó là thuật ngữ khó hiểu thôi.Với lại học thì nhanh, nhưng ứng dụng thì không phải một ngày, một buổi mà được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiện tại coi như là nhập môn, khi sử đụng thử lần đầu thì từ tốc độ tăng lên chỉ một phầnCoi như có tiến bộ rồi..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh tự an ủi..</span></p>', 0, '2022-08-05 01:21:34', '2022-08-05 01:21:34');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(115, 77, 'Chương 4: 4: Việt Võ Đạo Vovinam', 'chuong-4-4-viet-vo-dao-vovinam', 4, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*Võ đạo luyện chưa thành,Báo vàng đã tới nhanh.Thiếu niên không hề sợ ,Bữa tối càng ngon lành.*.......Sau khi ăn xong và nghỉ ngơi hai canh giờ, thì mọi người tiếp tục lên đường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh vừa đi vừa tiếp tục dùng thử Lạc Lôi Quyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bình Thường thần ấn kích hoạt được khoảng mười lăm phút là cực hạn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên Lạc Tinh cứ tập luyện tầm vài phút lại nghỉ ngơi , di chuyển bình thường tầm năm phút rồi lại tập tiếp vài phút.Cứ như vậy tới ngày tối ngày thứ tư.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người được dừng lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiện tại ai cũng im lặng , không nói chuyện phần vì mệt phần vì tránh thú dữ , Ban đêm là thời gian hung thú hoạt động mãnh liệt nhất .Mọi người tìm được một hang động và đốt lửa lên trước hang.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh nêu ý kiến cho mọi người ngủ xuốt đêm , thay vì chỉ nghỉ hai canh giờ mỗi ngày như các hôm trước.Vì người lớn chịu đựng được , nhưng những đứa trẻ em đã không còn sức di chuyển.Lạc Hồng dẫn mười người đi xung quanh tìm thức ăn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh phụ trách ở nhà bảo vệ mọi người , vì dù Lạc Tinh mười hai tuổi nhưng nói về sức mạnh , thì còn hơn cả Lạc Hồng , tuy nhiên là kinh nghiệm thì không có gì hết.Lạc Tinh thầm nghĩ mà hối hận, phải ngày xưa học kỹ càng các môn học về võ thuật thì may quá.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy trong trường cũng có môn thể dục bắt buột và Lạc Tinh học môn Vovinam , nhưng cái đó chỉ là tập theo tám bài quyền và chỉ múa là chủ yếu chứ ko có đối kháng.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong tám bài quyền thìcó Việt Võ Đạo Quyền, Long Hổ Quyền, Bạch Hạc Sơn Quyền , Ngũ Môn Quyền là Lạc Tinh còn nhớ , do vừa học ở đại học năm cuối.còn bốn bài còn lại học cách một năm, thì không còn nhớ hết nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Về các môn võ vũ khí thì có Tinh Hoa Lưỡng nghi kiếm pháp, Lạc Tinh từ bé mê kiếm hiệp , nên có học từ nhỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy nhiên chỉ tự học , không qua trường lớp , được cái múa đẹp chứ không có thực tiễn gì.Nhưng kệ , vì thời hiện tại không có vũ khí nóng và chủ yếu đánh nhau bằng gậy gộc , tay chân.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cộng thêm loài người có sức mạnh Thần ấn.Lạc Tinh tin là nếu cho mình thời gian , mình có thể phát huy tối đa năng lực của môn võ Vovinam.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn kiếm pháp mà không có kiếm , thì làm gì được ? dùng gậy thay kiếm thì không thực tế lắm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ở bộ lạc này ai cũng chủ yếu dùng cây vót nhọn đầu , có gắn đá hoặc xương thú là chính.\"Ưu tiên quyền cước trước vậy ! \"Lạc Tinh bắt đầu đi lại từng bài quyền , để khi nào mình đạt tới nhuần nhuyễn thành thục.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi sẽ tiếp tục đổi mới cho phù hợp với năng lực hiện tại..Việt võ đạo (Vovinam) trong lịch sử nhân loại , là dạng sinh sau đẻ muộn so với mấy ngàn năm võ thuật của thế giới , vì tới năm 1936 võ sư Nguyễn Lộc mới sáng tạo ra , ông tin rằng với môn võ này có thể giúp nhân dân chống lại đế quốc Pháp .Tuy ra đời chậm , nhưng không phải Vovinam không mạnh , mà ngược lại nó là tinh hoa tích lũy được từ môn vật cổ truyền việt Nam , kết hợp với những tinh hoa của các môn phái võ thuật Trung Quốc, Hàn Quốc và Nhật Bản.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có thể nói là Tinh hoa của hơn 4000 năm chiến tranh chống lại thú dữ ,chống lại kẻ thù xâm lược.Dựa trên nguyên lý Cương Nhu Phối Triển, môn sinh Vovinam được tập luyện những đòn thế tay không, cùi chỏ, chân, gối cho đến các loại vũ khí như kiếm, đao, mã tấu, dao, côn, quạt...Ngoài ra, môn sinh còn được học cách đối phó với vũ khí bằng tay không, các lối phản đòn,khóa gỡ và các đòn vật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong các môn võ của Việt Nam, Vovinam được phát triển quy mô và rộng lớn nhất với nhiều môn sinh có mặt ở hơn sáu mươi nước trên thế giới.Kỳ hiệu của Vovinam là vòng tròn Âm Dương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giao tương giữa lưỡng cực là bản đồ Việt Nam cong theo hình chữ S, tượng trưng cho sự Tương Thôi - Tương Giao - Tương Sinh và Thường Dịch của dòng Sống Miên Sinh phối hợp, hài hòa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bao bọc Lưỡng Nghi là vòng tròn trắng , tượng trưng cho Đạo Thể với sứ vụ Điều Hòa – Khắc Chế - Bao Dung giữa Âm tố và Dương tố để tác thành vĩnh cửu sự sống của muôn loài.Đang đứng ngoài động tập lại Việt võ đạo quyền , Lạc Tinh cảm giác như có cái gì đang nhìn mình .Lặng nghe một chút .\"-Xoạt\"Xuất hiện một con báo màu vàng to như con bò mộng nhảy tới ..Lạc Tinh trong lòng run lên biết là không ổn, có lẽ hang động này là nơi chú ẩn của con Báo này , bây giờ nó ăn no xong về nhà ngủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lập tức Lạc Tinh mở ra Thần Ấn, kịp lúc con Báo nhảy dến vồ một chân vào đầu Lạc TinhLạc Tinh cúi đầu xuống lộn một vòng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đồng thời tiện tay ôm hòn đá to bên cạnh.Từ khi được thần ấn , Lạc Tinh tốc độ phản ứng tăng cao nên kịp thời tránh được, Con báo vồ tát hụt , tức thời xoay lại nhảy tới , cào chân trước vào hông Lạc Tinh.\"- Liều mạng vậy\"Hai chân Lạc Tinh chùng lại nhảy cao lên , khi rơi xuống thuận tay ,xoay ngược mình lại cầm tảng đá vỗ mạnh vào đầu con Báo Vàng .Nghe một tiếng Binh, Con báo lảo đảo ngã ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh cố nén cơn tê rần từ tảng đá sinh phản lực truyền lên , tiếp tục ném mạnh tảng đá vào lưng nó thêm một phát nữa .Rồi lại chạy ra nhặt tiếp hòn khác nhỏ hơn, ném lần nữa , cứ thế liên tục năm ,sáu hòn đá được ném ra to có nhỏ có.Một lúc sau Con Báo không đứng dậy được nữa.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nó nhìn Lạc Tinh vài lần không cam tâm rồi gục xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh mừng rỡ vì không ngờ mình lại mạnh như vậy.Nếu khi xưa cầm hòn đá hai mươi cân còn không nổi nữa là , bây giờ ném hòn đá trăm cân như chơi.Nhóm người trong động nghe động tịnh chạy ra , thấy Lạc Tinh đứng cạnh con Báo đang gục bên vũng máu thì mừng rỡ.Cả trăm cặp mắt sùng bái nhìn Lạc Tinh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ngưu Bức a, cảm giác thật sảng khoái ..\"Mọi người đi xung quanh xem con Báo thật chết chưa ?\"\"Đã chết rồi ! \"Bây giờ nhìn kỹ lại Lạc Tinh mới thấy con báo ở Đùi, và bụng có hai vết thương sâu không phải do đá ném tạo thành.\"-Có lẽ nó đã bị con thú nào đó tấn công rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hèn gì mình giết nó dễ dàng vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" Nó bị thương từ trước , nên tốc độ cũng đã chậm lại , chứ Loài Báo nổi tiếng về tốc độ.Có thể loài Báo có sức mạnh thua xa Hổ , nhưng nó có thể nhảy cao , leo trèo trên cây là một đối thủ khó chịu trong rừng già.Sẵn lửa và bụng đói Lạc Tinh cho mấy người xẻ thịt ra nướng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai người thanh niên thường phụ trách mổ thịt thú rừng đứng ra cẩn thận dùng đá mài cắt cẩn thận bộ da.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì thời này quần áo tất cả làm bằng da thú.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con này to thế có thể làm ít nhất ba bộ quần áo hoặc một căn liều nhỏ, nên phải cẩn thận và là người khỏe mạnh mới cắt nổi lớp da của nó.Khi thịt mang ra thành nhiều khối nhỏ , mọi người xum quầy bên đám lửa nướng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lúc sao hương thịt thơm tỏa ra ngào ngạt.Lạc Tinh nuốt nước miếng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Và bắt đầu ăn như hổ đói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không biết do đói bụng và mệt, hay tại vì bốn ngày toàn gặm một ít thịt chuột mà giờ ăn thịt Báo cảm giác vừa ngọt vừa ngon , chỉ tiếc là không có gia vị.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng ăn một miếng thịt tươi cảm giác bụng ấm và nóng lên hẳn, bao nhiêu mệt mỏi tan hết.Lúc này thì nhóm Lạc Hồng đi săn cũng trở lại , mang theo một con nai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng đã đói rùi nên cũng không làm thịt con nai , mà mọi người tập trung ăn thịt Báo.Cuối cùng thì Thịt con báo chỉ còn lại mỗi cái đùi và phần lưng để dành Sáng mai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh thầm cảm thán\"-Nửa con báo hơn bốn trăm cân, bị một trăm người ăn trong một buổi tối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sức ăn thật khủng khiếp\"Lần đầu tiên Lạc Tinh thấy lạc quan cho lần di chuyển này, nhìn kỹ lại đám trẻ đang ngồi ăn cười nói vui vẻ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh nghĩ có thời gian sẽ dạy dỗ lại bọn trẻ giúp chúng mạnh hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Lôi Quyết thì lũ trẻ không học được rồi...vì phải có linh lực hỗ trợ.Một đêm trôi qua , có lẽ Lạc Tinh là người có Thần ấn nên giấc ngủ ít , nhưng sức khỏe vẫn tốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ có cái bụng là nhanh đói hơn bình thường .Là người thức sớm nhất, đi ra ngồi nhìn đống lửa đang gần tàn , bổng thấy có ánh sáng khác thường .\"- Đây là gì ?\"Tuy cũng màu đỏ nhưng lại sáng hơn cả đóm lửa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh làm lạ, đi lại nhặt một cây khều lên thử.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thấy trong đầu con Báo có một vết tròn màu đỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Do đầu con báo cứng quá không bổ ra được nên lúc tối mọi người chỉ ăn phần ngoài và bỏ nguyên cái đầu vào đốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ bị lửa cháy xuyên đêm lộ ra một khe nhỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh vận lực dùng tảng đá đập liên tục vài lần cuối cùng cạy ra được .\"-Có khi nào là nội đan ?\"Vì các truyện tiên hiệp mình hay đọc , thì yêu thú có khi có nội đan nghe nói có thể làm thuốc hoặc uống vào tăng sức mạnh.Nhưng trong thế giới này , Lạc Tinh chưa nghe ai nhắc đến , nên không biết cũng không dám nuốt thử.\"-Cứ giữ lại để dành , biết đâu mai mốt cần đến đến\"Lạc Tinh cẩn thận gói lại vào hành lý mình..Mấy hôm trước di chuyển , Lạc Tinh chỉ mang theo ba bộ đồ , nói đúng ra là ba tấm da thú , một ít thịt chuột nướng , nhưng một phần ăn , một phần cho mấy đứa trẻ trong đội , đã không còn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ được một bổ sung một ít thịt Nai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Do làm tộc trưởng nên được ưu tiên phần thịt đùi.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">ppphưng Lạc Tinh cũng ko ra vẻ từ chối , vì nghĩ mình cần nhiều năng lượng để rèn luyện và giữ sức , để có thể xử lý được công việc trong tộc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từ một sinh viên nghèo , không cần quản lý và có trách nhiệm với ai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiện tại thì thành thiếu Tộc Trưởng , quản lý hơn một trăm mười lăm người nên cũng khá áp lực.Tập hợp xong mọi người lại tiếp tục lên đường , Lạc Tinh cho mọi người bẻ ngoặc lại đông Nam con sông Lô.Cứ vậy ban ngày đi , tối mịt mới nghỉ lại hai canh giờ .Trong thời gian dừng chân lại thì Lạc Tinh , Lạc Hồng cùng với nhóm người bàn với nhau các trường hợp phát sinh có thể sảy ra , để nhanh chóng đưa ra phương án quyết định nhanh chóng tránh hỗn loạn phương án ứng biến khi gặp nhiều kẻ thù hay thú dữ.Lạc Tinh cũng thực hiện phân đội để khi có việc gì thì điều động nhanh mọi người không cần bỡ ngỡ không biết làm gì.Lạc Tinh nhớ lại cái thời học quốc phòng có phân tiểu đội , trung đội nên quyết định phân theo phương thức đó.Trung Đội 1 , gồm hai mươi sáu người do Lạc Hồng làm Trung đội trưởng với hai mươi lăm thành viên nam có nhiều kinh nghiệm nhất phụ trách chiến đấu chính , cảnh vệ , săn bắt thú.Trung Đội 2 , gồm hai mươi sáu người do Lạc Tinh làm Trung đội trưởng với hai mươi lăm thành viên nam độ tuổi tầm từ mười bảy tới hai mươi sáu tuổi phụ trách bổ xung viện trợ đội .Trung Đội 3 , gồm 34 người toàn là nữ trong Nhóm phụ trách bổ xung viện trợ đội 2 ,làm thịt thú rừng ,nướng đồ ăn.Trung Đội 4 gồm 20 người , do Lạc Kỳ làm đội Trung trưởng với gồm nam, nữ độ tuổi mười hai- 17 tuổi , phụ trách bổ xung viện trợ đội 2,3 và tìm củi lửa,nước uống..Trung Đội 5 gồm 10 người do Lạc Mỹ , làm Trung đội trưởng gồm 9 trẻ em nhỏ hơn mười hai tuổi, là trẻ con nên không phân công gì cụ thể .Lạc Hương là một phụ nữ nhưng sức vóc to cao như nam giới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sức mạnh cũng thuộc loại khá , nên được mọi người bầu làm Trung đội trưởng đội 3.Lạc Kỳ là em Trai Lạc Hương hai mươi tám tuổi cũng giống như Lạc Hương vóc người cao lớn, thân thủ nhanh nhẹn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếc là không có Thần ấn.Làm trung đội trưởng đội 4Còn Lạc Mỹ đã 25 tuổi, có một đứa con ba tuổi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có kinh nghiệm chăm trẻ nên được làm Trung đội trưởng đội 5 để tiện bề chăm sóc các em nhỏ.Lạc Tinh còn dự định phân Trung đội ra thành từng đội viên nhỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng không có thời gian và chưa biết nhiều về các thành viên , nên dự định khi nào tới địa điểm có thể dừng chân rùi thì sẽ xử lý.Trên đường đi mọi người tìm cách trang bị vũ khí cho mình ,tìm thấy cây nào dẻo và chắc đạt điều kiện làm giáo thì lấy,gặp hòn đá nào sắc nhọn thì nhặt.Lạc Tinh cũng tự làm cho mình cây giáo .nói đúng ra là cây côn thì đúng hơn .thân cây to cỡ miệng bát nhỏ, dài tầm một trượng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Do không có gì chuốt nhọn được , nên gặp cây đạt chuẩn vừa dẻo vừa dai vừa thẳng có thể cầm được đã tốt.Còn đá để gắn vào đầu thì khó tìm, phải là loại đá cứng và sắc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu không thì cũng không làm được gì và Lạc Tinh chưa từng dùng giáo nên cũng vướng víu.\"-Chẳng thà cầm thanh côn không thôi có khi tự mình hại mình, đâm trúng tay thì khổ.\"Ngày thứ chín, Lạc Tinh thấy lại dòng sông Lô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghỉ một chút rồi mọi người men theo dòng sông đi tiếp.Qua ngày thứ mười , Lạc Tinh thấy một cù lao giữa sông Lô là vị trí ông nội mình nói.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có lẽ thời trai trẻ ông đã từng đi tới đây.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng Lạc Tinh không dừng lại mà cho mọi người đi tiếp .Cần phải đi xa hơn một chút đề phòng bị kẻ địch đuổi kịp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh cũng hết sức lo lắng cho các đoàn người khác nhưng hiện tại bản thân mình không lo được nhiều như vậy..</span></p>', 0, '2022-08-05 01:22:02', '2022-08-05 01:22:02'),
(116, 77, 'Chương 5: 5: Đàn Sói Đói', 'chuong-5-5-dan-soi-doi', 5, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*Rừng thiêng nước độc hiểm nguyĐụng ngay đàn sói tức thì bị vâyLạc Tinh thần võ anh tàiCứu nguy mỹ nữ đong đầy ngợi khen.*.............Mọi việc thuận lợi cho đến chiều tối ngày thứ 12 , đoàn người đang chuẩn bị dừng chân thì nghe tiếng sói hú ....Mọi người cảm giác thần kinh căng thẳng lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Theo kinh nghiệm gặp sói còn khổ hơn gặp Hổ.vì Hổ tuy mạnh nhưng đi từng con .Sói tuy yếu nhưng lại đi theo đàn..Nhất là đàn sói đói , vì miếng ăn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên một khi đói lên , thì đàn sói có thể bất chấp tất cả tấn công con mồi, cho dù yếu thế hơn vẫn dũng cảm lao lên.Lạc Hồng ra lệnh :“*- Mọi người chú ý, phương án Phòng Thủ thứ hai ! *\"Trẻ em thuộc đội 5 quây vào giữa, vòng ngoài là Trung đội 4, mỗi người đều trên tay cầm hai cục đá ,loại có một đầu sắc nhọn nhặt được trên đường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếp Trung đội 3 ở ngoài cùng , mỗi người cầm một cây giáo mũi đá xoay ra ngoài.Là loại đá đen , nhỏ nhưng có một đầu sắc có thể ném cả cây giáo ra khi đàn sói ở xa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hoặc đâm tới khi đàn sói xông vào gần.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tương tự như phóng lao.Trung đội 2 thì đứng một cụm gần đó, sẵn sàng tiếp viện cho bất cứ bên nào bị tấn công.Trung đội 1 của Lạc Hồng và hơn hai mươi lăm thanh niên lớn tuổi thì dàn hai hàng ngang.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sẵn sàng tấn công trực tiếp đàn thú , vì họ đã đi săn với nhau nhiều lần nên rất ăn ý...Đàn sói đã xuất hiện khoảng ba mươi con , trong đó con đầu đàn to hơn cả con Báo hôm qua Lạc Tinh giết chết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cả người nó như con trâu to , bộ lông màu xám trắng , Hàm răng nhe lên lộ răng nanh to cở ngón chân cái người lớn dài cả tất.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đi sau đó là ba con sói lớn cỡ con bò nhỏ , thêm phía sau là hơn hai mươi con sói nhỏ hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con nào cặp mắt cũng đỏ rực.Chỉ sau một chút phân vân vì có ba nhóm người chưa biết tấn công ai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con sói to nhất tru lên, cả đàn tràn lên đội của Lạc Hồng là nhóm gần nhất, có vẻ có uy hiếp nhất .“- Trung đội 2 men theo cắt đuôi đàn sói .”Lạc Tinh hô lớn xong , kích hoạt Thần ấn lấy một hòn đá ném vào một con sói sau cùng.Tốc độ viên đá khá nhanh , nhưng con Sói lại nhanh hơn , nó xoay đầu qua viên đá chỉ sượt trúng chân con phía sau nó.Lạc Tinh bình tĩnh , dùng Lạc Lôi Thuật đã tăng ba phần sau tám ngày tập luyện.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đầu tiên nhảy xéo qua , rồi dùng côn dài vụt mạnh vào hông con sói .Bất ngờ với Tốc độ đột nhiên thay đổi của Lạc Tinh , nên nó không tránh được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ nghe OẲng một tiếng , Con sói ngã ra .Mấy thanh niên đội 2 cầm giáo đá gần đó , nhảy đâm bồi cho vài lần.Lạc Tinh không dừng lại , cầm tiếp tảng đá chọi con sói thứ hai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con sói bị đau nên hùng hổ chạy lại tấn công, bị Lạc Tinh vụt vào đầu hai phát , đầu nó vỡ ra không thể sống được nữa.Đã không còn đá ném Lạc Tinh hô lớn“-Đội 3 và 4 ném nó nào !“Liên tục hơn năm mươi viên ném ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đàn sói phía sau bị đau quay người lại .Nhất là Có ba con sói bị trúng ngay đầu , nhảy qua Lạc Tinh đang đứng gần nhất , như muốn ăn thua đủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh thầm hô không may , nhưng cũng đã thành công tách đàn sói ra làm hai.Phát động Lạc Lôi Thuật , Lạc Tinh tả xung hữu đột.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khoảng mười người gần đó , cũng dùng giáo đâm giết được hai con, Lạc Tinh giết thêm một con.Lúc này bên Lạc Hồng cũng giết hơn mười con , nhưng có khoảng bảy người bị thương nằm dưới đất.\"*-Trung đội 2 ba người một con ! Trung đội 3 phân nữa yểm hộ trung đội hai, phân nữa yểm hộ trung đội một.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*\" Lạc Tinh hô lớn.Sau khi chia cắt đàn sói nhỏ ra, thì Con sói đầu đàn bị Lạc Hồng và năm người Trung đội một quay quanh.Hễ nó vồ hướng nào thì năm người khác dùng giáo đâm giải quây giúp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Hồng tập trung chờ nó sơ hở là đâm trí mạng.Con sói đầu đàn tuy mạnh , nhưng lại bị giằng co .Lạc Tinh thì lợi dụng tốc độ chạy nhanh đi xung quanh, thấy nhóm nào gặp khó sẽ nhảy vào giúp một tay.Mỗi khi có nhóm giết được sói chết , thì nhóm đó tiếp tục chạy qua giúp đỡ nhóm khác.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho nên ba phút trôi qua , chỉ còn ba con sói lớn và một con sói đầu đàn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng có thêm hai người bị thương nằm ra đất.Trong phút chốc , với sự giúp đỡ của Lạc Tinh , ba coi sói lớn cũng bị giết , chỉ còn con sói đầu đàn đã bị thương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì cố giết ba con sói lớn , nên làm bị thương thêm bốn người.Ngay cả Lạc Hồng cũng bị cắn ngang vai , chỉ còn một tay cầm Búa Đá.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng vẫn như cũ , nén đau hành động nhịp nhàng phối hợp với nhóm người bao vây con sói.Bỗng nhiên con sói đầu đàn nhảy lên cao , Nó chạy trốn về phía nhóm trẻ em.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vốn đang co lại một một chỗ , ở đó chỉ có Lạc Mỹ là người lớn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cô ta đành nhắm mắt cố gắng dùng thân mình chặn lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh cũng đang đứng gần đó , dùng hết linh lực cuối cùng thi triển Lạc Lôi Thuật lao đến,\"-còn thiếu một chút nữa \"Lhông còn kịp suy nghĩ nhiều , Lạc Tinh cầm côn đâm thẳng vào mắt con sói, vừa lúc con sói mở miệng ra định cắn vào Lạc Mỹ đang chắn đường, hòng tìm đường thoát thân.\"- Phập \" cây côn không đâm trúng mắt , nhưng xảo diệu hướng vào họng sói đang mở to như cái xô nước.Thanh côn gãy đôi khi va trúng răng nó, nhưng còn hơn nữa đoạn còn lại, kịp đâm thấu từ cổ họng đang mở ra , xuyên vào tới ngực con sói chúa .\"-Đúng là hay không bằng hên.\"Lạc Tinh đau nhói vì một khúc côn gảy đâm ngược vào lòng bàn tay, máu con Sói cộng máu Lạc Tinh hòa vào nhau phun khắp mắt , đã không thấy gì nữa.Do quán tính con Sói lao tới đè Lạc Mỹ lẫn Lạc Tinh xuống..\"- Thiếu tộc Trưởng !\" .Mọi người ùa tới thấy con sói chúa đã chết không còn nhút nhích.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai ba người hè nhau kéo Xác con Sói ra .Lạc Tinh choáng váng , được mọi người đỡ dậy , Thấy máu nhuộm đỏ ai cũng lo lắng .Nhưng kiểm tra thương thế Lạc Tinh thì thấy chỉ bị đứt tay một đường , và thêm một vết cào nhẹ từ chân con sói trúng vai.Riêng Lạc Mỹ một phần sợ quá, một phần bị cả Lạc Tinh và Sói Chúa đè nên đã ngất xỉu chứ cũng không có bị thương.Nghỉ ngơi tầm năm phút mọi người kiểm tra lại, bị thương nặng hơn mười người , đã không thể di chuyển .Với hơn bốn mươi người bị thương nhẹ hơn có thể di chuyển.\"-* Thiếu tộc trưởng , chúng ta phải đi nhanh , vì mùi máu tươi sẽ dẫn đến thú dữ khác đến tìm kiếm thức ăn* \" Lạc Hồng lại gần nói .Lạc Tinh gật đầu ra hiệu đồng ý , rồi băng vội bàn tay bằng một lá cây , không biết lá gì do một cô gái đưa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Do đầu vẫn còn choáng và linh lực cạn kệt, nên Lạc Tinh cho Lạc Hồng toàn quyền phân công .\" *Nhóm hai cho người cõng người bị thương đi.nhóm một đi trước mở đường.Nhóm ba rinh đồ đạc của bộ lạc, số người còn lại mang theo xác sói .Nhớ dùng lá cây chà lên thân sói át mùi máu đi.Nhóm 4 đi sau nhóm hai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhanh ..*\"Mọi người làm theo Lạc Hồng .Khi đã hơi tỉnh táo Lạc Tinh xung phong vác con sói đầu Đàn to lớn , trong lòng thầm than :\"* Người ta Thiếu tộc Trưởng ăn sung mặt sướng , mà ta thì phải làm culi* \"Thật ra thì do con sói nặng cả gần sáu trăm cân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bộ lạc chỉ có Lạc Hồng vác nổi , nhưng Lạc Hồng đang bị Thương ở vai và phải chỉ huy cả đội.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ có Lạc Tinh bị đau ở bàn tay không ảnh hưởng gì…Còn Lạc Kỳ trung đội trưởng nhóm 4 cũng khá khỏe nhưng được phân công đi phía sau xóa dấu vết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đồng thời cố tình dùng xác con sói nhỏ xé ra nhiều mảnh để văng thịt mỗi trăm trượng một mảnh, theo hướng khác hướng mọi người di chuyển nhằm cho thú dữ có tìm dến mùi máu tươi cũng đi theo hướng khác.Đi suốt đêm tới bờ sông , cả đoàn dừng lại nghỉ.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong hơn mười người bị thương nặng có ba người hôn mê chưa tỉnh được hơ lá thuốc đắp lên vết thương và trán để bớt sốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mấy người còn lại đã có thể chống gậy đi từng bước .\"- Sức sống thời cổ đại này quá mạnh mẽ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" Lạc Tinh cảm thán\"-* Mọi người khỏe mạnh còn lại nghe đây :nhóm 1 cảnh giới, nhóm 2 lọc da sói, thịt sói ra riêng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nhóm 3 tìm củi, nhóm lửa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhóm 4 rửa thịt, xiên thịt thành từng que*.\"Lạc Tinh thì được rảnh rỗi trèo lên một cây cao , nhìn phương hướng xem nơi nào thích hợp làm nơi trú ẩn mới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì nếu đi xa nữa chưa chắc tốt vì có thể gặp bộ lạc khác , có thể là địch nhân vì thời kì này thêm người đồng nghĩa thêm thức ăn , mà thức ăn thì ít.Thành ra Không phải cùng tộc ta, chỉ có giết để bớt cạnh tranh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bỗng nghe tiếng ríu rít cười nói của đám con gái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiện tại là Đản Trung Kỳ nên Lạc Tinh có thể nhìn xa và nghe âm thanh ở khoảng cách gấp ba bốn lần người thường.Trong phân đội thứ 6 Lạc Tinh dẫn đi có gần năm mươi người nữ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tập hợp lại một đám tắm ở hạ du sông Lô.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa tắm vừa bình luận về các chàng trai trong tộc thỉnh thoảng có nhắc Lạc Tinh.Qua hai trận chiến giết được con Sói chúa và Kim Báo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh đã được mọi người Trong tộc tán thành, nhất là đi ngang các cô gái ai cũng nhìn Lạc Tinh bằng ánh mắt sùng bái gần như sẵn sàng Hiến thân, khiến cho chàng trai tơ hai kiếp như Lạc Tinh cũng tâm hồn xao xuyến…Tiếc là không ai chủ động.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phần vì Lạc Tinh là Thiếu Tộc Trưởng phần Lạc Tinh mới 16 tuổi ( bằng 18 tuổi thời hiện đại thành niên rồi xem như còn khá trẻ)\" Mô phật ! Cho con nghỉ tu năm phút \".Lạc Tinh nhìn các cô gái toàn thân trên không che một chút gì , thấp thoáng giữa làn nước trong , có chút xúc động, muốn ngắm vài lần.\" Dù gì cũng chỉ trời biết đất biết, bản thân mình biết thôi.\" Lạc Tinh thầm nhủ\"-* Mình đâu phải cố ý , là vô tình thôi.* \"Đấu tranh tư tưởng một lúc Lạc Tinh mới tiếc rẻ quay ánh mắt sang hướng khác làm chính sự ..Nhìn khắp nơi một mảnh rừng xanh , đồi núi sông ngòi trong lòng và nhớ lại kiến thức cơ bản.Một nơi lý tưởng sống phải gần nguồn nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dễ tìm thức ăn.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thêm nữa phải dễ phòng thủ.Như hôm qua chỉ có hơn ba mươi con sói mà hơn trăm người.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tính ra ba oánh một , mà để bị thương suýt chết mười một người.Nếu số lượng sói tăng lên gấp đôi, chắc toàn quân bị diệt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giờ có hai phương án Lạc Tinh Thầm nghĩ có thể.Một là gần bờ sông sẽ có nguồn nước ,có cá ăn, và chỉ phòng thủ một bên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì thời này chưa thấy tàu thuyền.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Và dưới sông cũng không yên bình lắm có cả Mãng xà to bằng cả thùng nước là bình thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên không ai dùng đường Thủy di chuyển.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng gần bờ sông có bất lợi là địa lý thấp, dễ bị lũ lụt nếu ở lâu dài vào mùa mưa, dễ bị phát hiện nếu có người men theo bờ sông di chuyển .Nhất là thú rừng cũng hay ra bờ sông uống nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Gặp thú hiền thì đỡ có thịt ăn nhưng gặp mãnh hổ,sói ,báo , thì không tránh khỏi một phen ác chiến.Chọn lựa Thứ hai là định cư trên núi cao, dễ canh gác, đề phòng thú dữ và người khác tấn công.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không sợ lũ lụt, có thể săn bắn tìm thức ăn trong rừng .Tốt nhất tìm một bên là vách núi một bên là rừng chỉ cần phòng thủ một mặt.Nhìn hướng Tây có thỏa điều kiện vách núi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hướng tây Nam thì có một ngọn thác cao chắc có suối lớn.có thể có cá, có nước uống.Còn nếu chọn vị trí bờ sông thì ngay vị trí các cô gái đang tắm cũng không tệ là một nơi lý tưởng..Lạc Tinh lại lừa dối bản thân là mình tìm vị trí dựng trại , nên quay về hướng các cô gái đang tắm , thì phát hiện không còn ai.\"-* Sao tắm nhanh quá.* \"Suy nghĩ một chút Lạc Tinh quyết định về hội ý Lạc Hồng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi về tới thì mọi việc đã xong.Đồ ăn đã xong thịt nướng chín, nước đầy đủ, lều giả chiến tạm bợ đã dựng.Đoàn người bắt đầu ăn, Lần đầu ăn thịt sói Lạc tinh cảm giác cũng khá ngon nhưng hơi ngán.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xuyên việt gần nữa tháng.Toàn ăn thit nướng.Không ngán không được, nhất là không có muối chủ yếu vị mặn từ một loại rễ cây nào đó giả nhiễn ra, có vị mặn nhưng vị đất đậm hơn.Là một người hiện đại có đầy đủ gia vị và dụng cụ nấu nướng phục vụ nấu ăn, thì món ăn này với Lạc Tinh như cực hình ,cứ như gặm khúc gỗ mục.....</span></p>', 0, '2022-08-05 01:22:35', '2022-08-05 01:22:35');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(117, 77, 'Chương 6: 6: Lạc Mỹ Cảm Ơn', 'chuong-6-6-lac-my-cam-on', 6, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"*Gái một con đong đầy sóng nướcTrai tân nhất thời cất bước không xong.Món ăn ngon miệng, lại ấm lòngNgười người ăn thử, lòng lòng sướng vui.*\"Mọi người nghỉ ngơi một đêm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một Lều dựng lên năm người ngủ chung, riêng Lạc Tinh được một Lều riêng.Đang ngồi vận chuyển Lạc Lôi Thuật , thì có tiếng động.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh mở mắt ra nhìn xung quanh.\"-Xoẹt \" là tiếng bước chân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vén cửa lên , thấy người trước mắt là Lạc Mỹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nàng ta đang đi tới đi lui trước lều mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lòng Lạc Tinh đánh thót một cái.Nhìn Lạc Mỹ trên người chỉ khoát một miếng da thú xoay vòng thân thể , kéo từ ngực xuống tới đùi.Cảm giác chỉ cần ghì sợi dây đang buộc ngang hông.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thì cái áo như cái khăn quấn qua người , có thể rớt bất kỳ lúc nào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Do vóc người nàng đầy đặn , làn da nâu đen pha vàng , tôn lên vẻ đẹp rực rỡ tràn đầy sức sống.\"-Đúng là gái một con trông mòn con mắt.\" Lạc tinh thầm nghĩ : \"- Giờ đi ngủ mà còn đến đây , hay do được mình cứu lúc sáng, nên định lấy thân báo đáp\"Nuốt nước miếng , ra vẻ trấn định :\"- Khuya rồi sao tỷ không ngủ.\"Lạc Mỹ quỳ xuống nói .\" Dạ nô tỳ cám ơn Thiếu tộc Trưởng liều mình cứu mạng , mà sợ phá giấc ngủ của Thiếu tộc Trưởng chưa dám vào.\"\"-* A không có gì , cùng chung một tộc mà, giúp đỡ nhau bình thường thôi.Tỷ lớn tuổi hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên cứ xưng là Mỹ tỷ đi , đừng xưng nô tỳ*\"\"-* Dạ Thiếu tộc Trưởng.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ân cứu mạng không thể không trả ,hay là , hay là …*\"\"- Rồi, tới rồi, lấy thân báo đáp rồi\" Lạc Tinh nội tâm nhảy loạn.\"* -Chắc mình sắp thoát kiếp con trai nheo nhẽo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*\" Từ trên nhìn xuống Lạc Mỹ đang quỳ , mà cả sóng ngực muốn lộ ra ngoài.Lạc Tinh nuốt nước miếng lần nữa.\"-* Con tỷ ngủ rồi à , hay mình vào phòng nha , đứng đây sương lạnh lắm ?* \"\"-* Dạ thôi ,Tỷ phải về kẻo tụi nhỏ nó khóc, từ giờ về sau, mạng của tỷ là của thiếu tộc Trưởng.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có cần gì cứ kiu tỷ nha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện khó tỷ không làm được nhưng nướng thịt, giặt đồ, dựng lều, rót nước , đấm lưng, bóp chân, cứ bao cho Tỷ hết.Thôi, Tỷ về à*.\" Nói xong Lạc Mỹ cúi đầu chào rồi đứng về.Bỏ lại một mình Lạc Tinh đang còn suy nghĩ linh tinh…Sao Mỹ tỷ chưa nói tiếp \" hay là, hay là … \",Một đêm không có gì xảy ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sáng sớm mọi người chuẩn bị lên đường hướng đi là giữa thác nước và vách đá.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc nhìn thì gần , mà đi cả ngày cũng chỉ gần đến.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người lại dựng trại và nướng thịt ăn lần nữa, chưa bao giờ Lạc Tinh thèm một chén canh như bây giờ.Ngày tiếp theo, Lạc Tinh cho mọi người ở lại , rồi cùng vài thành viên Trung Đội 1 phân biệt đi đến vách núi trước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một mình Lạc Tinh di chuyển nhanh hơn , nhưng do sợ lạc nên phải có người đi cùng.Băng rừng cả hai canh giờ , thấy phía trước là một vách đá dựng đứng .Bên trái là khoảng rừng già , cây nào cây đó to hai người ôm không xuể.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng bên phải thì lại một khoảng rừng tre.ở dưới có nhiều thú nhỏ như thỏ,sóc, đi lần theo lũ sóc một hồi , Lạc Tinh thấy một khe nước nhỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu làm vị trí dựng trại cũng khá tốt.Vì giữa rừng tre và rừng cổ thụ có một khoảng rộng , đủ cho ba tới bốn trăm người ở.Tuy nhiên Lạc Tinh không quyết định mà đánh dấu lại..rồi cùng nhóm người đi hướng về Thác nước.Một Canh giờ sau cũng đến được Thác nước, ở xa không thấy gì , nhưng lại gần mới biết thác nước hùng vĩ cỡ nào , kéo dài tận trời , có hơn 1000 trượng , Bọt trắng xóa một góc trời.Nhìn xung quanh bên phải là rừng tre.rồi tới nhiều con sông nhỏ do Thác nước kéo xuống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ban đầu dòng suối nhỏ nhưng kéo dài ra ngày càng lớn , như 1 dòng sông lớn ở phía xa .Bên trái dòng sông là rừng cây tạp.Ngạc nhiên là bên Tháp cũng có một khoảng vách đá kéo dài.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sau đó bị ngắt khúc bẻ một góc 120 độ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh ước lượng , nếu ở đây dựng nơi ở thì có thể dựa vào đây làm lối thoát hiểm , không sợ bị dí vào đường cùng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khe đá cũng khá nhỏ có thể dùng cây làm cổng phòng thủ.Mừng rỡ, lạc Tinh và nhóm người ra về, chuẩn bị định cư.Hôm sau mọi người di chuyển tới nơi, ở đây gần rừng tre nên khác mát mẻ.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bắt đầu phân nhóm, Người dùng đá chặc tre, Người tỉa cành.Riêng Lạc Tinh khá đơn giản với sức mạnh vượt Trội , Lạc Tinh nhanh chóng chặc hơn ba trăm cây , đủ làm hai mươi cái lều có sàn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiếp trước Lạc Tinh hay xem youtube cách làm nhà bằng tre , nên chỉ dẫn mọi người làm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhất là cách làm sàn tre cách mặt đất năm tất để tránh rắn và côn trùng..Lạc Tinh chọn trú ngụ rừng tre , vì thêm một điều nữa là có tre có thể đựng nước nấu canh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không phải ăn món nướng khô khan nữa.Dặn mọi người thấy cây tre non thì hái nguyên cây gom lại (măng tre nhưng bộ lạc không biết tên , do chưa từng ăn )Buổi trưa thì mọi người ăn vội chút thịt nướng sẵn rồi vì phải tranh thủ thời gian làm kẻo chiều tối, trong rừng thì rất mau tối.Tầm ba giờ chiều, hơn năm mươi căn nhà tre đã xong.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tất cả nhà cất xếp theo hình chữ U hay đúng hơn là nữa hình chữ nhật , một cạnh dài là mười căn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chia làm ba hàng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai cạnh ngắn cũng ba hàng mỗi bên , mỗi hàng bốn căn nhà , có nhà nhỏ hai người ở được có nhà bốn người.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phía trên lợp bằng da thú, coi như chỗ ở tạm ổn.mọi người mang đồ đạc vào phòng.Phòng Lạc Tinh ở giữa chữ U đó ,bên cạnh là phòng chứa thịt và da thú, kế bên hai phòng lớn nhất là phòng của đám con nít.Giữa các nhà là khoảng trống đã đốt đống lửa to , để dành làm khu sinh hoạt chung , tế tự ,ăn uống ,vui chơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh đang chỉ huy trung đội hai hì hụt cắt hơn trăm ống tre.cắt xong Lạc Tinh cho Trung đội 4 đi ra dòng suối kế con Thác múc nước đem về, xong lại hướng dẫn mọi người bỏ thịt Sói vào, tất cả thịt sói dã được nướng khô khốc nhưng ko sao..Sau đó vừa hướng dẫn vừa bỏ ống tre vào lửa để nấu, chuyện này thì Lạc Mỹ dành nấu nên Lạc Tinh cũng mặc kệ đi vài vòng hướng dẫn những ai chưa biết làm..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">mười phút sau, nước đã sôi , thịt đã mềm ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh bắt đầu lấy ống tre cẩn thận đổ ra một ống tre khác và dùng đôi đũa tre mình làm ra gắp ăn thử.\"- ôi chao thật mềm, thật thơm\"Húp thử một chút nước, vị ngọt, vị thơm của tre tươi hòa với thịt.\"- Ngất ngây ! \" Lâu lắm rồi mới được ăn canh, có cảm giác rơi nước mắt.\"- Tiếc là không có rau xanh \" Lạc Tinh chép miệng , Ông bà ta nói thật không sai Được Voi đòi Hai Bà Tưng..\"-ỰC ..ực ! \"Bỗng nghe tiếng nuốt nước miếng, Lạc Tinh mới ngẩn đầu lên thấy mọi người đang nhìn mình.\"- Mọi người cứ ăn, nhìn làm gì! nhớ là nóng đó \"Ào ào, mạnh ai nấy lấy ống của mình ào ào ăn, vừa ăn vừa xuýt xoa, vừa có tiếng khen ngon,vừa có tiếng la nóng….Lạc Tinh thấy thật hạnh phút vì mang lại bữa ăn ngon cho mọi người, nhất là các cô gái cứ liếc về Lạc Tinh…mười phút sau, hơn trăm ống tre sạch sẽ không còn gì, mọi người mới ôm bụng , nhìn lại mấy mớ thịt nướng còn nguyên.Trời đã tối hẳn, mọi người quay về lều.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh cũng vào lều, gặp Lac Mỹ đang lúi húi trải lớp da thú , Thấy Lạc Tinh vào .\"-* Thiếu tộc trưởng nằn nghỉ đi ,Tỷ bóp chân cho.*\"Lạc Tinh cả ngày mệt nhọc , nên nằm xuồng nhìn Lạc Mỹ ngồi dưới chân bóp cho mình , cảm giác Thật thoải mái.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Mỹ bóp dần lên đùi bàn tay con gái thật mềm mại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh muốn phạm tội quá .Dồn can đảm, Lạc Tinh mở miệng nói :\" Mỹ Tỷ ơi.\"Bỗng xoạt có bước chân nhỏ , quay lại là con của Lạc Mỹ đã đi vào lều từ lúc nào, đứa bé gái này tên là Lạc Na.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Na số cũng khổ , sinh ra không bao lâu thì cha mất.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">một mình Lạc Mỹ nuôi lớn nhưng do thức ăn thiếu thốn nên rất nhỏ con.\"- Mẹ ơi con muốn ngủ, trời tối con sợ quá \"\"-* Thôi tỷ về dỗ ngủ bé đi , ta cũng mệt quá , đi ngủ thôi.*.\" Lạc Tinh thấy vậy cho phép Lạc Mỹ bồng con về\"- Dạ Thiếu tộc trưởng cần gì cứ gọi ! \"\"-* Lại một đêm khó ngủ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*\" thần than thở..Lạc Tinh đành ngồi dậy vận chuyển Lạc Lôi quyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đầu tiên là nén linh lực xoắn lại .Cứ hai sợi nhỏ quấn nhau rồi nhập lại, bốn sợi, tám sợi ,cuối cùng tăng lên mười sáu sợi .Đã tới giới hạn rồi, sau gần nữa tháng Lạc Tinh đã vận dụng khá thành thục nhưng cũng chỉ nâng tới mười sáu sợi linh lực xoắn với nhau theo đồ hình Lạc Lôi Quyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Coi như là tăng ba phần tốc độ.Sáng hôm sau , Lạc Tinh bắt đầu phân công :\"-* Trung đội 2, mười người người chặt tre làm hai vọng gác.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta sẽ chỉ dẫn..*.\"Sau khi tre chặt xong ...Cứ buộc nối ba cây tre , nhập lại thành một trụ cao hai mươi trượng.Lại nhập ba trụ tre đó , được nối với nhau thành hình tam giác, để dựng đứng buột lại từng khoản một thước , để cho chắc và có đường trèo lên.Dự định sẽ làm trước hai vọng gác hai bên.Trung đội 1 thì Lạc Hồng đã cho đi xung quanh , vừa săn thú vừa kiểm tra các mối nguy hiểm tiềm ẩn, như khúc nào có đá lỡ, khúc nào có rắn độc, chỗ nào là Hang Sói, Hang hổ , Báo Heo Rừng ,Gấu….Trung Đội 3 thì toàn phụ nữ , Lạc Tinh cho đi vót tre nhỏ .Mục đích là làm đồ bắt cá, làm đũa, đồ trang trí, làm áo giáp, làm chông bẫy thú...Lạc Tinh nêu ý tưởng kiếp trước mình nhớ , như gỗ tre , nôm tre, bàn tre , ghế tre , ly uống bằng tre, gùi đựng bằng tre ..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nói chung tre tất tần tật...Trung Đội 4 là trẻ nhỏ Lạc Tinh định huấn luyện cho thành thục , một chi chiến sĩ đúng nghĩa , cho học tập võ thuật, các bài nâng cao thể lực , các điều lệnh quân đội,ngay cả chữ viết quốc ngữ A,B,C cho học luôn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trước mắt cho học thuộc trước rồi từ từ giảng giải sau..…Thời gian có hạn , cứ chỉ từ từ khi nào cần hướng dẫn thì Lạc Tinh hướng dẫn.\"-* Phải định hình lại phương hướng phát triển.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện này không gấp được.*\"Sau khi phân công xong , Lạc Tinh bắt đầu đi tìm hiểu sức mạnh của mình.Lúc trước chưa quen với sức bật và lực lượng cơ thể, Lạc Tinh biết mình mạnh hơn Đản Trung kỳ nhiều , nhưng không biết là bao nhiêu.Bắt đầu đi hướng núi đá , gom các hòn đá lại to nhỏ đủ cả.Sau đó dựa vào cân nặng của bản thân , mà làm ra ước lượng trọng lượng từng khối đá.Sau một buổi chuẩn bị.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"- Sân kiểm tra thể lực đã xong ha ha \"Lạc Tinh Thử Lực nâng từ thấp tới cao, một ngàn cân,hai ngàn cân, bốn ngàn cân, vẫn còn chút sức.\"- Có lẽ đạt bốn ngàn hai trăm cân đi \" vì Không còn dư lực nâng lên bốn ngàn rưỡi cân nữa , Lạc Tinh dừng lại.\" - Thử kích hoạt Thần ấn \"xoẹt Vết xăm nổi lên màu đậm hơi có pha ánh tím.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kiểm một hồi sức mạnh của mình không thay đổi vẫn không mang nổi bốn ngàn năm trăm cân .\"- Đúng như cảm giác của ta\"Tiếp theo là đo tốc độ , lấy khoảng cách trong một ngàn trượng chạy tới chạy lui.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thực lực bộc phát vài lần đúng là chênh lệch .vận tốc bộc phát đạt mười ba trượng một hơi thở , ở lúc đầu và đạt trung bình là là mười sáu trượng trong một hơi thở .Sau đó thử bật thần ấn, Vẫn không thay đổi gì.\"-* Gì kỳ, Không lẽ thần ấn của mình chỉ có cho đẹp mắt , không có tác dụng gì Không tăng sức mạnh chẳng tăng nhanh nhẹn.*\" Lạc Tinh uất nghẹn...Vậy mà nói là Thiên ấn..còn tệ hơn địa ấn.Thử phát động Lạc Lôi Thuật chạy thử .\" -* Xoạt* \" cảm giác gió phả vào mặt .một vòng hai vòng.Lạc Tinh nhẩm ra mình chạy nhanh nhất là 13.5 trượng trong một hơi thở , tức là khoảng 225 km/h một cảm giác sung sướng trào dâng,\"-Mình chạy nhanh hơn siêu xe hiện đại rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" Lạc Tinh nhớ ngày xưa chạy thử chiếc xe wave cũng chỉ dám tăng tốc 70 km/h đã hoa mắt...\" Á !!!! .\"Đang lúc hưng phấn , thì nghe tiếng hét .Nhìn lại thấy Lạc Mỹ đang dùng tay bịn miệng.\"*Gì mà la lớn thế Mỹ Tỷ *!\"Chưa biết chuyện gì, nhìn theo Lạc Mỹ chỉ tay, Lạc Tinh cúi xuống thì mát lạnh dưới chân .Cái khố quần do tốc độ nhanh quá, đã rớt lúc nào không hay, Lạc Tinh hay tay bụm lại ngồi xuống,\"-\"-thôi rồi.\"Lạc Mỹ dù sao cũng đã thành thục , nên vài giây đã hết ngượng ngùng, đi tới bỏ ống nước bằng tre xuống cho Lạc Tinh.\"- Thiếu Tộc trưởng, Ngài Sắc Lang quá! \" Sau đó đi thẳng.Lạc Tinh thì chưa kịp phân minh, đã không thấy bóng Lạc Mỹ..\"- Đúng là oan ức.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Sau khi loay hoay nhặt lại quần buộc vào.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh tổng kết lại.Mình không phải Đản Trung Kỳ , mà là Linh Đài Kỳ, nhưng là Linh Đài kỳ yếu nhất, vì không mở thần ấn của Linh đài Kì được.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bù lại cơ thể luôn trong tình trạng Kích hoạt thần ấn của Đản trung kỳ đỉnh phong.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nên Sức mạnh tốc độ bộc phát bằng Linh Đài kỳ Sơ kì lúc bình thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hèn gì vết xăm kéo dài ra cả huyệt Linh Đài sau lưng.Tuy nhiên có điều bất lợi là hình như mình không tăng lên được sức mạnh được nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Theo cảm nhận thì nửa tháng nay sức mạnh Lạc Tinh không hề tăng lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn đúng không thì không biết.Thêm nữa là phát động thần ấn xong , Không có gì tăng lên gồm sức mạnh, tốc độ, bền bĩ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh thử cả khả năng thị giác, thính giác điều không thay đổi.bù lại còn may là Lạc lôi thuật vẫn y nguyên tăng đúng như bình thường.Hiện tại coi như Lạc Tinh là mạnh nhất trong tộc kể cả ông mình rồi, nhưng sức mạnh và tốc độ là một chuyện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khả năng xử lý tình huống và phản xạ là một chuyện nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"- Nếu không tăng được thực lực thì phải tăng kỹ xảo.\" Lạc Tinh suy nghĩ..</span></p>', 0, '2022-08-05 01:23:10', '2022-08-05 01:23:10'),
(118, 77, 'Chương 7: 7: Việt Võ Đạo Quyền Biến Thể', 'chuong-7-7-viet-vo-dao-quyen-bien-the', 7, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*Làm trai cho đáng thân traiVõ công là phải luyện rèn thường xuyên*......Lạc Tinh quay về trại thì thấy hai tháp canh đã làm xong.Số tre cắt ra cũng đủ làm hơn hai mươi cái lợp (có thể gọi là lờ ) lớn nhỏ đủ loại, còn dư một chút có thể kết thành nôm cá.Ăn trưa xong, Lạc Tinh dẫn trung đội 3 ra suối đặt cá.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mặc dù trung đội ba có nhiều người không biết bơi so với trung đội Hai.\"Nhưng hơn ở chỗ trung đội 3 là một đám con gái khi dầm dầm nước có cái để ngắm hơn con trai.\"Cái đó nói vui thôi lý do là muốn phân công rõ ràng.\"Nữ bắt cá hái rau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nam săn thú, chống giặc.\"Trên đường ra bờ suối Lạc Tinh gặp được hơn năm cây măng tre.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lập tức cho người hái liền.Đồng thời chỉ đạo vài người đào giun, côn trùng , bắt bỏ vào ống tre có khoét lỗ sau đó bỏ vào mấy cái hộp tạm gọi là cái lờ để làm mồi dụ cá vào .Nếu ở hiện tại cái lờ là những mãnh tre vót nhọn kết lại thì Lạc Tinh cho tạo cái lờ bằng những cây tre được bện lại bằng dây mây rừng lớn hơn cả vòng tay người lớn với lý do là cá nơi này quá lớn có con cả trăm cân .Dùng cây tre còn không chịu nổi phải cố định lại cái hom lờ cho nhỏ để chỉ có cá nhỏ cỡ tầm 30 cân vào được.Không thì không cái lờ nào chịu nỗi.Những người biết bơi ở lại dùng nôm bắt cá cũng chỉ lựa loại cá nhỏ hơn 5 cân bắt, ai không biết bơi Theo Lạc Tinh về trại .Vừa đi Lạc Tinh vừa hi vọng tìm được vài loại rau quen biết nhưng tới Trại vẫn không được gì , trên đườngLạc Tinh kể về những loại rau , trái cây có thể ăn được cho các cô gái hi vọng 1 ngày có thế bắt gặp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn ai hỏi sao Lạc Tinh biết thì cứ nói mình được Lạc Hoàng báo mộng.Tới nhà (Lạc Tinh gọi đây là căn cứ).Lạc Tinh hướng dẫn một vài cô gái cách gọt măng , ngâm măng , nấu măng trong ống tre trước cho bớt đắng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hi vọng có cá về bỏ vào nấu chung sẽ có một bữa canh ngon lành .\"*Về lều tu luyện Lạc Lôi Thuật thôi *\" Bắt đầu giai đoạn hai là Lạc Lôi Bộ .Trong khẩu quyết nói Lạc Lôi Thuật không phải ý nhanh như tia sét .Mà quý ở chỗ đổi hướng như tia sét .Trong cơn giông tia sét bổ xuống lúc đông lúc tây không có qui tắt khiến ai cũng không đoán trước được.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Lôi Bộ là thứgiúp người thi triển lúc qua trái lúc qua phải đối phương không biết mình đánh hướng nào, phải phòng thủ ở đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đặc biệt mỗi lần chuyển hướng tốc độ lại tăng lên ,lần đầu tăng một phần, chuyển hướng lần hai tăng hai phần ,chuyển lần ba tăng ba phần,có thể do đó là Lạc Lôi Thuật bản thiếu nên tốc độ tăng lên năm phần cộng với năm phần của Lạc Lôi Quyết là ra 10 phần tức gấp đôi tốc độ hiện tại và được xếp vào Nhất Tinh võ Kỹ.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh đã học được ba phần Lạc Lôi Quyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đủ diều kiện học tiếp Lạc Lôi Bộ.Đang chạy nhanh chuyển hướng cũng rất khó, Phải thuộc bộ pháp, đồng thời biết thu phát linh lực đúng lúc .Không thì tốc độ không tăng mà còn giảm lại.\"- Keng \" tiếng gõ báo giờ ăn tới ,mọi người có mặt đầy đủ Lạc Tinh cũng ra ngồi ở bàn tre của mình ra hiệu mọi người ăn và mình thì cũng bắt đầu ăn.Canh măng và cá đặc biệt ngon, mọi người ai cũng ăn ngon lành như sơn hào hải vị , cảm giác lần đầu thì cái gì cũng ngon miệng tuy vừa ăn hơi nhẫn nhẫn nhưng càng ăn sẽ càng thấy ngon, vị ngọt của cá suối hòa huyện vào tạo hương thơm đặt biệt của núi rừng.Trời Tối rất nhanh.\"-Mọi người tập hợp lại trước sân \" Lạc Tinh ra lệnh .Sau khi đã đông đủ Lạc Tinh bắt đầu làm ra vẻ huyền bí.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh cứ nói đây Là Lạc Hoàng báo mộng truyền cho và bắt đầu truyền bá một trong bốn bài quyền mình còn nhớ rõ là Việt Võ Đạo Quyền cứ bắt học thuộc rồi tính sau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe nói vậy ai cũng nghiêm túc mà thực hiện , không một tiếng động mọi người chăm chú lắng nghe*- Mọi người hãy ghi nhớ Khẩu Quyết Việt Võ Đạo Quyền :Trung bình, đinh tấn đão quyền phongCung thủ hoành phi trảo mã công.Bát cước, phương dực, quyền, đao phạtXà tự song long trảo biến thôngBát bộ cương đao đã ngũ hànhÐăng sơn phi cước tảo thiên thanhSong thủ hiệp đồng xuyên bích chỉThập tự trường chinh độc cước hành..\"*Lập đi lập lại chừng hai mươi lần,mỗi lần hai câu thì có hai người thuộc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bất ngờ là trong đó đều hai đứa trẻ bảy tám tuổi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hình như con nít đặt biệt nhớ lâu hơn người lớn.\"- Mọi người yên lặng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bây giờ đã có hai người học thuộc.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tiếp theo mọi người cố gắng ghi nhớ .Ai quên chỗ nào thì theo thứ tự lên hỏi hai nhóc này nha.\"\"-Ta hạn độ một ngày phải thuộc khẩu quyết .Ai không thuộc miễn ăn thức ăn ngày mai .Cái này là ý chỉ của Lạc Hoàng ,mọi người nên cố gắng .\"\"*- Dạ ! Thiếu tộc trưởng ! *\"Lạc Tinh cho mọi người theo hai đứa trẻ đó học rồi mặt kệ đi vào , Hai đứa nhóc bỗng nhiên thành thầy giáo của hơn 100 người .cảm giác thật hưng phấn và tự hào, lên giọng ngay.Nè Thúc sai rồi câu thứ 4 là : \" Xà tự song long trảo biến thông *\" chứ làm gì có \"Xà bự ăn xong khỏi nhổ lông.*\"Về phần mình thì Lạc Tinh về phòng tự diễn luyện lại bài quyền cũng như nhớ lại cách giải thích ý nghĩa khẩu quyết.Lạc Tinh xưa đâu bằng nay , kết hợp hai linh hồn nên mạnh gấp đôi người thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại thêm được Thần ấn gia tăng đến Linh Đài kì nên Tinh thần lực đã gấp tám lần người thường rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Theo một khoảng thời gian Lạc Tinh đã thích ứng với sự tăng trưởng linh thức đó nếu về thế giới hiện tại chắc cũng là một nhất đẳng tông sư.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không thua kém ai.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhờ vậy Lạc Tinh có thể phát huy tối đa cái hay của môn võ mang lại cũng như loại bỏ điểm yếu và những thế võ không phù hợp thời kì hiện tại.Thử nghiệm , loại bỏ , Lạc Tinh đắm chìm trong thế giới của mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Giúp cho \"Việt Võ đạo quyền\" có một bước tiến dài dần chở thành một môn võ thực chiến lợi hại.Lúc choàng tỉnh dậy Lạc Tinh thấy trời đã sáng hẳn còn mình thì ngủ quên dưới nền nhà, bên cạnh là cái chăn lông thú trên cơ Thể.\"* Chắc là Lạc Mỹ vào phòng đắp cho mình.*\"Lững thững ra ngoài thì mọi người đã làm việc rồi không cần Lạc Tinh chỉ đạo nữa, người chặt tre, người vót tre, người buộc tre thành hình tam giác sau cho một loạt cây tre nhô phần nhọn ra trước tua tủa , vừa làm hàng rào chống thú dữ cũng như kẻ thù đi tới.Người thì đi Săn thú , người bắt cá.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi việc khá ổn thỏa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đều được Lạc Hồng và Lạc Hương quán xuyếnKiếp trước là sinh viên nên Lạc Tinh quen ngủ nướng rồi.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">trong khi bộ lạc ai cũng dậy sớm.Lạc Tinh cho gọi những người làm trinh sát hôm qua lại và hỏi ý từng vị trí để làm một bản đồ vẽ tạm bằng than tre trên tấm da thú.hỏi tới đâu ghi chú tới đó.Tới gần hết buổi chiều thì cũng xong bản đồ sơ lượt bán kính ba mươi dặm xung quanh .Gồm chỗ nào có sông ,có suối,có đá,có cây gì,chỗ nào nghi có thú dữ,chỗ nào có thú săn được…Lạc Tinh nghe và ghi chú xong phát hiện mọi người tò mò nhìn ko ai hiểu mới nhớ không ai biết chữ viết .\" Phải gấp làm một khóa xóa mù chữ nhanh\" .Hiện Tại người trong bộ lạc chủ yếu biết hình vẽ chứ còn chữ viết đặt biệt hệ chữ La Tinh thì không biết gì.Lạc Tinh lập tức dùng một sấp da thú cắt nhỏ , ghi lại từng chữ cái , suy nghĩ cách hướng dẫn dạy học.Mày mò lại kiến thức, hiểu thì dễ nhưng để dạy người khác hiểu cũng không đơn giản phảivừa có hình ảnh vừa có ví dụ cụ thể.Lạc Tinh nhớ thời trẻ trâu của mình học ngêu ngao : \"O thì như quả trứng gà ,ô thì đội mũ ,ơ thì mang râu \"Nhưng bây giờ dạy thì phải đổi tại vì làm gì có cái mũ nào mà làm ví dụ..Ngay cả con gà Lạc Tinh cũng đang cho người tìm bắt về nuôi nhưng chưa có nữa là có trứng..Nếu ai hỏi Lạc Tinh con gà có trước hay trứng gà có trước Lạc Tinh có thể hùng hồ tuyên bố con gà có trước..nếu không có con gà làm quái gì có trứng gà...Còn hỏi tại sao có con gà thì đương nhiên do Lạc Tinh đặt tên nên có con gà..Chứ nếu ban đầu con đó đặt là con chim thì trứng của nó đã là trứng chim rồi..Chiều đến mọi người lại tiếp tục canh cá và thịt sói nướng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hôm nay có săn được Nai và chuột nhưng phải tranh thủ ăn thịt cũ trước , nếu để lâu sợ thịt cũ hư mất.Buổi Tối , mọi người đã có mặt trên sân tập.Lạc Tinh đi lên và nói .\"*- Giờ ta sẽ kiểm tra lại lần cuối khẩu quyết.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai không thuộc phải hít đất một trăm cái còn hít đất là gì thì có người vi phạm ta sẽ nói *\"\" - Chia theo tiểu đội ra .Tiểu dội này kiểm tra tiểu đội kia .\"\"*Nhớ là không giúp nhau ăn gian phát hiện thì cả hai đội bị phạt hít đất Ngàn cái *\"Sau khi mọi người cùng đọc một lần không phát hiện ai đọc chậm hơn.Lạc Tinh nói \" Việt Võ Đạo quyền hay còn gọi là Bát Quái Quyền..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">là một môn võ kỹ của Lạc Hoàng truyền cho chúng ta .Tuy chỉ có tám thức nhưng chứa đựng được hầu như tất cả tinh túy của võ học.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người chỉ cần cố gắng luyện tập ta tin chắc Việt tộc chúng ta sẽ ngày càng mạnh mẽ hơn.\"Im lặng một chút .\" *Thức thứ nhất : -Trung bình, đinh tấn đão quyền phong- là thức đơn gián nhất.Nó hầu như không có tác dụng thực tiễn ,nhưng ngược lại nó lại là khởi thức ,cái gọi là khởi thức ,chính là khởi nguồn của tinh thần, tinh thần tốt ,thì mới có sức khỏe tốt, sự tập trung tốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tập trung tốt thì mới đưa ra phương án xử lý tốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi triển khai thức thứ nhất được ví như lúc con Hổ chuẩn bị vồ mồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Che dấu hết nhược điểm, chăm chú nhìn đối phương , một khi đối phương lộ ra điểm yếu thì sẽ lập tức dùng thế sấm sét kết liễu đối phương.Thức thứ hai tới thứ tám là tùy theo tình huống mà đưa ra phán định.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quan trọng là nhanh,chuẩn xác ,mạnh mẽ đã ra đòn là phải có sự dứt khoát, đòn nào ra đòn đó, thế nào ra thế đó.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi tập luyện quen ta sẽ thấy cơ thể sinh ra một nguồn lực, tâm động thì lực động , lực động thì có thể khai sơn đoạn thủy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tuy không b.iến th.ái như linh lực của Thần ấn.Nhưng nó sẽ khiến thực lực ta tăng cao.Ta Hứa với mọi người chỉ cần mọi người chăm chỉ, thể hiện được năng lực , ta sẽ tìm cách đưa mọi người trở thành Nhân ấn Đản Trung Kỳ*.\"Nghe Lạc Tinh hứa mọi người đều hào hứng lên , Có Nhân ấn tức là có sức mạnh ,có tuổi thọ dài hơn người khác ai lại không h.am muốn ,150 năm tuổi thọ đó.Đây chính là Lạc Tinh mong muốn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có tinh thần thì chuyện gì cũng có thể thành công trước hết phải vẽ ra viễn cảnh tốt đẹp để cổ vũ tinh thần sĩ khí mọi người.Lạc Tinh bắt đầu đánh lại từng thức Việt võ đạo quyền, mọi người tập theo.Cứ vậy mười ngày trôi qua.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mỗi tối đến , Lạc Tinh tập trước và mọi người đánh theo sau .Từng động tác một.Bài quyền mọi người ai cũng đánh được kể cả đứa bé năm tuổi do không có chiêu thức hoa mỹ chỉ là tinh giản tới mức cùng cực chú trọng độ ổn định tính chính xác và thực tiễn.Lạc Tinh qui định mỗi người hằng ngày phải luyện tập ít nhất nữa canh giờ , buổi tối có ba khung giờ cho tập luyệncòn giờ giấc có thể thay đổi ai thích tập giờ nào thì vào tập giờ đó,và có một người phụ trách kiểm tra.Thức ăn thì cũng khá ổn định không còn bữa nay ăn rồi không biết ngày mai còn gì ăn không.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có một tin vui là đoàn đi săn bắt được bốn con dê còn sốngTheo lời Lạc Tinh chú ý bắt sống loài dê này để làm giống, trong đó có một đực , hai con Cái, và một con dê con.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh hi vọng có thể từ từ làm đàn dê nhân rộng ra.Vừa có sữa dê vừa có thịt dê ăn.Do hôm trước ra bờ suối thấy đàn dê đi uống nước trong béo ú hơn cả lợn nhà mà cũng khá hiền, Loài này khá nhanh, nên về nói cho mọi người chú ý cố gắng bắt sống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kết quả vừa nói thì hôm sao đã bắt được , chứng tỏ là dê ở nơi này rất nhiều ,có cơ hội gầy lại thành một đàn dê lớn.Một tuần nữa trôi qua, trong tộc cuộc sống đã ổn định hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhờ tranh thủ đi xung quanh mà Lạc Tinh phát hiện được loại rau đầu tiên ăn được..lá lốt .có thứ này ăn kèm thịt nướng cũng bớt ngán Sau đó một ngày là phát hiện củ Mài, lá chua , vài loại trái cây dại cứ ăn được là tốt .Trong tuần này Lạc Tinh cũng phân bổ lại hệ thống Việt tộc của mình.Trung đội 1 tách ra làm năm Tiểu đội mỗi Tiểu đội có một đội Trưởng cho dễ quản lý.quan trọng có thể phân công rõ ràng hơn như đội một phụ trách săn thú bằng giáo, đội hai phụ trách làm bẫy thú, đội ba phụ trách liên lạc, cảnh giới…..Trung đội 2 cũng làm năm tiểu đội .Trung đội ba toàn nữ nên chỉ tách bốn tiểu đội ,gồm đội một phụ trách bắt cá đội hai phụ trách hái lượm, bẻ măng,hái trái cây đội ba phụ trách nấu nướng, chăm sóc người bệnh,Trung đội 4 là những phụ nữ ưu tú nhất có thể tập luyện võ thuật làm lực lượng bảo vệ cho ba đội còn lại nên không phân đội.Trung Đội 5 tách hai tiểu đội chủ yếu luyện tập phục vụ tương lai.Mọi việc Lạc Tinh chỉ làm việc với tiểu đội trưởng , còn lại trong đội tự phân công công việc ..</span></p>', 0, '2022-08-05 01:25:59', '2022-08-05 01:25:59');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(119, 77, 'Chương 8: 8: Lạc Tinh Chiến Mãnh Xà', 'chuong-8-8-lac-tinh-chien-manh-xa', 8, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*Mãng xà muốn nuốt trẻ thơLạc Tinh quyết giết không chờ lượt sau.Hang xâu bí ẩn nhiệm màu,Trần Quân di chúc kế thừa ngàn năm..*..............Thời gian lại trôi qua, hơn hai tháng Lạc Tinh tới Hùng Thiên đại lục.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi Người ai cũng tin tưởng Lạc Tinh , họ xem như Tộc Trưởng dù Lạc Tinh mới mười sáu tuổi.Ít ra hai tháng chưa có ai phải chết đói hay bị thú dữ làm thịt.Thời gian trước trong bộ lạc mọi người đã quen với việc vài hôm là có người mất.Hiện tại thì đỡ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phần vì nhóm theo lạc Tinh toàn trai trẻ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phần thì Lạc Hồng rất giỏi trong việc nhìn dấu chân thú.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu có gặp thú mạnh thì cho cả đoàn rút lui.hoặc cho người báo Lạc Tinh cùng nhau xử lý nếu ở gần đó.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nếu Lạc Tinh không có gần đó thì sẽ chọn từ bỏ.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh cũng có nói , con người là quan trọng nhất , tuyệt không vì thức ăn mà hi sinh mạng sống.Do Lạc Tinh phát minh ra món canh cá với măng, rau rừng nên dù ít thịt ăn thì cũng có cái no bụng nên không cần liều chết liều sống như ngày trước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thêm nữa nơi này khá thuận lợi , thức ăn từ suối, rừng tre...!củ mài , nai hoẵng , sóc chuột, có thể nói là thiên đường...Còn đàn dê thì cứ vài ngày lại bắt dược dê mới ,đàn dê ngày càng đông, sữa dê ngày càng có nhiều, đời sống mọi người cũng có nhiều lựa chọn thức ăn nước uống hơn.Qua hai tháng nay Lạc Tinh không ngày nào không luyện tập Lạc Lôi Quyết ít nhất một canh giờ, nhưng vẫn dậm chân ở mười sáu vòng xoắn linh lực.Riêng Lạc Lôi Bộ , Lạc Tinh đã có tiến bộ lớn đã đổi hướng được hai lần đồng nghĩa với tốc độ ổn định ở hai mươi mốt trượng và khi bộc phát tối đa là hai mươi lăm trượng một hơi thở..do càng về sau càng khó tăng.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh cũng khá thỏa mãn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quyết định thư giãn\"Tự thưởng cho mình bằng cách đi ngắm các chị em tắm suối.Phải nói là khảo sát tình hình đánh bắt cá.\"Hiện tại đánh bắt cá không như thời kì trước Khi Lạc Tinh đến chủ yếu bắt bằng tay hoặc phóng lao, Do Khi xưa nơi bộ lạc ở không có nhiều tre nên không ai nghĩ tới chuyện làm dụng cụ đánh bắt.bây giờ đã có nhiều cách dùng nôm, đặt lờ , kéo cá bằng lưới đan bằng dây mây sợ to hơn ngón tay .Do ở Hùng Thiên đại lục này cá đặc biệt to.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên tuy mắt lưới rất to nhưng vẫn dể dàng đánh bắt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ cần mỗi bên hai người cầm lưới căn ngang con suối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phía trên cho vài phụ nữ cầm đá ném xuống cho đàn cá di chuyển xuống sao đó kéo lưới lên.Nói lưới cho oách chứ thật ra nó cong vút như một mặt võng ...!khi cá vào rùi thì lật lại không cho nó thoát ra sau đó Có một đội cầm lao đâm vào những con to vì sợ nó quẫy rách lưới..sau đó tìm cách kéo lên bờ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh cũng cho người Đan một cái khung tre to hình vuông ở trên có nắp để thả cá sống vào đó rộng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khi nào cá nhiều thì bỏ vào , chỉ lấy cá đủ ăn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khi nào đánh bắt không đủ ăn thì lại lấy số cá đó bù vào tránh tình trạng có hôm cá ăn không đủ, hôm thì cá nhiều dư thừa..</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">.mà cá chế biến rồi để không được lâu, cho dù lâu ăn cũng không ngon nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì chỉ có một cách nướng và xấy xong bỏ đó.Cá vừa khô vừa mất di chất ngọt vốn có...Vừa lững thững bước ra bờ suối để thư giản đầu óc bỗng nghe tiếng hét thất thanh.Lạc Tinh dùng hết tốc độ vọt đi ra, thì thấy một con mãng xà đầu to cở thùng nước hai mươi lít đang bò gần đám trẻ con đang học bơi.\"- Cứu con tôi với.\" Lạc Tinh chạy lại vừa kịp lúc con mãng xà quấn ngang hông của một đứa bé .Miệng Há rộng định đớp tớiKhông có mang vũ khí khác mà Lạc Tinh vội cầm con dao làm bằng răng nanh con sói Chúa lúc trước .Tính luôn cán chỉ dài hơn một gang tay,\"- Lac Lôi Quyết !\"Như mũi tên rời cung Lạc Tinh lao nhanh tới đâm vào mắt nó.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con mãng xà trúng đòn bất ngờ tức giận buông khúc đuôi đang quấn đứa trẻ ra rồi quật tới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh nhanh tay đỡ lấy .Định chụp quăng đi nhưng đuôi con mãng xà kịp quấn lấy tay Lạc Tinh quăng không được đành giựt mạnh cho con Mãng xà bay về phía mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sau đó choàng tay ôm cổ con mãng xà.Con Mãng xà cũng không vừa dùng toàn thân quấn lấy Lạc Tinh , tuy nhiên nó lớn và dài nên cũng có cái bất tiện là khi bị người khác quấn cổ thỉ không dùng thân người xiết chặt được chỉ có quấn nhẹ một vòng và xoay thân người đè ép Lạc Tinh lên vách đá và dòng suối, hai bên vật nhau một lúc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhờ thân thể đã thay đổi khi thần ấn nên Lạc Tinh có thân thể Dẽo dai và mạnh mẽ Không sợ con mãng xà quấn lấy mà chỉ cần cố gắng né tránh vết cắn của mãng xà , vì không biết có độc gì không nhưng mùi tanh từ miệng nó phát ra nồng nặc , Tay Lạc Tinh thì đâm liên tục con dao vào mình nó.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">vết thương tuy không xâu, nhưng máu chảy đỏ cả khúc sông ..Một lúc sau con mãng xà thấm mệt vì mất máu, biết không làm gì được Lạc Tinh nên buông ra bò ngược theo dòng suối tìm đường chạy.Lạc Tinh thấy nó một bên mắt máu chảy đỏ ,sắp không thấy đường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mình nó thì cũng bị mình đâm nhiều nhát tiếc là con dao hơi nhỏ chỉ đâm xâu chừng hơn nữa tất không đâm tới nội tạng nó.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh nghĩ mình nên tìm vũ khí khác đài hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không thì sau này gặp yêu thú lớn rất bất lợi.\"Giờ ko giết nó thì có lẽ khi nó lành lại sẽ trả thù khiến không ai bắt cá được ! \" Mình thì không thể đi theo canh hoài.\" Tranh thủ nó bệnh cho nó một đao mới hợp lý !\" .Nghĩ là làm Lạc Tinh men theo dòng suối truy theo vết máu đi tới con Thác, ở trên bờ Lạc Tinh tin mình nhanh hơn nhưng bơi xuống suối đặc biệt bơi ngược dòng nước Lạc Tinh có phần yếu thế...đi một lúc đã tới chân thác.Thấy dòng Thác đang chảy xuống ầm vang, nước đánh vào vách núi tung ra bọt trắng xóa như cảnh tiên , nhưng con Mãng Xà mất tích không thấy.Nhìn tới nhìn lui, \"- Ohm bệ đá bên trái có khe nhỏ, còn vương một tí máu,thử xem \" Lạc Tinh liền trèo lên nhìn vào .\"Không ngờ trong thác nước lại có hang động .Sao lần trước ta không phát hiện ra\" .Chần chờ một chút Lạc Tinh vận sức dùng tay bẻ một cây tre thọt vào hang trước ,khi không thấy động tĩnh liền chui vào .Phía trong hang cũng không ẩm ước lắm , càng đi vào xâu càng khô, động càng tối.May mà Lạc Tinh coi như chuẩn Linh Đài Kì nên sau một phút nheo mắt thì đã thấy mờ mờ , đi gần cuối hang thấy con mãng xà đen thui đang nằm .Sẵn cây tre Lạc Tinh đập liên tục vào đầu nó .Trong hang nhỏ không có chỗ tránh,Con mãng xà cũng mất máu quá nhiều ,nên nó quần quại chịu trận , chỉ cần nó vừa lao lên là Lạc Tinh lại nhảy lui lại rùi lại một trận mưa tre tới tấp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nó chạy đi thì lạc Tinh lại tiến tới tay đập không ngừng nghỉ..Cây tre đã giập nát rồi,nhưng vô tình lại sắc bén thêm ,mỗi cú quật là con mãng xà lại mất một miếng da thịt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chưa đầy năm phút con mãng xà đã là một xác chết.Lạc Tinh cũng không còn sức nữa nên nằm vật xuống.\"-May quá có lẽ nó đi theo ông bà rồi ! \"Nghỉ ngơi một chút rồi mới lần mò cuối hang xem coi có thông tới đâu không.chỉ thấy ở xâu trong hang hơi có ánh sáng, đi vào thấy có một bộ xương người đang ngồi .Nơi phát sáng là giữa bụng người đó.\"*-gặp Ma rồi *\" Lạc Tinh hoảng sợ chân tay bủn rủn muốn rút lui , bỗng nhiên đóm sáng trong bụng bay ra, vờn xung quanh bay tới Lạc tinh, lạc Tinh nín thở nhớ lại lời kể về Lửa ma trơi hay xuất hiện ở nghĩa địa bằng đất ngày xưa , bỗng nhiên đóm sáng bay thẳng vào vào đầu.\" Thôi rồi hay là ta bị đoạt xá ! \"Lạc Tinh ngã vật xuống ngất đi , sau đó như một giấc mơ có muôn vàn kí ức truyền vào..Vài canh giờ sau Tỉnh Lại ,lại tốn một lúc sau thì Lạc Tinh hoàn hồn mừng rỡ đi lại Bộ xương quì xuống.Đa tạ sư phụ ban ân truyền đạo.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con hứa sẽ thực hiện lời hứa !Thì ra đây là một Bán Tiên biệt hiệu là Trần Quân .biết mình sắp chết nên dùng linh lực cuối cùng phong ấn một số tri thức võ đạo của mình để cho người hữu duyên.Là dùng tinh thần truyền nên Lạc Tinh cũng chưa kiểm tra hết mình được gì chỉ biết có diễn giải đầy đủ về Thiên ấn và cách tăng lên tầng thứ cao hơn, có thể phi thiên độn địa ,có thể giúp người khác mở địa ấn và nhiều kinh nghiệm quí giá khác, nhưng do tinh thần lực có hạn Lạc Tinh chỉ mới tiếp thu một phần nhỏ , chờ khi mạnh lên sẽ từ từ hấp thu tri thức của Trần Quân thêm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quan trọng nhất là Có Võ Kỹ thành danh của Trần Quân đạt võ kỹ Lục Tinh ,Vạn Tinh Biến .Tiếc là đạt tới cảnh gới cao hơn mới có thể học được.Nhưng không phải không có ràng buộc trong quá trình truyền thừa Lạc Tinh đã đồng ý nhận Trần Quân làm sư phụ cùng với hứa khi thực lực đã tăng cao thì sẽ tìm tới và bảo hộ Bộ Lạc của Trần Quân 300 năm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên Lạc Tinh đã đồng ý, dù sao khi mình có thực lực mới thực hiện lời hứa , nếu mình sống thọ hơn 300 năm thì sau lại sợ lời hứa, còn không có thực lực ,không sống lâu được vậy thì ko quan tâm lời hứa làm gì , cũng xem như không mất mát gì....thử tìm xem có linh khí nhẫn trữ vật gì không kết quả không có gì,Đào một hố nhỏ trôn đi di thể của Trần Quân .\"Thôi về thôi không thôi cả bộ lạc loạn lên \"Lạc Tinh gát lại mọi việc định đi về thì nhớ xác con mãng Xà ,sau khi khệ nệ vác xác con mãng xà đi ra khỏi hang rồi đi một mạch về căn cứ.Đi ra ngoài trời đang buổi trưa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc trở về mới biết mình đã đi gần một ngày đêm, Cả bộ lạc đã chia nhau tìm nhưng không thấy ,ai cũng lo lắng nên giờ thấy Lạc Tinh về lại mang theo xác mãng xà dài gần mười trượng ai cũng hoan hô và sùng bái mãnh Liệt.Nhất là Lạc Mỹ ,thấy Lạc Tinh đi lâu chưa về thì khóc tức tưởi , ai dỗ cũng không nín .Lạc Tinh cho người dùng dao nanh sói mài nhọn, cắt da rắn ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Con sói chúa lúc trước giết được hai nanh khá dài và nhọn .Bỏ thì tiếc .Với sức lực của Lạc Tinh việc mài bén nanh sói tuy tốn thời gian nhưng kết quả khá tốt .Được hai con dao bén và sắc hơn so với dao mài bằng đá, nhưng khá ngắn Lạc Tinh giữ một con , còn một cho bộ phận hậu cần làm thịt thú .Do con mãng xà quá lớn,thịt thì dai, mười người miệt mài lóc ra cả buổi chiều mới xong .Chiều hôm đó mọi người có món canh rắn hầm ăn tuy nhiên chỉ hết một phần con mãng xà.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">phần còn lại xấy khô.Là phương pháp Lạc Tinh dựa Theo Đồng bào Tây Bắc dùng tre đan treo trên cao,xếp thịt lên và dùng hơi nóng của khói tre xấy cho khô để có thể để lâu cả tháng,thay cho cách trực tiếp nướng trên lửa của bộ lạc,chỉ để ba ngày là ăn cứ thum thủm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Riêng da mãng xà lạc Tinh hướng dẫn người làm cho mình hai bộ áo như áo thun cổ tròn, hai bên hông thì may lại, xong đâm hàng trăm lỗ tròn nhỏ lên cho mát, sẵn tiện may quần cộc tới đầu gối theo thiết kế đơn giản nhất mình biết để tránh chạy nhanh quá sút cả quần.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh hết sức biết ơn con mãng xà này ,vừa mang thức ăn ,áo mặt vừa có một viên ngọc mãng xà màu xanh nước biển là viên lấy trong đầu rắn, từ lúc nhặt được viên ngọc đỏ từ con báo Lạc Tinh đều dặn mọi người kiểm tra kỹ các con thú xem có gì trong đầu không giữ lại cho mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng mãi tới nay mới có được một viên Ngọc nữa, quan trọng nhất nhờ con mãng xà này Lạc Tinh mới phát hiện được truyền Thừa của Trần Quân.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Có được truyền thừa như có một sư phụ kế bên , Không còn phải mò mẫn không biết gì về hệ thống Thần ấn nữa.Bỏ lại mọi người quay quần bên đống lửa, Lạc Tinh vào Lều thử tiêu thụ một phần trí nhớ .Hai điều quan trọng nhất của truyền thừa Lạc Tinh quan tâm Là tăng thực lực mình lên, thêm nữa là tăng thực lực người xung quanh mình.Người xung quanh muốn tăng lên thực lực thì phải dùng Nhân ấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vẽ nhân ấn phải dùng máu yêu thú hoặc Yêu Ngọc sinh ra trong đầu nó.Nếu dùng máu của các loài hung thú cần phải là máu tươi của con thú lấy ra phải dùng ngay, hoặc lưu trữ trong bình chứa đặt biệt nhưng bộ lạc làm gì có đồ chứa quí hiếm đó.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng dùng máu vẽ Nhân ấn có khuyết điểm là có xác xuất thất bại cao , cộng thêm khá cuồng bạo đòi người được xăm phải mạnh mẽ về ý trí lẫn tinh thần.Một cách khác là dùng Yêu Ngọc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Yêu Ngọc là tinh hoa tích lũy cả đời của yêu thú, có thể giúp một người dễ dàng đạt được Nhân ấn.Và Nhân ấn do Yêu Ngọc mang lại có thần kỹ mạnh mẽ hơn Nhân ấn do dùng máu của Yêu thú nhiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng một con vật có thể lấy máu nhiều lần tạo ra Nhân ấn...Tùy theo mức độ chịu đựng của nó có thể tạo ra bốn năm người ..nhưng Yêu ngọc chỉ đủ tạo một người...Cái nào cũng có mặt lợi..</span></p>', 0, '2022-08-05 01:26:30', '2022-08-05 01:26:30'),
(120, 77, 'Chương 9: 9: Lạc Mỹ - Nhân Xà Ấn', 'chuong-9-9-lac-my-nhan-xa-an', 9, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*Mỹ nhân xinh đẹp động lòng.Xà Tinh Nhân ấn mặt hồng càng xinh.*....Cầm viên Ngọc Mãng Xà trong tay lên Lạc tinh suy nghĩ nếu mình thực sự đạt chuẩn cấp Linh Đài Kì thì đã hoàn toàn có thể vẽ ra Nhân ấn cho người trong Bộ Lạc rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đang suy nghĩ thì Lạc Mỹ vào lều , Lạc Tinh vừa xoay lại thì Lạc Mỹ đã quỳ xuống ôm chân Lạc Tinh khóc.\"-Nô tỳ cảm ơn thiếu tộc trưởng cứu giúp con gái nô tỳ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đời này kiếp này nô tỳ nguyện làm trâu ngựa phục vụ thiếu tộc Trưởng.\" Thì ra đứa bé Lạc Tinh cứu là Lạc Na lúc đó gấp gáp nên Lạc Tinh cũng không biết là ai.Lạc Tinh bối rối vì Lạc Mỹ ôm chân mình chặc quá nhất là hai quả đào của Lạc mỹ cứ chà sát vào chân.\"-Tỷ đứng dậy đi, ta chịu không nổi…\"-\"Tỷ không biết lấy gì đền ơn thiếu tộc trưởng..\"Lạc Mỹ xúc động quá nên cứ quỳ không chịu đứng chỉ cứ lặp lại mỗi câu.Lạc Tinh muốn choàng tay kéo dậy ,một người ghị lên một người kéo xuống \"- Xoạt \" Dây buộc thắt lưng của Lạc Mỹ xút ra cả thân hình nõn nà hiện ra trước mắt .\"- Ực \" Lạc Tinh cả người xúc động mãnh liệt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bàn tay sắp không còn nghe lời mình rồi đột nhiên viên Ngọc Mãng Xà đang được Lạc Tinh nắm chặt sáng lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một hương thơm tỏa ra ngào ngạt từ viên ngọc.Lạc Tinh như chìm vào mộng mị , Hai tay không còn tự chủ , ôm chặt Lạc Mỹ ,bắt đầu du tẩu khắp đèo cao cao vực thẳm, Lạc Mỹ cũng như ngây dại ôm chặc lưng Lạc Tinh ,vừa mở miệng rên khẽ thì đã bị đôi môi Lạc Tinh tìm đến, hai người quấn vào nhau một đoàn, một đêm mưa gió trong phòng...( tỉnh lược ba trăm chữ).Sáng hôm sau, tỉnh dậy thấy bên cạnh mình không có ai, Lạc Tinh tưởng là mơ nhưng nhìn thấy áo quần mình không có .Chỉ choàng một lớp chăn lông thú thì biết có lẽ là sự thật không phải là giấc mộng xuân , Lạc Tinh lấy làm lạ.Dẫu biết bản thân mình sắc tâm có thừa , nhưng đó giờ đâu có Sắc đảm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lại Thấy viên ngọc mãng xà nằm lăn lóc bên cạnh .Lục Tìm trong ký ức Truyền thừa ,tìm thấy hình như có vài loài mãng xà có Ngọc chứa chất độc khi tiếp xúc môi trường đặc biệt như máu hoặc dung dịch nào đó trừ nước sẽ bùng phát ra .Trong trường hợp này chắc trong lúc Lạc Tinh cầm nó , đúng lúc bị k*ch th*ch nhiều , mồ hôi tay chảy ra nhiều thấm vào viên ngọc làm kích hoạt chất độc.May mắn đây chỉ là loại dâm độc chỉ khiến người ta điên cuồng làm theo d.ục vọng ..chuyện này có thể tự giải thích vậy thôi..chứ có phải viên ngọc hay chính bản Thân Lạc Tinh làm bậy rồi đổi thừa hoàn cảnh thì không biết .\"*Cộc, cộc ! *\" cửa lều mở ra Lạc Tinh quay lại thì thấy là Lạc Mỹ đang mang vào bữa sáng là một chúc sữa dê và nai nướng.Lạc Tinh giật mình chùm chăn lại vì chưa mặt gì..ấp úng nói .\"-Chuyện tối hôm qua.Thật xin lỗi.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"\"- Nô tỳ đã nói là người của thiếu Tộc trưởng rồi, làm sao mà phải xin lỗi.Ngài mau mặt đồ đi rồi ăn sáng..\"Lạc Mỹ đứng một bên cười nói,nhưng khuôn mặt cũng phơn phớt hồng.nói khẽ :\"*- Nhưng ngài về sau có thể nhẹ chút không,Nô tỳ nô tỳ sợ chịu không nỗi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*\"\" *Còn có lần sau nữa à.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*\" Tâm hồn Lạc Tinh lại có con Nai chạy Loạn.\"E hèm Ta nói tỷ bao nhiêu lần rùi cứ xưng hô là tỷ đi.Sau này Tỷ vào phòng cũng không cần gõ cửa.Nhưng nếu thấy ta đang luyện công gì thì cứ yên lặng là được.\"\"Dạ Thiếu tộc Trưởng…\"\"Còn nữa , ta sẽ nói với Lạc Hương cho Tỷ và Lạc Na có đãi ngộ cao nhất.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi việc Tỷ không cần tham gia nhiều.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chỉ cần chú ý chăm sóc lũ trẻ khi rảnh rỗi là được.\"Lạc Mỹ sau khi quét lại phòng, xếp lại chăn , chờ Lạc Tinh ăn sáng xong , thì xin ra ngoài vì có cả đám trẻ con cần chăm sóc.Lạc Tinh ở lại ăn sáng rồi ngồi lại lục tìm cách chế tạo Nhân Ấn sau khi hiểu rõ Lạc Tinh biến cây trượng Lạc Hoàng Lúc Trước ông nội trao cho, đi ra vách đá bắt đầu vẽ lên .tập vẽ từ sáng tới chiều thì cũng đã ra hình thù rõ ràng là con mãng xà hôm trước Lạc Tinh giết.Đúng là tinh thần lực gấp tám người bình thường có khác.Trở về kiếp trước có lẽ đi làm họa sĩ đường phố cũng không chết đói.Lạc Tinh đi về thấy mọi người chuẩn bị cơm chiều thì vội Tìm kéo Lạc Mỹ vào phòng.\"- Từ từ Thiếu Tộc Trưởng,còn sớm mà .\"\"*- Không được, chuyện này quan trọng.Tỷ cởi áo ra đi xong rồi lại ghế tre đó ngồi nha, sẽ hơi đau nên tỷ lấy cái khúc tre này cắn nha, cứ nhắm mắt lại đừng nhút nhích.*\"Trong lòng thầm nghĩ \" Sao Thiếu tộc trưởng có sở thích gì b*ến th*i vậy !\"Nhưng Lạc Mỹ vẫn nghe lời không dám cãi ,kéo mí da thú choàng bên vai xuống vậy là coi như kéo áo rồi vì cả áo và quần cùng chung tắm da thú.Sau đó Lạc Mỹ lại ghế tre ngồi,mắt nhắm chặt miệng cắn khúc tre.Về phần mình , Lạc Tinh tranh thủ dùng dao cắt máu mình đổ vào một chén nhỏ.Xong khâu chuẩn bị , Lạc Tinh bỏ viên Ngọc mãng xà vào một lỗ khảm hình thoi trên Lạc Hoàng TrượngXong rồi, sẽ đau nên tỷ cố chịu đựng đừng nhút nhích nhé.Lạc Mỹ hai mắt nhấm nghiền, dáng người đầy đặn, trước ngực cao vút hơi thở phập phồngLàm Lạc Tinh phải cố gắp lắm mới tập trung được, hai tay nâng trượng lên đọc nhẩm trong lòng câu chú ca ngợi Lạc Hoàng.Sau đó kích hoạt Thần ấn của mình,dùng đầu nhọn cây trượng chấm vào chén máu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chỉ trong sát na máu trong chén bị Trượng Lạc Hoàng hút hết,Cây trượng trở nên long lanh màu huyết ngọc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh làm theo cách cả ngày tập luyện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vẽ lên người Lạc Mỹ .Bắt đầu từ Nhân Trung vẽ lần ra b.ầu ng.ực của Lạc Mỹ vừa căng vừa tròn nên Lạc Tinh phải một tay vẽ một tay vịn để cho hình xăm uống lượn theo b.ầu ng.ực mà không lệch.Lạc Mỹ lúc này cảm giác vừa nhột vừa ngứa như hàng vạn con kiến cắn,sau đó lan tỏa thành cảm giác nóng rực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cố cắn chặt răng , mặc cho Lạc Tinh dày vò.Mười lăm phút sau vết xăm đã thành hình.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đó là một con rắn cuộn tròn làm ba vòng có đầu nằm ở giữa , đuôi vắt lên xương vai bên trái , giữa đầu rắn có vòng xoáy tròn với trung tâm là huyệt Đản Trung .Giữ chặt trượng Lạc Tinh hô : \" *Cẩn Tuân theo ý chỉ Lạc Hoàng ,Ta Lạc Tinh của Việt Tộc,dùng năng lực của ngài ,ban Nhân ấn Linh xà cho Lạc Mỹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cấp cấp như Luật Lệnh.*\"Sau đó đâm mạnh về huyệt Đản Trung của Lạc Mỹ , từ mũi Trượng đâm vào chảy ra dòng máu tươi lần theo đồ họa hình rắn bắt đầu đi một vòng rồi quay về vị trí cũ.Trượng Lạc Hoàng sáng rực lần cuối, rồi luồng sáng đó đã chạy hết qua Lạc Mỹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Người Lạc Mỹ bừng sáng màu trắng nhạt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến tận bây giờ Lạc Mỹ đã biết Lạc Tinh muốn làm gì ,cảm giác năng lượng đang tràn trong người Lạc Mỹ, có cảm giác như mơ.Nhìn lại Lạc Tinh, Lạc Mỹ không nói gì nhưng nước mắt ngập tràn khóe mắt.không ngờ mình cũng thành Thần ấn có cảm giác Lạc Tinh giờ bảo Lạc Mỹ đi chết Lạc Mỹ cũng lập tức chết ngay,Về Phần Lạc Tinh đang ngồi thở dưới đất , bên cạnh cây trượng cũng im lìm nằm đó, còn viên Ngọc Mãng xà đã tan ra như bột phấn .Cảm giác hưng phấn qua đi Lạc Tinh ngắm nhìn thành quả của mình mộtCon rắn sống động , như tô điểm cho cặp tuyết phong ngạo nghễ của Lạc Mỹ.Có chút ăn không tiêu Lạc Tinh cố dời mắt lên khuôn mặt Lạc Mỹ , có cảm thấy hình như Lạc Mỹ hình như càng xinh hơn, làn da mịn màng, ánh mắt tỏa sáng, bờ môi tràn đầy sức sống.\" Thôi xong rồi, mặc áo vào Mỹ tỷ ơi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Năng lực Nhân ấn của Mỹ tỷ là gì?\" Lạc Tinh đánh trống lãngLạc Mỹ đang đắm chìm trong nguồn năng lực mới.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghe Lạc Tinh hỏi mới lật đật kéo áo lên trên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hiện tại nhìn Lạc Mỹ nét mặt như hoa, khiến Lạc Tinh nhìn như say mê không rời mắt được.\" Tỷ cũng không biết, chắc phải thử mới biết.Có gì tỷ báo lại thiếu tộc trưởng sau.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng hình như có thanh âm nói là Xà Hình Bộ,tăng phúc tốc độ, mỵ hoặc !\"\"Ôi! Lại Là Thần kỹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sao mình không có mà tùy tiện vẽ nhân ấn lại có Thần Kỹ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xác xuất ngàn người có một mà cũng được \"Tỷ cứ ở lại tập cho quen với cơ thể, rồi hẳn về kẻo không thích ứng sức mạnh của mình làm đau mấy đứa bé.Mà Chuyện Tỷ có Nhân ấn đừng nói ai biết nha.Dặn dò xong Lạc tinh nằm xuống giường nghỉ vì thấm mệt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tinh thần lực cùng với Linh lực đã hao tổn hết do lần đầu chưa quen.Lúc mở mắt ra là trời sáng.Theo Thường lệ Lạc Mỹ mang sữa dê và thịt nướng vào.trong bộ Lạc thường chỉ ăn sáng và ăn chiều tối.vì ăn sáng xong phân công công việc đi làm tới chiều mới về rồi quây quần ăn chung.buổi sáng mọi người bình thường chỉ có thịt nhưng từ 5Tiểu đội trưởng trở lên thì có thêm sữa dê.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau nhiều đợt săn bắn số lượng dê đã hơn 30 con.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh muốn làm giống nên ra lệnh không được giết thịt dê cái mà giữ lại đợi gầy giống thêm, chỉ có dê đực để dành khi có tế lễ .Dê ở đây sắp thành tinh, toàn nhảy lên đọt tre cao vài chụt trượng để ăn lá nên khi bắt được cũng làm chuồng tre chắc chắn mới nuôi nhốt nó được.\"*Mọi Người chú ý tối nay sẽ có lễ tế Lạc Hoàng mọi người tắm rửa sạch sẽ để làm lễ *\"\"*-Bộ phận hậu cần làm một con Dê Đực cúng Lạc Hoàng nhé *\"\"-Vâng ạ !\"Lạc Tinh điều động một người tổ 3 qua tổ 4 thay cho Lạc Mỹ chăm sóc các em nhỏ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Rồi dẫn Lạc Mỹ vào rừng.Nhìn Lạc Mỹ đi với Lạc Tinh ,hai cô gái đang đi hái Măng nhìn theo:\"-Thôi rồi Thiếu tộc trưởng bị Lạc Mỹ cướp mất rồi.Sao người đó không phải là tỷ ,tỷ trẻ hơn mà.\"\"-Ai biểu tỷ không tấn công trước, cứ bảo Lạc Tinh còn trẻ sẽ không hiểu phong tình!Mà sao dạo này Lạc Mỹ ngày càng xinh đẹp động lòng người ,em là con gái mà còn mê..\"\"-Em thì biết gì chắc cái đó là công của Thiếu tộc trưởng tưới nước cho.\"\"-Tưới nước gì ,em cũng muốn đẹp.\"\"-Lạc Hải cứ chê em ko đẹp.hay em sang nhờ tộc truỏng tưới nước cho vài lần cho đẹp nha.\"\"-Thôi đi cô nương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">con nít biết gì mà đòi tưới nước.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">CÓ tưới chỉ tưới tỷ thôi nhé.\"\"Ai là con nít em 15 tuổi rồi 1 năm nữa có chồng được rồi nhá…\"Đang đi với Lạc Mỹ nhưng lạc Tinh thính tai cỡ nào lời hai cô gái đều nghe rõ, nhìn qua thấy lạc Mỹ ,\"*không biết tỷ ấy có nghe không ? nhưng sao má tỷ ấy đỏ hồng *\"Đến vị trí lạc Tinh kiểm tra năng lực mình lần trước trước với vô số tản đá lớn nhỏ đủ loại.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sao một buổi kiểm tra Thì sơ bộ kiểm tra được.Lạc Mỹ coi như khá ưu tú với Thần kỹ Xà Hoàng bộ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bình thường Lực nâng tầm 1050 cân, chạy hơn ba trượng trong một hơi thở .Lúc mở Thần ấnthì lực nâng 1100 cân, tốc độ đạt gần bảy trượng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khi phát động thần kỹ Xà Hoàng bộ thì sức mạnh giữ nguyên nhưng tốc độ tăng lên khoảng 6 phần là hơn mười một trượng..Nếu học Lạc Lôi Thuật xong thì lại tăng lên nữa.Nên Lạc Tinh truyền thụ cho Lạc Mỹ Lạc Lôi Thuật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Mỹ cũng là một mầm mống tốtcoi như thua Lạc Dương có địa cấp thần ấn phân nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng với Nhân ấn thì coi như không tệ rồi,vì với Lạc tộc thì tốc độ là tốt nhất.Thêm nữa khi mở ra Thần ấn Lạc Mỹ có thân hình dẽo kinh người có thể uốn éo theo mọi gốc độ tay, chân toàn thân.và không biết có phải tác dụng của ngọc Mãng xà không mà Lạc Mỹ toát lên hương thơm nhẹ khiến người đối diện như say đắm.Lạc Tinh không kìm nỗi Lòng mình nhưng đang giữa rừng , không thể làm gì vì sợ người khác thấy ,hoặc thú dữ đột nhiên xuất hiện đành phải cố nhịnchỉ dám động chân tay một chút….khuôn mặt của Lạc Mỹ cũng đỏ hồng không chịu nổi xin tha thứ rồi chạy về.Riêng Lạc Tinh tiếp tục dùng đá thử vẻ hình con Kim Báo..Chiều đến, Lạc Tinh y phục chỉnh chu đến sân giữa doanh trại .Mọi người đã có mặt đầy đủ..Sau một hồi diễn thuyết ý nghĩa buổi hội họp Lạc Tinh cho Lạc Hồng đi lên .Lạc Hồng hết sức phấn chấn, Giấc mơ được Thần ấn Nhân cấp 30 năm nay được bùng cháy trở lại.Nghi thức bắt đầu , Lạc Tinh lại gắn Yêu ngọc Kim Báo vào Lạc Hoàng Trượng sau đó bắt đầu đọc văn tế rồi dùng máu mình vẽ hình Kim Báo bằng một nét, sao khi vẽ xong đâm mạnh vào để máu Lạc Hồng lan ra phủ đầy hình xăm Kim Báo.Hoàn Thành.Lạc Hồng bỗng như to lớn thêm, cơ thể như có sức mạnh to lớn, cơ bắp cuồn cuộn nổi bật.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một con Kim báo lóe sáng trên ngực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sau đó tới toàn thân Lạc Hồng ánh màu đỏ nhẹ nhàng.Nhìn Lạc Hồng không thua kém gì các Lực sĩ Thể hình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh cũng phải ganh tỵ hình thể tuyệt đẹpMọi người hoan hô , mừng rỡ vì tộc đã có thêm một người có Thần Ấn.Không kịp kiểm tra sức Mạnh, Lạc Hồng lại quỳ trước Lạc Tinh :\"Cảm ơn thiếu tộc trưởng ban cho Nhân ấn,ân tình này Lạc Hồng ta sẽ không bao giờ quên.\"Lạc Tinh đỡ Lạc Hồng dậy..\"Thúc đừng bận tâm,cố gắng vì bộ lạc cống hiến là được..\"\"*Hôm nay ngày vui của Bộ Lạc mọi người tối nay cứ vui chơi thoải máisáng mai được nghỉ ngơi không phải làm việc trừ đội bảo vệ luân phiên thay nhau cảnh giới thôi* \"\"DẠ Thiếu Tộc trưởng.\" Hoan Hô.........!mọi người hưng phấn vì được nghỉ ngơi vui chơi.Dù ở thời kì nào , ngày nghỉ lễ luôn được mọi người hoan hô.</span></p>', 0, '2022-08-05 01:26:58', '2022-08-05 01:26:58');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(121, 77, 'Chương 10: 10: Kẻ Địch Đến', 'chuong-10-10-ke-dich-den', 10, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*Thù xưa nợ cũ chất chồng.Kẻ thù tìm dến một lòng chiến thôi.*Mọi người bắt đầu nhảy múa, ca hát bên ánh lữa .Riêng Lạc Tinh mệt quá nên về phòng ngủ, vừa vào phòng đã Thấy Lạc Mỹ đi vào theo.\"-Tỷ không tham gia vui chơi hả .\"\"-Trách nhiêm tỷ là chăm sóc Thiếu Tộc Trưởng mà .\"Lạc Tinh mệt mỏi nằm lên giường mặt kệ Lạc Mỹ bên cạnh xoa bóp vai và hay tay.\"-Kỳ quái sao ta cứ thấy Lạc Tỷ ngày càng xinh ra.\"\"-Thiếu tộc trưởng ngày càng dẽo miệng rồi.\"\"-Không tin Tỷ ra ngoài hỏi mọi người xem, ai cũng nói vậy.\"Lạc Mỹ biết không nói lại nên tập trung bóp chân.Lạc Tinh khá thoải mái, Tay nghề Mỹ Tỷ quả là số một.Nhưng không hưởng thu được Lâu , Lạc Mỹ đã phép đi chăm Lạc Na.Lạc Tinh đành phải đồng ý.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lòng bức rứt rồi dần chìm trong giấc mộng.Hôm sau Lạc Tinh chính thức rảnh rỗi, lục tìm ký ức truyền thừa tìm được một phương pháp tăng Linh Lực lên đó là Học đồ hình của bản Thân, sau đó điều khiển Linh Lực đi theo đồ án đó , khi hoàn thành một chu kì khép kinh thì linh lực sẽ tăng một tia.Lạc Tinh vừa buồn vừa vui , vui vì ra phương pháp tăng linh lực buồn vì Thần ấn của mình khá phức tạp.không biết lần từ đâu ra lộ tuyến chính xác để luyện thêm và nếu luyện thêm thì không lẽ người khác tập một chu thiên còn mình còn mình làm một phát 10 phút mới xong chu thiên tức là tốc độ chậm gấp 10 lần.Trước mắt phải biết rõ hình xăm mình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chuyện này phải nhờ người khác rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì hình xăm Lạc Tinh kéo từ trước ra sau thì sao mà tự mình nhìn được.Phải có người nhìn rồi vẽ ra.người đó phải đáng tin cậy, vì nếu sai một nét thì tác dụng sẽ giảm nhiều.Cái này chắc nhờ Lạc Mỹ nữa vì chỉ có Lạc Mỹ có Nhân ấn nên khi kích hoạt nhân ấn thì Tinh thần lực gấp 4 người thường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">có lẽ năng lực vẽ cũng tăng nhiều.Lạc Tinh quyết cho Lạc Mỹ theo bên cạnh mình luôn, còn Nhóm 5 cho người khác làm trung đội trưởng.Lạc Mỹ cho Lạc Na ngủ rồi vào phòng thì Lạc Tinh cũng chuẩn bị xong một bó tre vót nhỏ rồi đốt cháy cho thành than làm bút.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bên cạnh là cuộn da thú phơi khô có thể làm giấy.Lạc Tinh nói ý định của mình xong rồi đưa bút cho Lạc Mỹ tập vẽ trên đất trước, khi nào cầm bút ổn định rồi thì .Lạc Tinh cởi áo ra kích hoạt Thần ấn, Lạc Mỹ hết vòng ra sau rồi vòng ra trước nhìn tấm tắc\"Thiếu tộc trưởng thân hình thật là oai hùng.Không biết thiếu phu nhân nào có phúc hưởng đây.\"Lạc Tinh cười nói : \"Thiếu phu nhân chưa có ,hay Mỹ tỷ tối nay ở lại hưởng đi..\"Lạc Mỹ cười nhẹ , không nói gì tập trung vẽ.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau một giờ thì cũng đại công cáo thành.Nhìn Thần xăm của mình từ hai mảnh da thú ghép lại một bên là phía trước một bên phía sau.Nhập lại thành một vòng tròn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh trong lòng lại có chút hoài niệm kiếp trước.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cũng cái hoa văn này.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh vét hết tiền mua hi vọng đổi đời.Không ngờ đổi đời thật.Đổi cả thế giới sống lẫn thân phận sống.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trời đã cho cơ hội mình cũng phải phấn đấu vì giấc mộng.\"Túy chẩm mỹ nhân tất,Tỉnh ốc thiên hạ quyền.\"quay sang thấy Lạc Mỹ đang ngồi xoa tay .Lạc Tinh kiềm không được ôm người ngọc vào lòng, hôn một cái:\"Cảm ơn tỷ nha.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Để đền đáp tỷ hay là để đệ đấm bóp cho.\"Nói xong, không đợi Lạc mỹ phản hồi tay lạc Tinh đã lùa vào áo Lạc Mỹ mà xoa bóp.\"- Thôi đi, Lạc Na sắp thức rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hay đợi tối Tỷ qua nha.\"nói xong Lạc Mỹ hôn má Lạc Tinh một cái rồi chạy ra ngoài, Không thể làm gì khác ..\" Ài tưởng tượng so với hiện thực khác xa , ta còn phải cố gắng nhiều.\"Lạc Tinh bỏ đi tạp niệm , tập trung nhìn đồ họa, ở giữa là vòng tròn hình ngôi sao.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ở ngoài là các vòng tròn đồng tâmxen kẻ có hình thú, hình người, hình chim Lạc , viền ngoài cùng là 1 vòng như dây leo hoặc như hoa văn khung viền nào đó như một chuỗi hình thoi nối tiếp nhau…bây giờ quan trọng là theo suy luận để biết Thần Xăm bắt đầu đi theo hướng nào làm thể nào điểm bắt đầu đi ra và điểm kết thúc đi vào cho tất cả vị trí không được lặp lại hai lần.Lạc tinh thấy giống trò chơi quân mã cờ vua trong môn học ở trường đại học, sắp xếp sao cho quân mã đi qua tất cả vị trí trong bàn cờ mà không vị trí nào lập lại hai lần.và lần cuối trùng lần đầu để tạo một chu kì khép kín nữa chứ.Lạc tinh đi ra vách đá dể vẽ thử trước lên đá.thắm thoát tới chiều tối.lần nào cũng hoàn thành phân nữa đồ hình và không cách nào tiếp tục được..\"Thôi chuyện này gấp cũng không được , về thôi…\"Vài ngày trôi qua, Lạc Tinh vẫn dành nhiều thời gian tìm hiểu Thần ấn của mình nhưng không có kết quả.Xem là phải thư dãn chút.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vừa định đi ra, Lạc Hồng tìm đến Vẻ mặt không tốt lắm Thiếu tộc trưởng , có chuyện lớn rồi.Sau năm phút trình bài Lạc Tinh đại khái biết được căn nguyên.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Do Lạc Hồng đạt được Nhân ấn nên gan cũng lớn hơn.Dẫn đội mình đi săn xa hơn đạt được nhiều thú hơn .Nhưng hôm nay ,nhóm săn Lạc Hồng chỉ có hai tiểu đội 10 người đang sắp đuổi bắt được con Hươu To lạc đàn,Con Hươu khá thông minh chạy đông chạy tây khiến Lạc Hồng hăng máu mà rượt đi ngày càng xa vế hướng bắc,lúc sắp giết được con Hươu thì một nhóm Mãnh Thú Tộc tầm 40 tên xuất hiện bất ngờ Làm Lạc Hồng không xử lý kịp .Lạc Hồng tuy vừa mới đạt Nhân ấn khá mạnh mẽ nhưng bên bên Mãnh thú tộc kia cũng có một tênNhân ấn kìm chân Thành ra chỉ có thể yểm trợ cho hai tiểu đội rút lui.Kết quả có một tộc nhân chạy không kịp bị giết tại đương trường.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mọi người còn lại thì rút lui kịp nhưng cũng có hai người bị thương nặng.*\"- Thưa Thiếu Tộc Trưởng,lúc rút đi ta đã đi đường vòng để tránh bọn Mãnh Thú nhân phát hiện,Nhưng sợ không bao lâu chúng sẽ tìm ra.Bây giờ chỉ còn cách tránh đi xa hơn hoặc tìm cách giết hết bọn người kia tránh cho bọn chúng tập hợp lực lượng nhiều hơn.\"*Lạc Tinh trầm ngâm một Lúc, vốn là người hiện đại quen an cư lạc nghiệp, không thích lối sống du thủ du thực .Suy nghĩ thật kỹ , rồi triệu tập toàn bộ lạc lại.Lạc Tinh nói :\"*-Chắc mọi người đã biết chuyện gì sảy ra rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ta không nhắc lại.Thưa mọi người, Nơi đây là vị trí tốt nhất trong bán kính trăm dặm để định cư rồi.Vừa gần con suối có nhiều cá, nhiều nước rừng thì nhiều thú ,săn một buổi ăn nhiều ngày,vừa có tre giúp làm nhà,làm bàn ghế,dụng cụ sinh hoạt tiện lợi.vừa cập hai vách núi khiến dễ phòng thủ vừa có cửa hậu có thể rút lui.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên không thể bỏ đi lúc còn có thể.Nhất là chúng ta đã tốn hơn hai tháng trời cải tạo.có cả đàn dê gần trăm con nữa làm sao di chuyển được.*\"\"*Nên ta muốn tranh thủ chiến với chúng một trận .mọi người có ý kiến gì không.Chiến hay chạy *.\"\" Chiến ,chiến,chiến \"\"Nói to lên ta nghe không rõ \"\"CHIẾN ! CHIẾN ! CHIẾN! \" Trăm người như một vang lên chiến.\" *Tốt ,tuy ta nhỏ tuổi nhưng mà được Lạc Hoàng ban cho sức mạnh ,luận sức mạnh ta nghĩ không thua ai trong tộc.Hơn nữa ta đã đạt được tộc trưởng đích thân trao cho Lạc Hoàng Trượng cũng trao chức trưởng tộc.Nói xong lạc Tinh biến chiếc nhẫn về Lạc Hoàng Trường cầm trên tay đưa lên cao .Hi vọng mọi người xem lời ta là lệnh tuyệt đối thi hành , có vậy mới có thể thắng kẻ thù .*\"\" MỌI NGƯỜI CO Ý KIẾN KHÔNG? \"\" *KHÔNG,THƯA TỘC TRƯỞNG.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">*\"Lạc Hồng đứng ra hô,mọi người lập tức hô theo:\" Tộc Trưởng,Tộc Trưởng,Tộc Trưởng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"\" *Tốt ! giờ theo lệnh ta.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Toàn thể chuẩn bị chiến tranh , trong vòng ba ngày :Trung đội 1 nghe lệnh : cho 4 tiểu đội đi trinh sát bốn hướng dàn ra xa 2000 bước một người.Có gặp kẻ địch lập tức thổi kèn báo hiệu rồi tìm cách dẫn dụ chúng đi về phía cổng làng, khi đó mọi người lập tức rút về làng ngay ,không ham chiến chỉ phụ trách dẫn dụ là chính.*(Đây là loại kèn lạc tinh dùng sừng dê đực làm thành do bắt chướcKèn sừng trâu của đồng bào miền núi may mắn là dê ở đây khá to nên sừng cũng to bằng sừng trâu đủ làm kèn lệnh).*Trung đội hai nghe lệnh : lập tức đào xung quanh mặt trước làng một hào rộng để một người nhảy không qua .Ngay vị trí ta sẽ chỉ dẫn.Trung đội 3 cho người vót chông nhọn ghim dưới hào vừa đào.đồng thời dùng thân tre vót nhọn chỉa hướng phía từ phía bên này hào vừa đào.để khi kẻ địch phát hiện hào có chông cũng không nhảy qua được .Trung đội 4 đi nhặt đá nhỏ đá to khắp nơi về bỏ trước cổng làng vị trí ta chỉ định nhặt tầm ba ngàn viên .Khi nào xong thì tiếp tục chặt tre ,cây gãy,hay bất cứ cái gì đem lại bỏ phía bên kia hào xâu để hạn chế tốc độ địch nhân.Đội thứ 5 ai lớn chút , đi lấy thịt khô dữ trữ cắt ra từng phần nhỏ ,nước thỉ bỏ vào từng ồng tre riêng biệt ,để khi các đội khác cần ăn là có ngay.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\" *Ra lệnh xong Lạc Tinh chỉ định tiếp vài người mang đá lên các tháp canh để có địch dến thì ném xuống , Ngồi trước cổng chăm chỉ tranh thủ luyện tập.Hiện tại Lạc Lôi Thuật , Lạc Tinh vẫn dậm chân tại chỗ đạt nhị chuyển Lạc Lôi Bộ , tam chuyển Lạc Lôi quyết.Xem như tăng năm phần tốc độ di chuyển và khó tăng lên trong thời gian ngắn nên bỏ qua.Về tập năng cao linh lực Linh Đài Kỳ Thì chưa có đầu mối.Nên hiện tại Lạc Tinh chỉ có một cách ,thử làm Tiểu Lý phi đá : ném đá ,Tận dụng sức mạnh nâng 4000 cân của mình nếu ném một hòn đá 1 - 2 cân thì khác nào đại bác.Nghĩ là làm, Lạc tinh lại đống đá mà đội 4 nhặt mang lại thí nghiệm tập ném vào cây nhỏ cách trăm trượng, khi nào chuẩn rùi thì tăng lên ném tảng đá 200 trượng ,300 trượng.Cứ vậy tới ngày thứ hai vẫn chưa có động tịnh , Lạc tinh cho mọi người thay phiên nghỉ hai canh giờ .Sang ngày thứ ba , Bỗng nhiên có tiếng còi nhỏ ở hướng Bắc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đến rồi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh choàng dậy .Ra lệnh nhanh :Lạc Mỹ, tỷ dẫn lũ trẻ lên cuối làng nhanh.Nhớ cẩn Thận.\" Trung đội 4 tập trung lại hướng đông bắc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">cầm kèn theo nếu kẻ địch đi theo hướng đó thì thổi lên nghi bình và để bên này tiếp ứng.\"\"*Trung đội 3 cho hai đội mang giÁo tre đi hướng Tây bắc nếu kẻ địch đi tới thì tấn công nhanh cầm chânChờ trung đội 1 tiếp ứng.*\"*\"Hai đội còn lại ở lại phụ Trung đội 1 *\"\"-Rõ !\"Tầm mười phút sau trung đội 1 đã chạy tập trung về cổng làng..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">đi lên 5 thanh tre dài làm cầu vào làng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi Trung đội 1 rút về xong Lạc Tinh cho người rút hết 5 thanh tre đi.Đợi thêm một phút thì đã thấy bóng người Là Mãnh Thú Nhân rất dễ phân biệt vì Mãnh Thú Nhân có thân hình to cao và có đầu cạo trọc và săm hình thú lên trông rất dữ tợn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhìn Sơ lượt kẻ địch tầm 400 tên nhiều hơn dự định của Lạc Tinh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thường Mãnh Thú Nhân có cấu trúc quân đội rõ ràng.Một Đản Trung Kỳ sẽ dẫn đầu một nhóm 100 người.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Như vậy chắc là chỉ có 4 Đản Trung kỳ vẫn còn sức Liều mạng.Xác nhận được 4 kẻ cầm đầu mặc đồ sặc sở nhất , chạy trước nhất .Lạc Tinh lúc luyện tập ném có thể ném vị trí ba trăm trượng không trật phát nào,nhưng mục tiêu di động thì chưa biết,đợi tới khi còn hơn Hai trăm trượng ,Lạc Tinh bắt đầu vận sức ném đi , phập viên đá bay nhanh trúng đầu 1 tên cầm đầu , Do hắn ta sơ ý nên bị trúng đạn, đầu tên thú nhân vỡ tung, thâm mình còn theo quán tính tiến về trước.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ba tên cầm đầu còn lại lập tức phản ứng nhanh.Lúc chạy trái lúc chạy phải đồng thời núp sau lưng mấy Mãnh Thú Tộc khác không còn chạy trước nữa .Lạc Tinh không còn nhìn kịp nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mặc kệ, Lạc Tinh chụp ném liên tục, kẻ trúng đầu người trúng vai, chân, tay một viên đá phi đi có thể làm té ngãy cả 3,4 người mới dừng lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">khoảng cách 400 bước,300 bước ,200 bước có phân nữa mãnh Thú tộc đã nằm xuống ,150 bước cuối lạc tinh không ném kịp nữanhưng đã vào tầm ném của hơn ba mươi người trung đội 1 và 2 đội của trung đội 2 ,tuy mức xác thương thấp nhưng cũng hơn 20 người bị té nằm tại chỗ ngay loạt đầu tiên , loạt thứ 2 té thêm mười người nữa.\"Tiêu giệt chớp nhoáng một tên Đản Trung kỳ nữa thì may ra mới chiến Thắng\"Lạc Tinh thì cầm một cây tre vót nhọn chuẩn bị sẵnMãnh thú tộc chỉ trong mười hơi thở mà thiệt hại nặng nề..</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">chúng vẫn nhât quyết tiến tới sát cổng\"- Phập ,phập \" Hơn 20 người lọt vào bẫy chông ,những nguời phía saU đang đà tiến tới nhảy lên thì vướng hàng rào tre...nhưng có ba người nhảy cao qua được là ba Đản Trung Kì.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh nắm chắc cơ hội Thần ấn và Lạc Lôi thuật phát động cùng lúc .Một Đản Trung Kỳ to lớn vừa nhảy qua chưa chuyển hướng được đã bị một cây tre tốc độ siêu nhanh đâm tới hắn ta chỉ kịp dùng tay đỡ ngay ngực nhưng cũng bị nữa nhánh tre đâm xuyên và ghim cả người xuống hào,Sau đó là mưa đá dến từ 2 đội Trung đội 2 kết quả là hắn ta chưa kịp phát động tuyệt kỹ Thần ấn đã chết.Còn Lại hai Đản Trung Kỳ nhảy được vào căn cứ.Không cần Lạc Tinh ra lênh , Lạc Hồng cầm búa đá tấn công 1 tên đồng thời phát động kỹ năng Lạc Lôi Thuật dược Lạc Tinh truyền cho, tuy chỉ mới tăng hai phần nhanh nhẹn nhưng mà cái Lạc Hồng dựa vào là sức mạnh không phải nhanh nhẹn vì Thần ấn của Lạc Hồng cộng thêm 7 phần sức mạnhTuy không có thần kỹ nhưng vì do hậu tích bộc phát trước khi đạt Nhân ấn thì Lạc Hồng đã có sức mạnh 800 cân giờ bình thường đã lên 1600 cân công thêm thần ấn đã tăng vượt lên 2700 cân cũng khá mạnh mẽ trong các Đản Trung kỳ .\"Xem Búa của Hồng Gia ta !\" chỉ cần đối phương không kịp né tránh mà cứng đối cứng với Lạc Hồng là cầm chắc thiệt thòi.Riêng tên còn lại có vẻ mạnh hơn thì được Lạc Tinh Tiếp đón.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đối phương tay cầm 1 khúc xương động vật to tròn , nhọn như ngà voinhưng chắc chỉ có voi Mamut mới lớn vậy.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc tinh dùng cây tre gần đó đón đỡ một lần mà cây tre như nát ra , tay Diệp Tinh tê rần.Đối phương Đản Trung kì đỉnh phong rồi hay sao mà lực đánh vượt xa bốn Ngưu Lực.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh lấy làm kinh hãi..</span></p>', 0, '2022-08-05 01:27:24', '2022-08-05 01:27:24');
INSERT INTO `chapter` (`id`, `truyen_id`, `tieude`, `slug_chapter`, `chuong`, `tomtat`, `noidung`, `kichhoat`, `created_at`, `updated_at`) VALUES
(122, 77, 'Chương 11: 11: Man Đao', 'chuong-11-11-man-dao', 11, NULL, '<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trí cao gắng sức vẫy vùng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mãnh Tượng kiêu hùng cũng phải táng thân.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh Tộc Trưởng như thần</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thôn dân bị bắt, nên cần giải nguy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">.....</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Bản thân Lạc Tinh tiếp cận Linh Đài kỳ sơ cấp, nên đạt 4 Ngưu lực, tưởng là sức mạnh mình sẽ cao hơn đối phương, có thể bù đắp chênh lệch kinh nghiệm chiến đấu không bằng đối phương.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng không ngờ đối phương cường hãn như thế.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từng cú đánh ngang, quét dọc, đập xuống mạnh mẽ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh đành đổi chiến thuật, chủ trương dùng Lạc Lôi Quyết né tránh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mà không dám trực diện tấn công, một bên đánh một bên tránh.Tên Mãnh Thú tộc kia trong lòng càng dậy sóng hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn ta đã 52 tuổi xưng danh Mãnh Tượng, nổi danh của Mãnh Thú Tộc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Từng chỉ huy một nhóm 1000 người, nhưng hôm nay do trải qua vài trận chiến trước đó, giờ chỉ dẫn theo 500 người, vì hắn đã chia quân đang vận chuyển tù nhân, cũng như người bị thương về bộ Lạc.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn ta có Sức mạnh Đản trung Kỳ đỉnh phong, cộng thêm học Kỹ năng Bạo Thú Thuật tăng sức mạnh gấp đôi.Tưởng là một chiêu giết địch, nào ngờ chỉ đánh nát được cây tre, chứ không nát tay của đối phương được.Mà Tre thì ở dưới đất một đống lớn, nát cây này Lạc Tinh lại tìm đến cây khác.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một lúc sao Mãnh Tượng có vẻ nóng lòng, vì Thần ấn mình sắp không duy trì được, do dùng võ kỹ liên tục.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhìn lại đoàn quân 500 người, giờ còn có vài chục người còn chiến đấu được hỏi sao không tức, cho dù giờ giết hết nhóm Lạc Tinh thì về cũng bị sự trừng trị của bộ tộc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Càng nóng lòng, nên Hắn ta càng sơ xuất, chỉ lo công không lo thủ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Lạc Lôi bộ.\" - Cơ hội đến rồi, Lạc Tinh nắm được tiết tấu ra chiêu thức của tên kia.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Nhất Chuyển.\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh chớp thời cơ xuyên qua trái, vừa đủ né cây Ngà Voi, rồi tấn công vào cổ đối phương.</span><br />\r\n&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; color: rgb(43, 43, 43); font-family: &quot;Palatino Linotype&quot;, Arial, &quot;Times New Roman&quot;, sans-serif; font-size: 26px; background-color: rgb(244, 244, 244); display: flex;\">&nbsp;</div>\r\n\r\n<p><span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Mãnh Tượng bất ngờ, nhưng với kinh nghiệm dày dặn, vẫn kịp buông vũ khí hai tay che lại cổ là vị trí yếu hại.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Nhị Chuyển.\" - Lạc Tinh tiếp tục thực hiện phóng tiếp qua bên phải với tốc độ siêu nhanh.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Phập.\" - Con dao răng sói của Lạc Tinh không còn gì ngăn trở, đâm thẳng vào cổ họng, một lỗ to bị khoét ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hắn ta hốt hoảng, hai tay bịn lại cổ, ngăn cản máu chảy ra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Đỡ liên hoàn đập nào.\" - Lạc Tinh tranh thủ lộn một vòng dưới chân cầm thêm cây tre, đập liên tục vào người Mãnh Tượng đang nằm...!Không có kì tích gì phát sinh, sau mười nhát đập liên hoàn, hắn ta đã chết do mất nhiều máu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Xoay qua bên cạnh thì Lạc Hồng không biết bằng cách nào cũng giết được tên đản Trung kỳ còn lại, nhưng bản thân đã bị thương không đứng vững.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Hay lắm thúc.\"</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh chuyển mắt nhìn qua bên kia.Tình hình khá tốt, do có hào xâu và cọc tre làm bảo vệ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phía trên hai tháp canh lại có người quăng đá xuống liên tục.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">bọn Mãnh thú tộc bị chậm nhịp tấn công, nên chỉ hơn ba mươi người của trung đội 1 và 2 có thể kìm chân được bọn chúng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Chạy mau!\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi thấy Đội trưởng của mình tử trận, thì bọn mãnh thú tộc chỉ còn hơn 60 tên, lập tức nhanh chóng chạy trốn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Phần lớn chúng hướng trung đội Hai đang phục kích.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không chờ Lạc Tinh chỉ huy, cả đám bị trung đội Hai tấn công tới tấp.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thần hồn nát thần tính, bọn chúng tiếp tục đổi hướng chạy qua hướng khác, thì bị hàng loạt tiếng kèn của trung đội Ba thổi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Sao lại còn phục kích?\" - Lũ chúng hoảng sợ, bắt đầu mạnh ai nấy chạy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"A A A, đừng dẫm lên người ta.\" - Mấy tên Mãnh Thú tộc la lên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Qúa hoang mang, chúng không còn biết phương hướng, dẫm đạp cả lên nhau.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh và hai mươi người đội Một đã kịp thời chạy đến.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ vài phút sau, chỉ còn hai tên xông ra vòng vây chạy đi...!là Do Lạc Tinh cố ý thả để xem địa điểm tập trung của nhóm Mãnh thú tộc ở đâu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Còn lại bị giết hết.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Ai còn khỏe mạnh theo sau ta, số bị thương về bộ lạc.\" - Lạc Tinh chậm rãi đi theo hai tên vừa thoát vây.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Băng rừng hai ngày, theo dấu ở khoảng cách 200 dặm thì Lạc Tinh cũng tìm đến được vị trí Mãnh Thú Tộc trú đóng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ở đây chỉ còn khoảng hơn trăm tên Mãnh Thú tộc.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Bổn cũ soạn lại vậy.\" - Lạc Tinh bò lên cây cao chờ đợi đúng lúc tên Đản Trung kì đi trong tầm ném...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Chíu!\" Viên đá b.ắn ra lao nhanh như đại bác.Tên chỉ huy này chưa kịp làm gì, đã bị oanh ngất đi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh tranh thủ tràn tới đâm cho một nhát.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Có kẻ địch tập kích...!\" - Tiếng báo động vang lên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng cảnh báo muộn rồi, chỉ còn trăm tên người thường, Lạc Tinh xông tới tốc độ siêu nhanh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chỉ sau vài phút, hơn trăm người cũng đã ngã ra, Những người bị thương thì bị nhóm người đi theo Lạc Tinh xử lý.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi đi vào trong, phát hiện có hơn 120 người Việt tộc bị trói chân, nằm la liệt trong hang đá gần đó.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Cứu người trước.\" Lạc Tinh ra lệnh, mười người đi tới bắt đầu cởi trói ra.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhìn Lại thì là người quen đó là nhóm người do Hộ pháp Lạc Văn, Lạc Võ dẫn đầu.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lúc trước chia nhau trốn để đánh du kích.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng vị trí trốn bị phát hiện dẫn dến chết hơn phân nữa, Lạc Văn, Lạc Võ cũng hi sinh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"-Các ngươi còn sức đi không? ai không đi nổi thì nói.\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Người đâu mang vài dây mây về làm giá võng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Chúng ta chở người bị thương về\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Dạ Tộc trưởng!\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Qua ba ngày băng rừng, thì Lạc Tinh cũng về tới bộ lạc và được chào đón như anh hùng...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó Lạc Tinh bắt đầu kiểm tra lại tình hình, cả hai trận chiến chết mất sáu người, khoảng ba mươi người bị thương so với kẻ địch thì xem như là thành công vượt bật.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng Lạc Tinh trong lòng không vui lắm, vì bản thân mình lần đầu tiên giết người, không phải một hai người mà tới vài trăm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Tối nằm ngủ không khéo mơ thấy ác mộng.\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhìn lại trong bộ lạc hiện tại không biết vài tháng nữa ai sẽ còn ai sẽ mất.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thế giới này quá điên cuồng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Phải tìm các nhóm Việt Tộc còn lại không thôi tương lai chờ họ chỉ là cái chết.\" - Lạc Tinh suy tính - \"Nhưng trước đó phải nâng lên trình độ của Bộ Lạc mới được.\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh quyết định tinh chỉnh lực lượng lại.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần trước cứu được hơn 120 người trong đó có gần 90 phụ nữ và hơn 30 nam tất cả điều tuổi tầm 30 tới 50 có lẽ những trai tráng đã bị giết sạch.Thành ra ở địa phương này giờ có hơn môt trăm bốn mươi nữ và hơn tám mươi nam.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh bắt đầu chia lại đội ngũ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Mọi người chú ý ta muốn chia mọi người lại thành hai đại đội.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đại đội Một toàn là nam phụ trách chiến đấu, đặt tên cho oách là Thần Ưng đoàn do Lạc Hồng Chỉ Huy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">sau đó trong Thần Ưng đoàn lại tách làm hai trung đội mỗi trung đội 41 người, mỗi trung đội lại tách ra 8 tiểu đội, mỗi tiểu đội gồi một đội trưởng và 4 đội viên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đại đội Hai toàn là nữ, tên Thần Phượng đoàn, do Lạc Hương chỉ huy đồng thời chia ra làm ba trung đội, mỗi trung đội phụ trách săn bắt các loài thú nhỏ, hái trái cây, bắt cá...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một trung đội phụ trách làm các vật liệu như vót tre làm chông, làm hàng rào, xây dựng, sửa chữa nhà cửa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trung đội còn lại phụ trách nấu nướng, may vá các loại áo giáo, dụng cụ sinh hoạt và chăm sóc trẻ nhỏ.\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Rõ, tộc trưởng!\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó, Lạc Tinh khuyến khích đi xa chặt tre.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhất là nơi xa con suối để tạo môi trường cho thú nhỏ, tránh cạn kiệt nguồn thức ăn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đồng thời qui định mỗi bụi tre chỉ được chặt số lượng hạn chế.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Để cho bụi tre dễ phát triển hơn, tránh bị chặt nhiều sinh ra tàn lùi không mọc lên kịp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hái măng cũng chỉ là tỉa măng, không phải cứ mọc mười cây măng thì hái hết, mà hái mỗi khóm phân nữa chừa phân nữa.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Cho nó phát triển rộng rãi để sau này còn tre mà dùng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Riêng Lạc Mỹ, Lạc Tinh chỉ định làm Tham mưu trưởng, chỉ là chức vụ trống nhưng hưởng đãi ngộ bằng với hai đoàn trưởng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hết cách rùi, cho dù bị người khác chỉ trỏ này nọ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng ai biểu Lạc Mỹ là người của Lạc Tinh.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Coi như nhiệm vụ chỉ có chăm sóc Lạc Tinh, và thay lạc Tinh hạ các lệnh cần thiết đến hai đại đổi trưởng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lần trước chiến đấu tác dụng của Việt Võ Đạo quyền có tác dụng rõ rệt, tăng sức chiến đấu của mọi người lên một tầm mới, nếu một chọi một thì không ai bằng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh quyết định mỗi người đại đội Thần Ưng đoàn trong trường hợp không có nhiệm vụ nhiều, thì phải tập luyện một canh giờ trở lên.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đội trưởng tập một canh giờ rưỡi, Trung đội trưởng phải tập hai canh giờ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Ai không muốn tập nhiều thì cứ xin hạ chức để người khác lên thay thế.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đoàn trưởng thì tùy tình hình.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vì phải xử lý nhiều việc, nên không giới hạn thời gian tập được.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Hồng thì Lạc Tinh hoàn toàn yên tâm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Riêng Lạc Hương lạc Tinh ko yên tâm, vì Lạc Hương tuy khá mạnh nhưng không phải là Đản Trung Kỳ thì cũng không tác dụng gì.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lực tay nàng tầm 600 cân, tốc độ bộc phát 4 mét mỗi giây, chỉ có thể mạnh hơn người bình thường một chút.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhưng không biết làm sao để tạo Nhân ấn cho Lạc Hương, trừ khi tìm được cùng loại Ngọc Mãng xà lần trước.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không thì bắt sống một loài vật mạnh mẽ để dùng máu vẽ Nhân Ấn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nghĩ là làm nên Lạc Tinh tìm dến Lạc Hồng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Lạc Thúc cho vài đội đi tìm Yêu Thú mạnh mẽ, để thử xem có thể tạo được thêm Nhân Ấn không.\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Rõ thưa tộc trưởng\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"À, thúc cho người đào thêm hào xâu và lớn hơn đi, lần trước nó tỏ ra rất hiệu quả.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thúc cũng đừng có nghiêm quá, cứ xưng hô bình thường đi\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Rõ\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó, vài ngày là Hào đã đào xâu hơn và dài hơn cả mười trượng.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh dự định khi nào đường hào ra tới suối gần con thác, sẽ dẫn nước vào vừa có nước sinh hoạt vừa cắm cọc tre ở dưới cho kẻ địch không phát hiện.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đồng thời có thể nuôi cá bớt lệ thuộc vào con suối.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi nào cá bắt là cá con thì sẽ thả vào đó nuôi.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Để khi nào có biến động còn có cá mà ăn, Không cần đi xa.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nhìn số đất được đào lên toàn là đất sét tốt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Sao ta không tận dụng nó luôn?\"- Lạc Tinh tìm Lạc Hương và hướng dẫn - \"Phần đất đào lên thì một phần để đắp tường thành xung quanh làng, một phần Tỷ chọn phần đất sét tốt để cho vài tiểu đội nữ nắn thành hình dạng ta sẽ vẽ ra, đồng thời hướng dẫn trực tiếp cách làm...\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau đó là một loạt nồi đất nhỏ có, lớn có, hình như cái lu cũng có được tạo ra.</span><br />\r\n<br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sẵn hào xâu Lạc Tinh cho bỏ các đồ vật đó xuống, Bỏ than tre xuống rồi đấp kín lại đốt trong vòng ba ngày đêm.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi mở ra cũng có một số hư hỏng nứt vỡ nhưng một số hoàn hảo có thể làm đồ chứa nước và để nấu nướng được.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Kệ xấu mà dùng tốt là được.\" - Lạc Tinh tự an ủi mình dù sao canh củi lửa, cách nắn đất, phương pháp làm lò không phải tự nhiên là biết phải qua nhiều giai đoạn thực tiễn rồi rút ra kinh nghiệm...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Dù sao hiện tại mỗi lần nấu canh thì chặt cả 10 cây tre lấy ống nấu, hết sức phung phí hiện tại tre rừng còn nhiều nhưng theo thời gian cung sẽ vượt cầu lúc đó thì vất vả...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Quan trọng là có nồi lớn dễ dàng nấu thức ăn hơn...</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Chậm nhất một tháng nữa, Phải đi tìm thử Lạc Dương\".</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trong lòng tự nhủ như vậy.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Một hôm, có thông tin tìm được một loại dây mây hết sức dẻo dai, lại có độ đàn hồi tốt.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Khi được thông báo Lạc Tinh mừng rỡ lập tức đi kiểm tra.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Có lẽ thích hợp làm cung.\" - Lại một bản vẻ hướng dẫn được tạo ra dùng tre già làm thân cung, dây mây xe lại làm thân cung, tre vót làm mũi tên.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Hai ngày sau, cung tên hoàn thành chế tạo ra sau nhiều lần sửa chữa phương pháp.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Có cung tên rồi thử nghiệm thôi.\" - Lạc Tinh hưng phấn chạy đi bắn thử.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau một buổi kiểm tra, Tầm bắn cũng được 250 mét, nhưng xác thương mạnh nhất thì tầm 180 mét, tức khoảng gần 400 bước chân người lớn, dùng săn thú nhỏ hoặc là bắn người bình thường thì khá ổn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh giao cho bên đại đội Lạc Hương phụ trách tìm cách nâng cấp và làm thêm cho mỗi người một cây cung.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Tin là qua thời gian cải tiến tầm bắn và độ chính xác sẽ còn nâng cao hơn.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Vốn dĩ đá ném tuy sát thương cao, nhưng tầm ném thấp, không di chuyển xa được và cũng không chuẩn xác lắm.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Kẻ địch trúng đá phần lớn té ra do đau đớn nhưng sẽ có thể đứng đậy chiến đấu tiếp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Trừ khi trúng đầu thì mới có thể gi*t ch*t tại chỗ.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Đương nhiên với Lạc Tinh Thì Cung Tre không có tác dụng gì.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sức mạnh 4000 cân mà kéo thì cung chỉ có gãy đôi, mà kéo nhẹ thì sát thương thấp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Không bằng phi đá tảng to ra sức sát thương cỡ đại bác.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Lạc Tinh lại dành thời gian của mình mài lại cây Ngà voi to là vũ khí của Mãnh Tượng.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sau khi giết được thì Lạc Tinh lấy làm của riêng và cho người mài nhỏ lại nhưng thấy vẫn còn to lớn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Nên khi nào bản thân rảnh rỗi, thì lại tự tay lấy ra mài, dùng sức lực của Lạc Tinh mài Đao thì cũng khá tốt.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Sao năm ngày chăm chỉ mài bên dòng suối, thì nó đã thay đổi, giống như con đao cong hình lưỡi liềm, nhưng còn hơi lớn, Kệ dùng được là được.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">\"Đao thì phải có tên, tên là Man Đao đi.\"</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Thơi kì này không có mỏ Sắt, mỏ Đồng, cho dù có Lạc Tinh cũng không biết cách tinh luyện.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Với lại do Thú ở nơi này toàn dạng đột biến, nên xương thú thật sự gắn chắc không thua kim loại, làm vũ khí cũng không tệ, chỉ tiếc khó mà ghèn giũa theo hình dáng thuận lợi thôi.</span><br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">( Tại hạ đang tinh chỉnh đơn vị từ trượng về mét cho dễ hiểu.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">nên có thể các chương kế chưa fix kịp.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(244, 244, 244); color:rgb(43, 43, 43); font-family:palatino linotype,arial,times new roman,sans-serif; font-size:26px\">Độc giả thông cảm.)</span></p>', 0, '2022-08-05 01:27:55', '2022-08-05 01:27:55');

